INSERT INTO `comment` (`id`, `idUser`, `idTin`, `NoiDung`, `Ngay`) VALUES
(1, 3, 3, 'Đà Lạt thành phố mộng mơ', '2021-11-04 01:17:46'),
(2, 3, 3, 'Yêu màu vàng rực rỡ này !', '2021-11-04 01:18:01'),
(3, 3, 16, 'Thế mỗi lần ai đó gọi hắn nghe sẽ sao nhỉ? Hihi..', '2021-11-04 01:19:39'),
(4, 3, 17, '40 mà nhìn như 2x-3x vậy', '2021-11-04 01:20:23'),
(5, 3, 17, 'Ivanka quá đẹp so với độ tuổi của cô! vẻ đẹp khiến nhiều cô gái trẻ cũng phải ghen tỵ.', '2021-11-04 01:20:42'),
(6, 3, 17, 'Thật là khả ái', '2021-11-04 01:20:56'),
(7, 3, 32, 'Thật đáng tiếc. Anh chồng đẹp trai, tương lai sáng lạn, tự chôn vùi cuộc đời mình chỉ vì chọn nhầm vợ.', '2021-11-04 01:21:56'),
(8, 3, 32, 'Cô ấy rất xinh, có một sự nghiệp vững chắc sau khi cai nghiện nhờ nỗ lực của bản thân và của chồng. Tuy nhiên, cô ấy đã ngoại tình. Phụ nữ khi đã ngoại tình là vô cùng nguy hiểm.\r\nThương thay cho kiếp đàn ông!', '2021-11-04 01:22:14'),
(9, 3, 34, 'Tướng sát thê là có thật.', '2021-11-04 01:22:52'),
(10, 3, 45, 'Cố gắng lên cô gái bé nhỏ nhưng đầy nghị lực. Chúc con nhanh chóng khỏe mạnh và gặp nhiều may mắn!', '2021-11-04 01:23:25'),
(11, 3, 45, 'Bé An ơi con thật là kiên cường,đã tạm gọi là chiến thắng bệnh tật sau 3 năm quá nhiều khắc nghiệt như thế,đọc đến đâu xót đến đó,mong rằng chữ An được ba mẹ đặt cho sẽ mang bình an bên con cả đời!', '2021-11-04 01:23:38'),
(12, 3, 39, 'Tiếc cho em quá, ước gì các cặp đôi sẽ mãi yêu nhau như ngày mới cưới!', '2021-11-04 01:33:19'),
(13, 3, 39, 'Những ngã rẽ của cuộc đời mà chỉ người trong cuộc mới nhìn thấy \"biển báo \", còn lại là sự bất ngờ.', '2021-11-04 01:33:29'),
(14, 3, 38, 'Mãi mãi là bao lâu?', '2021-11-04 01:34:07');

