  `urlHinh` varchar(255) DEFAULT NULL,
  `Ngay` timestamp NULL DEFAULT NULL,
  `idUser` int(11) NOT NULL DEFAULT 0,
  `Content` text DEFAULT NULL,
  `idLT` int(11) NOT NULL DEFAULT 0,
  `idTL` int(11) DEFAULT 1,
  `SoLanXem` int(11) DEFAULT 0,
  `TinNoiBat` tinyint(1) DEFAULT 0,
  `AnHien` tinyint(1) DEFAULT 1
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `tin`
--

INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(1, 'Du lịch Quảng Ninh thích ứng trong giai đoạn bình thường mới', 'Du-Lich-Quang-Ninh-Thich-Ung-Trong-Giai-Doan-Binh-Thuong-Moi', '<p>Nhiều đại diện trong ng&agrave;nh du lịch cho rằng Quảng Ninh c&oacute; cơ hội l&agrave;m mới ch&iacute;nh m&igrave;nh v&agrave; định hướng ph&aacute;t triển du lịch trong t&igrave;nh h&igrave;nh mới.</p>\r\n', 'du-lich-quang-ninh.jpg', '2021-10-31 00:11:11', 1, '<p>Sở Du lịch tỉnh Quảng Ninh vừa phối hợp Tập đo&agrave;n Sun Group tổ chức hội thảo &quot;Du lịch Quảng Ninh giai đoạn b&igrave;nh thường mới: Cơ hội v&agrave; Th&aacute;ch thức&quot; v&agrave;o s&aacute;ng 30/10. Hội thảo l&agrave; diễn đ&agrave;n để c&aacute;c cơ quan quản l&yacute; nh&agrave; nước về du lịch c&ugrave;ng c&aacute;c Hiệp hội Du lịch, doanh nghiệp trong ng&agrave;nh c&ugrave;ng thảo luận, đ&aacute;nh gi&aacute; về cơ hội v&agrave; th&aacute;ch thức của du lịch Quảng Ninh. Đồng thời, c&aacute;c đại biểu cũng như trao đổi về định hướng v&agrave; giải ph&aacute;p đột ph&aacute; để đưa ng&agrave;nh c&ocirc;ng nghiệp kh&ocirc;ng kh&oacute;i của v&ugrave;ng di sản sớm phục hồi, bứt ph&aacute; nhanh, mạnh, trong điều kiện b&igrave;nh thường mới.</p>\r\n\r\n<p>Mở đầu hội thảo, b&agrave; Cao Ng&acirc;n H&agrave; - Trưởng ban Marketing (Tập đo&agrave;n Sun Group), nhận định: Nh&igrave;n chung du lịch ở trạng th&aacute;i b&igrave;nh thường mới sẽ c&oacute; xu hướng du lịch gi&atilde;n c&aacute;ch, ưu ti&ecirc;n c&aacute;c điểm đến kh&ocirc;ng đ&ocirc;ng đ&uacute;c; ưu ti&ecirc;n c&aacute;c trải nghiệm an to&agrave;n; h&agrave;nh tr&igrave;nh kh&ocirc;ng chạm; du lịch bền vững c&oacute; tr&aacute;ch nhiệm Wellness Travel; ra quyết định nhanh hơn, c&oacute; t&iacute;nh linh động cao. Nhờ lợi thế về tốc độ ti&ecirc;m chủng cao; chiến lược mở cửa du lịch c&oacute; kiểm so&aacute;t chặt chẽ; m&ocirc; h&igrave;nh du lịch an to&agrave;n, biệt lập... Quảng Ninh đang kh&ocirc;ng chỉ khẳng định vị thế điểm đến gi&agrave;u tiềm năng m&agrave; c&ograve;n c&oacute; cơ hội phục hồi du lịch tốt bậc nhất cả nước.</p>\r\n\r\n<p>&Ocirc;ng Phạm Ngọc Thủy - Gi&aacute;m đốc Sở Du lịch Quảng Ninh, cho biết b&ecirc;n cạnh những ảnh hưởng lớn của đại dịch Covid-19, ng&agrave;nh du lịch Quảng Ninh cũng nh&igrave;n thấy &quot;trong nguy c&oacute; cơ&quot;, l&agrave;m mới ch&iacute;nh m&igrave;nh v&agrave; c&oacute; những định hướng ph&aacute;t triển du lịch trong t&igrave;nh h&igrave;nh mới. &quot;Đ&aacute;nh gi&aacute; sức khỏe doanh nghiệp l&agrave; quan trọng nhất, l&atilde;nh đạo tại Quảng Ninh kh&ocirc;ng chỉ ban h&agrave;nh c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ tối đa cho doanh nghiệp vượt qua kh&oacute; khăn do dịch bệnh m&agrave; c&ograve;n tạo điều kiện để mở cửa du lịch trở lại, bởi chỉ khi hoạt động v&agrave; đ&oacute;n kh&aacute;ch, doanh nghiệp mới c&oacute; thể kh&ocirc;i phục v&agrave; ph&aacute;t triển&quot;, &ocirc;ng Thuỷ cho biết.</p>\r\n\r\n<p>Đồng t&igrave;nh với &yacute; kiến l&atilde;nh đạo Sở Du lịch Quảng Ninh, theo &ocirc;ng Nguyễn Qu&yacute; Phương - Vụ trưởng Vụ Lữ h&agrave;nh, trước những ảnh hưởng nặng nề v&agrave; l&acirc;u d&agrave;i của đại dịch Covid-19 đối với ng&agrave;nh du lịch, Tổng cục Du lịch đ&atilde; v&agrave; đang nỗ lực r&agrave; so&aacute;t, đ&aacute;nh gi&aacute; để tiếp tục kiến nghị Bộ Văn h&oacute;a - Thể thao - Du lịch v&agrave; Ch&iacute;nh phủ tăng cường c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ cho doanh nghiệp v&agrave; người lao động k&eacute;o d&agrave;i đến hết năm 2023. Tuy nhi&ecirc;n, &ocirc;ng Phương cũng khẳng định việc mở cửa đ&oacute;n kh&aacute;ch trở lại an to&agrave;n l&agrave; &quot;ch&igrave;a kh&oacute;a&quot; để gi&uacute;p doanh nghiệp hồi phục.</p>\r\n', 1, 1, 2, 1, 1),
(2, 'Hà Giang mở thêm các sản phẩm du lịch mạo hiểm', 'Ha-Giang-Mo-Them-Cac-San-Pham-Du-Lich-Mao-Hiem', '<p>Tỉnh nghi&ecirc;n cứu khai th&aacute;c bộ m&ocirc;n đu d&acirc;y mạo hiểm, bay khinh kh&iacute; cầu, b&ecirc;n cạnh sản phẩm d&ugrave; lượn, đường chạy sẵn c&oacute;.</p>\r\n', 'hagiang1-5368-1635519941.jpg', '2021-10-31 00:15:22', 1, '<p>Đ&acirc;y l&agrave; một trong những kế hoạch ph&aacute;t triển du lịch, nhằm thu h&uacute;t du kh&aacute;ch sau Covid-19. C&aacute;c sản phẩm đang được nghi&ecirc;n cứu ph&aacute;t triển l&agrave; đi bộ, đu d&acirc;y mạo hiểm trong c&aacute;c hang động tại huyện M&egrave;o Vạc,Y&ecirc;n Minh; đi bộ, leo n&uacute;i, đi xe đạp ở khu vực x&atilde; Linh Hồ, huyện Vị Xuy&ecirc;n; tổ chức c&aacute;c buổi bay khinh kh&iacute; cầu tr&ecirc;n cao nguy&ecirc;n đ&aacute; hay chạy bộ, leo n&uacute;i, bơi v&agrave; đi xe đạp ở khu vực đ&egrave;o M&atilde; P&igrave; L&egrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, tỉnh n&acirc;ng cấp c&aacute;c sản phẩm du lịch mạo hiểm sẵn c&oacute; như duy tr&igrave; giải marathon quốc tế tr&ecirc;n cung đường Hạnh Ph&uacute;c, giải d&ugrave; bay tr&ecirc;n ruộng bậc thang Ho&agrave;ng Su Ph&igrave;, đua thuyền kayak v&agrave; thi đấu xe m&ocirc; t&ocirc;, &ocirc; t&ocirc; địa h&igrave;nh. Tất cả hoạt động du lịch mạo hiểm được tỉnh hướng tới quy m&ocirc; lớn, t&iacute;nh chuy&ecirc;n nghiệp cao.</p>\r\n\r\n<p>Du lịch mạo hiểm l&agrave; một trong những kế hoạch ph&aacute;t triển du lịch H&agrave; Giang giai đoạn 2021-2025, tầm nh&igrave;n đến 2030. Tỉnh khai th&aacute;c th&ecirc;m sản phẩm du lịch thương mại - bi&ecirc;n giới, khai th&aacute;c chức năng của c&aacute;c cửa khẩu Thanh Thủy, Săm Pun, Mốc Năm theo m&ocirc; h&igrave;nh trung t&acirc;m thương mại v&agrave; hệ thống cửa h&agrave;ng miễn thuế. X&atilde; bi&ecirc;n giới P&agrave; Vầy Sủ (huyện S&iacute;n Mần) được x&acirc;y dựng, đầu tư để trở th&agrave;nh điểm đến mang thương hiệu quốc gia với c&aacute;c trải nghiệm như du thuyền, thăm cột mốc bi&ecirc;n giới 172...</p>\r\n\r\n<p>Trong họp b&aacute;o c&ocirc;ng bố sản phẩm du lịch chiều 29/10, Ph&oacute; chủ tịch UBND tỉnh H&agrave; Giang Trần Đức Qu&yacute;, cho biết tỉnh đ&atilde; sẵn s&agrave;ng đ&oacute;n kh&aacute;ch du lịch trở lại. Hiện tỉnh đ&atilde; đạt 80% d&acirc;n số tr&ecirc;n 18 tuổi được ti&ecirc;m ph&ograve;ng một mũi vaccine v&agrave; nh&acirc;n lực trong ng&agrave;nh du lịch đ&atilde; được đ&agrave;o tạo c&aacute;c quy tr&igrave;nh đ&oacute;n kh&aacute;ch, đảm bảo an to&agrave;n. Hiện nay, du kh&aacute;ch đến H&agrave; Giang cần ti&ecirc;m ph&ograve;ng đủ hai mũi vaccine, c&oacute; giấy x&eacute;t nghiệm &acirc;m t&iacute;nh với nCoV, thực hiện khai b&aacute;o nghi&ecirc;m t&uacute;c bằng việc qu&eacute;t m&atilde; QR v&agrave; quy định 5K về ph&ograve;ng chống dịch.</p>\r\n\r\n<p>Cũng trong buổi trao đổi, nhiều doanh nghiệp lữ h&agrave;nh, c&aacute;c chuy&ecirc;n gia, c&aacute;c nh&agrave; nghi&ecirc;n cứu cũng c&oacute; &yacute; kiến để c&aacute;c sản phẩm du lịch thu h&uacute;t kh&aacute;ch hơn nữa. Một trong đ&oacute; l&agrave; ph&aacute;t triển du lịch từ bản sắc văn h&oacute;a địa phương, qua đ&oacute; vừa giữ g&igrave;n được gi&aacute; trị cốt l&otilde;i, vừa tạo n&eacute;t ri&ecirc;ng thu h&uacute;t kh&aacute;ch du lịch.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/hagiang.jpg\" style=\"height:510px; width:680px\" /></p>\r\n\r\n<p>Hiện H&agrave; Giang h&igrave;nh th&agrave;nh 3 kh&ocirc;ng gian du lịch gồm: Kh&ocirc;ng gian du lịch đồi n&uacute;i thấp (th&agrave;nh phố H&agrave; Giang, Bắc M&ecirc;, Vị Xuy&ecirc;n); đồi n&uacute;i đ&aacute; &ndash; C&ocirc;ng vi&ecirc;n địa chất to&agrave;n cầu (gồm c&aacute;c huyện Quản Bạ, Y&ecirc;n Minh, Đồng Văn, M&egrave;o Vạc) v&agrave; đồi n&uacute;i ph&iacute;a T&acirc;y Nam (Ho&agrave;ng Su Ph&igrave;, X&iacute;n Mần, Quang B&igrave;nh). Đầu năm, H&agrave; Giang nằm trong top 10 điểm đến tuyệt vời n&ecirc;n kh&aacute;m ph&aacute; tại Việt Nam do&nbsp;<em>CNN</em>&nbsp;b&igrave;nh chọn.</p>\r\n\r\n<p>Những năm gần đ&acirc;y, H&agrave; Giang đ&atilde; x&acirc;y dựng nhiều điểm du lịch hấp dẫn du kh&aacute;ch. Chịu ảnh hưởng của dịch Covid-19, tỉnh nằm trong số &iacute;t địa phương tăng trưởng du lịch năm 2020 (hơn 1,5 triệu lượt kh&aacute;ch, tăng 7% so với 2019). T&iacute;nh đến hết qu&yacute; 1, tỉnh đ&oacute;n hơn 352.000 lượt kh&aacute;ch, doanh thu đạt hơn 580 tỷ đồng.</p>\r\n', 1, 1, 1, 1, 1),
(3, 'Hoa dã quỳ đầu mùa ở Đà Lạt', 'Hoa-Da-Quy-Dau-Mua-O-Da-Lat', '<p>L&Acirc;M ĐỒNGHoa d&atilde; quỳ đ&atilde; bắt đầu đua nở tr&ecirc;n những nẻo đường Đ&agrave; Lạt, nhất l&agrave; v&ugrave;ng ngoại &ocirc; c&ugrave;ng c&aacute;c huyện l&acirc;n cận như Đơn Dương.</p>\r\n', 'mua-da-quy-da-lat-7999-1635538079.jpg', '2021-10-31 00:20:46', 1, '<p>Khi những cơn mưa nặng hạt cuối m&ugrave;a đ&atilde; vơi bớt, nhường chỗ cho c&aacute;i nắng dịu nhẹ chan h&ograve;a khởi đầu m&ugrave;a kh&ocirc;, l&agrave; l&uacute;c m&ugrave;a hoa d&atilde; quỳ về, khoe sắc v&agrave;ng thắm khắp T&acirc;y Nguy&ecirc;n.</p>\r\n\r\n<p>M&ugrave;a n&agrave;y ở Đ&agrave; Lạt, sắc v&agrave;ng của d&atilde; quỳ như &aacute;nh mặt trời sưởi ấm mọi ng&oacute;c ng&aacute;ch của th&agrave;nh phố, từ những triền đồi ven c&aacute;c ng&ocirc;i biệt thự cổ k&iacute;nh đến những g&oacute;c cua v&ograve;ng quanh con đường nhỏ, b&ecirc;n bờ hồ, bờ r&agrave;o, cạnh rừng th&ocirc;ng.</p>\r\n\r\n<p>Xa khu vực trung t&acirc;m th&agrave;nh phố, hoa d&atilde; quỳ lớn v&agrave;ng rực đua nhau khoe sắc, như đường về Đơn Dương, dọc bờ hồ Tuyền L&acirc;m hay đường đi đồi ch&egrave; Cầu Đất... Một trong những con đường ngắm d&atilde; quỳ nổi tiếng ở th&agrave;nh phố sương m&ugrave; h&uacute;t kh&aacute;ch mọi năm l&agrave; Đ&agrave; Lạt - Cầu Đất - Đơn Dương - đồi th&ocirc;ng Ch&acirc;u Sơn - Phi N&ocirc;m - Tu Tra.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/mua-da-quy-da-lat-7999-1635538079.jpg\" style=\"height:453px; width:680px\" /></p>\r\n\r\n<p>L&ecirc; Quang Hải, 41 tuổi, du kh&aacute;ch từ Gia Lai, n&oacute;i m&igrave;nh y&ecirc;u những m&ugrave;a hoa của Đ&agrave; Lạt, mỗi năm hoa đ&agrave;o, hoa ban trắng, hoa phượng t&iacute;m bung nở anh đều c&oacute; mặt ở th&agrave;nh phố m&ugrave; sương để thưởng hoa, m&ugrave;a hoa d&atilde; quỳ năm nay cũng kh&ocirc;ng ngoại lệ. Theo anh, d&atilde; quỳ mang n&eacute;t hoang dại, d&ugrave; ở ho&agrave;n cảnh n&agrave;o cũng đều đẹp theo c&aacute;ch ri&ecirc;ng, c&oacute; khi ch&uacute;ng n&eacute;p m&igrave;nh trong những cơn mưa nặng hạt cuối m&ugrave;a, m&agrave;u hoa sẫm lại hơi trầm buồn, khi lại tỏa sắc rực rỡ dưới &aacute;nh ban mai.</p>\r\n\r\n<p>&quot;Năm n&agrave;o m&igrave;nh cũng l&ecirc;n Đ&agrave; Lạt đ&uacute;ng m&ugrave;a hoa d&atilde; quỳ, năm nay hoa mới v&agrave;o vụ n&ecirc;n chưa rộ nhưng cũng đ&atilde; nở kh&aacute; nhiều. Thời gian n&agrave;y hoa đang v&agrave;o độ đẹp nhất, hoa rất tươi, c&oacute; lẽ do năm nay &iacute;t mưa hơn năm ngo&aacute;i. Năm ngo&aacute;i th&igrave; mưa nhiều l&agrave;m c&aacute;nh hoa bị tơi, kh&ocirc;ng đẹp như năm nay&quot;, anh Hải n&oacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/mua-hoa-da-quy-da-lat.jpg\" style=\"height:491px; width:680px\" /></p>\r\n\r\n<p>L&ecirc; Thị Uy&ecirc;n Trinh, 30 tuổi, đến từ Ninh Thuận, l&agrave;m việc trong lĩnh vực du lịch tại TP Đ&agrave; Lạt, L&acirc;m Đồng từ 3 năm nay. Gần đ&acirc;y, Trinh hay chia sẻ ảnh hoa d&atilde; quỳ chụp c&ugrave;ng bạn l&ecirc;n c&aacute;c hội nh&oacute;m y&ecirc;u Đ&agrave; Lạt v&agrave; nhận được nhiều phản hồi t&iacute;ch cực của người xem, đa số d&agrave;nh lời khen tặng cho m&ugrave;a hoa đẹp, b&ecirc;n cạnh c&oacute; kh&ocirc;ng &iacute;t lời tiếc nuối v&igrave; kh&ocirc;ng được đến th&agrave;nh phố du lịch v&agrave;o dịp n&agrave;y.</p>\r\n\r\n<p>&quot;M&ugrave;a n&agrave;y ở Đ&agrave; Lạt hầu như đi đ&acirc;u cũng thấy hoa d&atilde; quỳ nở, bạn c&oacute; thể đi chụp ảnh, ngắm hoa v&agrave;o buổi s&aacute;ng từ 7 giờ, c&oacute; thể chọn trang phục t&ocirc;ng m&agrave;u nhẹ nh&agrave;ng, v&agrave;ng đất, trắng, n&acirc;u nhạt để chụp ảnh đẹp hơn&quot;, Trinh gợi &yacute;. Du kh&aacute;ch c&oacute; thể di chuyển bằng xe m&aacute;y để chu du khắp c&aacute;c nẻo đường c&oacute; hoa d&atilde; quỳ nở.</p>\r\n', 1, 1, 6, 1, 1),
(4, 'Phố sủi cảo lớn nhất Sài Gòn', 'Pho-Sui-Cao-Lon-Nhat-Sai-Gon', '<p>Xe cộ tấp nập, người xếp h&agrave;ng chờ mua thức ăn, tiếng gọi m&oacute;n... khiến con phố H&agrave; T&ocirc;n Quyền được hồi sinh.</p>\r\n', 'sui-cao-ha-ton-quyen-JPG-2499-1635479062.jpg', '2021-10-31 00:24:21', 1, '<p>Đường H&agrave; T&ocirc;n Quyền, quận 11, l&agrave; địa chỉ b&aacute;n sủi cảo gốc Hoa c&oacute; tiếng, với c&aacute;c cửa h&agrave;ng dọc hai b&ecirc;n đường từ số nh&agrave; 150 đến hơn 200. Cả khu phố l&uacute;c n&agrave;o cũng tấp nập, đ&ocirc;ng đ&uacute;c người ra v&agrave;o, nhất l&agrave; chiều v&agrave; tối. C&aacute;c m&oacute;n ở đ&acirc;y phong ph&uacute; gồm chi&ecirc;n, kh&ocirc; (sốt dầu h&agrave;o), m&igrave; sủi cảo, t&ocirc;m mực, thập cẩm, c&aacute; vi&ecirc;n, m&igrave; k&eacute;o... với gi&aacute; trung b&igrave;nh khoảng 55.000 đồng/phần.</p>\r\n\r\n<p>Kh&aacute;ch đến đ&acirc;y thường chạy xe dọc phố, sẽ c&oacute; người từ h&agrave;ng qu&aacute;n hai b&ecirc;n đường ra ch&agrave;o mời kh&aacute;ch, hướng dẫn chỗ để xe miễn ph&iacute; rồi v&agrave;o nh&agrave; gọi m&oacute;n. Sau gần 5 th&aacute;ng kh&ocirc;ng phục vụ tại chỗ để ph&ograve;ng chống Covid-19, tối 28/10, nhiều thực kh&aacute;ch đ&atilde; đến ngay con phố nổi tiếng n&agrave;y để thưởng thức m&oacute;n sủi cảo thanh ngọt v&agrave; &iacute;t dầu mỡ.</p>\r\n\r\n<p>Chị Hương, 30 tuổi, quản l&yacute;, thu ng&acirc;n một qu&aacute;n lớn tr&ecirc;n đường H&agrave; T&ocirc;n Quyền cho biết rất vui khi thấy kh&aacute;ch quay trở lại. &quot;Đường x&aacute; tấp nập, cho b&aacute;n tại chỗ n&ecirc;n nhiều người cũng tới đ&acirc;y v&igrave; họ th&iacute;ch ngồi tại qu&aacute;n, ăn ngon miệng hơn. So với trước dịch th&igrave; giờ kh&aacute;ch đến qu&aacute;n tương đối đ&ocirc;ng, thậm ch&iacute; ng&agrave;y đầu c&oacute; phần đ&ocirc;ng hơn b&igrave;nh thường&quot;, chị Hương n&oacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/sui-cao-ha-ton-quyen-JPG-2499-1635479062.jpg\" style=\"height:408px; width:680px\" /></p>\r\n\r\n<p>Hai bạn Hiếu, ở B&igrave;nh T&acirc;n v&agrave; Nam, ở T&acirc;n B&igrave;nh được gh&eacute;p b&agrave;n với một kh&aacute;ch lẻ. Nam n&oacute;i đ&acirc;y l&agrave; lần đầu anh tới đ&acirc;y v&agrave; bất ngờ v&igrave; cảnh tượng đ&ocirc;ng đ&uacute;c, nhộn nhịp, kiếm chỗ đậu xe kh&oacute;. &quot;B&agrave;n m&igrave;nh c&oacute; 3 người, cũng hơi ngại khi ngồi chung với người lạ v&igrave; qu&aacute;n hết chỗ, đợi th&igrave; l&acirc;u, nh&igrave;n xung quanh th&igrave; mỗi b&agrave;n cũng hai người n&ecirc;n m&igrave;nh với bạn ngồi ăn chung lu&ocirc;n&quot;, Nam chia sẻ.</p>\r\n\r\n<p>Sau khi gọi sủi cảo c&ugrave;ng nước s&acirc;m, Hiếu v&agrave; Nam kh&ocirc;ng phải đợi l&acirc;u, thức ăn được mang đến nhanh. Cả hai ăn kh&aacute; ngon miệng. &quot;Ở nh&agrave; mấy th&aacute;ng trời m&igrave;nh thấy ngột ngạt lắm, nay được đi ra đường ăn uống tại chỗ thấy thoải m&aacute;i hơn, ăn tại qu&aacute;n vẫn l&agrave; ngon nhất&quot;, Hiếu n&oacute;i, sau thời gian gi&atilde;n c&aacute;ch x&atilde; hội, d&ugrave; được ăn tại chỗ, kh&ocirc;ng kh&iacute; nhộn nhịp, đồ ăn ngon hơn nhưng c&ocirc; vẫn c&oacute; t&acirc;m l&yacute; e ngại, kh&ocirc;ng tự nhi&ecirc;n.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/sui-cao-ha-ton-quyen-01-JPG-6678-1635479063.jpg\" style=\"height:453px; width:680px\" /></p>\r\n\r\n<p>rong số đ&ocirc;ng thực kh&aacute;ch đến phố H&agrave; T&ocirc;n Quyền, nhiều người vẫn chọn mua mang về v&igrave; vẫn c&ograve;n e d&egrave; khi ăn tại chỗ. &Ocirc;ng Long ở quận 3 đi c&ugrave;ng vợ cho biết: &quot;Chỗ n&agrave;y t&ocirc;i ăn quen rồi, hay ra đ&acirc;y mua sủi cảo lắm. Nghe tin cho b&aacute;n l&agrave; chở vợ ra đ&acirc;y, nhưng tới nơi th&igrave; thấy đ&ocirc;ng người qu&aacute;, nh&igrave;n nhộn nhịp cũng vui lắm nhưng m&igrave;nh cũng hơi sợ. Trải qua đợt dịch vừa rồi n&ecirc;n giờ phải cẩn thận hơn, &yacute; thức hơn. Ăn uống t&ocirc;i vẫn nấu tại nh&agrave;, c&oacute; th&egrave;m m&oacute;n g&igrave; th&igrave; mua mang về chứ ngại ngồi tại qu&aacute;n&quot;.</p>\r\n\r\n<p>Trước đợt dịch Covid-19 lần thứ tư, con phố sủi cảo hoạt động cả ng&agrave;y, đ&ocirc;ng đ&uacute;c nhất l&agrave; từ 17h đến 22h v&agrave; nhiều tiệm cũng mở đến khuya. Hiện c&aacute;c h&agrave;ng qu&aacute;n chỉ được ph&eacute;p b&aacute;n đến 21h v&agrave; phục vụ tối đa 50% c&ocirc;ng suất. Kh&aacute;ch đến thưởng thức sủi cảo n&ecirc;n đi sớm để c&oacute; thể chọn lựa đầy đủ m&oacute;n hơn v&igrave; c&oacute; nhiều m&oacute;n rất nhanh &quot;ch&aacute;y&quot; h&agrave;ng.</p>\r\n', 1, 1, 0, 1, 1),
(5, 'Trải nghiệm du lịch tại chỗ sau giãn cách', 'Trai-Nghiem-Du-Lich-Tai-Cho-Sau-Gian-Cach', '<p>H&igrave;nh thức du lịch tại chỗ gi&uacute;p tr&aacute;nh tiếp x&uacute;c dẫn đến nguy cơ l&acirc;y lan Covid-19 nhưng vẫn đ&aacute;p ứng nhu cầu nghỉ ngơi sau những ng&agrave;y gi&atilde;n c&aacute;ch.</p>\r\n', 'Hotel-Overview-6-9636-1635239352.jpg', '2021-10-30 10:00:00', 1, '<p>Khảo s&aacute;t mới của&nbsp;<em>Agoda</em>, một phần tư người được hỏi cho biết khi du lịch trở lại, họ ưu ti&ecirc;n h&igrave;nh thức staycation (du lịch tại chỗ), lựa chọn c&aacute;c điểm đến gần nh&agrave; v&agrave; c&oacute; thể l&aacute;i xe được. Xu hướng n&agrave;y được dự đo&aacute;n vẫn c&ograve;n tiếp tục ph&aacute;t triển mạnh mẽ trong giai đoạn b&igrave;nh thường mới.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/Hotel-Overview-6-9636-1635239352.jpg\" style=\"height:453px; width:680px\" />&Ocirc;ng Walt Power - Tổng gi&aacute;m đốc Điều h&agrave;nh Tổ hợp Nghỉ dưỡng The Grand Ho Tram Strip cho biết, Covid-19 b&ugrave;ng ph&aacute;t, c&aacute;c loại h&igrave;nh staycation ng&agrave;y c&agrave;ng trở n&ecirc;n được ưa chuộng tr&ecirc;n thế giới bởi mối lo ngại dịch bệnh vẫn hiện hữu, t&acirc;m l&yacute; muốn c&oacute; sự chủ động về thời gian v&agrave; linh hoạt trong lịch tr&igrave;nh nghỉ dưỡng. Việt Nam cũng kh&ocirc;ng đứng ngo&agrave;i xu hướng n&agrave;y khi nhiều kh&aacute;ch sạn ra mắt c&aacute;c g&oacute;i nghỉ dưỡng tập trung đa dạng chương tr&igrave;nh giải tr&iacute;, ẩm thực với gi&aacute; ph&ograve;ng ưu đ&atilde;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/2-8257-1635329151.png\" style=\"height:374px; width:627px\" />Đơn cử như khu nghỉ dưỡng The Grand Ho Tram Strip (Xuy&ecirc;n Mộc, B&agrave; Rịa - Vũng T&agrave;u) vừa tung ra g&oacute;i nghỉ dưỡng đặc biệt Ho Tram Staycation, cho ph&eacute;p du kh&aacute;ch trải nghiệm 7 đ&ecirc;m nghỉ tại kh&aacute;ch sạn InterContinental Grand Ho Tram c&ugrave;ng ba lựa chọn dịch vụ d&agrave;nh cho 2 kh&aacute;ch. Cụ thể, g&oacute;i 7 đ&ecirc;m nghỉ k&egrave;m bữa s&aacute;ng c&oacute; gi&aacute; từ 36,708 triệu đồng, g&oacute;i 7 đ&ecirc;m nghỉ k&egrave;m bữa s&aacute;ng v&agrave; tối với gi&aacute; từ 46 triệu đồng v&agrave; g&oacute;i 7 đ&ecirc;m nghỉ k&egrave;m ba bữa s&aacute;ng, trưa v&agrave; tối c&oacute; gi&aacute; từ 53,82 triệu dồng (gi&aacute; &aacute;p dụng cho hạng ph&ograve;ng Grand Golf View d&agrave;nh cho 2 kh&aacute;ch).</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/Buffet-at-Ginger-Restaurant-5921-1635239352.jpg\" style=\"height:450px; width:680px\" /></p>\r\n\r\n<p>Theo &ocirc;ng Walt Power, chương tr&igrave;nh vừa triển khai ngay sau khi nhận được sự chấp thuận của B&agrave; Rịa - Vũng T&agrave;u cho ph&eacute;p 4 cơ sở lưu tr&uacute; hoạt động tr&ecirc;n địa b&agrave;n tỉnh, trong đ&oacute; c&oacute; The Grand Ho Tram Strip, thực hiện th&iacute; điểm đ&oacute;n kh&aacute;ch du lịch từ ng&agrave;y 15/10 nhằm k&iacute;ch th&iacute;ch du lịch. Nếu thuận lợi, từ đầu th&aacute;ng 11, tỉnh sẽ từng bước nới lỏng, mở rộng c&aacute;c hoạt động của ng&agrave;nh du lịch.</p>\r\n\r\n<p>&quot;Tỉnh B&agrave; Rịa - Vũng T&agrave;u hỗ trợ người d&acirc;n, doanh nghiệp v&agrave; c&aacute;c ng&agrave;nh nhằm hồi phục kinh tế sau gi&atilde;n c&aacute;ch v&igrave; Covid-19. The Grand Ho Tram Strip được chọn tham gia chương tr&igrave;nh th&iacute; điểm n&agrave;y với mục đ&iacute;ch hỗ trợ ng&agrave;nh, c&ocirc;ng ty du lịch của c&aacute;c tỉnh th&agrave;nh th&iacute;ch nghi với t&igrave;nh h&igrave;nh mới, mau ch&oacute;ng trở lại hoạt động b&igrave;nh thường&quot;, &ocirc;ng Walt Power chia sẻ.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:519px; top:-49px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/16.png\" /></div>\r\n', 1, 1, 6, 1, 1),
(6, '7 bất ngờ trên hành trình khám phá Ấn Độ', '7-Bat-Ngo-Tren-Hanh-Trinh-Kham-Pha-An-Do', '<p>Lam Trang ngạc nhi&ecirc;n khi c&ocirc; b&aacute;n dừa dạo v&agrave; ch&uacute; b&aacute;n h&agrave;ng rong đều nhận thanh to&aacute;n bằng v&iacute; điện tử, trang phục của người d&acirc;n th&igrave; sặc sỡ, m&ugrave;i hoa nh&agrave;i ở khắp nơi.</p>\r\n', 'ando.jpg', '2021-10-31 00:35:10', 1, '<p><strong>Người d&acirc;n ăn nhiều đồ chay v&agrave; đồ n&oacute;ng</strong></p>\r\n\r\n<p>Điều l&agrave;m Trang sốc văn ho&aacute; nhất khi đặt ch&acirc;n tới Ấn Độ l&agrave; người d&acirc;n ăn nhiều đồ chay. C&ocirc; cho biết, c&oacute; khoảng một phần ba d&acirc;n số Ấn Độ thường xuy&ecirc;n ăn chay. Căng tin của trường đại học c&ocirc; đến thực tập cũng chỉ phục vụ đồ ăn chay. Tuần đầu sang đến Ấn Độ, Trang kh&ocirc;ng thể quen v&agrave; chưa biết nơi n&agrave;o b&aacute;n thịt n&ecirc;n buộc phải ăn chay. Việc chuyển sang ăn chay một c&aacute;ch đột ngột l&agrave;m cơ thể Trang đ&oacute;i nhanh v&agrave; mệt mỏi. Sau đ&oacute;, c&ocirc; t&igrave;m được qu&aacute;n thịt nhưng lại chỉ phục vụ thịt g&agrave;, thịt cừu th&igrave; đắt m&agrave; kh&ocirc;ng phải nơi đ&acirc;u chế biến cũng ngon. Suốt 2 th&aacute;ng ở Ấn Độ, Trang ho&agrave;n to&agrave;n kh&ocirc;ng ăn thịt lợn v&agrave; thịt b&ograve;, những m&oacute;n phổ biến v&agrave; dễ t&igrave;m ở Việt Nam.</p>\r\n\r\n<p>Đồ ăn Ấn Độ thường n&oacute;ng v&agrave; nhiều gia vị. Người d&acirc;n th&iacute;ch ăn chay nhưng trong khẩu phần ăn lại thiếu rau xanh, chủ yếu l&agrave; c&aacute;c loại c&agrave; ri nấu c&ugrave;ng c&aacute;c loại hạt. Gạo ở Ấn d&agrave;i, dẹt v&agrave; tơi, kh&ocirc;ng dẻo v&agrave; thơm như Việt Nam. Tuy nhi&ecirc;n, Trang lại rất th&iacute;ch ăn b&aacute;nh kẹo, nước hoa quả, chocolate, đặc biệt l&agrave; tr&agrave; tại Ấn Độ do c&aacute;c mặt h&agrave;ng n&agrave;y đa dạng, rẻ v&agrave; dễ ăn.</p>\r\n\r\n<p><strong>Mua sắm th&ocirc;ng minh</strong></p>\r\n\r\n<p>Thanh to&aacute;n điện tử phổ biến tại Ấn Độ. Những thứ nhỏ như cốc tr&agrave;, hộp b&aacute;nh hay s&aacute;ch, t&agrave;i liệu đều được thanh to&aacute;n bằng m&atilde; QR. Người d&acirc;n tại đ&acirc;y d&ugrave;ng Paytm, một v&iacute; điện tử được ch&iacute;nh người Ấn thiết kế v&agrave; vận h&agrave;nh. &quot;C&ocirc; b&aacute;n dừa dạo v&agrave; ch&uacute; b&aacute;n h&agrave;ng rong đều nhận thanh to&aacute;n bằng v&iacute; điện tử&quot;, Trang n&oacute;i.</p>\r\n\r\n<p>Ở Ấn Độ, kh&aacute;ch mua h&agrave;ng cần trả gi&aacute; rất kỹ, v&igrave; người b&aacute;n thường n&oacute;i th&aacute;ch rất cao v&agrave; n&oacute;i những c&acirc;u như kiểu thề với th&aacute;nh thần l&agrave; b&aacute;n đ&uacute;ng gi&aacute;, song đội gi&aacute; l&ecirc;n nhiều.</p>\r\n\r\n<p><strong>Người d&acirc;n chuộng m&agrave;u sắc rực rỡ</strong></p>\r\n\r\n<p>Vốn biết Ấn Độ ưa c&aacute;c m&agrave;u sắc sặc sỡ bằng việc xem những bộ phim truyền h&igrave;nh, Trang vẫn kh&ocirc;ng khỏi ngạc nhi&ecirc;n khi đến trực tiếp. Quần &aacute;o được b&agrave;y b&aacute;n trong c&aacute;c chợ, khu mua sắm ở Ấn Độ đủ m&agrave;u sắc. M&agrave;u n&agrave;o c&agrave;ng rực rỡ, c&agrave;ng nổi bật th&igrave; người d&acirc;n c&agrave;ng chuộng. &quot;M&igrave;nh mua được đồ tốt với gi&aacute; giảm kịch s&agrave;n, rẻ như cho, chỉ v&igrave; n&oacute; c&oacute; m&agrave;u x&aacute;m. M&igrave;nh nghĩ do m&agrave;u x&aacute;m kh&ocirc;ng ai mua n&ecirc;n họ hạ gi&aacute;&quot;, Trang chia sẻ.</p>\r\n\r\n<p>Ngo&agrave;i ra, c&ocirc; g&aacute;i cho biết người Ấn Độ c&ograve;n rất &quot;cuồng&quot; v&agrave;ng, c&oacute; thể v&igrave; n&oacute; c&oacute; m&agrave;u sắc rực rỡ. Ấn Độ l&agrave; một trong những quốc gia ti&ecirc;u thụ v&agrave;ng nhiều nhất thế giới. Người d&acirc;n th&iacute;ch v&agrave;ng v&agrave; những sản phẩm từ v&agrave;ng. Nếu m&oacute;n trang sức đ&oacute; kh&ocirc;ng l&agrave; v&agrave;ng th&igrave; họ sẽ mạ v&agrave;ng hoặc l&agrave;m những m&oacute;n đồ m&agrave;u v&agrave;ng m&agrave; như tại Việt Nam ta quen gọi l&agrave; đồ mỹ k&yacute;. &quot;Nếu kh&ocirc;ng phải m&agrave;u v&agrave;ng, th&igrave; n&oacute; cũng phải rất rực rỡ&quot;, Trang n&oacute;i.</p>\r\n\r\n<p><strong>Nhiều cửa h&agrave;ng b&aacute;n trang phục truyền thống</strong></p>\r\n\r\n<p>Đi dạo c&aacute;c trung t&acirc;m thương mại, điều m&agrave; Trang ấn tượng nhất l&agrave; c&oacute; nhiều cửa h&agrave;ng b&aacute;n trang phục truyền thống. C&oacute; nhiều tầng ở trung t&acirc;m thương mại chỉ b&aacute;n đồ truyền thống v&agrave; rất nhiều loại, đa dạng từ đắt đến rẻ, trẻ con đến người lớn. &quot;Người Việt Nam, đặc biệt l&agrave; phụ nữ sẽ kh&aacute; kh&oacute; mua quần &aacute;o tại Ấn Độ do cỡ người ở đ&acirc;y to hơn người Việt. Tại Việt Nam, m&igrave;nh thường xuy&ecirc;n mặc size L v&agrave; XL, song đến Ấn Độ, size S vẫn c&ograve;n rộng&quot;, Trang chia sẻ.</p>\r\n\r\n<p>Một điều đặc biệt m&agrave; Trang để &yacute; l&agrave; tại những khu chợ, người b&aacute;n h&agrave;ng thường l&agrave; nam. Theo Trang, việc đi chợ v&agrave; nh&igrave;n ngắm c&aacute;c cửa h&agrave;ng gi&uacute;p bạn biết được th&ecirc;m về kinh tế, đặc trưng văn ho&aacute; của đất nước m&agrave; m&igrave;nh gh&eacute; thăm.</p>\r\n\r\n<p><strong>M&ugrave;i hoa nh&agrave;i ở khắp nơi</strong></p>\r\n\r\n<p>M&ugrave;i hương gợi nhắc về Ấn Độ trong tr&iacute; nhớ của Trang l&agrave; m&ugrave;i hoa nh&agrave;i. C&ocirc; cho biết, người phụ nữ miền nam Ấn Độ thường sử dụng hoa để tết t&oacute;c. C&aacute;c c&ocirc; d&acirc;u thường c&agrave;i những ch&ugrave;m hoa nh&agrave;i để trang điểm trong lễ cưới. Thậm ch&iacute;, Chennai, th&agrave;nh phố m&agrave; Trang đến thực tập, c&ograve;n được đặt biệt danh l&agrave; th&agrave;nh phố hương hoa nh&agrave;i. Chỉ cần đi dạo trong th&agrave;nh phố sẽ bắt gặp rất nhiều người b&aacute;n h&agrave;ng ngồi kết từng ch&ugrave;m hoa nh&agrave;i để b&aacute;n.</p>\r\n\r\n<p><strong>Nhiều danh lam thắng cảnh đẹp</strong></p>\r\n\r\n<p>Du kh&aacute;ch khi đến Ấn Độ thường tập trung v&agrave;o c&aacute;c địa điểm ở ph&iacute;a bắc, như thủ đ&ocirc; New Delhi, đền Taj Mahal, th&agrave;nh phố Mumbai hay v&ugrave;ng đất Phật gi&aacute;o Bodh Gaya. Tuy nhi&ecirc;n, c&ocirc; g&aacute;i lại được kh&aacute;m ph&aacute; những địa điểm ở ph&iacute;a nam Ấn Độ, v&agrave; coi đ&oacute; l&agrave; một điều may mắn.</p>\r\n\r\n<p>Nơi Trang thấy đẹp nhất trong chuyến h&agrave;nh tr&igrave;nh c&oacute; lẽ l&agrave; khu đền thờ Chidambaram Nataraja, hay c&ograve;n gọi l&agrave; đền Thillai Nataraja. Đ&acirc;y l&agrave; đền thờ Nataraja - vị thần vũ điệu của người Ấn Độ gi&aacute;o, được x&acirc;y dựng từ thế kỷ 10 dưới Vương triều Chola huy ho&agrave;ng v&agrave; l&agrave; một trong những khu đền c&ograve;n hoạt động l&acirc;u đời nhất ở Nam &Aacute;. Nh&igrave;n từ xa, phần m&aacute;i c&oacute; những hoạ tiết trang tr&iacute; tỉ mỉ v&agrave; kết hợp m&agrave;u sắc rực rỡ. V&agrave;o b&ecirc;n trong th&igrave; c&ocirc; g&aacute;i đặc biệt th&iacute;ch những bức chạm khắc tr&ecirc;n tường h&igrave;nh c&aacute;c vũ c&ocirc;ng trong điệu Bharatanatyam - một điệu m&uacute;a cổ điển nổi tiếng Ấn Độ.</p>\r\n\r\n<p><strong>Người d&acirc;n đọc s&aacute;ch nhiều</strong></p>\r\n\r\n<p>Trang ch&uacute; &yacute; c&aacute;c cửa tiệm s&aacute;ch tại Ấn Độ. Đất nước n&agrave;y l&agrave; một trong những thị trường ti&ecirc;u thụ s&aacute;ch lớn nhất thế giới. Tại Ấn Độ &quot;s&aacute;ch g&igrave; cũng c&oacute;, loại n&agrave;o cũng thấy&quot;. Trong đ&oacute;, c&aacute;c t&aacute;c phẩm của Chetan Bhagat (t&aacute;c giả của &quot;Ba ch&agrave;ng ngốc&quot;) v&agrave; sử thi tiếng Phạn nổi tiếng Mahabharata c&ugrave;ng Ramayana được b&agrave;y b&aacute;n nhiều nhất, được xếp tại quầy to v&agrave; dễ ch&uacute; &yacute;.</p>\r\n\r\n<p>Kh&aacute;m ph&aacute; Ấn Độ, Trang học được nhiều điều. Ấn Độ cho c&ocirc; thấy tất cả những giới hạn của con người, những mặt đối lập s&acirc;u sắc. Ở đ&oacute; c&oacute; những người gi&agrave;u nhất thế giới, nhưng cũng c&oacute; những người ngh&egrave;o c&ugrave;ng cực, những người vĩ đại, t&agrave;i giỏi nhưng cũng l&agrave; những tệ nạn nguy hiểm. Những con người b&igrave;nh thường sống chậm r&atilde;i, từ tốn nhưng trong c&aacute;c lễ hội lại nồng nhiệt, dữ dội. &quot;Ấn Độ dạy m&igrave;nh về việc t&ocirc;n trọng sự kh&aacute;c biệt v&agrave; đa dạng, kh&ocirc;ng chỉ giữa c&aacute;c người với người, giữa c&aacute;c nh&oacute;m cộng đồng, m&agrave; hơn hết l&agrave; giữa c&aacute;c quốc gia, d&acirc;n tộc với nhau&quot;, Trang chia sẻ.</p>\r\n', 2, 1, 1, 1, 1),
(7, 'Những nẻo đường miền Tây trong mắt bạn trẻ 9x', 'Nhung-Neo-Duong-Mien-Tay-Trong-Mat-Ban-Tre-9X', '<p>L&agrave; người Bến Tre đ&atilde; qu&aacute; quen với những cung đường ngược xu&ocirc;i miền T&acirc;y s&ocirc;ng nước, nhưng Duy Tường chưa bao giờ cảm thấy ch&aacute;n.</p>\r\n', '12-Nui-To-Tri-Ton-An-Giang-JPG-3290-1635157444.jpg', '2021-10-31 00:42:40', 1, '<p>Sinh ra v&agrave; lớn l&ecirc;n ở xứ dừa Bến Tre, Lữ Duy Tường, 24 tuổi, gần đ&acirc;y quen thuộc với giới trẻ nhờ c&aacute;c bức ảnh đậm chất qu&ecirc;, đặc biệt ảnh về n&ocirc;ng th&ocirc;n miền T&acirc;y.</p>\r\n\r\n<p>Tốt nghiệp đại học năm 2020, anh hiện l&agrave;m ph&aacute;p l&yacute; v&agrave; gi&aacute;m s&aacute;t cho một c&ocirc;ng ty, đồng thời quản l&yacute; homestay tại quận 7, TP HCM. Ngo&agrave;i c&ocirc;ng việc ch&iacute;nh, Tường c&ograve;n tham gia l&agrave; quản trị vi&ecirc;n group du lịch&nbsp;<em>Việt Nam Ơi</em>&nbsp;c&oacute; gần 670.000 th&agrave;nh vi&ecirc;n.</p>\r\n\r\n<p>&quot;T&ocirc;i lớn l&ecirc;n với c&ocirc;ng việc đồng &aacute;ng của cha mẹ n&ecirc;n chỉ biết quẩn quanh ở x&oacute;m ấp. Hồi nhỏ, gia đ&igrave;nh ở căn nh&agrave; l&aacute; giữa đồng ruộng, kh&ocirc;ng c&oacute; điện sinh hoạt v&agrave; cũng kh&ocirc;ng c&oacute; TV. Ch&iacute;nh v&igrave; vậy, t&ocirc;i khao kh&aacute;t được đi xa hơn, kh&aacute;m ph&aacute; v&agrave; cảm nhận nhiều hơn về cuộc sống&quot;, Tường kể về động lực v&agrave; đam m&ecirc; du lịch.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/12-Nui-To-Tri-Ton-An-Giang-JPG-3290-1635157444.jpg\" style=\"height:436px; width:680px\" /></p>\r\n\r\n<p>Từ khi l&ecirc;n TP HCM, anh c&oacute; cơ hội được chu du đ&oacute; đ&acirc;y. Mỗi chuyến đi Tường đều tranh thủ chụp v&agrave; chia sẻ ảnh đẹp để quảng b&aacute; đất nước m&igrave;nh. Đ&acirc;y l&agrave; những bức ảnh kỷ niệm, cũng l&agrave; m&oacute;n qu&agrave; &yacute; nghĩa gửi tặng cho người d&acirc;n từng cho Tường t&aacute; t&uacute;c.</p>\r\n\r\n<p>Tường thường d&agrave;nh cuối tuần hoặc c&aacute;c dịp lễ để phượt về c&aacute;c tỉnh miền T&acirc;y, v&agrave; th&aacute;ng n&agrave;o cũng đi một v&agrave;i chuyến. Tham gia cộng đồng du lịch n&ecirc;n Tường quen th&ecirc;m bạn ở c&aacute;c tỉnh. Đ&oacute; l&agrave; l&yacute; do anh hay ở nhờ nh&agrave; bạn để giảm tiền thu&ecirc; nh&agrave; nghỉ cũng như ăn uống. Anh c&ograve;n được chi&ecirc;u đ&atilde;i bằng cơm nh&agrave; v&agrave; sự hiếu kh&aacute;ch của người d&acirc;n. &quot;Nhờ vậy, c&aacute;c chuyến đi của t&ocirc;i lu&ocirc;n ngon, bổ, rẻ, kh&ocirc;ng tốn nhiều chi ph&iacute; v&agrave; chứa đầy những ho&agrave;i niệm, h&agrave;o sảng v&agrave; t&igrave;nh người miền qu&ecirc;&quot;.</p>\r\n\r\n<p>Ch&agrave;ng trai trẻ đ&atilde; chu du 28 tỉnh th&agrave;nh cả nước, ri&ecirc;ng đồng bằng s&ocirc;ng Cửu Long l&agrave; 11/13 tỉnh. Kh&ocirc;ng chọn c&aacute;ch đi thật nhiều điểm để check-in, Tường đi s&acirc;u v&agrave;o c&aacute;c l&agrave;ng qu&ecirc;, x&oacute;m nhỏ để t&igrave;m hiểu về cuộc sống, c&ugrave;ng ăn, ở với người địa phương. Tường cho biết về miền T&acirc;y rất dễ bắt gặp những nụ cười tươi của người d&acirc;n đi k&egrave;m c&aacute;c c&acirc;u n&oacute;i ch&acirc;n chất như &quot;Ngồi chơi, c&ocirc; ra đằng sau chặt tr&aacute;i dừa uống cho m&aacute;t&quot;, &quot;Con ở tr&ecirc;n đ&oacute; mần nghề g&igrave;?&quot; hay &quot;Chừng n&agrave;o con c&oacute; dịp quay lại đ&acirc;y chơi?&quot;.</p>\r\n\r\n<p>Mỗi nơi ch&agrave;ng trai 24 tuổi đi qua đều để lại nhiều k&yacute; ức đẹp.&nbsp;<strong>Tiền Giang&nbsp;</strong>l&agrave; nơi Tường đến nhiều nhất để gi&uacute;p đỡ b&agrave; con v&agrave; c&aacute;c em thiếu nhi, được l&agrave;m b&aacute;nh, bắt ốc, h&aacute;i s&uacute;ng, ch&egrave;o xuồng, tắm s&ocirc;ng hay tham quan c&aacute;c di t&iacute;ch lịch sử. Ngo&agrave;i ra, anh c&ograve;n cắm trại ngủ qua đ&ecirc;m đ&oacute;n b&igrave;nh minh v&agrave; chụp ảnh tại c&acirc;y mắm &quot;c&ocirc; đơn&quot; ở b&atilde;i biển T&acirc;n Th&agrave;nh.</p>\r\n\r\n<p>Tường đến&nbsp;<strong>Long An</strong>&nbsp;trong những ng&agrave;y nắng gắt với những ruộng thanh long đỏ au đang rộ m&ugrave;a, những c&aacute;nh đồng l&uacute;a N&agrave;ng Thơm, đến thăm h&agrave;ng cau T&acirc;n Trụ vừa to vừa cao tạo n&ecirc;n b&oacute;ng m&aacute;t nguy&ecirc;n cả con đường d&agrave;i, gh&eacute; qua di t&iacute;ch V&agrave;m Nhựt Tảo, viếng ch&ugrave;a T&ocirc;n Thạnh hay m&aacute;i đ&igrave;nh Vạn Phước...</p>\r\n\r\n<p>Về&nbsp;<strong>Đồng Th&aacute;p</strong>&nbsp;hai lần, Tường th&iacute;ch rong ruổi v&agrave; ngắm bạt ng&agrave;n những c&aacute;nh đồng sen từ nội đ&ocirc; cho đến n&ocirc;ng th&ocirc;n, t&igrave;m đến ng&ocirc;i ch&ugrave;a nằm s&acirc;u trong khu di t&iacute;ch G&ograve; Th&aacute;p, Th&aacute;p Mười. &quot;H&ocirc;m ấy trời mưa, t&ocirc;i xin v&agrave;o tr&uacute; mưa v&agrave; gặp được sư cũng vừa đi vườn về với buồng chuối ch&iacute;n v&agrave; một &iacute;t m&iacute;t thơm. Sư đ&atilde; mời tr&agrave;, thưởng thức tr&aacute;i c&acirc;y v&agrave; được thỉnh chuyện. Trước khi về, sư c&ograve;n mời cơm v&agrave; treo l&ecirc;n xe t&ocirc;i nải chuối để ăn dọc đường. Đ&ocirc;i khi chỉ vậy nhưng l&agrave;m t&ocirc;i nhớ m&atilde;i&quot;, Tường n&oacute;i.</p>\r\n\r\n<p>Trong khi đ&oacute;,&nbsp;<strong>Tr&agrave; Vinh</strong>&nbsp;v&agrave;<strong>&nbsp;S&oacute;c Trăng</strong>&nbsp;lu&ocirc;n để lại ấn tượng với những ng&ocirc;i ch&ugrave;a Khmer thiết kế tinh xảo. Tường cứ mải m&ecirc; ngắm nh&igrave;n những bức ph&ugrave; đi&ecirc;u được chạm trổ c&ocirc;ng phu, kh&ocirc;ng biết họ đ&atilde; bỏ ra bao nhi&ecirc;u c&ocirc;ng sức mới s&aacute;ng tạo, x&acirc;y dựng kiệt t&aacute;c được như vậy.</p>\r\n\r\n<p>Vốn l&agrave; một đứa &quot;ghiền&quot; nghe cải lương v&agrave; đờn ca t&agrave;i tử từ nhỏ, Tường &ocirc;m giấc mơ t&igrave;m về&nbsp;<strong>Bạc Li&ecirc;u</strong>, c&aacute;i n&ocirc;i của bộ m&ocirc;n nghệ thuật n&agrave;y. V&agrave; chuyến đi lần ấy đọng lại rất nhiều cảm x&uacute;c. &quot;Nghe lời ca của bản Dạ cổ ho&agrave;i lang của Cao Văn Lầu vang l&ecirc;n văng vẳng, tr&ecirc;n nền tiếng đ&agrave;n k&igrave;m, t&ocirc;i b&ugrave;i ng&ugrave;i, một cảm gi&aacute;c lưu luyến kh&oacute; tả, thanh &acirc;m vọng cổ cứ m&atilde;i khắc ghi v&agrave;o l&ograve;ng&quot;, ch&agrave;ng trai Bến Tre kể.</p>\r\n\r\n<p>Tường c&ograve;n nhớ đến cảm gi&aacute;c l&ecirc;nh đ&ecirc;nh tr&ecirc;n s&ocirc;ng tuyệt vời v&agrave;o s&aacute;ng sớm tại chợ nổi C&aacute;i Răng -<strong>Cần Thơ</strong>&nbsp;v&agrave; n&eacute;t sinh hoạt đặc trưng của miền s&ocirc;ng nước. C&ograve;n những lần gh&eacute; thăm&nbsp;<strong>Hậu Giang</strong>&nbsp;g&acirc;y ấn tượng với Tường ở một c&ocirc;ng vi&ecirc;n giải tr&iacute; mang đến kh&ocirc;ng gian &quot;ch&acirc;u &Acirc;u thu nhỏ&quot; giữa l&ograve;ng miền T&acirc;y.</p>\r\n\r\n<p>Về&nbsp;<strong>An Giang</strong>, Tường nhớ nhất lần l&ecirc;n n&uacute;i T&ocirc; bằng xe &ocirc;m địa phương. Qua từng kh&uacute;c cua, đoạn dốc đứng, xe c&oacute; thể lật ngang hoặc tuột bất cứ l&uacute;c n&agrave;o. Anh ngồi sau m&agrave; muốn th&oacute;t cả tim. L&aacute;i xe t&acirc;m sự nghề n&agrave;y nguy hiểm nhưng v&igrave; mưu sinh n&ecirc;n vẫn b&aacute;m trụ.</p>\r\n\r\n<p><strong>C&agrave; Mau</strong>, chuyến đi về v&ugrave;ng đất cực Nam của Tổ quốc, Tường hăng h&aacute;i v&agrave; phải đặt ch&acirc;n đến cho bằng được. Anh đến thăm chợ Năm Căn, chạm tay v&agrave;o cột mốc tọa độ, ra ngo&agrave;i H&ograve;n Đ&aacute; Bạc, hay đến Ph&uacute; T&acirc;n bằng c&aacute;c chuyến đ&ograve; đ&ecirc;m qua c&aacute;c con s&ocirc;ng. Khoảng thời gian ở C&agrave; Mau đ&aacute;ng nhớ với rất nhiều điều tuyệt vời, nhất l&agrave; được ch&uacute; Tư - người ở đ&acirc;y cho theo xuồng ra đầm t&ocirc;m đổ l&uacute; (ngư cụ) l&uacute;c hừng đ&ocirc;ng.</p>\r\n\r\n<p>&quot;Buổi s&aacute;ng h&ocirc;m ấy, trời c&ograve;n tối mịt, ch&uacute; Tư gọi t&ocirc;i theo, ch&uacute; đội đ&egrave;n rọi s&aacute;ng tr&ecirc;n đầu, tay th&igrave; chống chiếc xuồng ba l&aacute; nhỏ x&iacute;u vừa đủ chở hai người, len lỏi trong con rạch hẹp, hai b&ecirc;n đầy những bụi &ocirc; r&ocirc;, dừa nước, mắm, bần&quot;, Tường nhớ lại.</p>\r\n\r\n<p>Từng qua&nbsp;<strong>Ki&ecirc;n Giang</strong>&nbsp;v&agrave;&nbsp;<strong>Vĩnh Long</strong>&nbsp;nhưng chưa c&oacute; thời gian kh&aacute;m ph&aacute; nhiều, Tường tiếc chưa được trải nghiệm đ&uacute;ng nghĩa m&ugrave;a nước nổi, thưởng thức c&aacute;c đặc sản như c&aacute; linh, b&ocirc;ng đi&ecirc;n điển...</p>\r\n\r\n<p>Cuối c&ugrave;ng nhắc đến&nbsp;<strong>Bến Tre</strong>, Tường n&oacute;i &quot;kh&ocirc;ng nơi n&agrave;o đặc biệt bằng nơi m&igrave;nh sinh ra&quot; n&ecirc;n cứ c&oacute; thời gian rảnh l&agrave; lại về thăm qu&ecirc;. Tường th&iacute;ch rong ruổi c&aacute;c con đường qu&ecirc; chụp lại c&aacute;c h&igrave;nh ảnh b&igrave;nh dị tr&ecirc;n qu&ecirc; hương, từ con k&ecirc;nh, đ&agrave;n g&agrave;, g&oacute;c bếp, những h&agrave;ng dừa cho đến c&aacute;c l&agrave;ng nghề thủ c&ocirc;ng....</p>\r\n\r\n<p>Tường c&oacute; ước mơ l&agrave; được đặt ch&acirc;n đi khắp nước Việt Nam để t&igrave;m hiểu th&ecirc;m nhiều bản sắc văn h&oacute;a v&ugrave;ng miền v&agrave; tận hưởng thi&ecirc;n nhi&ecirc;n. Tường muốn hiểu hơn về con người v&agrave; cuộc sống đ&oacute; đ&acirc;y, được phi&ecirc;u bạt tự do v&agrave; sống với ch&iacute;nh đam m&ecirc; của m&igrave;nh. Sắp tới, khi t&igrave;nh h&igrave;nh Covid-19 được kiểm so&aacute;t, Tường c&oacute; kế hoạch thực hiện chuyến đi v&ugrave;ng cao miền Bắc.</p>\r\n', 2, 1, 0, 1, 1),
(8, 'Người chuyên làm tour du lịch cho khách VVIP', 'Nguoi-Chuyen-Lam-Tour-Du-Lich-Cho-Khach-Vvip', '<p>Jaclyn Sienna India chuy&ecirc;n thiết kế h&agrave;nh tr&igrave;nh du lịch cho những người rất rất quan trọng (VVIP) như tổng thống, giới nh&agrave; gi&agrave;u, người nổi tiếng...</p>\r\n', 'http-cdn-cnn-com-cnnnext-dam-a-1186-4149-1635244596.jpg', '2021-10-31 00:45:01', 1, '<p>Trong chuyến du lịch c&aacute; nh&acirc;n đến Ethiopia năm 2015, cựu tổng thống Mỹ George W. Bush đ&atilde; c&oacute; những trải nghiệm bất ngờ v&agrave; th&uacute; vị. Khi mới đến thị trấn Lalibela cổ k&iacute;nh, h&agrave;ng chục trẻ em đội mũ Texas Rangers (đội b&oacute;ng ch&agrave;y y&ecirc;u th&iacute;ch của bang Texas) ch&agrave;o đ&oacute;n &ocirc;ng nồng nhiệt.</p>\r\n\r\n<p>Jaclyn Sienna India, nh&agrave; s&aacute;ng lập c&ocirc;ng ty du lịch phong c&aacute;ch sang trọng Sienna Charles, chia sẻ với&nbsp;<em>CNN</em>: &quot;Ch&uacute;ng t&ocirc;i đ&atilde; chuẩn bị thật nhiều bất ngờ cho chuyến đi đ&oacute;. T&ocirc;i cố gắng nghĩ ra nhiều c&aacute;ch để kh&aacute;ch hứng th&uacute;, n&ecirc;n đ&atilde; mang theo 100 chiếc mũ Texas Rangers. Khi &ocirc;ng Bush tới l&agrave;ng, lũ trẻ đ&atilde; đồng loạt đội ch&uacute;ng&quot;.</p>\r\n\r\n<p>&Ocirc;ng Bush kh&ocirc;ng phải l&agrave; vị kh&aacute;ch cao cấp đầu ti&ecirc;n m&agrave; India từng tổ chức tour. C&ocirc;ng ty của c&ocirc; đ&atilde; l&ecirc;n kế hoạch v&agrave; tổ chức hơn 100 chuyến đi cho 15 tổng thống, bộ trưởng, thủ tướng... từ năm 2012.</p>\r\n\r\n<p>India học về nghệ thuật tiếp kh&aacute;ch VIP ở Le Bec Fin (từng l&agrave; một nh&agrave; h&agrave;ng 3 sao Michelin). C&ocirc; l&agrave;m việc tại Le Bec Fin trong khi vẫn đang học để lấy bằng cử nh&acirc;n về lịch sử nghệ thuật tại một trường đại học tại Philadelphia, bang Pennsylvania, Mỹ.</p>\r\n\r\n<p>&quot;D&ugrave; l&agrave; một cựu tổng thống, tỷ ph&uacute; hay nh&acirc;n vật nổi tiếng th&igrave; ai cũng l&agrave; một c&aacute; nh&acirc;n. Khi hiểu được điều g&igrave; c&oacute; thể l&agrave;m họ hứng th&uacute; v&agrave; tại sao họ chọn chuyến đi đ&oacute; nghĩa l&agrave; bạn sẽ c&oacute; một mối quan hệ th&agrave;nh c&ocirc;ng. T&ocirc;i y&ecirc;u c&ocirc;ng việc phục vụ những người gi&agrave;u c&oacute;. T&ocirc;i thực sự thấu hiểu những y&ecirc;u cầu của họ. Đ&oacute; ch&iacute;nh l&agrave; b&iacute; quyết th&agrave;nh c&ocirc;ng trong sự nghiệp của t&ocirc;i&quot;.</p>\r\n\r\n<p>Tốt nghiệp đại học, c&ocirc; g&aacute;i New York chuyển đến Palm Beach, Florida với bạn trai v&agrave; hiện l&agrave; chồng c&ocirc;, Freddy Charles Reinert. Tại đ&acirc;y India nhanh ch&oacute;ng nhận ra những thiếu s&oacute;t của ng&agrave;nh dịch vụ du lịch xa xỉ.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/http-cdn-cnn-com-cnnnext-dam-a-9801-5001-1635244596.jpg\" style=\"height:354px; width:628px\" /></p>\r\n\r\n<p>&quot;Thật kh&oacute; hiểu khi ngồi ở một nh&agrave; h&agrave;ng 3 tiếng đồng hồ v&agrave; ti&ecirc;u 5.000 - 10.000 USD. C&oacute; tới 7 người đứng chờ để phục vụ bạn từng thứ một, kể cho bạn về nguồn gốc từ rượu cho đến m&oacute;n gan ngỗng...&quot; Mọi thứ dường như bị &aacute;m ảnh bởi lối phục vụ nh&agrave; h&agrave;ng m&agrave; kh&ocirc;ng ph&ugrave; hợp cho một chuyến du lịch sang trọng. Cũng kiểu kh&aacute;ch h&agrave;ng đ&oacute; họ c&oacute; thể ti&ecirc;u 100.000 USD cho một chuyến đi ở Italy hoặc Ph&aacute;p nhưng c&ocirc;ng ty du lịch lại chưa từng đến đ&oacute;.</p>\r\n\r\n<p>Mong muốn c&oacute; những chuyến du lịch tốt hơn cho giới si&ecirc;u gi&agrave;u, India v&agrave; chồng th&agrave;nh lập C&ocirc;ng ty Sienna Charles năm 2008. Mỗi chuyến đi của họ đều l&agrave;m theo y&ecirc;u cầu ri&ecirc;ng của kh&aacute;ch.</p>\r\n\r\n<p>Năm 2012, Sienna Charles lần đầu c&oacute; cơ hội tổ chức chuyến đi cho một l&atilde;nh đạo cấp cao thế giới. Khi tiếp cận India, vị kh&aacute;ch n&agrave;y (kh&ocirc;ng tiết lộ danh t&iacute;nh) đ&atilde; nhắm điểm đến Ai Cập. Người n&agrave;y từng c&oacute; c&aacute;c chuyến c&ocirc;ng t&aacute;c v&agrave; rất hứng th&uacute; với nền văn minh Ai Cập. V&igrave; thế c&ocirc;ng ty của India sắp xếp một h&agrave;nh tr&igrave;nh 12 ng&agrave;y đưa kh&aacute;ch kh&aacute;m ph&aacute; những n&eacute;t lịch sử, văn h&oacute;a c&ugrave;ng c&aacute;c học giả.</p>\r\n\r\n<p>Chuyến đi bắt đầu từ Cairo, di chuyển bằng thuyền ri&ecirc;ng l&ecirc;n du thuyền Nile, dừng ở nhiều điểm đến lịch sử tr&ecirc;n đường v&agrave; kết th&uacute;c ở cố đ&ocirc; Luxor, nơi được mệnh danh l&agrave; bảo t&agrave;ng mở vĩ đại nhất thế giới. Nhờ c&oacute; h&agrave;nh tr&igrave;nh xa xỉ đầu ti&ecirc;n cho l&atilde;nh đạo thế giới đ&oacute;, India tiếp tục nhận lời l&ecirc;n kế hoạch cho chuyến đi của George W. Bush v&agrave; nhiều vị kh&aacute;ch cao cấp kh&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/http-cdn-cnn-com-cnnnext-dam-a-1186-4149-1635244596.jpg\" style=\"height:336px; width:596px\" /></p>\r\n\r\n<p>Năm 2015, India trực tiếp thiết kế tour cho cựu tổng thống Bush v&agrave; vợ &ocirc;ng Laura, 4 người bạn, b&aacute;c sĩ v&agrave; 30 nh&acirc;n vi&ecirc;n Sở Mật Vụ. &Ocirc;ng Bush chọn Ethiopia v&igrave; &ocirc;ng từng đến đ&acirc;y trong thời gian l&agrave;m tổng thống nhưng chưa c&oacute; nhiều thời gian để kh&aacute;m ph&aacute; s&acirc;u hơn. &Ocirc;ng ấy kể đ&atilde; tới 80 quốc gia khi c&ograve;n đương nhiệm nhưng phần lớn điểm đến l&agrave; những nơi đẹp đẽ như Taj Mahal n&ecirc;n &iacute;t khi &ocirc;ng được thấy nhiều hơn do lịch tr&igrave;nh c&ocirc;ng việc.</p>\r\n\r\n<p>Để cựu tổng thống được t&igrave;m hiểu s&acirc;u về văn h&oacute;a, lịch sử điểm du lịch, India l&ecirc;n kế hoạch một chuyến đi d&agrave;i từ thung lũng xa x&ocirc;i ở s&ocirc;ng Omo (nơi c&oacute; những bộ tộc t&aacute;ch biệt nhất với thế giới) rồi đi l&ecirc;n miền bắc ở Lalibela. H&agrave;nh tr&igrave;nh đ&oacute; cần di chuyển bằng nhiều phương tiện từ SUV, trực thăng, cho tới m&aacute;y bay ri&ecirc;ng v&agrave; cả thuyền.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ thiết kế lịch tr&igrave;nh ri&ecirc;ng, những chi tiết như đồ ăn thức uống cũng được đ&aacute;p ứng theo y&ecirc;u cầu. V&iacute; như, &ocirc;ng Bush th&iacute;ch ăn sandwich với bơ lạc, v&agrave; mỳ ống khi đi du lịch n&ecirc;n India đảm bảo c&aacute;c m&oacute;n n&agrave;y lu&ocirc;n sẵn c&oacute; d&ugrave; ở những nơi hẻo l&aacute;nh của Ethiopia. V&agrave; v&igrave; c&aacute;c kh&aacute;ch sạn ở nước n&agrave;y kh&ocirc;ng đ&aacute;p ứng kỳ vọng, c&ocirc; g&aacute;i trẻ đ&atilde; kết nối với đối t&aacute;c địa phương để x&acirc;y dựng mới hẳn một khu cắm trại sang trọng cho &ocirc;ng Bush v&agrave; đo&agrave;n kh&aacute;ch đi c&ugrave;ng.</p>\r\n\r\n<p>Một điểm hay khi l&agrave;m việc với những vị kh&aacute;ch cao cấp n&agrave;y l&agrave; India c&oacute; thể tr&igrave;nh b&agrave;y đề xuất v&agrave; được ph&eacute;p thực hiện theo &yacute; m&igrave;nh. Một lần c&ocirc; tổ chức tiệc sinh nhật cho vợ một ch&iacute;nh kh&aacute;ch bằng c&aacute;ch phủ hết s&agrave;n villa bằng hoa hồng Cherry Snow nhập từ Ecuador. Lần kh&aacute;c c&ocirc; l&agrave;m việc với c&aacute;c nh&agrave; sản xuất phim v&agrave; chuy&ecirc;n gia thời trang để tạo bối cảnh phim như Paris giữa Miami (Mỹ). Ở Italy c&ocirc; từng xếp chuyến đi ri&ecirc;ng cho một l&atilde;nh đạo thế giới với một nh&agrave; thiết kế trang sức, gi&uacute;p &ocirc;ng t&igrave;m được m&oacute;n qu&agrave; ưng &yacute; cho vợ m&igrave;nh.</p>\r\n\r\n<p>Được l&ecirc;n kế hoạch nhưng India cũng gặp nhiều kh&oacute; khăn như an ninh, bảo mật cho chuyến đi của những người nổi tiếng. Vấn đề an ninh th&aacute;ch thức hơn khi kh&aacute;ch l&agrave; l&atilde;nh đạo quốc tế v&agrave; lu&ocirc;n c&oacute; vệ sĩ đi c&ugrave;ng. C&ocirc; từng phải t&igrave;m c&aacute;ch c&oacute; thể đ&oacute;ng cửa rạp h&aacute;t Sydney Opera, nh&agrave; thờ Hagia Sophia, bảo t&agrave;ng Louvre, điện Versailles, điểm du lịch như tượng nh&acirc;n sư, Machu Picchu... v&agrave; rất nhiều nơi kh&aacute;c khi kh&aacute;ch tới tham quan. &quot;Kh&aacute;ch sẵn l&ograve;ng trả tiền th&igrave; c&ocirc;ng việc n&agrave;y dễ d&agrave;ng hơn&quot;, c&ocirc; cho biết.</p>\r\n', 2, 1, 0, 1, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(9, 'Giới trẻ Sài Gòn lên kế hoạch xê dịch', 'Gioi-Tre-Sai-Gon-Len-Ke-Hoach-Xe-Dich', '<p>Đi chơi c&ugrave;ng gia đ&igrave;nh, xin nghỉ việc đi phượt, c&ugrave;ng bạn leo n&uacute;i... l&agrave; dự định của nhiều bạn trẻ khi S&agrave;i G&ograve;n trở về trạng th&aacute;i b&igrave;nh thường mới.</p>\r\n', 'gioi-tre-du-lich-02-7642-1635266184.jpg', '2021-10-31 00:55:49', 1, '<p>Minh Tr&acirc;m, Yến Nhi, Th&agrave;nh Chu... l&agrave; những người trẻ thuộc thế hệ cuối 9X đang sống v&agrave; l&agrave;m việc ở S&agrave;i G&ograve;n. Họ đều vừa trải qua khoảng thời gian căng thẳng v&igrave; dịch Covid-19. Với họ, được đi đ&acirc;y đi đ&oacute; khi t&igrave;nh h&igrave;nh ổn định hơn l&agrave; mong muốn lớn nhất hiện nay.</p>\r\n\r\n<p><strong>C&ugrave;ng nh&oacute;m bạn đi leo n&uacute;i</strong></p>\r\n\r\n<p>&quot;Dịch ổn rồi, th&aacute;ng sau l&ecirc;n k&egrave;o đi chị em&quot; l&agrave; tin nhắn rủ đi chơi từ một người bạn gửi đến nh&oacute;m chat của Minh Tr&acirc;m, 23 tuổi. Tin nhắn l&agrave;m Tr&acirc;m như được &quot;sống lại&quot; sau những ng&agrave;y chỉ xoay quanh dịch bệnh v&agrave; h&agrave;ng chục c&acirc;u hỏi như: ổn kh&ocirc;ng, c&oacute; mua được đồ ăn kh&ocirc;ng, đ&atilde; khỏe hơn chưa, c&oacute; bị giảm lương kh&ocirc;ng... suốt 4 th&aacute;ng TP HCM gi&atilde;n c&aacute;ch.</p>\r\n\r\n<p>V&igrave; Covid-19, Tr&acirc;m đ&atilde; ho&atilde;n kế hoạch về thăm qu&ecirc;, đi du lịch c&ugrave;ng bạn b&egrave;... Th&ecirc;m v&agrave;o đ&oacute;, những &aacute;p lực v&igrave; dịch bệnh, sức khỏe, c&ocirc;ng việc khiến c&ocirc; khao kh&aacute;t được đi chơi. &quot;Hiện t&ocirc;i đ&atilde; c&oacute; thẻ xanh Covid-19 v&agrave; h&agrave;ng tuần đều c&oacute; x&eacute;t nghiệm ở c&ocirc;ng ty, tu&acirc;n thủ 5K n&ecirc;n rất an t&acirc;m v&agrave; sẵn s&agrave;ng đi chơi c&ugrave;ng bạn b&egrave;&quot;, Tr&acirc;m hiện hức t&igrave;m kiếm th&ocirc;ng tin về nơi m&igrave;nh sắp đến, nghĩ tới việc sẽ mua th&ecirc;m quần &aacute;o...</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/gioi-tre-du-lich-4089-1635266184.jpg\" style=\"height:779px; width:680px\" /></p>\r\n\r\n<p>Tr&acirc;m n&oacute;i nh&oacute;m c&ocirc; dự định sẽ đi leo n&uacute;i Chứa Chan cao 837 m ở Đồng Nai, ngọn n&uacute;i cao thứ hai Đ&ocirc;ng Nam Bộ xếp sau n&uacute;i B&agrave; Đen, 986 m ở T&acirc;y Ninh dự kiến v&agrave;o giữa th&aacute;ng 11. Nh&oacute;m kết hợp cắm trại qua đ&ecirc;m để được gần gũi với thi&ecirc;n nhi&ecirc;n, h&iacute;t thở kh&iacute; trời.</p>\r\n\r\n<p>Tuy vậy, Tr&acirc;m cho biết mọi người đều kh&ocirc;ng chủ quan, lịch tr&igrave;nh di chuyển dự kiến theo dạng kh&eacute;p k&iacute;n, từ S&agrave;i G&ograve;n thẳng đến Đồng Nai, kh&ocirc;ng dừng ch&acirc;n dọc đường v&agrave; kh&ocirc;ng tiếp x&uacute;c người lạ. &quot;Nh&oacute;m m&igrave;nh mua sẵn đồ ăn, thức uống, vật dụng cần thiết từ S&agrave;i G&ograve;n mang đến Đồng Nai rồi đi leo n&uacute;i n&ecirc;n chuyến đi vẫn được đảm bảo. M&igrave;nh cũng thấy y&ecirc;n t&acirc;m, đảm bảo an to&agrave;n cho m&igrave;nh v&agrave; người kh&aacute;c&quot;, Tr&acirc;m n&oacute;i.</p>\r\n\r\n<p><strong>Đưa cả gia đ&igrave;nh đi nghỉ dưỡng</strong></p>\r\n\r\n<p>Sau gi&atilde;n c&aacute;ch x&atilde; hội, đầu th&aacute;ng 10, Yến Nhi, 22 tuổi, bắt đầu t&igrave;m kiếm th&ocirc;ng tin c&aacute;c tỉnh th&agrave;nh mở cửa du lịch để đưa gia đ&igrave;nh đi chơi. Chuyến đi Nha Trang được Nhi ấp ủ từ trước đợt dịch Covid-19 lần thứ tư nhưng rồi bị ho&atilde;n. Sau đ&oacute; c&ocirc; c&agrave;ng quyết t&acirc;m phải thực hiện cho bằng được chuyến đi khi b&agrave; v&agrave; mẹ của c&ocirc; lần lượt mắc Covid-19 v&agrave; rồi an to&agrave;n vượt qua dịch bệnh.</p>\r\n\r\n<p>&quot;Cuộc sống ngắn ngủi n&ecirc;n m&igrave;nh phải tr&acirc;n trọng, d&agrave;nh những điều tốt nhất cho người m&igrave;nh y&ecirc;u thương&quot;, Nhi b&agrave;y tỏ.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/gioi-tre-du-lich-01-4288-1635266184.jpg\" style=\"height:453px; width:680px\" /></p>\r\n\r\n<p>Gia đ&igrave;nh Nhi dự định sẽ c&oacute; chuyến nghỉ dưỡng ở Nha Trang 3 ng&agrave;y 2 đ&ecirc;m, gồm b&agrave; ngoại, mẹ, bạn của mẹ v&agrave; Nhi, thời gian dự kiến v&agrave;o giữa th&aacute;ng 11. Hiện c&ocirc; vẫn đang ph&acirc;n v&acirc;n giữa đi tự t&uacute;c v&agrave; tour kh&eacute;p k&iacute;n từ c&aacute;c c&ocirc;ng ty lữ h&agrave;nh. Nhi vẫn th&iacute;ch được tự l&ecirc;n kế hoạch cho chuyến đi v&igrave; gia đ&igrave;nh th&iacute;ch những điểm đến t&acirc;m linh, v&atilde;n cảnh ch&ugrave;a.</p>\r\n\r\n<p>N&oacute;i về chuyến đi sắp tới, c&ocirc; dự định sẽ tới tắm biển Nha Trang, đi thăm ch&ugrave;a Suối Đổ, Tr&uacute;c L&acirc;m Tịnh Viện rồi đến Viện Hải dương học Nha Trang, tranh thủ đi những ng&agrave;y trong tuần để tr&aacute;nh đ&ocirc;ng người.</p>\r\n\r\n<p><strong>Xin nghỉ l&agrave;m một tuần để đi phượt</strong></p>\r\n\r\n<p>L&agrave; người y&ecirc;u th&iacute;ch độc h&agrave;nh, từ năm 17 tuổi đến nay, Th&agrave;nh Chu, 24 tuổi đ&atilde; đến 54 tỉnh, th&agrave;nh tr&ecirc;n cả nước, trong đ&oacute; c&oacute; một chuyến xuy&ecirc;n Việt. Đợt dịch Covid-19 lần thứ tư, Th&agrave;nh l&agrave;m việc tại nh&agrave; v&agrave; bị ảnh hưởng th&ocirc;ng tin ti&ecirc;u cực về dịch bệnh, khiến t&acirc;m trạng thường xuy&ecirc;n buồn b&atilde;. L&uacute;c n&agrave;y anh lại c&agrave;ng nhớ đến h&agrave;nh tr&igrave;nh hơn 25 ng&agrave;y ở đảo Thổ Chu ở tỉnh Ki&ecirc;n Giang, những ng&agrave;y rong ruổi khắp nẻo đường T&acirc;y Bắc...</p>\r\n\r\n<p>&quot;Dự định đầu ti&ecirc;n của m&igrave;nh sau gi&atilde;n c&aacute;ch l&agrave; sẽ xin nghỉ l&agrave;m một tuần để đi phượt một v&ograve;ng Nha Trang rồi đi đ&egrave;o Kh&aacute;nh L&ecirc;, nơi c&oacute; bốn m&ugrave;a trong một ng&agrave;y, sau đ&oacute; l&ecirc;n Đ&agrave; Lạt&quot;, Th&agrave;nh n&oacute;i về kế hoạch phượt sắp tới của m&igrave;nh bằng xe m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/gioi-tre-du-lich-02-7642-1635266184.jpg\" style=\"height:473px; width:680px\" />Ảnh hưởng t&acirc;m l&yacute; từ dịch Covid-19, Th&agrave;nh cho biết anh ngại đến c&aacute;c điểm du lịch đ&ocirc;ng người, sẽ chọn c&aacute;c nơi gần gũi với thi&ecirc;n nhi&ecirc;n, &iacute;t người, c&oacute; kh&ocirc;ng gian xanh m&aacute;t để tận hưởng cảm gi&aacute;c được một m&igrave;nh, h&iacute;t thở kh&iacute; trời thật thoải m&aacute;i. Hiện anh đ&atilde; c&oacute; thẻ xanh Covid-19 v&agrave; sẽ t&igrave;m hiểu thật kỹ y&ecirc;u cầu đi lại, du lịch tại c&aacute;c địa phương nơi m&igrave;nh đi qua v&agrave; tu&acirc;n thủ c&aacute;c quy định ph&ograve;ng chống dịch Covid-19 trong suốt h&agrave;nh tr&igrave;nh một tuần.</p>\r\n', 2, 1, 0, 1, 1),
(10, 'Điểm vui chơi Halloween ở Hà Nội và TP HCM', 'Diem-Vui-Choi-Halloween-O-Ha-Noi-Va-Tp-Hcm', '<p>Khi nhiều hoạt động chưa mở lại, những qu&aacute;n c&agrave; ph&ecirc;, d&atilde;y phố trang tr&iacute; theo chủ đề lễ hội Halloween l&agrave; gợi &yacute; cho người d&acirc;n hai th&agrave;nh phố.</p>\r\n', 'hlw1-4304-1635509085.jpg', '2021-10-31 00:58:33', 1, '<p>Halloween l&agrave; ng&agrave;y hội truyền thống của c&aacute;c nước phương T&acirc;y được tổ chức v&agrave;o ng&agrave;y 31/10 hằng năm. Đ&acirc;y l&agrave; ng&agrave;y đ&aacute;nh dấu sự kết th&uacute;c của vụ m&ugrave;a thu hoạch v&agrave; bắt đầu m&ugrave;a đ&ocirc;ng, đồng thời tưởng nhớ những người đ&atilde; qua đời, gồm cả c&aacute;c vị th&aacute;nh, c&aacute;c vị tử đạo...</p>\r\n\r\n<p>Ng&agrave;y lễ n&agrave;y được du nhập v&agrave;o Việt Nam, được xem l&agrave; dịp để mọi người h&oacute;a trang ma quỷ, trang tr&iacute; b&iacute; ng&ocirc;... Trong khi nhiều nơi chưa mở cửa trở lại, song c&oacute; một v&agrave;i địa điểm đ&atilde; h&ograve;a chung kh&ocirc;ng kh&iacute; dịp n&agrave;y, đặc biệt ở H&agrave; Nội v&agrave; TP HCM.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/hlw1-4304-1635509085.jpg\" style=\"height:492px; width:680px\" /></p>\r\n\r\n<p><strong>H&agrave; Nội - &quot;Thời tiết l&agrave; thứ đ&aacute;ng sợ nhất Halloween&quot;</strong></p>\r\n\r\n<p>Thời tiết 3 ng&agrave;y cuối tuần dịp Halloween tại thủ đ&ocirc; dự đo&aacute;n sẽ mưa li&ecirc;n tục. V&igrave; vậy c&aacute;c qu&aacute;n ăn, c&agrave; ph&ecirc; được dự đo&aacute;n sẽ h&uacute;t kh&aacute;ch. Nếu du kh&aacute;ch muốn trải nghiệm chụp ảnh với m&ocirc; h&igrave;nh b&iacute; ng&ocirc;, c&oacute; thể tham khảo c&aacute;c qu&aacute;n: Cafe de Mo ở phố Ph&ugrave;ng Hưng với c&aacute;c m&ocirc; h&igrave;nh ma qu&aacute;i kiểu Nhật kết hợp với b&iacute; ng&ocirc; v&agrave; đ&egrave;n lồng; Lofita ở phố H&agrave;o Nam với m&ocirc; h&igrave;nh đầu l&acirc;u v&agrave; mạng nhện... Nếu muốn thử c&aacute;c đồ uống được trang tr&iacute; theo kiểu Halloween, người d&acirc;n thủ đ&ocirc; c&oacute; thể đến hệ thống Starbucks hoặc Tous les Jours. Ngo&agrave;i ra, qu&aacute;n Always Cafe ở H&agrave;ng Tre với chủ đề trang tr&iacute; theo phim Harry Potter phục vụ Pumpkin Spice Latte (Latte hương b&iacute; ng&ocirc;).</p>\r\n\r\n<p>Như c&aacute;c dịp lễ kh&aacute;c như Tết Nguy&ecirc;n đ&aacute;n, Trung thu hay Gi&aacute;ng sinh, người d&acirc;n thủ đ&ocirc; c&oacute; thể đến phố H&agrave;ng M&atilde; để cảm nhận kh&ocirc;ng kh&iacute;. V&agrave;o c&aacute;c dịp n&agrave;y, con phố lại b&agrave;y b&aacute;n c&aacute;c m&oacute;n đồ trang tr&iacute; tương ứng. Trước ng&agrave;y Halloween, c&aacute;c h&agrave;ng qu&aacute;n đ&atilde; trưng b&agrave;y nhiều trang phục v&agrave; đồ trang tr&iacute; như mũ ph&ugrave; thủy, lồng đ&egrave;n b&iacute; ng&ocirc;, quần &aacute;o in h&igrave;nh xương, gậy thần chết, mặt nạ kinh dị...</p>\r\n\r\n<p>Mọi năm, phố đi bộ Hồ Gươm thường thu h&uacute;t c&aacute;c bạn trẻ mặc đồ h&oacute;a trang đến đi dạo, song năm nay, do t&igrave;nh h&igrave;nh dịch bệnh, phố đi bộ chưa quay trở lại hoạt động.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/hlw3-1924-1635509086.jpg\" style=\"height:418px; width:680px\" /></p>\r\n\r\n<p><strong>TP HCM - Halloween vắng lặng hơn mọi năm</strong></p>\r\n\r\n<p>Ở TP HCM, mọi năm đến ng&agrave;y Halloween, phố đi bộ B&ugrave;i Viện, c&ocirc;ng vi&ecirc;n L&ecirc; Thị Ri&ecirc;ng, trung t&acirc;m thương mại c&ugrave;ng nhiều qu&aacute;n c&agrave; ph&ecirc; quận 1, quận 3 được tạo h&igrave;nh, trang tr&iacute; đủ kiểu cho kh&aacute;ch chụp ảnh, check in. Ri&ecirc;ng năm nay, do ảnh hưởng từ dịch Covid-19, nơi đ&acirc;y vắng lặng hơn.</p>\r\n\r\n<p>Phố đi bộ B&ugrave;i Viện, quận 1 thường l&agrave; điểm đến h&agrave;ng đầu cho kh&aacute;ch Việt v&agrave; cả kh&aacute;ch nước ngo&agrave;i v&igrave; diễn ra nhiều hoạt động h&oacute;a trang tưng bừng. Năm nay, nhiều h&agrave;ng qu&aacute;n tr&ecirc;n phố đ&oacute;ng cửa hoặc chuyển đổi m&ocirc; h&igrave;nh kinh doanh. Hiện dọc con phố đ&atilde; c&oacute; c&aacute;c h&agrave;ng qu&aacute;n ẩm thực b&agrave;y b&aacute;n trở lại như h&agrave;ng ốc hấp, lẩu, chả vi&ecirc;n chi&ecirc;n... cho thực kh&aacute;ch ăn tại chỗ. D&ugrave; kh&ocirc;ng qu&aacute; nhộn nhịp c&aacute;c hoạt động h&oacute;a trang nhưng đ&acirc;y cũng l&agrave; điểm đến gợi &yacute; cho du kh&aacute;ch TP HCM trong dịp Halloween n&agrave;y.</p>\r\n\r\n<p>Sau phố đi bộ B&ugrave;i Viện, phố đi bộ Nguyễn Huệ cũng l&agrave; điểm gợi &yacute; cho du kh&aacute;ch, con phố d&agrave;i hơn 600 m c&oacute; kh&ocirc;ng gian tho&aacute;ng m&aacute;t, nhiều h&agrave;ng qu&aacute;n c&agrave; ph&ecirc;, tr&agrave; sữa xung quanh gi&uacute;p bạn c&oacute; một nơi tr&ograve; chuyện, gặp gỡ bạn b&egrave; l&yacute; tưởng. Nơi n&agrave;y được dự đo&aacute;n sẽ c&oacute; đ&ocirc;ng du kh&aacute;ch vui chơi tối 30, 31/10. Hiện con phố cũng c&oacute; c&aacute;c bạn trẻ h&oacute;a th&acirc;n th&agrave;nh c&aacute;c si&ecirc;u anh h&ugrave;ng, T&ocirc;n Ngộ Kh&ocirc;ng... chụp ảnh c&ugrave;ng kh&aacute;ch tham quan c&oacute; t&iacute;nh ph&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/quan-ca-phe-sai-gon-05-9947-16-7678-7117-1635509086.jpg\" style=\"height:680px; width:680px\" />Một qu&aacute;n c&agrave; ph&ecirc; được gợi &yacute; trong dịp Halloween n&agrave;y l&agrave; Cheese Coffee, nằm ở vị tr&iacute; đắc địa tr&ecirc;n đường Đinh Ti&ecirc;n Ho&agrave;ng, gần đoạn giao Nguyễn Thị Minh Khai, quận 1. Qu&aacute;n được thiết kế theo phong c&aacute;ch Ph&aacute;p, điểm nhấn l&agrave; t&ocirc;ng m&agrave;u đen từ sơn tường, m&aacute;i v&ograve;m, viền cửa k&iacute;nh, đ&egrave;n trang tr&iacute;, lan can... T&ecirc;n qu&aacute;n, địa chỉ th&igrave; nhấn nh&aacute; bằng m&agrave;u v&agrave;ng đồng nổi bật, h&uacute;t nhiều bạn trẻ đến mua nước v&agrave; chờ chụp ảnh b&ecirc;n ngo&agrave;i. Du kh&aacute;ch c&oacute; thể đến d&ugrave;ng nước tại qu&aacute;n v&agrave; check in với bức tường m&agrave;u đen huyền ảo tại qu&aacute;n để c&oacute; ảnh đẹp, đ&uacute;ng kh&ocirc;ng kh&iacute; ma mị ng&agrave;y Halloween.</p>\r\n', 3, 1, 0, 1, 1),
(11, 'Lịch sự trên máy bay, hành khách sẽ được hưởng lợi', 'Lich-Su-Tren-May-Bay,-Hanh-Khach-Se-Duoc-Huong-Loi', '<p>&quot;Nếu cơ trưởng y&ecirc;u cầu xếp lại chỗ ngồi v&agrave; t&ocirc;i phải chọn một trong hai người l&ecirc;n vị tr&iacute; thoải m&aacute;i hơn, t&ocirc;i sẽ chọn người đối xử tử tế với m&igrave;nh&quot;, một tiếp vi&ecirc;n bật m&iacute;.</p>\r\n', 'img-5448-1635129700.jpg', '2021-10-31 01:03:04', 1, '<p>Tiếp vi&ecirc;n h&agrave;ng kh&ocirc;ng Helena Afroughi đ&atilde; tiết lộ những l&yacute; do gi&uacute;p h&agrave;nh kh&aacute;ch ngồi hạng phổ th&ocirc;ng c&oacute; thể nhận được một số đặc quyền như hạng thương gia.</p>\r\n\r\n<p>Trước hết, điều n&agrave;y dựa v&agrave;o h&agrave;nh động lịch sự, tử tế của h&agrave;nh kh&aacute;ch v&agrave; c&aacute;ch họ tạo ấn tượng với phi h&agrave;nh đo&agrave;n. Điều lu&ocirc;n hiệu quả l&agrave; khi mọi người mang tặng tiếp vi&ecirc;n một số m&oacute;n đồ, chẳng hạn như chocolate. Khi l&agrave;m thế, họ gi&uacute;p bản th&acirc;n được phi h&agrave;nh đo&agrave;n để &yacute; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/img-5448-1635129700.jpg\" style=\"height:291px; width:680px\" /></p>\r\n\r\n<p>Th&ocirc;ng thường, mọi người l&agrave;m vậy như một lời cảm ơn, hoặc động vi&ecirc;n với tiếp vi&ecirc;n. Nhưng với phi h&agrave;nh đo&agrave;n, họ đ&aacute;nh gi&aacute; cao những cử chỉ nhỏ. Helena cho rằng những người l&agrave;m điều n&agrave;y v&igrave; họ thực sự tốt bụng. &quot;Khi mọi người l&agrave;m như thế, tiếp vi&ecirc;n trưởng của chuyến bay sẽ thể hiện với ch&uacute;ng t&ocirc;i kiểu: h&atilde;y chắc chắn l&agrave; c&aacute;c bạn sẽ đến gặp vị kh&aacute;ch tử tế đ&oacute;, v&agrave; hỏi xem anh ấy c&oacute; muốn tr&agrave;, hay c&agrave; ph&ecirc; kh&ocirc;ng. V&igrave; những m&oacute;n n&agrave;y, ch&uacute;ng t&ocirc;i c&oacute; thể cung cấp miễn ph&iacute;&quot;. Tuy nhi&ecirc;n, Helena n&oacute;i rằng việc tặng qu&agrave; kh&ocirc;ng đồng nghĩa với việc h&agrave;nh kh&aacute;ch được ăn miễn ph&iacute; mọi thứ.</p>\r\n\r\n<p>Một trong những điều dễ thương nhất m&agrave; Helena từng gặp trong sự nghiệp l&agrave;m tiếp vi&ecirc;n của m&igrave;nh l&agrave; một vị kh&aacute;ch nh&iacute; đ&atilde; đến tặng phi h&agrave;nh đo&agrave;n những chai xịt khuẩn tay. Đ&oacute; l&agrave; m&oacute;n qu&agrave; m&agrave; cậu nh&oacute;c đ&atilde; mua ở một cửa h&agrave;ng mỹ phẩm tại Mỹ trong chuyến du lịch. &quot;Đ&oacute; l&agrave; thứ dễ thương nhất từ trước đến nay&quot;, Helena n&oacute;i.</p>\r\n\r\n<p>Ngo&agrave;i ra, c&oacute; một mẹo rất hữu &iacute;ch kh&aacute;c để g&acirc;y ấn tượng với c&aacute;c tiếp vi&ecirc;n l&agrave; ch&agrave;o hỏi họ, v&agrave; c&oacute; thể n&oacute;i một v&agrave;i c&acirc;u chuyện. Tất nhi&ecirc;n, bạn n&ecirc;n tr&aacute;nh l&uacute;c tiếp vi&ecirc;n đang hỗ trợ người kh&aacute;c. &quot;Bạn c&oacute; thể bắt chuyện với ch&uacute;ng t&ocirc;i, v&agrave; tất nhi&ecirc;n trong trường hợp ch&uacute;ng t&ocirc;i kh&ocirc;ng bận. V&agrave; sau đ&oacute; th&igrave;, nếu bạn cần y&ecirc;u cầu g&igrave;, h&atilde;y n&oacute;i. Nếu bạn hỏi một c&aacute;ch tử tế v&agrave; lịch sự, rất hiếm khi phi h&agrave;nh đo&agrave;n từ chối&quot;, Helena n&oacute;i.</p>\r\n\r\n<p>C&aacute;c h&atilde;ng bay đều c&oacute; quy định ri&ecirc;ng, v&agrave; c&oacute; những thứ tiếp vi&ecirc;n được ph&eacute;p tặng miễn ph&iacute;. Nhưng việc c&oacute; thể n&acirc;ng hạng cho một vị kh&aacute;ch thực sự l&agrave; y&ecirc;u cầu kh&oacute;. Họ sẽ hiếm khi đồng &yacute; v&igrave; điều n&agrave;y c&oacute; thể ảnh hưởng đến c&ocirc;ng việc, vượt qu&aacute; giới hạn. Do đ&oacute;, nếu muốn xin n&acirc;ng hạng, bạn h&atilde;y tới gặp những tiếp vi&ecirc;n cao cấp, như trưởng nh&oacute;m. Đ&oacute; l&agrave; những người biết r&otilde; quyền hạn tối đa được ph&eacute;p. &quot;Hoặc h&atilde;y nhờ một người c&oacute; vẻ ngo&agrave;i th&acirc;n thiện, tốt bụng. Đừng t&igrave;m tiếp vi&ecirc;n nghi&ecirc;m khắc hoặc gắt gỏng&quot;, Helena bật m&iacute;.</p>\r\n\r\n<p>Ngo&agrave;i ra, bạn kh&ocirc;ng n&ecirc;n xin bất kỳ thứ g&igrave; miễn ph&iacute; trước mặt c&aacute;c h&agrave;nh kh&aacute;ch kh&aacute;c. Điều đ&oacute; sẽ g&acirc;y hiệu ứng ghen tỵ v&agrave; cả m&aacute;y bay sẽ c&ugrave;ng đ&ograve;i thế, dẫn đến lời đề nghị của bạn dễ bị từ chối. Ngo&agrave;i ra, việc hỗ trợ tiếp vi&ecirc;n xử l&yacute; những kẻ g&acirc;y rối tr&ecirc;n m&aacute;y bay cũng l&agrave; một lợi thế gi&uacute;p bạn &quot;gần hơn&quot; với tổ bay. &quot;Những vị kh&aacute;ch g&acirc;y rối lu&ocirc;n l&agrave; cơn &aacute;c mộng với ch&uacute;ng t&ocirc;i&quot;, một tiếp vi&ecirc;n kh&aacute;c chia sẻ.</p>\r\n\r\n<p>Miguel Munoz, một tiếp vi&ecirc;n sống tại Anh v&agrave; c&oacute; nhiều năm kinh nghiệm bay, n&oacute;i th&ecirc;m: &quot;Để được đổi sang một chỗ ngồi thoải m&aacute;i, bạn cần tử tế v&agrave; tử tế, v&agrave; đưa ra y&ecirc;u cầu&quot;. Miguel cũng hy vọng h&agrave;nh kh&aacute;ch khi gặp tiếp vi&ecirc;n đứng ở cửa m&aacute;y bay c&oacute; thể đ&aacute;p lại lời ch&agrave;o hỏi của họ, hỏi họ ng&agrave;y h&ocirc;m đ&oacute; như thế n&agrave;o v&agrave; &quot;h&atilde;y đối xử với ch&uacute;ng t&ocirc;i như những con người, chứ kh&ocirc;ng phải những kẻ phục vụ bạn&quot;. Miguel n&oacute;i th&ecirc;m. Tiếp vi&ecirc;n c&oacute; mặt tr&ecirc;n m&aacute;y bay kh&ocirc;ng chỉ phục vụ kh&aacute;ch. Ưu ti&ecirc;n số một của họ l&agrave; đảm bảo sự an to&agrave;n cho h&agrave;nh kh&aacute;ch. &quot;Do đ&oacute;, nếu cơ trưởng y&ecirc;u cầu sắp xếp lại một số chỗ ngồi tr&ecirc;n m&aacute;y bay, v&agrave; t&ocirc;i phải chọn giữa hai người để ngồi vị tr&iacute; thoải m&aacute;i hơn, t&ocirc;i sẽ chọn người đối xử tử tế với m&igrave;nh&quot;, nam tiếp vi&ecirc;n n&oacute;i.</p>\r\n', 3, 1, 0, 1, 1),
(12, 'Canh bọ cạp - món ăn làm thực khách rùng mình', 'Canh-Bo-Cap---Mon-An-Lam-Thuc-Khach-Rung-Minh', '<p>L&agrave; m&oacute;n ăn truyền thống của phần lớn d&acirc;n miền nam Trung Quốc, nhưng canh bọ cạp hầm nguy&ecirc;n con vẫn khiến nhiều người khiếp sợ.</p>\r\n', 'ezgif-3-495f59575a59-3297-1635325781.jpg', '2021-10-31 01:04:21', 1, '<p>Trong khi ở nhiều nước phương T&acirc;y, ăn những lo&agrave;i c&ocirc;n tr&ugrave;ng chứa độc tố l&agrave; điều đ&aacute;ng sợ, th&igrave; ở miền nam Trung Quốc, người d&acirc;n lại c&oacute; th&oacute;i quen l&acirc;u đời ăn c&aacute;c lo&agrave;i như rết đầu đỏ, bọ cạp, bọ x&iacute;t h&ocirc;i n&acirc;u... Trong đ&oacute;, bọ cạp được ưa chuộng nhất v&igrave; người Trung Quốc tin đ&acirc;y l&agrave; một vị thuốc qu&yacute;. Kh&ocirc;ng xi&ecirc;n que nướng, chi&ecirc;n gi&ograve;n như c&aacute;c h&agrave;ng qu&aacute;n b&aacute;n cho kh&aacute;ch du lịch ở Bắc Kinh v&agrave; nhiều th&agrave;nh phố ph&iacute;a bắc, người d&acirc;n miền nam nấu th&agrave;nh m&oacute;n canh bọ cạp truyền thống v&agrave; ăn như khai vị hoặc d&ugrave;ng trong bữa cơm.</p>\r\n\r\n<p>Để canh ngon cần tất cả c&aacute;c nguy&ecirc;n liệu tươi sống. Đặc biệt bọ cạp cần được ng&acirc;m nước hoặc nước muối để xử l&yacute; kỹ phần đu&ocirc;i c&oacute; độc. Bọ cạp được rửa sạch, xử l&yacute; độc tố rồi hầm nhiều giờ c&ugrave;ng thịt lợn, tỏi, gừng v&agrave; một số rau củ hoặc kết hợp c&aacute;c loại thịt kh&aacute;c như rắn, cừu, ba ba.</p>\r\n\r\n<p>Nhiều thực kh&aacute;ch m&ocirc; tả m&oacute;n canh bọ cạp c&oacute; vị gần giống canh g&agrave; hay thịt th&ocirc;ng thường. Kh&ocirc;ng mang lại nhiều hương vị đặc trưng nhưng bọ cạp l&agrave; nguy&ecirc;n liệu quan trọng v&agrave; c&oacute; nhiều t&aacute;c dụng cho sức khỏe. D&ugrave; vậy, canh nấu bọ cạp nguy&ecirc;n con n&ecirc;n mới nh&igrave;n, nhiều người kể cả l&agrave; người Trung Quốc hay du kh&aacute;ch nước ngo&agrave;i, cũng cảm thấy r&ugrave;ng m&igrave;nh.</p>\r\n\r\n<p>Nếu bạn đến những khu chợ thực phẩm tươi sống ở tỉnh Quảng Ch&acirc;u sẽ dễ gặp c&aacute;c sạp h&agrave;ng b&aacute;n c&ocirc;n tr&ugrave;ng độc để l&agrave;m c&aacute;c m&oacute;n ăn chữa bệnh. Ở đ&acirc;y c&oacute; qu&aacute;n ăn Dade Dun Pin ở huyện Yuexiu, mở cửa từ năm 1984 v&agrave; chỉ b&aacute;n c&aacute;c m&oacute;n canh rắn v&agrave; bọ cạp. Theo nh&acirc;n vi&ecirc;n qu&aacute;n, canh được hầm kỹ trong v&ograve;ng 3-4 tiếng, l&agrave; một m&oacute;n bổ dưỡng c&oacute; t&aacute;c dụng giải độc cơ thể. D&ugrave; lời giới thiệu l&ocirc;i cuốn chừng n&agrave;o th&igrave; khi b&aacute;t canh đem ra, kh&ocirc;ng phải ai cũng muốn ăn v&igrave; bề ngo&agrave;i k&eacute;m hấp dẫn.</p>\r\n\r\n<p>Theo Matthew Bossons, một c&acirc;y viết người Canada hiện sống ở Thượng Hải, b&aacute;t canh bọ cạp ở Dae Dun Pin c&oacute; những lớp thịt b&ocirc;ng nổi trong nước d&ugrave;ng c&ugrave;ng với một con bọ cạp đen c&ograve;n nguy&ecirc;n ch&acirc;n v&agrave; đu&ocirc;i. Anh thử th&igrave;a canh đầu ti&ecirc;n v&agrave; nghe thấy những người d&acirc;n địa phương ngồi b&agrave;n b&ecirc;n chia sẻ, m&oacute;n n&agrave;y hơi giống canh g&agrave;, vị ngọt quyện với lớp thịt lợn mềm v&agrave; tan trong miệng.</p>\r\n\r\n<p>Sau khi thấy nhiều thực kh&aacute;ch ăn con bọ cạp, nh&acirc;n vi&ecirc;n qu&aacute;n cho hay, thực chất ch&uacute;ng kh&ocirc;ng phải để ăn v&igrave; chất bổ đ&atilde; ngấm ra phần nước canh. Cuối c&ugrave;ng, Mathew Bossons vẫn thử cắn con bọ cạp, lớp vỏ cứng gi&ograve;n vỡ l&agrave;m phần thịt b&ecirc;n trong bung ra, để lại hương vị kh&oacute; tả.</p>\r\n', 3, 1, 0, 1, 1),
(13, 'Đi leo núi, cắm trại một mình sau giãn cách', 'Di-Leo-Nui,-Cam-Trai-Mot-Minh-Sau-Gian-Cach', '<p>Sau Covid-19, nhiều bạn trẻ chọn đi leo n&uacute;i một m&igrave;nh hoặc theo nh&oacute;m nhỏ để giải tỏa sự &quot;cuồng ch&acirc;n&quot; m&agrave; vẫn đảm bảo an to&agrave;n.</p>\r\n', 'cam-trai-mot-minh-02-3267-1635328084.jpg', '2021-10-31 01:07:24', 1, '<p>Từ giữa th&aacute;ng 10, hội nh&oacute;m rủ nhau leo n&uacute;i Chứa Chan tr&ecirc;n Facebook thường đăng tải h&igrave;nh ảnh, video c&aacute;c bạn trẻ leo n&uacute;i trở lại sau gi&atilde;n c&aacute;ch. B&ecirc;n dưới h&igrave;nh ảnh check in tr&ecirc;n những cung đường l&agrave; loạt thắc mắc về c&aacute;c thủ tục cần thiết như c&oacute; cần giấy x&eacute;t nghiệm &acirc;m t&iacute;nh nCoV kh&ocirc;ng, c&oacute; gặp chốt chặn tr&ecirc;n đường kh&ocirc;ng... Theo hướng dẫn, người leo n&uacute;i cần c&oacute; thẻ xanh Covid-19, x&eacute;t nghiệm trước khi đi nếu c&oacute; triệu chứng, tu&acirc;n thủ c&aacute;c biện ph&aacute;p ph&ograve;ng chống dịch.</p>\r\n\r\n<p>Phạm Mạnh Tiến, sinh năm 1994, vừa c&oacute; chuyến leo n&uacute;i Chứa Chan 837 m hồi đầu tuần. Đ&acirc;y l&agrave; ngọn n&uacute;i cao thứ hai ở Đ&ocirc;ng Nam Bộ, sau n&uacute;i B&agrave; Đen, 986 m ở T&acirc;y Ninh.</p>\r\n\r\n<p>N&uacute;i Chứa Chan l&agrave; điểm đến y&ecirc;u th&iacute;ch của Tiến sau những ng&agrave;y l&agrave;m việc căng thẳng. Ngọn n&uacute;i c&aacute;ch th&agrave;nh phố khoảng 110 km, c&oacute; thể đi trong ng&agrave;y hoặc cắm trại nghỉ qua đ&ecirc;m tr&ecirc;n đỉnh. &quot;4 th&aacute;ng ở nh&agrave; vừa rồi m&igrave;nh cuồng ch&acirc;n kh&ocirc;ng chịu được n&ecirc;n khi được đi lại m&igrave;nh đ&atilde; mang theo hết &#39;đồ nghề&#39; l&ecirc;n n&uacute;i cắm trại. Dịch cũng mới được kiểm so&aacute;t n&ecirc;n m&igrave;nh chọn c&aacute;ch đi một m&igrave;nh để hạn chế tiếp x&uacute;c&quot;, Tiến n&oacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/cam-trai-mot-minh-1260-1635328083.jpg\" style=\"height:510px; width:680px\" /></p>\r\n\r\n<p>Trước khi l&ecirc;n đường, Tiến chuẩn bị đồ đạc, vật dụng cắm trại rồi mang thức ăn từ nh&agrave; theo, dọc đường chỉ gh&eacute; một tiệm cơm tại ng&atilde; ba Dầu D&acirc;y nghỉ ch&acirc;n khi qu&aacute;n kh&ocirc;ng c&oacute; kh&aacute;ch v&agrave; tiếp tục di chuyển đến n&uacute;i Chứa Chan.</p>\r\n\r\n<p>Sau 4 th&aacute;ng quay lại leo n&uacute;i, Tiến chậm r&atilde;i từng bước nhỏ khi khởi h&agrave;nh cho cơ thể quen dần. Mang theo nhiều đồ n&ecirc;n anh cũng cẩn thận để tr&aacute;nh qu&aacute; sức hoặc bị chuột r&uacute;t. Chuyến đi n&agrave;y anh mất 3 giờ để l&ecirc;n đến đỉnh ngắm ho&agrave;ng h&ocirc;n. Thời gian cũng tương đương với những chuyến đi trước đ&acirc;y.</p>\r\n\r\n<p>Từng đi leo n&uacute;i, cắm trại một m&igrave;nh tr&ecirc;n n&uacute;i Chứa Chan trước đ&oacute; n&ecirc;n Tiến kh&ocirc;ng gặp nhiều kh&oacute; khăn. Tuy nhi&ecirc;n, anh n&oacute;i phải biết tự lượng sức v&igrave; khi đi một m&igrave;nh c&oacute; sức khỏe ổn định l&agrave; điều quan trọng.</p>\r\n\r\n<p>Sau nhiều th&aacute;ng kh&ocirc;ng đến đ&acirc;y, Tiến thấy cỏ xanh tr&ecirc;n n&uacute;i c&oacute; đoạn mọc vượt qu&aacute; đầu người, kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; m&aacute;t mẻ hơn. &quot;Cảm gi&aacute;c được h&iacute;t thở kh&iacute; trời tr&ecirc;n n&uacute;i cao, khi chỉ c&oacute; một m&igrave;nh v&agrave; kh&ocirc;ng phải đeo khẩu trang rất tuyệt&quot;, Tiến n&oacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/cam-trai-mot-minh-03-8813-1635328083.jpg\" style=\"height:404px; width:680px\" /></p>\r\n\r\n<p>Tương tự, Phạm Minh Vy, 24 tuổi, cũng đ&atilde; c&oacute; chuyến đi leo n&uacute;i đầu ti&ecirc;n sau gi&atilde;n c&aacute;ch x&atilde; hội tại n&uacute;i Chứa Chan. C&ocirc; đ&atilde; theo d&otilde;i th&ocirc;ng tin về t&igrave;nh h&igrave;nh dịch Covid-19 tại địa phương trong suốt thời gian qua v&agrave; thấy rất phấn khởi khi dịch bệnh được kiểm so&aacute;t.</p>\r\n\r\n<p>&quot;Chuyến đi n&agrave;y chỉ c&oacute; hai anh em m&igrave;nh th&ocirc;i, hạn chế tiếp x&uacute;c tối đa với người ở đo&agrave;n kh&aacute;c, tu&acirc;n thủ 5K suốt chuyến đi v&agrave; m&igrave;nh cũng đ&atilde; c&oacute; thẻ xanh Covid-19&quot;, Vy n&oacute;i c&ocirc; thường leo n&uacute;i Chứa Chan rồi nghỉ lại tr&ecirc;n n&uacute;i ban đ&ecirc;m, cắm trại, ăn uống. Nhưng lần n&agrave;y hai anh em chỉ đi trong ng&agrave;y, mang theo một &iacute;t b&aacute;nh ăn vặt, nước uống rồi bắt đầu leo n&uacute;i buổi s&aacute;ng v&agrave; xuống n&uacute;i buổi chiều, h&iacute;t thở kh&iacute; trời, chụp ảnh v&agrave; ngắm nh&igrave;n khung cảnh.</p>\r\n\r\n<p>&quot;Chắc do qu&aacute; h&agrave;o hứng v&agrave; nhiều năng lượng từ 4 th&aacute;ng qua t&iacute;ch lại n&ecirc;n m&igrave;nh leo n&uacute;i kh&ocirc;ng thấy mệt g&igrave;, leo cũng nhanh hơn b&igrave;nh thường nữa&quot;, Vy b&agrave;y tỏ. Với c&ocirc;, Xu&acirc;n Lộc, Đồng Nai kh&ocirc;ng chỉ c&oacute; n&uacute;i Chứa Chan, m&agrave; c&ograve;n nhiều điểm đến kh&aacute;c như hồ N&uacute;i Le, hồ Gia Ui, du kh&aacute;ch c&oacute; thể đi ch&egrave;o sup hoặc c&acirc;u c&aacute;, tham gia c&aacute;c hoạt động ngo&agrave;i trời để thỏa cảm gi&aacute;c nhớ thi&ecirc;n nhi&ecirc;n, n&uacute;i rừng.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/cam-trai-mot-minh-02-3267-1635328084.jpg\" style=\"height:680px; width:680px\" /></p>\r\n', 4, 1, 6, 1, 1),
(14, '3 phòng nghỉ kỳ lạ trên cây', '3-Phong-Nghi-Ky-La-Tren-Cay', '<p>Ph&ograve;ng tổ chim, đĩa bay, hộp gương... l&agrave; những lựa chọn cho kh&aacute;ch muốn ngủ tr&ecirc;n c&acirc;y trong khu rừng phương bắc.</p>\r\n', 'phongnghikyla.png', '2021-10-31 01:11:18', 1, '<p>Thuỵ Điển nổi tiếng với nhiều kh&aacute;ch sạn độc đ&aacute;o, trong đ&oacute; c&oacute; kh&aacute;ch sạn JumboStay tại s&acirc;n bay ở Stockholm để du kh&aacute;ch ngắm m&aacute;y bay, kh&aacute;ch sạn&nbsp;<a href=\"https://vnexpress.net/mua-dong-ngu-trong-khach-san-bang-tuyet-4203178.html\" rel=\"dofollow\">Icehotel</a>&nbsp;l&agrave;m ho&agrave;n to&agrave;n bằng băng gi&aacute;... Trong số đ&oacute;, kh&aacute;ch sạn Treehotel ở miền bắc v&ugrave;ng Lapland cung cấp c&aacute;c ph&ograve;ng nghỉ tr&ecirc;n c&acirc;y độc đ&aacute;o. Nếu may mắn, bạn c&oacute; thể ngắm được bắc cực quang tại đ&acirc;y v&agrave;o m&ugrave;a đ&ocirc;ng.</p>\r\n\r\n<p><strong>Ph&ograve;ng tổ chim</strong></p>\r\n\r\n<p>Căn ph&ograve;ng được thiết kế giống một tổ chim khổng lồ, d&agrave;nh cho những du kh&aacute;ch muốn h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n. Để l&ecirc;n được căn ph&ograve;ng, kh&aacute;ch phải leo l&ecirc;n thang v&agrave; chui qua một cửa sập tr&ecirc;n s&agrave;n nh&agrave;. Căn ph&ograve;ng nhỏ gọn, c&oacute; cửa sổ nhỏ nhưng đảm bảo &aacute;nh s&aacute;ng. Giữa c&aacute;c ph&ograve;ng ngủ ngăn bằng cửa trượt v&agrave; c&oacute; nh&agrave; vệ sinh b&ecirc;n trong.</p>\r\n\r\n<p>Căn ph&ograve;ng tổ chim c&oacute; diện t&iacute;ch 17 m2, ph&ugrave; hợp với kh&aacute;ch l&agrave; gia đ&igrave;nh c&oacute; trẻ em v&agrave; nh&oacute;m 4 người lớn. Ở đ&acirc;y du kh&aacute;ch n&ecirc;n để vali lớn dưới ph&ograve;ng h&agrave;nh l&yacute; v&igrave; rất kh&oacute; để mang l&ecirc;n xuống. Gi&aacute; một đ&ecirc;m nghỉ tại căn ph&ograve;ng l&agrave; 550 USD, thay đổi theo theo m&ugrave;a.</p>\r\n\r\n<p><strong>Ph&ograve;ng đĩa bay</strong></p>\r\n\r\n<p>C&oacute; chuyện đ&ugrave;a rằng, người ngo&agrave;i h&agrave;nh tinh chỉ hạ c&aacute;nh ở Mỹ do nhiều bộ phim Hollywood l&agrave;m về đề t&agrave;i n&agrave;y. Tuy nhi&ecirc;n, ngay tại c&aacute;nh rừng ph&iacute;a bắc Thuỵ Điển, bạn c&oacute; thể ngủ tr&ecirc;n căn ph&ograve;ng đĩa bay nằm lơ lửng giữa những th&acirc;n c&acirc;y. Vỏ ngo&agrave;i của căn ph&ograve;ng l&agrave;m bằng vật liệu composite c&oacute; độ bền cao, l&agrave;m giảm trọng lượng cấu tr&uacute;c.</p>\r\n\r\n<p>B&ecirc;n trong căn ph&ograve;ng, du kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng bầu trời đầy sao tr&ecirc;n trần nh&agrave; được chiếu bằng hệ thống đ&egrave;n. Khi thư gi&atilde;n trong ph&ograve;ng, bạn c&oacute; thể cảm thấy n&oacute; đang di chuyển do được treo tr&ecirc;n d&acirc;y v&agrave; cố định bởi c&aacute;c th&acirc;n c&acirc;y.</p>\r\n\r\n<p>Ph&ograve;ng nghỉ c&oacute; sức chứa 5 người, gồm một giường đ&ocirc;i v&agrave; 3 giường đơn, ph&ugrave; hợp với c&aacute;c gia đ&igrave;nh. N&oacute; kh&ocirc;ng ph&ugrave; hợp cho 5 người lớn v&igrave; c&aacute;c giường đơn hơi ngắn. Để v&agrave;o ph&ograve;ng, du kh&aacute;ch di chuyển bằng thang điện, đ&oacute;ng mở qua cửa sập tr&ecirc;n s&agrave;n. Gi&aacute; cho một đ&ecirc;m nghỉ tại ph&ograve;ng l&agrave; 550 USD, tuỳ theo m&ugrave;a.</p>\r\n\r\n<p><strong>Ph&ograve;ng gương</strong></p>\r\n\r\n<p>Khối lập phương c&oacute; k&iacute;ch thước 4x4x4 m với bốn bề l&agrave; những bức tường gương phản chiếu khung cảnh, l&agrave;m cho n&oacute; h&ograve;a với m&ocirc;i trường xung quanh như đang t&agrave;ng h&igrave;nh. Một th&acirc;n c&acirc;y đi xuy&ecirc;n qua căn ph&ograve;ng, được bao bọc bởi một khung nh&ocirc;m, tạo th&agrave;nh trụ của căn ph&ograve;ng.</p>\r\n\r\n<p>B&ecirc;n trong ph&ograve;ng gương l&agrave; nội thất với những bức tường l&agrave;m bằng v&aacute;n &eacute;p. Một trong những điều th&uacute; vị nhất về căn ph&ograve;ng kỳ lạ n&agrave;y l&agrave; một ban c&ocirc;ng ph&iacute;a sau mặt tiền bằng gương, khiến kh&aacute;ch nghỉ c&oacute; thể đi ra ngo&agrave;i m&agrave; kh&ocirc;ng bị nh&igrave;n thấy. Ph&ograve;ng c&oacute; sức chứa hai người lớn.</p>\r\n', 4, 1, 1, 1, 1),
(15, 'Khách sạn có vị trí đắc địa của tuyển Việt Nam ở Saudi Arabia', 'Khach-San-Co-Vi-Tri-Dac-Dia-Cua-Tuyen-Viet-Nam-O-Saudi-Arabia', '<p>Trong trận ra qu&acirc;n lượt đi v&ograve;ng loại thứ ba World Cup 2022, tuyển Việt Nam lưu tr&uacute; tại kh&aacute;ch sạn Gardino sang trọng, nhiều tiện &iacute;ch ở Riyadh.</p>\r\n', '309438815-jpeg-9580-1630306922.jpg', '2021-10-31 01:19:35', 1, '<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/236687763-1834394623412657-129-8665-6370-1630306922.jpg\" style=\"height:527px; width:680px\" />Thầy tr&ograve; huấn luyện vi&ecirc;n Park Hang-seo đ&atilde; đến Saudi Arabia để chuẩn bị cho trận ra qu&acirc;n đầu ti&ecirc;n của v&ograve;ng loại thứ ba World Cup 2022 v&agrave;o ng&agrave;y 2/9 với đội chủ s&acirc;n. C&aacute;c cầu thủ lưu tr&uacute; tại kh&aacute;ch sạn 4 sao Gardino, nằm tại vị tr&iacute; đắc địa ở thủ đ&ocirc; Riyadh. Ảnh:&nbsp;<em>Facebook Nguyễn Tiến Linh</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/310154068-jpeg-1183-1630306922.jpg\" style=\"height:454px; width:680px\" /></em>Kh&aacute;ch sạn Gardino c&oacute; bề ngo&agrave;i được sơn m&agrave;u trắng, tạo cảm gi&aacute;c m&aacute;t mắt trong kh&iacute; hậu kh&ocirc; n&oacute;ng tại Saudi Arabia. Cổng ch&agrave;o lớn, mang &acirc;m hưởng kiến tr&uacute;c Hồi gi&aacute;o, trang trọng v&agrave; nguy nga. Khu nghỉ nằm ở vị tr&iacute; thuận tiện trong thủ đ&ocirc; Riyadh, c&aacute;ch trung t&acirc;m mua sắm Riyadh Gallery 1,9 km v&agrave; c&oacute; tầm nh&igrave;n to&agrave;n cảnh th&agrave;nh phố. Ảnh:&nbsp;<em>Booking</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/309154261-jpeg-1723-1630306922.jpg\" style=\"height:454px; width:680px\" /></em>Sảnh lễ t&acirc;n vẫn c&oacute; tone m&agrave;u trắng chủ đạo, đồng nhất với b&ecirc;n ngo&agrave;i. Ngo&agrave;i ra, c&aacute;c chi tiết c&ograve;n lại được trang tr&iacute; m&agrave;u v&agrave;ng, được ưa chuộng tại Saudi Arabia, tạo cảm gi&aacute;c sang trọng, qu&yacute; tộc v&agrave; cao cấp. Ảnh:&nbsp;<em>Booking</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/309909284-jpeg-9963-1630306922.jpg\" style=\"height:454px; width:680px\" /></em>B&ecirc;n trong kh&aacute;ch sạn được trang tr&iacute; nhiều c&acirc;y xanh v&agrave; hoa, tạo cảm gi&aacute;c dễ chịu, m&aacute;t mắt cho du kh&aacute;ch giữa v&ugrave;ng đất sa mạc Saudi Arabia. Ảnh:&nbsp;<em>Booking</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/t%C6%B0%20v%E1%BA%A5n/309438815-jpeg-9580-1630306922.jpg\" style=\"height:454px; width:680px\" /></em>Kh&aacute;ch sạn c&oacute; tổng cộng 426 ph&ograve;ng ngủ được thiết kế nh&atilde; nhặn, sang trọng v&agrave; hiện đại, chủ yếu l&agrave; ph&ograve;ng deluxe c&oacute; sức chứa 2 người lớn v&agrave; một trẻ em. B&ecirc;n trong mỗi ph&ograve;ng c&oacute; đầy đủ tiện nghi như b&agrave;n l&agrave;m việc, tủ lạnh, k&eacute;t an to&agrave;n, TV... Một đ&ecirc;m nghỉ tại Gardino c&oacute; gi&aacute; thấp nhất từ khoảng 4 triệu đồng. Ảnh:&nbsp;<em>Booking</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/%C4%83n%20v%C3%A0%20ch%C6%A1i/310155744-jpeg-9905-1630306922.jpg\" style=\"height:454px; width:680px\" /></em>Tại Gardino, du kh&aacute;ch được thưởng thức bữa s&aacute;ng kiểu địa phương. C&aacute;c bữa c&ograve;n lại trong ng&agrave;y l&agrave; sự kết hợp đa dạng giữa ẩm thực Hồi gi&aacute;o v&agrave; ch&acirc;u &Acirc;u. Đội tuyển Việt Nam sẽ được căn chỉnh thực đơn để ph&ugrave; hợp khẩu vị, đủ dinh dưỡng. Ảnh:&nbsp;<em>Booking</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/du%20l%E1%BB%8Bch/%C4%83n%20v%C3%A0%20ch%C6%A1i/310154847-jpeg-5072-1630306923.jpg\" style=\"height:454px; width:680px\" /></em>Tr&ecirc;n trang đặt ph&ograve;ng&nbsp;<em>Booking</em>, kh&aacute;ch sạn đang nhận được số điểm 8,8/10. Trong đ&oacute;, kh&aacute;ch h&agrave;ng cho điểm gần như tuyệt đối với c&aacute;c ti&ecirc;u ch&iacute; nh&acirc;n vi&ecirc;n phục vụ, sự thoải m&aacute;i, độ sạch sẽ v&agrave; vị tr&iacute; kh&aacute;ch sạn. Ảnh:&nbsp;<em>Booking</em></p>\r\n', 4, 1, 274, 1, 1),
(16, 'Bé trai nổi tiếng vì tên ABCDEF GHIJK', 'Be-Trai-Noi-Tieng-Vi-Ten-Abcdef-Ghijk', '<p>B&eacute; trai 12 tuổi với c&aacute;i t&ecirc;n độc lạ ABCDEF GHIJK g&acirc;y ch&uacute; &yacute; sau một lần đi ti&ecirc;m chủng.</p>\r\n', 'I-used-to-have-a-feeling-of-in-7952-5882-1635566908.jpg', '2021-10-31 01:22:27', 1, '<p><em>Tribunnews&nbsp;</em>tuần n&agrave;y đưa tin b&eacute; trai t&ecirc;n đầy đủ l&agrave; ABCDEF GHIJK Zuzu đ&atilde; g&acirc;y ch&uacute; &yacute; khi đăng k&yacute; ti&ecirc;m chủng vaccine Covid-19 ở Nam Sumatra, Indonesia. Khi nh&igrave;n t&ecirc;n cậu b&eacute; trong danh s&aacute;ch ti&ecirc;m chủng, nh&acirc;n vi&ecirc;n y tế ban đầu c&ograve;n nghĩ ai đ&oacute; tr&ecirc;u đ&ugrave;a họ.</p>\r\n\r\n<p>Zulfahmi, cha của cậu b&eacute;, cho biết anh đặt t&ecirc;n ABCDEF GHIJK Zuzu cho con v&igrave; rất m&ecirc; tr&ograve; chơi giải &ocirc; chữ v&agrave; đ&atilde; chuẩn bị c&aacute;i t&ecirc;n n&agrave;y từ 6 năm trước khi con ra đời. Anh n&oacute;i th&ecirc;m Zuzu l&agrave; t&ecirc;n viết tắt của anh v&agrave; vợ Zuhro.</p>\r\n\r\n<p>Zulfahmi cũng từng muốn đặt t&ecirc;n cho c&aacute;c con thứ hai v&agrave; thứ ba lần lượt l&agrave; NOPQ RSTUV v&agrave; XYZ. Tuy nhi&ecirc;n, gia đ&igrave;nh sau đ&oacute; đ&atilde; thay đổi quyết định v&agrave; lựa chọn những t&ecirc;n kh&aacute;c.</p>\r\n\r\n<p>B&eacute; trai ABCDEF GHIJK Zuzu thừa nhận ban đầu rất xấu hổ v&igrave; t&ecirc;n của m&igrave;nh v&agrave; c&ograve;n bị c&aacute;c bạn ở trường tr&ecirc;u chọc, song b&acirc;y giờ em cảm thấy rất tự h&agrave;o.</p>\r\n\r\n<p>Hồi th&aacute;ng 4, một người cha ở th&agrave;nh phố Brebes, Indonesia, cũng g&acirc;y ch&uacute; &yacute; tr&ecirc;n mạng x&atilde; hội khi đặt t&ecirc;n con l&agrave; &quot;Ph&ograve;ng truyền th&ocirc;ng th&ocirc;ng tin thống k&ecirc;&quot;, cơ quan anh đang l&agrave;m việc.</p>\r\n', 5, 2, 366, 1, 1),
(17, 'Ái nữ nhà Trump mở tiệc mừng tuổi 40', 'Ai-Nu-Nha-Trump-Mo-Tiec-Mung-Tuoi-40', '<p>Ivanka Trump mở tiệc với nh&oacute;m bạn th&acirc;n thiết ở Miami, Florida, để chuẩn bị mừng sinh nhật ở tuổi 40.</p>\r\n', 'IMG-19201-1604-1635479330.jpg', '2021-10-31 01:24:37', 1, '<p>&Aacute;i nữ nh&agrave; cựu tổng thống Mỹ Donald Trump sẽ tr&ograve;n 40 tuổi v&agrave;o ng&agrave;y 30/10 v&agrave; một nh&oacute;m bạn th&acirc;n thiết của c&ocirc; đ&atilde; bay đến Miami để dự tiệc ăn mừng ngay đầu tuần.</p>\r\n\r\n<p>Ivanka Trump c&ugrave;ng nh&oacute;m bạn được cho l&agrave; đ&atilde; đến Surf Club ở resort Four Seasons v&agrave; khi&ecirc;u vũ tại một club ri&ecirc;ng tư ở Miami Design sau khi thưởng thức bữa tối th&acirc;n mật ngo&agrave;i trời.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/cu%E1%BB%99c%20s%E1%BB%91ng%20%C4%91%C3%B3%20%C4%91%C3%A2y/IMG-19201-1604-1635479330.jpg\" style=\"height:408px; width:680px\" /></p>\r\n\r\n<p>&quot;Đ&oacute; l&agrave; hai ng&agrave;y tiệc t&ugrave;ng tr&ecirc;n thuyền, b&atilde;i biển v&agrave; tiệc rượu. Đ&acirc;y l&agrave; bữa tiệc của c&aacute;c c&ocirc; g&aacute;i, với những người bạn th&acirc;n thiết đến từ New York v&agrave; Miami, song Jared (chồng Ivanka) cũng g&oacute;p vui trong bữa tối ở Surf Club&quot;, nguồn thạo tin tiết lộ về tiệc mừng của &aacute;i nữ nh&agrave; Trump.</p>\r\n\r\n<p>Sau khi bố rời Nh&agrave; Trắng, gia đ&igrave;nh Ivanka v&agrave; Jared Kushner chuyển tới Miami hồi năm ngo&aacute;i. Hai người thu&ecirc; một căn hộ để ở tạm, trong thời gian t&acirc;n trang căn biệt thự 6 ph&ograve;ng ngủ trị gi&aacute; 24 triệu USD m&agrave; họ đ&atilde; mua tr&ecirc;n đảo Indian Creek.</p>\r\n\r\n<p>&Aacute;i nữ nh&agrave;&nbsp;<a href=\"https://vnexpress.net/chu-de/donald-trump-1023\" rel=\"dofollow\">Trump</a>&nbsp;dường như d&agrave;nh nhiều thời gian thư gi&atilde;n hơn sau khi rời Nh&agrave; Trắng. C&ocirc; thường xuy&ecirc;n được bắt gặp đưa con đi học, ra b&atilde;i biển chơi hay chạy bộ c&ugrave;ng chồng.</p>\r\n', 5, 2, 98, 1, 1),
(18, 'Con gái Bill Gates đăng ảnh mừng sinh nhật bố', 'Con-Gai-Bill-Gates-Dang-Anh-Mung-Sinh-Nhat-Bo', '<p>Jennifer Gates đăng ảnh chụp c&ugrave;ng bố trong đ&aacute;m cưới của c&ocirc; v&agrave; gửi lời cảm ơn tới &ocirc;ng nh&acirc;n dịp tỷ ph&uacute; Mỹ bước sang tuổi 66.</p>\r\n', 'image-9196-1635474005.jpg', '2021-10-31 01:26:28', 1, '<p>&quot;Mừng sinh nhật tuổi 66 của bố. Con lu&ocirc;n biết ơn v&igrave; được học hỏi từ tấm gương của bố về sự t&ograve; m&ograve; v&ocirc; tận, kh&ocirc;ng ngừng kh&aacute;m ph&aacute; v&agrave; mong muốn g&oacute;p sức cho nh&acirc;n loại. Cảm ơn bố v&igrave; lu&ocirc;n ủng hộ t&igrave;nh cảm v&agrave; mơ ước của ch&uacute;ng con. Đ&oacute; l&agrave; những kỷ niệm sẽ theo con suốt cuộc đời&quot;, Jennifer Gates đăng l&ecirc;n Instagram h&ocirc;m 28/10.</p>\r\n\r\n<p>Trong ảnh l&agrave; khoảnh khắc Bill Gates ngồi tr&ecirc;n bức tường đ&aacute;, cười rạng rỡ nh&igrave;n con g&aacute;i Jennifer đang kho&aacute;c l&ecirc;n m&igrave;nh chiếc v&aacute;y cưới lộng lẫy trong h&ocirc;n lễ h&ocirc;m 17/10. Bức ảnh nhận được h&agrave;ng ngh&igrave;n lượt th&iacute;ch v&agrave; b&igrave;nh luận tr&ecirc;n Instagram.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/cu%E1%BB%99c%20s%E1%BB%91ng%20%C4%91%C3%B3%20%C4%91%C3%A2y/image-9196-1635474005.jpg\" style=\"height:850px; width:680px\" /></p>\r\n\r\n<p><a href=\"https://vnexpress.net/chu-de/bill-gates-1851\" rel=\"dofollow\">Bill Gates</a>&nbsp;cũng để lại lời b&igrave;nh luận trong b&agrave;i đăng của con g&aacute;i: &quot;Cảm ơn Jenn v&igrave; những lời ch&uacute;c tới bố. Ta thật may mắn v&igrave; được l&agrave;m bố của con&quot;.</p>\r\n\r\n<p>Jennifer Gates v&agrave; chồng Nayer Nasser, 30 tuổi, kết h&ocirc;n hồi giữa th&aacute;ng ở điền trang North Salem, ngoại &ocirc; New York, với chi ph&iacute; ước t&iacute;nh hai triệu USD. Chủ rể l&agrave; con một gia đ&igrave;nh triệu ph&uacute; gốc Ai Cập v&agrave; cũng l&agrave; vận động vi&ecirc;n cưỡi ngựa chuy&ecirc;n nghiệp cho đội tuyển nước n&agrave;y, đ&atilde; thi đấu tại Tokyo Olympic 2020.</p>\r\n\r\n<p>Trong đ&aacute;m cưới của con g&aacute;i, tỷ ph&uacute; Mỹ Bill Gates v&agrave; vợ cũ đ&atilde; t&aacute;i ngộ, c&ugrave;ng dắt tay con v&agrave;o lễ đường. Hai người cũng đồng loạt đăng ảnh cưới đẹp như mơ của con g&aacute;i,&nbsp;<a href=\"https://vnexpress.net/bill-gates-va-vo-cu-chuc-con-gai-hanh-phuc-4374285.html\" rel=\"dofollow\">ch&uacute;c con</a>&nbsp;c&oacute; cuộc h&ocirc;n nh&acirc;n vi&ecirc;n m&atilde;n.</p>\r\n', 5, 2, 47, 1, 1),
(19, 'Mỹ, Hàn bí mật diễn tập không quân', 'My,-Han-Bi-Mat-Dien-Tap-Khong-Quan', '<p>Mỹ v&agrave; H&agrave;n Quốc huy động 200 kh&iacute; t&agrave;i kh&ocirc;ng qu&acirc;n, gồm nhiều ti&ecirc;m k&iacute;ch, cho cuộc diễn tập kh&ocirc;ng được c&ocirc;ng bố.</p>\r\n', 'quan-su-1.jpg', '2021-11-01 01:32:19', 1, '<p>Quan chức quốc ph&ograve;ng H&agrave;n Quốc giấu t&ecirc;n cho biết cuộc diễn tập kh&ocirc;ng qu&acirc;n giữa hai nước khởi động h&ocirc;m nay v&agrave; sẽ k&eacute;o d&agrave;i 5 ng&agrave;y, trong bối cảnh căng thẳng tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n tăng l&ecirc;n sau khi Triều Ti&ecirc;n thử h&agrave;ng loạt vũ kh&iacute; như t&ecirc;n lửa đạn đạo ph&oacute;ng từ t&agrave;u ngầm (SLBM) v&agrave; đầu đạn lướt si&ecirc;u vượt &acirc;m.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/quan-su-1.jpg\" style=\"height:409px; width:680px\" /></p>\r\n\r\n<p>Cuộc diễn tập kh&ocirc;ng được c&ocirc;ng bố v&agrave; đặt t&ecirc;n, tiền th&acirc;n l&agrave; tập trận quy m&ocirc; lớn Vigilant Ace. Quan chức H&agrave;n Quốc cho biết hoạt động năm nay c&oacute; sự tham gia của 200 kh&iacute; t&agrave;i kh&ocirc;ng qu&acirc;n từ hai nước, trong đ&oacute; gồm ti&ecirc;m k&iacute;ch F-15K, KF-16 H&agrave;n Quốc v&agrave; F-16 Mỹ, nhưng kh&ocirc;ng c&oacute; binh sĩ v&agrave; trang bị điều động từ l&atilde;nh thổ Mỹ.</p>\r\n\r\n<p>Ph&aacute;t ng&ocirc;n vi&ecirc;n kh&ocirc;ng qu&acirc;n H&agrave;n Quốc từ chối b&igrave;nh luận về th&ocirc;ng tin tr&ecirc;n.</p>\r\n\r\n<p>Triều Ti&ecirc;n cũng chưa b&igrave;nh luận, nhưng nước n&agrave;y từng chỉ tr&iacute;ch c&aacute;c cuộc diễn tập chung giữa Mỹ v&agrave; H&agrave;n Quốc l&agrave; h&agrave;nh động g&acirc;y hấn v&agrave; chuẩn bị cho chiến tranh.</p>\r\n\r\n<p>Tập trận Vigilant Ace từng huy động h&agrave;ng chục ngh&igrave;n binh sĩ v&agrave; h&agrave;ng trăm m&aacute;y bay, trong đ&oacute; c&oacute; ti&ecirc;m k&iacute;ch v&agrave; oanh tạc cơ chiến lược. Tuy nhi&ecirc;n, quy m&ocirc; tập trận đ&atilde; thu nhỏ đ&aacute;ng kể từ năm 2017 nhằm tạo điều kiện đối thoại với Triều Ti&ecirc;n.</p>\r\n\r\n<p>Đầu th&aacute;ng n&agrave;y, l&atilde;nh đạo Triều Ti&ecirc;n Kim Jong-un đổ lỗi cho Mỹ về c&aacute;c lệnh trừng phạt, đồng thời b&aacute;c bỏ khẳng định của Washington về &yacute; định th&ugrave; địch của họ.</p>\r\n\r\n<p>Kim Jong-un từng ba lần gặp cựu tổng thống Donald Trump nhưng kh&ocirc;ng đạt được thỏa thuận to&agrave;n diện về việc chấm dứt chương tr&igrave;nh hạt nh&acirc;n của nước n&agrave;y. Tổng thống Joe Biden cam kết tiếp tục đ&agrave;m ph&aacute;n ngoại giao với B&igrave;nh Nhưỡng nhưng với c&aacute;ch tiếp cận thận trọng hơn.</p>\r\n', 6, 2, 153, 1, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(20, 'Tàu ngầm Mỹ có thể bị đâm trực diện ở Biển Đông', 'Tau-Ngam-My-Co-The-Bi-Dam-Truc-Dien-O-Bien-Dong', '<p>Hư hại tập trung ở phần mũi cho thấy USS Connecticut c&oacute; thể đ&atilde; va chạm trực diện với một t&agrave;u lặn kh&ocirc;ng người l&aacute;i, theo chuy&ecirc;n gia Trung Quốc.</p>\r\n', 'quan-su-2.jpg', '2021-11-01 01:34:51', 1, '<p>C&aacute;c điều tra vi&ecirc;n Mỹ đến nay vẫn chưa x&aacute;c định được t&agrave;u ngầm USS Connecticut va phải vật thể g&igrave; dưới nước ở Biển Đ&ocirc;ng trong vụ va chạm ng&agrave;y 2/10. Tuy nhi&ecirc;n, dựa tr&ecirc;n h&igrave;nh ảnh vệ tinh, c&aacute;c chuy&ecirc;n gia Trung Quốc cho rằng t&agrave;u ngầm hạt nh&acirc;n Mỹ c&oacute; thể đ&atilde; đối đầu với thứ g&igrave; đ&oacute; trong l&ograve;ng biển.</p>\r\n\r\n<p>Ảnh vệ tinh do h&atilde;ng Planet chụp ng&agrave;y 20/10 cho thấy một t&agrave;u ngầm c&oacute; thể l&agrave; USS Connecticut đang neo đậu tại cảng Guam. Giới chuy&ecirc;n gia ban đầu cho rằng ph&iacute;a tr&ecirc;n mũi t&agrave;u v&agrave; v&ograve;m thủy &acirc;m kh&ocirc;ng c&oacute; dấu hiệu hư hại, nhưng một cựu sĩ quan t&agrave;u ngầm Mỹ sau đ&oacute; nhận định mũi t&agrave;u đ&atilde; được th&aacute;o bỏ v&agrave; hệ thống định vị thủy &acirc;m (sonar) của USS Connecticut đang ng&acirc;m dưới nước biển.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/quan-su-2.jpg\" style=\"height:408px; width:680px\" /></p>\r\n\r\n<p>&quot;Chỉ c&oacute; mũi t&agrave;u hư hỏng, trong khi đ&agrave;i chỉ huy v&agrave; th&acirc;n t&agrave;u kh&ocirc;ng c&oacute; dấu hiệu bị nứt. Gần như chắc chắn USS Connecticut đ&atilde; đ&acirc;m trực diện với thứ g&igrave; đ&oacute;, khiến phần mũi hỏng nặng v&agrave; ảnh hưởng tới sonar. Con t&agrave;u về cơ bản đ&atilde; bị m&ugrave; khi lặn v&agrave; phải nhanh ch&oacute;ng đ&igrave;nh chỉ nhiệm vụ&quot;, chuy&ecirc;n gia qu&acirc;n sự Antony Wong Tong ở Macau, Trung Quốc n&ecirc;u quan điểm.</p>\r\n\r\n<p>Tổ chức S&aacute;ng kiến T&igrave;nh h&igrave;nh Chiến lược Biển Đ&ocirc;ng (SCSPI) thuộc Viện Nghi&ecirc;n cứu Đại dương Đại học Bắc Kinh, Trung Quốc, hồi giữa th&aacute;ng c&ocirc;ng bố ảnh vệ tinh độ ph&acirc;n giải thấp chụp h&ocirc;m 3/10, một ng&agrave;y sau vụ va chạm, cho thấy t&agrave;u ngầm nghi l&agrave; USS Connecticut di chuyển ở trạng th&aacute;i nổi c&aacute;ch quần đảo Ho&agrave;ng Sa khoảng 80 km về ph&iacute;a đ&ocirc;ng nam. Ho&agrave;ng Sa l&agrave; quần đảo thuộc chủ quyền Việt Nam, nhưng đang bị Trung Quốc chiếm đ&oacute;ng tr&aacute;i ph&eacute;p v&agrave; x&acirc;y dựng nhiều c&ocirc;ng tr&igrave;nh qu&acirc;n sự.</p>\r\n\r\n<p>Wong cho rằng vụ va chạm c&oacute; thể xảy ra gần c&aacute;c đảo bị Trung Quốc chiếm đ&oacute;ng tr&aacute;i ph&eacute;p tại Ho&agrave;ng Sa, trong những khu vực t&agrave;u chiến Mỹ hay tiến v&agrave;o để thực thi quyền tự do h&agrave;ng hải.</p>\r\n\r\n<p>&quot;Hoạt động của t&agrave;u ngầm nhạy cảm hơn nhiều so với t&agrave;u mặt nước, buộc hải qu&acirc;n Mỹ che giấu hoạt động của USS Connecticut v&agrave; tr&aacute;nh đ&aacute;nh động ph&iacute;a Trung Quốc. T&agrave;u ngầm Mỹ c&oacute; thể ở dưới nước l&acirc;u như vậy cho thấy hư hại kh&ocirc;ng qu&aacute; nghi&ecirc;m trọng&quot;, &ocirc;ng n&oacute;i th&ecirc;m.</p>\r\n\r\n<p>Chuy&ecirc;n gia qu&acirc;n sự L&yacute; Kiệt ở Bắc Kinh cho rằng bức ảnh vệ tinh của SCSPI chứng tỏ hải qu&acirc;n Trung Quốc c&oacute; thể nắm được h&agrave;nh tr&igrave;nh của t&agrave;u ngầm Mỹ ở Biển Đ&ocirc;ng, nhưng kh&ocirc;ng biết biết về vụ va chạm cho đến khi hải qu&acirc;n Mỹ c&ocirc;ng bố th&ocirc;ng tin.</p>\r\n\r\n<p>&quot;Hư hại của t&agrave;u ngầm Mỹ c&oacute; thể bắt nguồn từ vật thể nhỏ như t&agrave;u lặn kh&ocirc;ng người l&aacute;i, chứ kh&ocirc;ng phải một t&agrave;u ngầm cỡ lớn kh&aacute;c. Sự cố n&agrave;y l&agrave; lời nhắc nhở hải qu&acirc;n Trung Quốc cần củng cố năng lực gi&aacute;m s&aacute;t dưới l&ograve;ng biển&quot;, L&yacute; Kiệt n&oacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/quan-su-3.jpg\" style=\"height:408px; width:680px\" /></p>\r\n\r\n<p>Hải qu&acirc;n Mỹ đang mở hai cuộc điều tra li&ecirc;n quan vụ va chạm, cho biết l&ograve; phản ứng hạt nh&acirc;n v&agrave; kh&ocirc;ng gian b&ecirc;n trong t&agrave;u ngầm Connecticut &quot;l&agrave;m đủ c&ocirc;ng năng v&agrave; kh&ocirc;ng bị ảnh hưởng&quot;. Hai quan chức quốc ph&ograve;ng Mỹ ng&agrave;y 27/10 cho biết USS Connecticut đ&atilde; bị hỏng bể dằn ph&iacute;a trước mũi.</p>\r\n\r\n<p>Vụ va chạm c&oacute; thể g&acirc;y thiệt hại kinh tế rất lớn v&agrave; ảnh hưởng tới năng lực t&aacute;c chiến của hải qu&acirc;n Mỹ, do USS Connecticut l&agrave; một trong ba chiếc thuộc lớp Seawolf, loại t&agrave;u ngầm tấn c&ocirc;ng đắt nhất thế giới với chi ph&iacute; ước t&iacute;nh tới 8,5 tỷ USD/chiếc.</p>\r\n\r\n<p>Quyền Thứ trưởng Hải qu&acirc;n Mỹ phụ tr&aacute;ch nghi&ecirc;n cứu, ph&aacute;t triển v&agrave; mua sắm Jay Stefany h&ocirc;m qua cảnh b&aacute;o sửa chữa USS Connecticut ở nh&agrave; m&aacute;y thuộc sở hữu ch&iacute;nh phủ sẽ dẫn tới t&igrave;nh trạng rối loạn, gi&aacute;n đoạn to&agrave;n bộ c&ocirc;ng việc, do c&aacute;c cơ sở n&agrave;y đang qu&aacute; tải với sửa chữa t&agrave;u ngầm hạt nh&acirc;n mang t&ecirc;n lửa đạn đạo v&agrave; t&agrave;u s&acirc;n bay. Trong khi đ&oacute;, Guam kh&ocirc;ng c&oacute; cơ sở hạ tầng v&agrave; nh&acirc;n lực để sửa chữa t&agrave;u ngầm.</p>\r\n', 6, 2, 201, 1, 1),
(21, 'Đặc nhiệm do Mỹ huấn luyện đầu quân cho IS', 'Dac-Nhiem-Do-My-Huan-Luyen-Dau-Quan-Cho-Is', '<p>Một số cựu đặc nhiệm v&agrave; sĩ quan t&igrave;nh b&aacute;o Afghanistan từng được Mỹ huấn luyện quyết định gia nhập IS để chống lại Taliban.</p>\r\n', 'quan-su-4.jpg', '2021-11-01 01:38:14', 1, '<p>C&aacute;c cựu quan chức Afghanistan ng&agrave;y 31/10 cho biết ng&agrave;y c&agrave;ng nhiều cựu đặc nhiệm v&agrave; đặc vụ t&igrave;nh b&aacute;o gia nhập Nh&agrave; nước Hồi gi&aacute;o Khorasan (IS-K), chi nh&aacute;nh ở Afghanistan của tổ chức khủng bố Nh&agrave; nước Hồi gi&aacute;o (IS) tự xưng, kể từ khi Taliban l&ecirc;n nắm quyền ở nước n&agrave;y.</p>\r\n\r\n<p>Đặc nhiệm v&agrave; t&igrave;nh b&aacute;o Afghanistan l&agrave; hai lực lượng tinh nhuệ từng được Mỹ huấn luyện, trang bị suốt 20 năm qua. Những th&agrave;nh vi&ecirc;n n&agrave;y mang đến cho IS kiến thức chuy&ecirc;n m&ocirc;n quan trọng về kỹ thuật thu thập th&ocirc;ng tin t&igrave;nh b&aacute;o v&agrave; t&aacute;c chiến, c&oacute; thể tăng cường năng lực cho nh&oacute;m n&agrave;y trong việc tranh gi&agrave;nh quyền lực với&nbsp;<a href=\"https://vnexpress.net/chu-de/taliban-3633\" rel=\"dofollow\">Taliban</a>, c&aacute;c cựu quan chức Afghanistan cảnh b&aacute;o.</p>\r\n\r\n<p>Một cựu binh&nbsp;<a href=\"https://vnexpress.net/chu-de/afghanistan-2324\" rel=\"dofollow\">Afghanistan</a>, từng chỉ huy kho qu&acirc;n kh&iacute; tại th&agrave;nh phố Gardez, gia nhập IS-K v&agrave; chết trong cuộc đụng độ với Taliban một tuần trước, một cựu quan chức quen biết người n&agrave;y cho biết. Nhiều cựu binh v&agrave; sĩ quan t&igrave;nh b&aacute;o Afghanistan kh&aacute;c gia nhập IS sau khi Taliban tới kh&aacute;m nh&agrave; v&agrave; y&ecirc;u cầu họ ra tr&igrave;nh diện ch&iacute;nh quyền mới.</p>\r\n\r\n<p>Một người ở huyện Qarabagh, ph&iacute;a bắc Kabul, cho biết anh họ của m&igrave;nh l&agrave; một cựu đặc nhiệm cấp cao &quot;biến mất hồi th&aacute;ng 9&quot; v&agrave; giờ l&agrave; th&agrave;nh vi&ecirc;n của IS-K. Người n&agrave;y cũng cho biết 4 cựu binh Afghanistan kh&aacute;c gia nhập IS-K trong v&agrave;i tuần gần đ&acirc;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/quan-su-4.jpg\" style=\"height:408px; width:680px\" /></p>\r\n\r\n<p>&quot;IS trở n&ecirc;n rất hấp dẫn với những cựu th&agrave;nh vi&ecirc;n lực lượng an ninh Afghanistan, sau khi họ bị bỏ rơi&quot;, Rahmatullah Nabil, cựu l&atilde;nh đạo Tổng cục An ninh Afghanistan phụ tr&aacute;ch c&ocirc;ng t&aacute;c t&igrave;nh b&aacute;o, cho biết. &quot;Nếu c&oacute; một cuộc kh&aacute;ng chiến, họ sẽ tham gia. Tuy nhi&ecirc;n, l&uacute;c n&agrave;y chỉ c&oacute; IS l&agrave; nh&oacute;m vũ trang đối lập ở Afghanistan&quot;.</p>\r\n\r\n<p>Taliban hồi th&aacute;ng 9 dập tắt phong tr&agrave;o kh&aacute;ng chiến ở thung lũng Panjshir do Ahmad Massoud l&atilde;nh đạo, buộc c&aacute;c thủ lĩnh phe kh&aacute;ng chiến bỏ trốn ra nước ngo&agrave;i. Ahmad Massoud l&agrave; con trai của Ahmad Shah Massoud, thủ lĩnh nh&oacute;m vũ trang chống Taliban bị al-Qaeda &aacute;m s&aacute;t năm 2001.</p>\r\n\r\n<p>Taliban c&aacute;o buộc IS-K l&agrave; tổ chức do t&igrave;nh b&aacute;o Afghanistan v&agrave; Mỹ dựng l&ecirc;n nhằm g&acirc;y chia rẽ trong lực lượng nổi dậy Hồi gi&aacute;o tại quốc gia Trung &Aacute;. Mỹ v&agrave; ch&iacute;nh phủ Afghanistan cũ b&aacute;c th&ocirc;ng tin n&agrave;y.</p>\r\n\r\n<p>H&agrave;ng trăm ngh&igrave;n cựu đặc vụ t&igrave;nh b&aacute;o, binh sĩ v&agrave; cảnh s&aacute;t của Afghanistan đang thất nghiệp v&agrave; lo lắng cho t&iacute;nh mạng của m&igrave;nh, d&ugrave; Taliban đ&atilde; ban h&agrave;nh lệnh &acirc;n x&aacute; cho họ. Chỉ một phần nhỏ trong số n&agrave;y, chủ yếu thuộc Tổng cục An ninh Quốc gia Afghanistan, quay lại l&agrave;m việc dưới sự gi&aacute;m s&aacute;t của Taliban. Họ cũng bị nợ lương trong nhiều th&aacute;ng, giống c&aacute;c nh&acirc;n vi&ecirc;n ch&iacute;nh phủ Afghanistan kh&aacute;c.</p>\r\n\r\n<p>&quot;Đ&acirc;y ch&iacute;nh x&aacute;c l&agrave; những g&igrave; từng diễn ra ở Iraq, khi c&aacute;c tướng lĩnh thời Saddam Hussein thấy thất vọng với ch&iacute;nh quyền mới. Ch&uacute;ng ta cần thận trọng&quot;, một quan chức phương T&acirc;y cảnh b&aacute;o. &quot;Mỹ giải t&aacute;n lực lượng an ninh Iraq sau chiến dịch qu&acirc;n sự năm 2003. Với vũ kh&iacute; giấu tại nh&agrave; v&agrave; nhiều năm kinh nghiệm chiến đấu, họ l&agrave; nguồn nh&acirc;n sự dồi d&agrave;o cho c&aacute;c nh&oacute;m vũ trang, bao gồm al-Qaeda v&agrave; tổ chức tiền th&acirc;n của IS&quot;.</p>\r\n\r\n<p>Ngo&agrave;i việc tiếp nhận cựu binh v&agrave; cựu đặc vụ Afghanistan, IS-K đang vung tiền chi&ecirc;u mộ c&aacute;c th&agrave;nh vi&ecirc;n mới, giới chức an ninh&nbsp;<a href=\"https://vnexpress.net/chu-de/my-712\" rel=\"dofollow\">Mỹ</a>&nbsp;cho biết. Trong phi&ecirc;n điều trần gần đ&acirc;y tại thượng viện, Thứ trưởng Quốc ph&ograve;ng Mỹ phụ tr&aacute;ch ch&iacute;nh s&aacute;ch Colin Kahl cảnh b&aacute;o chi nh&aacute;nh IS ở Afghanistan c&oacute; thể tấn c&ocirc;ng phương T&acirc;y v&agrave; đồng minh trong v&ograve;ng 6-12 th&aacute;ng nữa.</p>\r\n\r\n<p>IS-K th&agrave;nh lập năm 2014 bởi c&aacute;c th&agrave;nh vi&ecirc;n Taliban tại Afghanistan v&agrave; Pakistan, những người bất m&atilde;n với c&aacute;c l&atilde;nh đạo của nh&oacute;m đang t&igrave;m c&aacute;ch đ&agrave;m ph&aacute;n với Mỹ. IS-K kiểm so&aacute;t một số huyện miền đ&ocirc;ng Afghanistan tới năm 2015, rồi suy yếu đ&aacute;ng kể sau c&aacute;c cuộc tấn c&ocirc;ng của Taliban.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, IS-K trỗi dậy khi ch&iacute;nh quyền cũ của Afghanistan sụp đổ v&agrave; Mỹ r&uacute;t hết qu&acirc;n khỏi quốc gia Trung &Aacute;. Một vụ đ&aacute;nh bom liều chết của IS-K hồi th&aacute;ng 8 tại s&acirc;n bay Kabul khiến 13 binh sĩ Mỹ v&agrave; khoảng 200 người Afghanistan thiệt mạng.</p>\r\n\r\n<p>IS-K sau đ&oacute; tổ chức nhiều cuộc tấn c&ocirc;ng nhằm v&agrave;o Taliban v&agrave; đ&aacute;nh bom liều chết tại nhiều th&aacute;nh đường Hồi gi&aacute;o nhằm tranh gi&agrave;nh ảnh hưởng v&agrave; quyền lực với Taliban.</p>\r\n', 6, 2, 410, 1, 1),
(22, 'Người Việt ở Nhật sa bẫy cờ bạc đồng hương', 'Nguoi-Viet-O-Nhat-Sa-Bay-Co-Bac-Dong-Huong', '<p>Số vụ bắt c&oacute;c, giam giữ người Việt ở Nhật để đ&ograve;i nợ hoặc tiền chuộc từ người th&acirc;n ở Việt Nam tăng nhanh trong thời kỳ Covid-19.</p>\r\n', 'nguoi-viet-5-chau.jpg', '2021-11-01 01:39:55', 1, '<p>Giới chức Nhật Bản cho hay nhiều thực tập sinh Việt Nam gặp kh&oacute; khăn t&agrave;i ch&iacute;nh sau khi tới nước n&agrave;y v&igrave; Covid-19 c&oacute; li&ecirc;n quan đến những vụ &aacute;n tr&ecirc;n. V&igrave; mất việc, họ sa v&agrave;o c&aacute;c hoạt động cờ bạc do người Việt tổ chức v&agrave; phải vay nợ đồng hương.</p>\r\n\r\n<p>Hồi th&aacute;ng 5, cảnh s&aacute;t tỉnh Aichi t&igrave;m thấy 6 người đ&agrave;n &ocirc;ng Việt Nam bị x&iacute;ch ch&acirc;n v&agrave;o nhau trong một căn hộ ở Ichinomya. Họ đều khai l&agrave; đang mắc nợ, một số phải gọi điện về nước nhờ người th&acirc;n trả tiền thay.</p>\r\n\r\n<p>Một người đ&atilde; vay 900.000 y&ecirc;n (7.900 USD) từ một người được cho l&agrave; kẻ đứng đầu đường d&acirc;y. Cảnh s&aacute;t đ&atilde; bắt 8 người Việt li&ecirc;n quan tới vụ n&agrave;y. H&ocirc;m 12/10, t&ograve;a &aacute;n Nagoya kết luận ba bị c&aacute;o trong số đ&oacute; phạm tội giam giữ người tr&aacute;i ph&eacute;p v&agrave; c&aacute;c tội danh kh&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/nguoi-viet-5-chau.jpg\" style=\"height:408px; width:680px\" /></p>\r\n\r\n<p>Nhiều vụ tương tự được ghi nhận khắp Nhật Bản. Từ th&aacute;ng 1 tới th&aacute;ng 9, &iacute;t nhất 42 người Việt Nam bị bắt v&igrave; t&igrave;nh nghi bắt c&oacute;c đồng hương để đ&ograve;i tiền chuộc hoặc đ&ograve;i nợ. Theo Lực lượng Cảnh s&aacute;t Quốc gia Nhật Bản, kh&ocirc;ng người Việt n&agrave;o bị bắt v&igrave; những tội n&agrave;y trong giai đoạn 2009-2019. Hoạt động lừa đồng hương v&agrave;o bẫy cờ bạc của người Việt chỉ xuất hiện từ năm 2020, khi Covid-19 b&ugrave;ng ph&aacute;t, với 10 người bị bắt.</p>\r\n\r\n<p>Một người Việt 28 tuổi sang Nhật l&agrave;m thực tập sinh kỹ thuật đ&atilde; mắc nợ 600.000 y&ecirc;n (5.200 USD) v&igrave; cờ bạc. Anh bị giam v&agrave;i tiếng trong căn hộ ở tỉnh Aichi m&ugrave;a h&egrave; 2020, sau khi nh&oacute;m chủ nợ cho rằng anh sẽ quỵt nợ v&agrave; bỏ trốn. Thực tập sinh n&agrave;y sau đ&oacute; được cảnh s&aacute;t Nhật ph&aacute;t hiện v&agrave; phải trở về nước.</p>\r\n\r\n<p>Hồi th&aacute;ng 1, 6 người Việt bị cảnh s&aacute;t tỉnh Shizuoka bắt v&igrave; c&aacute;o buộc bắt c&oacute;c một thực tập sinh người Việt v&agrave; đ&ograve;i 2,5 triệu y&ecirc;n (22.000 USD) tiền chuộc. Hồi th&aacute;ng 6, nh&oacute;m 12 người Việt bị bắt ở Hiroshma với c&aacute;o buộc giam v&agrave; tống tiền một người Việt 310.000 y&ecirc;n (2.700 USD).</p>\r\n\r\n<p>&quot;C&oacute; nhiều trường hợp người nước ngo&agrave;i sa v&agrave;o cờ bạc v&igrave; cảm thấy kh&ocirc;ng thể hồi hương nếu kh&ocirc;ng đem về được một khoản tiền lớn, từ đ&oacute; ch&igrave;m v&agrave;o nợ nần&quot;, theo Yoshihisa Saito, ph&oacute; gi&aacute;o sư Đại học Kobe, chuy&ecirc;n gia về luật lao động v&agrave; c&aacute;c mối quan hệ lao động đặc biệt ở Việt Nam, n&oacute;i.</p>\r\n\r\n<p>&quot;Cần phải c&oacute; biện ph&aacute;p hỗ trợ ph&ugrave; hợp cho những người Việt bị thất nghiệp hoặc mất chỗ ở tại Nhật Bản do đại dịch&quot;, Saito n&oacute;i.</p>\r\n', 7, 2, 139, 1, 1),
(23, 'Chứng khoán đứt chuỗi lập đỉnh mới', 'Chung-Khoan-Dut-Chuoi-Lap-Dinh-Moi', '<p>VN-Index đảo chiều v&agrave;o cuối phi&ecirc;n bởi &aacute;p lực b&aacute;n mạnh từ rổ VN30, dẫn đến kh&ocirc;ng thể nối d&agrave;i chuỗi đi l&ecirc;n phi&ecirc;n thứ 5 li&ecirc;n tiếp.</p>\r\n', 'chung-khoan.jpg', '2021-11-01 01:42:41', 1, '<p>Sau tuần tăng mạnh nhất trong gần 5 th&aacute;ng, thị trường chứng kho&aacute;n được nhiều nh&oacute;m ph&acirc;n t&iacute;ch dự đo&aacute;n tiếp tục thăng hoa trong tuần n&agrave;y do nh&agrave; đầu tư đ&atilde; cởi bỏ t&acirc;m l&yacute; thận trọng. Thế nhưng, ngay phi&ecirc;n giao dịch đầu tuần, thị trường đ&atilde; cho thấy điều ngược lại.</p>\r\n\r\n<p>VN-Index giằng co mạnh trong phi&ecirc;n s&aacute;ng, nhiều lần đi l&ecirc;n v&agrave; chạm 1.450 điểm rồi đảo chiều về s&aacute;t tham chiếu. Chỉ số chuyển sang sắc đỏ giữa phi&ecirc;n chiều, c&oacute; l&uacute;c mất hơn 8 điểm khi &aacute;p lực b&aacute;n d&acirc;ng l&ecirc;n ở nh&oacute;m vốn ho&aacute; lớn. Bi&ecirc;n độ giảm thu hẹp v&agrave;o cuối phi&ecirc;n nhưng chỉ số vẫn đ&oacute;ng cửa dưới tham chiếu tại 1.438,97 điểm.</p>\r\n\r\n<p>20 cổ phiếu trong rổ VN30 giảm điểm l&agrave; t&aacute;c nh&acirc;n ch&iacute;nh khiến chỉ số đại diện cho s&agrave;n TP HCM đứt chuỗi tăng v&agrave; lập đỉnh mới 4 phi&ecirc;n li&ecirc;n tiếp. Những cổ phiếu trụ gi&uacute;p thị trường thăng hoa trong tuần trước như MSN, HPG, GAS th&igrave; nay đồng loạt giảm s&acirc;u v&agrave; dẫn đầu danh s&aacute;ch những m&atilde; t&aacute;c động ti&ecirc;u cực đến VN-Index. Ba m&atilde; n&agrave;y đều giảm tr&ecirc;n 2,5%, c&ograve;n c&aacute;c cổ phiếu kh&aacute;c như KDH, TPB, PDR mất tr&ecirc;n 3%. Ở chiều ngược lại, CTG tăng 2,2% v&agrave; trở th&agrave;nh trụ đỡ khoẻ nhất b&ecirc;n cạnh DIG, VCI, BID, SHB, POW.</p>\r\n\r\n<p>Chứng kho&aacute;n l&agrave; nh&oacute;m cổ phiếu c&oacute; trạng th&aacute;i hưng phấn nhất khi phần lớn đều tăng điểm, trong đ&oacute; nhiều m&atilde; t&iacute;ch luỹ tr&ecirc;n 4% so với tham chiếu. Trong khi đ&oacute;, nh&oacute;m bất động sản, ng&acirc;n h&agrave;ng, th&eacute;p, cảng biển, ph&acirc;n b&oacute;n... đều ph&acirc;n ho&aacute; mạnh hoặc đồng thuận giảm sau một tuần khả quan.</p>\r\n\r\n<p>Lượng tiền đổ v&agrave;o thị trường h&ocirc;m nay đạt 33.700 tỷ đồng, tăng gần 5.000 tỷ đồng so với phi&ecirc;n cuối tuần trước v&agrave; l&agrave; mức cao nhất trong v&ograve;ng hai th&aacute;ng trở lại đ&acirc;y. C&aacute;c cổ phiếu t&agrave;i ch&iacute;nh h&uacute;t tiền mạnh nhất khi gi&aacute; trị giao dịch xấp xỉ 6.800 tỷ đồng, tiếp đến l&agrave; nguy&ecirc;n vật liệu, bất động sản v&agrave; c&ocirc;ng nghiệp. T&iacute;nh ri&ecirc;ng cổ phiếu th&igrave; HPG đứng đầu với 2.546 tỷ đồng, vượt xa những cổ phiếu xếp sau như TCB 1.315 tỷ đồng, SSI 980 tỷ đồng, VHM 834 tỷ đồng.</p>\r\n\r\n<p>Nh&agrave; đầu tư nước ngo&agrave;i quay lại xả h&agrave;ng với gi&aacute; trị b&aacute;n ra hơn 2.400 tỷ đồng, trong khi mua v&agrave;o chưa đến ph&acirc;n nửa. Những cổ phiếu chịu &aacute;p lực b&aacute;n mạnh nhất l&agrave; HPG, VNM, MSN, VHM, SSI v&agrave; NLG.</p>\r\n', 8, 3, 362, 1, 1),
(24, 'Chứng khoán tăng phiên thứ tư liên tiếp', 'Chung-Khoan-Tang-Phien-Thu-Tu-Lien-Tiep', '<p>VN-Index tăng phi&ecirc;n thứ tư, tiếp tục x&aacute;c lập mức kỷ lục mới, nhờ sự hỗ trợ của nh&oacute;m cổ phiếu bất động sản.</p>\r\n', 'chung-khoan-2.jpg', '2021-11-01 01:44:04', 1, '<p>Đ&agrave; tăng của thị trường tiếp tục được nối d&agrave;i trong phi&ecirc;n h&ocirc;m nay (29/10), khi nh&agrave; đầu tư vẫn mạnh mẽ xuống tiền. Lực cầu v&agrave;o đều từ đầu phi&ecirc;n, tập trung v&agrave;o những nh&oacute;m dẫn dắt như bất động sản, x&acirc;y dựng, ng&acirc;n h&agrave;ng, gi&uacute;p VN-Index giữ tr&ecirc;n tham chiếu bất chấp &aacute;p lực b&aacute;n gia tăng.</p>\r\n\r\n<p>Sang phi&ecirc;n chiều, thị trường giằng co trong nửa đầu thời gian, trước khi bật nhanh trong phần c&ograve;n lại của phi&ecirc;n giao dịch. Sự hỗ trợ từ nh&oacute;m mid-cap v&agrave; bluechip gi&uacute;p nới rộng đ&agrave; tăng. Chốt phi&ecirc;n, VN-Index tăng hơn 6 điểm, l&ecirc;n 1.444,27 điểm. VN30-Index tăng 0,2% l&ecirc;n 1.532,35 điểm. Tr&ecirc;n s&agrave;n H&agrave; Nội, HNX-Index v&agrave; UPCOM-Index c&ugrave;ng vượt tr&ecirc;n tham chiếu.</p>\r\n\r\n<p>Mặc d&ugrave; tăng điểm, sắc đỏ vẫn &aacute;p đảo hơn v&agrave;o cuối phi&ecirc;n khi thị trường c&oacute; sự ph&acirc;n h&oacute;a. S&agrave;n HoSE ghi nhận 235 m&atilde; giảm, so với 227 m&atilde; tăng. Ri&ecirc;ng nh&oacute;m VN30, số m&atilde; tăng - giảm giữ tỷ lệ 13:16.</p>\r\n\r\n<p>Bất động sản v&agrave; x&acirc;y dựng l&agrave; nh&oacute;m đ&aacute;ng ch&uacute; &yacute; nhất trong phi&ecirc;n h&ocirc;m nay. VHM - doanh nghiệp vốn h&oacute;a cao nhất s&agrave;n chứng kho&aacute;n - chốt phi&ecirc;n tăng mạnh nhất VN30, c&oacute; th&ecirc;m 5,6%. KHG, CEO tăng hơn 4%, DIB c&oacute; th&ecirc;m 3,9%, KDH tăng 3%, NVL vượt tham chiếu hơn 1%. Nh&oacute;m cổ phiếu ng&agrave;nh x&acirc;y dựng, x&acirc;y lắp diễn biến tương tự, với HBC, SCI tăng kịch bi&ecirc;n độ.</p>\r\n\r\n<p>Nh&oacute;m ng&acirc;n h&agrave;ng cũng khởi sắc với BID tăng 4,1%, ACB c&oacute; th&ecirc;m 3,6%, TPB, CTG, VCB giữ sắc xanh khi đ&oacute;ng cửa.</p>\r\n\r\n<p>Ngược lại, th&eacute;p, bảo hiểm chịu &aacute;p lực sau nhịp tăng mạnh gần đ&acirc;y. HPG chốt phi&ecirc;n giảm 1,6%, NKG mất hơn 2%, HSG giảm 1,25%. Trong nh&oacute;m bảo hiểm, BVH, MIG, BMI c&ugrave;ng đ&oacute;ng cửa dưới tham chiếu.</p>\r\n\r\n<p>Thanh khoản thị trường tiếp tục duy tr&igrave; ở mức cao, với gi&aacute; trị giao dịch gần 29.000 tỷ đồng tr&ecirc;n HoSE. Khối ngoại giao dịch c&acirc;n bằng với gi&aacute; trị mua v&agrave; b&aacute;n c&ugrave;ng đạt tr&ecirc;n 1.700 tỷ đồng.</p>\r\n', 8, 3, 172, 1, 1),
(25, 'Cổ phiếu Vietnam Airlines chỉ được giao dịch phiên chiều', 'Co-Phieu-Vietnam-Airlines-Chi-Duoc-Giao-Dich-Phien-Chieu', '<p>Sở Giao dịch chứng kho&aacute;n TP HCM (HoSE) vừa quyết định chuyển cổ phiếu HVN của Vietnam Airlines từ diện cảnh b&aacute;o sang kiểm so&aacute;t.</p>\r\n', 'chung-khoan-3.jpg', '2021-11-01 01:45:00', 1, '<p>Theo th&ocirc;ng b&aacute;o của HoSE, cổ phiếu HVN sẽ bị chuyển sang diện kiểm so&aacute;t từ ng&agrave;y 3/11. L&yacute; do l&agrave; lợi nhuận sau thuế 6 th&aacute;ng đầu năm nay của cổ đ&ocirc;ng c&ocirc;ng ty mẹ Vietnam Airlines &acirc;m 8.458 tỷ đồng v&agrave; lợi nhuận sau thuế chưa ph&acirc;n phối tại ng&agrave;y 30/6 &acirc;m 17.808 - vượt qu&aacute; vốn điều lệ thực g&oacute;p căn cứ b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh hợp nhất so&aacute;t x&eacute;t b&aacute;n ni&ecirc;n 2021.</p>\r\n\r\n<p>Từ ng&agrave;y 3/11, cổ phiếu HVN sẽ bị hạn chế thời gian giao dịch, chỉ được giao dịch trong phi&ecirc;n chiều. Căn cứ giải tr&igrave;nh của c&ocirc;ng ty, HoSE sẽ th&ocirc;ng b&aacute;o việc cổ phiếu được giao dịch to&agrave;n thời gian trở lại dưới dạng chứng kho&aacute;n bị kiểm so&aacute;t. Trước đ&oacute; từ giữa th&aacute;ng 4, cổ phiếu Vietnam Airlines đ&atilde; bị đưa v&agrave;o diện cảnh b&aacute;o.</p>\r\n\r\n<p>Tại b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh so&aacute;t x&eacute;t 6 th&aacute;ng đầu năm của h&atilde;ng bay n&agrave;y, Deloitte Việt Nam nghi ngờ khả năng hoạt động li&ecirc;n tục của Vietnam Airlines, phụ thuộc v&agrave;o sự hỗ trợ t&agrave;i ch&iacute;nh của Ch&iacute;nh phủ v&agrave; được gia hạn c&aacute;c khoản vay từ ng&acirc;n h&agrave;ng, tổ chức t&iacute;n dụng, c&aacute;c khoản phải trả cho nh&agrave; cung cấp, cho thu&ecirc;, cũng như diễn biến dịch Covid-19.</p>\r\n\r\n<p>T&iacute;nh đến giữa năm nay, nợ ngắn hạn của Vietnam Airlines vượt qu&aacute; t&agrave;i sản ngắn hạn với số tiền 34.664 tỷ đồng, c&aacute;c khoản phải trả qu&aacute; hạn 14.805 tỷ đồng v&agrave; vốn chủ sở hữu &acirc;m gần 2.800 tỷ đồng.</p>\r\n\r\n<p>Hồi cuối th&aacute;ng 9, Vietnam Airlines th&ocirc;ng b&aacute;o ph&aacute;t h&agrave;nh th&agrave;nh c&ocirc;ng gần 800 triệu cổ phiếu để bổ sung tăng vốn điều lệ gần 8.000 tỷ đồng. Trong giải tr&igrave;nh gửi HoSE, Vietnam Airlines đang đ&agrave;m ph&aacute;n quyết liệt với nh&agrave; cung cấp, đặc biệt l&agrave; c&aacute;c chủ sở hữu t&agrave;u bay, c&aacute;c cơ sở bảo dưỡng ở nước ngo&agrave;i để giảm gi&aacute;, gi&atilde;n ho&atilde;n thanh to&aacute;n gi&uacute;p h&atilde;ng vượt qua giai đoạn kh&oacute; khăn.</p>\r\n\r\n<p>Vietnam Airlines tin tưởng thị trường sẽ sớm phục hồi gi&uacute;p c&aacute;c h&atilde;ng từng bước ổn định hoạt động sản xuất kinh doanh trong bối cảnh c&aacute;c địa phương dần kiểm so&aacute;t tốt dịch bệnh, c&aacute;c chuyến bay nội địa bắt đầu được nối lại.</p>\r\n', 8, 3, 382, 1, 1),
(26, 'Sun Property nâng tầm giá trị sống tại dự án Sun Tropical Village', 'Sun-Property-Nang-Tam-Gia-Tri-Song-Tai-Du-An-Sun-Tropical-Village', '<p>Theo chủ đ&acirc;̀u tư, cư d&acirc;n &quot;ng&ocirc;i làng nhi&ecirc;̣t đới&quot; Sun Tropical Village được hưởng dịch vụ 5 sao tại gia, cùng chu&ocirc;̃i ti&ecirc;̣n ích chăm sóc sức khỏe đẳng cấp.</p>\r\n', 'bat-dong-san-2.jpg', '2021-11-01 01:48:12', 1, '<p>Triển l&atilde;m nghệ thuật trực tuyến với chủ đề &quot;Sun Tropical Village - H&agrave;nh tr&igrave;nh n&acirc;ng tầm gi&aacute; trị sống&quot; diễn ra v&agrave;o ng&agrave;y 29/10, mang đến cho kh&aacute;ch h&agrave;ng những trải nghiệm ấn tượng. Th&ocirc;ng qua sự kiện, Sun Property (th&agrave;nh vi&ecirc;n Sun Group) tiếp tục mang đến những th&ocirc;ng tin mới về dự &aacute;n Sun Tropical Village - khu đ&ocirc; thị nghỉ dưỡng phong c&aacute;ch nhiệt đới kiểu mẫu theo m&ocirc; h&igrave;nh &quot;wellness second home&quot; tại Nam Ph&uacute; Quốc.</p>\r\n\r\n<p>Mỗi bức tranh trong triển l&atilde;m kể lại một c&acirc;u chuyện về h&agrave;nh tr&igrave;nh n&acirc;ng tầm gi&aacute; trị sống, n&ecirc;u bật những đặc quyền ri&ecirc;ng d&agrave;nh cho chủ nh&acirc;n tương lai tại khu đ&ocirc; thị nghỉ dưỡng Sun Tropical Village.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/bat-dong-san.jpg\" style=\"height:419px; width:680px\" /></p>\r\n\r\n<p><strong>Tận hưởng dịch vụ nghỉ dưỡng 5 sao tại gia</strong></p>\r\n\r\n<p>Với mong muốn đem đến cho chủ sở hữu ng&ocirc;i nh&agrave; thứ hai &quot;2 in 1&quot; vừa để ở, vừa nghỉ dưỡng theo ti&ecirc;u chuẩn 5 sao, Sun Property thiết kế m&ocirc; h&igrave;nh quản l&yacute; vận h&agrave;nh kh&aacute;c biệt tại Sun Tropical Village. Theo đ&oacute;, thương hiệu dịch vụ quản l&yacute; vận h&agrave;nh c&aacute;c khu đ&ocirc; thị Sun Property Management (thuộc Sun Property) sẽ mang đến cho chủ nh&acirc;n biệt thự 5 dịch vụ vận h&agrave;nh cao cấp gồm: quản l&yacute; vận h&agrave;nh khu vực chung khu đ&ocirc; thị, dịch vụ gi&aacute; trị gia tăng d&agrave;nh ri&ecirc;ng cho c&aacute;c căn biệt thự, chăm s&oacute;c kh&aacute;ch h&agrave;ng đặc quyền, vận h&agrave;nh hệ thống tiện &iacute;ch gồm 4 c&ocirc;ng vi&ecirc;n nội khu v&agrave; c&ocirc;ng vi&ecirc;n Wellness B&atilde;i Kem, dịch vụ tại s&acirc;n golf B&atilde;i Kem.</p>\r\n\r\n<p>Nổi bật trong dịch vụ quản l&yacute; vận h&agrave;nh chung khu đ&ocirc; thị l&agrave; chăm s&oacute;c c&acirc;y xanh. Với quy m&ocirc; 19.000 m2, hệ thống c&acirc;y xanh trong khu đ&ocirc; thị được lựa chọn tỉ mỉ theo phong c&aacute;ch nhiệt đới v&agrave; thiết kế 3 lớp: c&acirc;y b&oacute;ng m&aacute;t, c&acirc;y tầm trung, c&acirc;y bụi v&agrave; thảm cỏ. Đội ngũ nh&acirc;n sự chuy&ecirc;n nghiệp sẽ chăm s&oacute;c từng lớp c&acirc;y mỗi ng&agrave;y theo quy tr&igrave;nh, ti&ecirc;u chuẩn cao nhất. Một vườn ươm quy m&ocirc; h&agrave;ng ngh&igrave;n c&acirc;y xanh cũng được đầu tư để đảm bảo kh&ocirc;ng gian khu đ&ocirc; thị lu&ocirc;n xanh mướt, phủ đầy b&oacute;ng m&aacute;t. B&ecirc;n cạnh đ&oacute; l&agrave; dịch vụ an ninh 3 lớp 24/7, đảm bảo an to&agrave;n cho cư d&acirc;n v&agrave; kh&aacute;ch nghỉ dưỡng.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/bat-dong-san-1.jpg\" style=\"height:382px; width:680px\" /></p>\r\n\r\n<p>4 c&ocirc;ng vi&ecirc;n nội khu cũng được chăm s&oacute;c, bảo dưỡng, duy tu, vệ sinh thường xuy&ecirc;n theo c&aacute;c ti&ecirc;u chuẩn ri&ecirc;ng để lu&ocirc;n đảm bảo t&iacute;nh thẩm mỹ, tối ưu c&ocirc;ng năng. Đặc biệt, việc phun diệt c&ocirc;n tr&ugrave;ng được thực hiện h&agrave;ng tuần, giữ an to&agrave;n cho cư d&acirc;n khi vui chơi ở c&aacute;c b&atilde;i cỏ, vườn c&acirc;y.</p>\r\n\r\n<p>Ngo&agrave;i ra, lần đầu ti&ecirc;n, Sun Property đem đến Ph&uacute; Quốc dịch vụ quản gia cao cấp nhằm hỗ trợ chủ nh&acirc;n g&igrave;n giữ, bảo quản t&agrave;i sản, chăm s&oacute;c biệt thự lu&ocirc;n tươi mới. Đội ngũ quản gia c&ograve;n gi&uacute;p chủ nh&acirc;n thực hiện mọi kh&acirc;u từ đặt dịch vụ, tiện &iacute;ch như: ẩm thực, massage tại gia, nấu ăn tại gia, đặt lịch chơi golf hay xe di chuyển... Quản gia c&ograve;n đại diện chủ sở hữu trong qu&aacute; tr&igrave;nh cho thu&ecirc; từ nhận booking, check-in, check-out, hỗ trợ kh&aacute;ch lưu tr&uacute;. Do đ&oacute;, chủ nh&acirc;n biệt thự lu&ocirc;n an t&acirc;m d&ugrave; kh&ocirc;ng c&oacute; mặt tại ng&ocirc;i nh&agrave; của m&igrave;nh v&agrave; lu&ocirc;n sẵn s&agrave;ng cho c&aacute;c kỳ nghỉ chủ động với ti&ecirc;u chuẩn dịch vụ 5 sao tại gia.</p>\r\n\r\n<p><strong>Dịch vụ chăm s&oacute;c sức khỏe &quot;đ&aacute;ng mơ ước&quot;</strong></p>\r\n\r\n<p>Điểm nhấn đặc biệt l&agrave;m n&ecirc;n chất ri&ecirc;ng cho Sun Tropical Village kh&ocirc;ng chỉ l&agrave; dịch vụ nội khu 5 sao m&agrave; c&ograve;n l&agrave; những trải nghiệm chăm s&oacute;c sức khỏe đẳng cấp. Nếu Wellness Center rộng 5.300 m2 đem đến h&agrave;ng trăm liệu tr&igrave;nh chăm s&oacute;c sức khỏe, sắc đẹp đỉnh cao được vận h&agrave;nh bởi đơn vị quốc tế uy t&iacute;n, th&igrave; c&ocirc;ng vi&ecirc;n Wellness B&atilde;i Kem kề b&ecirc;n dự &aacute;n lại mang đến những trải nghiệm wellness giữa rừng nhiệt đới.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/bat-dong-san-2.jpg\" style=\"height:385px; width:680px\" /></p>\r\n\r\n<p>Từ khu đ&ocirc; thị, cư d&acirc;n Sun Tropical Village sẽ được &quot;chạm&quot; v&agrave;o thi&ecirc;n nhi&ecirc;n tại c&ocirc;ng vi&ecirc;n Wellness B&atilde;i Kem rộng 4,1 ha, tận hưởng nhiều liệu tr&igrave;nh c&acirc;n bằng th&acirc;n - t&acirc;m - tr&iacute;. C&ocirc;ng vi&ecirc;n được thiết kế tr&ecirc;n nguy&ecirc;n tắc t&ocirc;n trọng tối đa tự nhi&ecirc;n, đem đến những thảm cỏ xanh rộng lớn h&ograve;a m&igrave;nh giữa rừng c&acirc;y, những căn nh&agrave; gỗ tr&ecirc;n c&acirc;y độc đ&aacute;o, c&aacute;c tr&ograve; chơi mạo hiểm đầy th&aacute;ch thức nhưng th&uacute; vị, hệ thống đường dạo bộ, khu vườn bướm, vườn nấm... Đặc biệt, tại đ&acirc;y, đội ngũ kiến tr&uacute;c sư đ&atilde; &aacute;p dụng kỹ thuật hiện đại, nương theo địa h&igrave;nh tự nhi&ecirc;n để kiến tạo m&ocirc; h&igrave;nh bể bơi thi&ecirc;n nhi&ecirc;n v&agrave; bể bơi thủy sinh lần đầu ti&ecirc;n c&oacute; mặt tại Ph&uacute; Quốc.</p>\r\n\r\n<p>&quot;Mọi người nghĩ đi Ph&uacute; Quốc chỉ c&oacute; biển nhưng điều kh&aacute;c biệt l&agrave; ở đ&acirc;y c&oacute; đầy đủ trải nghiệm: đi bơi ở biển B&atilde;i Kem v&agrave; kh&aacute;m ph&aacute; rừng tại c&ocirc;ng vi&ecirc;n Wellness B&atilde;i Kem. C&ocirc;ng vi&ecirc;n n&agrave;y đ&aacute;p ứng y&ecirc;u cầu trải nghiệm, chăm s&oacute;c sức khỏe giữa thi&ecirc;n nhi&ecirc;n của 3 thế hệ trong gia đ&igrave;nh. Kh&ocirc;ng chỉ l&agrave; một c&ocirc;ng vi&ecirc;n đơn thuần, ở đ&acirc;y c&ograve;n được tổ chức c&aacute;c liệu tr&igrave;nh wellness, detox, thiền, yoga... T&ocirc;i tin rằng du kh&aacute;ch sẽ lựa chọn lưu tr&uacute; tại Sun Tropical Village v&igrave; kề b&ecirc;n c&ocirc;ng vi&ecirc;n Wellness B&atilde;i Kem, c&ograve;n c&aacute;c chủ nh&acirc;n sẽ hưởng đầy đủ gi&aacute; trị lớn từ c&ocirc;ng vi&ecirc;n n&agrave;y&quot;, b&agrave; Nguyễn Ngọc Th&uacute;y Linh, Tổng gi&aacute;m đốc Sun Property chia sẻ.</p>\r\n\r\n<p>Điểm nhấn mới mẻ tại ph&ograve;ng triển l&atilde;m l&agrave; bức tranh s&acirc;n golf thượng lưu. Sun Property h&eacute; lộ s&acirc;n golf sẽ sớm được bổ sung ngay kế b&ecirc;n Sun Tropical Village, l&agrave;m d&agrave;y th&ecirc;m chuỗi tiện &iacute;ch đẳng cấp hiện hữu tại B&atilde;i Kem. Đ&acirc;y cũng l&agrave; điểm thu h&uacute;t kh&aacute;ch thượng lưu đam m&ecirc; bộ m&ocirc;n golf đến với B&atilde;i Kem, khiến c&aacute;c bất động sản tại khu vực cận kề gia tăng gi&aacute; trị kh&ocirc;ng ngừng.</p>\r\n\r\n<p>Sun Tropical Village sở hữu trọn bộ những gi&aacute; trị đẳng cấp của một khu đ&ocirc; thị nghỉ dưỡng 5 sao, kề b&ecirc;n c&aacute;c tiện &iacute;ch như: s&acirc;n golf, c&aacute;c resort 5 sao quốc tế, c&ocirc;ng vi&ecirc;n wellness độc đ&aacute;o giữa rừng, nằm trong quần thể B&atilde;i Kem v&agrave; hệ sinh th&aacute;i tỷ đ&ocirc; Nam Ph&uacute; Quốc. Dự &aacute;n l&agrave; dấu ấn ti&ecirc;n phong của Sun Property trong việc đưa m&ocirc; h&igrave;nh &quot;wellness second home thế hệ mới&quot; đến đảo Ngọc. Điều n&agrave;y cũng l&yacute; giải cho con số kỷ lục 100% quỹ căn biệt thự Sun Tropical Village sau hai đợt giới thiệu đến kh&aacute;ch h&agrave;ng th&acirc;n thiết đ&atilde; được đăng k&yacute; đặt chỗ, d&ugrave; mới h&eacute; lộ th&ocirc;ng tin ra thị trường thời gian qua.</p>\r\n\r\n<p>Nằm trong quần thể B&atilde;i Kem - top 50 b&atilde;i biển đẹp nhất h&agrave;nh tinh, Sun Tropical Village c&oacute; quy m&ocirc; khoảng 14 ha, gồm 346 căn biệt thự đơn lập, song lập, tứ lập thuộc 3 ph&acirc;n khu: Tropical Wellness, Tropical Park v&agrave; Tropical Valley.</p>\r\n\r\n<p>Chủ đầu tư cũng c&ocirc;ng bố c&aacute;c ưu đ&atilde;i hấp dẫn d&agrave;nh cho cư d&acirc;n tương lai của &quot;Ng&ocirc;i l&agrave;ng nhiệt đới&quot; như: ưu đ&atilde;i vay vốn ng&acirc;n h&agrave;ng l&ecirc;n tới 70% gi&aacute; trị biệt thự, hỗ trợ l&atilde;i suất 0% trong 30 th&aacute;ng, &acirc;n hạn nợ gốc l&ecirc;n đến 40 th&aacute;ng; chiết khấu 10% nếu kh&aacute;ch h&agrave;ng kh&ocirc;ng d&ugrave;ng g&oacute;i ưu đ&atilde;i vay; c&ugrave;ng nhiều qu&agrave; tặng l&agrave; những g&oacute;i trải nghiệm wellness đẳng cấp...</p>\r\n', 9, 3, 0, 1, 1),
(27, 'Hơn 100 ngày vượt dịch khó quên của nữ CEO bốn con', 'Hon-100-Ngay-Vuot-Dich-Kho-Quen-Cua-Nu-Ceo-Bon-Con', '<p>Bốn th&aacute;ng qua, kh&ocirc;ng chỉ l&egrave;o l&aacute;i POPS Worldwide vượt qua m&ugrave;a gi&atilde;n c&aacute;ch ở TP HCM, Esther Nguyễn c&ograve;n chăm v&eacute;n tổ ấm với 4 con đang tuổi hiếu động.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'doanh-nhan.jpg', '2021-11-01 01:51:13', 1, '<p>&quot;Ban đầu t&ocirc;i nghĩ rằng thời gian gi&atilde;n c&aacute;ch n&agrave;y cũng nhanh th&ocirc;i, như những đợt b&ugrave;ng ph&aacute;t ngắn trước. Kh&ocirc;ng ngờ, n&oacute; lại k&eacute;o qu&aacute; d&agrave;i&quot;, Esther Nguyễn n&oacute;i với&nbsp;<em>VnExpress</em>&nbsp;nh&acirc;n dịp 20/10, khi c&ocirc; nh&igrave;n lại hơn 100 ng&agrave;y c&ugrave;ng h&agrave;ng triệu người trải qua đợt gi&atilde;n c&aacute;ch ở TP HCM.</p>\r\n\r\n<p>Esther Nguyễn sinh năm 1976, l&agrave; CEO POPS Worldwide. C&ocirc; được mệnh danh l&agrave; &quot;nữ ho&agrave;ng nhạc số&quot; khi c&ocirc;ng ty c&ocirc; l&agrave; đơn vị sản xuất v&agrave; ph&aacute;t h&agrave;nh nội dung giải tr&iacute; kỹ thuật số h&agrave;ng đầu Đ&ocirc;ng Nam &Aacute; suốt 14 năm qua, với 235 tỷ lượt xem tr&ecirc;n to&agrave;n hệ thống v&agrave; hơn 456 triệu người theo d&otilde;i t&iacute;nh đến thời điểm n&agrave;y.</p>\r\n\r\n<p>Đ&atilde; c&oacute; 3 lần POPS nhận được đầu tư từ c&aacute;c quỹ lớn. Gần đ&acirc;y nhất l&agrave; Mirae Asset - Naver Asia Growth Fund bắt tay quỹ đầu tư tư nh&acirc;n Eastbridge Partners, r&oacute;t 30 triệu USD cho c&ocirc;ng ty. Bản th&acirc;n Esther Nguyễn từng v&agrave;o top &quot;10 nữ CEO truyền cảm hứng tại Đ&ocirc;ng Nam &Aacute;&quot; năm 2017 v&agrave; &quot;60 nữ doanh nh&acirc;n - người s&aacute;ng lập những startup l&agrave;m rung chuyển giới c&ocirc;ng nghệ to&agrave;n cầu&quot; năm 2018 của&nbsp;<em>Forbes.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/doanh-nhan.jpg\" style=\"height:453px; width:680px\" /></em></p>\r\n\r\n<p>Năm ngo&aacute;i, lĩnh vực giải tr&iacute; số, một cấu phần của nền kinh tế số Việt Nam tiếp tục ph&aacute;t triển. Nhờ thế, POPS đ&atilde; tăng trưởng 89% so với c&ugrave;ng kỳ 2019. Nhưng sang 2021, khi l&agrave;n s&oacute;ng dịch b&ugrave;ng ph&aacute;t lần thứ tư, c&ocirc; thừa nhận năng suất c&ocirc;ng việc giảm v&agrave; giao tiếp giữa mọi người trong c&ocirc;ng ty gặp nhiều trở ngại. &quot;C&oacute; những chương tr&igrave;nh m&agrave; POPS ấp ủ thực hiện nhưng rồi gi&atilde;n c&aacute;ch khiến việc ghi h&igrave;nh, tổ chức nội dung... kh&ocirc;ng thể n&agrave;o thực hiện&quot;.</p>\r\n\r\n<p>Chia sẻ về b&iacute; quyết vượt dịch của đội ngũ m&igrave;nh, Esther Nguyễn cho biết kinh nghiệm đầu ti&ecirc;n l&agrave; giữ vững tinh thần bằng c&aacute;ch nỗ lực giao tiếp, kết nối, động vi&ecirc;n, truyền cảm hứng, quan t&acirc;m v&agrave; cổ vũ lẫn nhau. &quot;Mỗi ng&agrave;y, trước khi bắt đầu, ch&uacute;ng t&ocirc;i đều hỏi thăm &#39;h&ocirc;m nay bạn thế n&agrave;o?&#39;, sau đ&oacute; ch&uacute;ng t&ocirc;i mới b&agrave;n về c&ocirc;ng việc&quot;, c&ocirc; kể. Khi một nh&acirc;n sự bị ốm hoặc phải chăm s&oacute;c một th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh bị ốm, họ chia sẻ khối lượng c&ocirc;ng việc cho nhau.</p>\r\n\r\n<p>L&agrave; CEO, Esther Nguyễn cũng lao v&agrave;o chuẩn bị c&aacute;c b&agrave;i thuyết tr&igrave;nh cho c&aacute;c đối t&aacute;c gi&uacute;p c&aacute;c th&agrave;nh vi&ecirc;n trong nh&oacute;m. &quot;T&ocirc;i cũng l&agrave;m lu&ocirc;n cả việc l&agrave; người đi thuyết tr&igrave;nh với c&aacute;c đối t&aacute;c gi&aacute;o dục/trường học; hoặc phải n&oacute;i chuyện trực tiếp với c&aacute;c gi&aacute;o vi&ecirc;n để kết nối họ với dự &aacute;n POPS Kids Learn&quot;, c&ocirc; n&oacute;i.</p>\r\n\r\n<p>POPS Kids Learn l&agrave; một nền tảng gi&aacute;o dục trực tuyến m&agrave; c&ocirc;ng ty c&ocirc; vừa cho ra mắt trong bối cảnh trẻ em phải học online ho&agrave;n to&agrave;n v&igrave; dịch bệnh. Đ&oacute; cũng ch&iacute;nh l&agrave; kinh nghiệm thứ hai của c&ocirc; khi nh&igrave;n ra cơ hội trong khủng hoảng.</p>\r\n\r\n<p>&quot;Trong thời gian gi&atilde;n c&aacute;ch vừa qua, t&ocirc;i cảm thấy tuyệt vọng cho c&aacute;c em v&agrave; muốn l&agrave;m điều g&igrave; đ&oacute; để gi&uacute;p ch&uacute;ng c&oacute; thể tiến bộ về mặt x&atilde; hội, tinh thần v&agrave; thể chất&quot;, Esther Nguyễn n&oacute;i về l&yacute; do ra đời của nền tảng tr&ecirc;n.</p>\r\n\r\n<p>Trong v&ograve;ng 2 th&aacute;ng, c&ocirc;ng ty cho ra mắt dự &aacute;n với hơn 150 kh&oacute;a học tương t&aacute;c trực tiếp ở nhiều lĩnh vực như STEM, &acirc;m nhạc, to&aacute;n, khi&ecirc;u vũ, nấu ăn...từ c&aacute;c gi&aacute;o vi&ecirc;n từ Học viện s&aacute;ng tạo c&ocirc;ng nghệ Teky v&agrave; Everest Education. &quot;Ch&uacute;ng t&ocirc;i muốn tất cả trẻ em đều c&oacute; thể tiếp cận c&aacute;c lớp học của m&igrave;nh, cho ph&eacute;p ch&uacute;ng tương t&aacute;c với những đứa trẻ kh&aacute;c để ch&uacute;ng kh&ocirc;ng cảm thấy bị xa c&aacute;ch&quot;, c&ocirc; n&oacute;i.</p>\r\n\r\n<p><strong>Nhưng kh&ocirc;ng chỉ c&oacute; c&ocirc;ng việc, Esther Nguyễn c&ograve;n l&agrave; một b&agrave; mẹ c&oacute; 4 con đang tuổi hiếu động, với 2 b&eacute; nhỏ 2 v&agrave; 4 tuổi</strong>. &quot;Để c&acirc;n bằng cả gia đ&igrave;nh v&agrave; c&ocirc;ng việc thật sự l&agrave; một thử th&aacute;ch&quot;, c&ocirc; n&oacute;i. Nữ CEO cho hay, kh&ocirc;ng c&oacute; sự ngăn c&aacute;ch của gia đ&igrave;nh v&agrave; c&ocirc;ng việc, mọi thứ cứ h&ograve;a quyện v&agrave;o nhau. V&igrave; vậy c&oacute; những ng&agrave;y cảm gi&aacute;c như kh&ocirc;ng bao giờ kết th&uacute;c.</p>\r\n\r\n<p>Bọn trẻ kh&ocirc;ng hiểu rằng bố mẹ phải l&agrave;m việc. Ch&uacute;ng nghĩ bố mẹ đang ở nh&agrave; n&ecirc;n phải chơi đ&ugrave;a với ch&uacute;ng. Con g&aacute;i &uacute;t của c&ocirc; vẫn thường hay n&oacute;i: &quot;Mẹ ơi, h&atilde;y đặt điện thoại xuống&quot; hay &quot;Mẹ đừng l&agrave;m việc nữa&quot;. V&igrave; vậy, gia đ&igrave;nh c&ocirc; phải t&igrave;m sự c&acirc;n bằng, bằng c&aacute;ch tạo ra c&aacute;c ranh giới kỳ vọng cho con c&aacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BA%BF%20gi%E1%BB%9Bi/cu%E1%BB%99c%20s%E1%BB%91ng%20%C4%91%C3%B3%20%C4%91%C3%A2y/doanh-nhan1.jpg\" style=\"height:453px; width:680px\" /></p>\r\n\r\n<p>D&ugrave; vậy, &aacute;p lực vẫn rất lớn. &quot;Giữa nấu nướng, dọn dẹp, chăm s&oacute;c con c&aacute;i, l&agrave;m việc, họp h&agrave;nh v&agrave; mọi thứ kh&aacute;c, t&ocirc;i đ&atilde; kiệt sức thật sự&quot;, c&ocirc; thừa nhận. Esther Nguyễn n&oacute;i, v&agrave;o m&ugrave;a gi&atilde;n c&aacute;ch, c&ocirc; phải chấp nhận rất nhiều thứ kh&ocirc;ng ho&agrave;n hảo hoặc dưới chuẩn b&igrave;nh thường của bản th&acirc;n.</p>\r\n\r\n<p>Đ&oacute; l&agrave; ng&ocirc;i nh&agrave; sẽ kh&ocirc;ng được sạch sẽ hay ngăn nắp. Bọn trẻ c&oacute; thể phải ăn thức ăn kh&ocirc;ng tốt cho sức khỏe v&igrave; c&ocirc; kh&ocirc;ng c&oacute; đủ thời gian nấu nướng. C&ocirc; kh&ocirc;ng thể trả lời mọi email hoặc tham gia tất cả cuộc họp. &quot;T&ocirc;i phải chấp nhận rằng c&aacute;c con t&ocirc;i sẽ c&oacute; nhiều thời gian sử dụng thiết bị hơn b&igrave;nh thường để t&ocirc;i c&oacute; thể... đi tắm. T&ocirc;i phải chấp nhận rằng t&ocirc;i chỉ đang cố hết sức m&igrave;nh rồi!&quot;, c&ocirc; bộc bạch.</p>\r\n\r\n<p>Hơn 100 ng&agrave;y gi&atilde;n c&aacute;ch, Esther Nguyễn may mắn kh&ocirc;ng bị mất ngủ hay trầm cảm, nhưng sự căng thẳng v&agrave; lo lắng thường xuy&ecirc;n lu&ocirc;n hiện hữu. &quot;Thật kh&oacute; để giữ được sự t&iacute;ch cực v&agrave; thể hiện một gương mặt dũng cảm cho c&aacute;c con v&agrave; cho cả team&quot;, c&ocirc; n&oacute;i. Tuy nhi&ecirc;n, c&ocirc; tin rằng phụ nữ l&agrave; nền tảng cơ bản của một gia đ&igrave;nh, l&agrave; người gắn kết mọi thứ v&agrave; mọi người lại với nhau.</p>\r\n\r\n<p>&quot;Phụ nữ ch&uacute;ng ta thật mạnh mẽ v&agrave; ki&ecirc;n cường. T&ocirc;i thực sự tin v&agrave;o điều đ&oacute;. Ch&uacute;ng ta c&oacute; được sức mạnh b&ecirc;n trong bộc ph&aacute;t ra l&uacute;c ta cần nhất&quot;, c&ocirc; n&oacute;i. Esther Nguyễn tin rằng, phụ nữ kh&ocirc;ng n&ecirc;n đặt ra qu&aacute; nhiều &aacute;p lực m&agrave; qu&ecirc;n mất việc chăm s&oacute;c bản th&acirc;n m&igrave;nh. V&agrave; đ&oacute; cũng l&agrave; c&aacute;ch c&ocirc; vượt qua hơn 100 ng&agrave;y gi&atilde;n c&aacute;ch.</p>\r\n\r\n<p>Theo kinh nghiệm của c&ocirc;, phụ nữ n&ecirc;n d&agrave;nh th&ecirc;m chỉ 30 ph&uacute;t cho bản th&acirc;n, để thiền tịnh, cầu nguyện, ngồi trong im lặng, tập thể dục, hoặc đơn giản l&agrave; c&oacute; thể t&igrave;m thấy sự b&igrave;nh y&ecirc;n cho m&igrave;nh. Phương thức n&agrave;y gi&uacute;p c&ocirc; nạp năng lượng v&agrave; lan tỏa những năng lượng đ&oacute; cho những đứa con.</p>\r\n\r\n<p>Esther Nguyễn cũng &aacute;p dụng tinh thần san sẻ c&ocirc;ng việc trong c&ocirc;ng ty v&agrave;o gia đ&igrave;nh, nơi tất cả th&agrave;nh vi&ecirc;n phải l&agrave;m việc c&ugrave;ng nhau để ho&agrave;n th&agrave;nh việc nh&agrave;. &quot;Đ&oacute; l&agrave; một b&agrave;i học hữu &iacute;ch. V&igrave; giờ đ&acirc;y, đứa 2 tuổi v&agrave; 4 tuổi nh&agrave; t&ocirc;i đ&atilde; biết gấp quần &aacute;o v&agrave; cất v&agrave;o tủ. Khoảng thời gian n&agrave;y cũng dạy ch&uacute;ng tự lập hơn&quot;, c&ocirc; kể.</p>\r\n\r\n<p>H&ocirc;m 18/10, Esther Nguyễn v&agrave; c&aacute;c nh&acirc;n vi&ecirc;n quay lại văn ph&ograve;ng l&agrave;m việc. Họ bắt đầu khởi động những kế hoạch bị đ&igrave;nh trệ v&igrave; m&ugrave;a gi&atilde;n c&aacute;ch với một t&acirc;m l&yacute; thận trọng. &quot;Sau một thời gian lo sợ v&agrave; kh&ocirc;ng chắc chắn đối với tất cả ch&uacute;ng ta, kh&ocirc;ng chỉ bản th&acirc;n m&igrave;nh, t&ocirc;i nghĩ rằng mọi người đều cảm thấy nhẹ nh&otilde;m nhưng vẫn thận trọng về &#39;điều b&igrave;nh thường mới&#39;. C&oacute; một cảm gi&aacute;c hy vọng, nhưng vẫn c&oacute; một cảm gi&aacute;c d&egrave; dặt về việc lập kế hoạch cho bất cứ điều g&igrave; l&acirc;u d&agrave;i&quot;, c&ocirc; n&oacute;i.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, về mặt tổng thể, gi&atilde;n c&aacute;ch x&atilde; hội thường gi&uacute;p thị trường nội dung số ph&aacute;t triển mạnh mẽ. Sau ch&acirc;u &Acirc;u, Mỹ, thế giới chứng kiến dịch bệnh b&ugrave;ng ph&aacute;t ở c&aacute;c nước ch&acirc;u &Aacute; như Th&aacute;i Lan, Philippines, Indonesia... l&agrave; những thị trường m&agrave; POPS đầu tư nhiều. Nhưng đ&oacute; cũng l&agrave; cơ hội.</p>\r\n\r\n<p>&quot;Ch&uacute;ng t&ocirc;i đ&atilde; nhận thấy sự chuyển dịch mạnh mẽ hơn nữa sang việc ti&ecirc;u thụ c&aacute;c nội dung trực tuyến v&agrave; ch&uacute;ng t&ocirc;i c&oacute; kế hoạch tiếp tục đ&aacute;p ứng nhu cầu đ&oacute;&quot;, c&ocirc; n&oacute;i.</p>\r\n\r\n<p>Thị trường khối ASEAN với hơn 400 triệu người, doanh thu hằng năm của ng&agrave;nh c&ocirc;ng nghiệp nội dung số khu vực ước đạt 150 tỷ USD, trong đ&oacute; nguồn thu từ bản quyền đạt 5-7 tỷ USD v&agrave; l&ecirc;n tới 55-65 tỷ USD cho c&aacute;c dịch vụ nội dung số. Hiện, Việt Nam c&oacute; khoảng 4.500 doanh nghiệp v&agrave; hơn 70.000 lao động hoạt động trong ng&agrave;nh c&ocirc;ng nghiệp nội dung số.</p>\r\n\r\n<p>Chia sẻ về tham vọng của m&igrave;nh, Esther Nguyễn cho hay vẫn muốn tiếp tục mở rộng quy m&ocirc; POPS Kids Learn v&agrave; trở th&agrave;nh nền tảng lớp học trực tuyến lớn nhất về c&aacute;c m&ocirc;n học ch&iacute;nh thống, cũng như c&aacute;c lớp ph&aacute;t triển t&agrave;i năng v&agrave; kỹ năng. &quot;T&ocirc;i muốn l&agrave;m cho việc gi&aacute;o dục trở n&ecirc;n th&uacute; vị, đầy cảm hứng v&agrave; c&oacute; thể tiếp cận được với tất cả mọi người!&quot;, c&ocirc; n&oacute;i.</p>\r\n', 10, 3, 0, 1, 1),
(28, 'Vì sao Ronaldo xoay 180 độ khi mừng bàn thắng?', 'Vi-Sao-Ronaldo-Xoay-180-Do-Khi-Mung-Ban-Thang?', '<p>Cristiano Ronaldo bắt đầu ăn mừng sau khi ghi b&agrave;n bằng động t&aacute;c xoay người tr&ecirc;n kh&ocirc;ng từ năm 2013, khi c&ograve;n kho&aacute;c &aacute;o Real Madrid.</p>\r\n', 'bong-da.jpg', '2021-11-01 01:55:27', 1, '<p>Động t&aacute;c xoay người 180 độ rồi tiếp đất đ&atilde; trở n&ecirc;n phổ biến c&ugrave;ng&nbsp;<a href=\"https://vnexpress.net/chu-de/cristiano-ronaldo-194\" rel=\"dofollow\">Ronaldo&nbsp;</a>t&aacute;m năm qua. Cầu thủ 36 tuổi thường chạy về cột cờ g&oacute;c, nhảy l&ecirc;n, xoay người 180 độ rồi vung hai tay xuống khi tiếp đất, k&egrave;m tiếng h&eacute;t &quot;Si&quot;, nghĩa l&agrave; &quot;Đ&uacute;ng vậy&quot;. Hai ch&acirc;n của Ronaldo dang rộng khi tiếp đất, tạo n&ecirc;n tư thế quyền lực trước đ&aacute;m đ&ocirc;ng.</p>\r\n\r\n<p>&quot;H&agrave;nh động đ&oacute; ho&agrave;n to&agrave;n tự nhi&ecirc;n&quot;, Ronaldo chia sẻ trong một cuộc phỏng vấn năm 2019. &quot;Khi ch&uacute;ng t&ocirc;i thắng, cả đội thường n&oacute;i &lsquo;Si&rsquo; với nhau. T&ocirc;i kh&ocirc;ng biết tại sao, n&oacute; như phản xạ vậy. Khi Real du đấu Mỹ v&agrave; gặp Chelsea năm 2013, t&ocirc;i bắt đầu mừng b&agrave;n thắng như vậy. Sau đ&oacute; t&ocirc;i l&agrave;m điều n&agrave;y thường xuy&ecirc;n hơn. T&ocirc;i cảm thấy c&aacute;c CĐV phấn kh&iacute;ch, đến mức khi gặp t&ocirc;i họ h&eacute;t l&ecirc;n &lsquo;Cristiano, đ&uacute;ng vậy&rsquo;. T&ocirc;i ngạc nhi&ecirc;n v&igrave; được nhớ đến với động t&aacute;c n&agrave;y. T&ocirc;i thấy rất vui v&agrave; sẽ tiếp tục l&agrave;m thế&quot;.</p>\r\n\r\n<p>Ronaldo th&iacute;ch h&eacute;t từ &quot;Si&quot; đến nỗi anh đ&atilde; l&agrave;m điều n&agrave;y tr&ecirc;n s&acirc;n khấu lễ trao giải Quả b&oacute;ng V&agrave;ng 2014, năm anh vượt k&igrave;nh địch&nbsp;<a href=\"https://vnexpress.net/chu-de/lionel-messi-300\" rel=\"dofollow\">Lionel Messi</a>&nbsp;v&agrave; thủ th&agrave;nh Manuel Neuer.</p>\r\n', 14, 5, 0, 1, 1),
(29, 'Messi muốn trở lại Barca sau khi giải nghệ', 'Messi-Muon-Tro-Lai-Barca-Sau-Khi-Giai-Nghe', '<p>Si&ecirc;u sao Lionel Messi tiết lộ &yacute; định trở lại Barca để giữ vai tr&ograve; gi&aacute;m đốc kỹ thuật sau khi treo gi&agrave;y.</p>\r\n', 'bong-da-1.jpg', '2021-11-01 01:56:19', 1, '<p>Trong cuộc phỏng vấn với tờ&nbsp;<em>Diario Sport&nbsp;</em>(T&acirc;y Ban Nha), khi được hỏi về khả năng trở lại Barca một ng&agrave;y n&agrave;o đ&oacute;, Messi đ&aacute;p: &quot;Chắc chắn rồi. T&ocirc;i lu&ocirc;n muốn gi&uacute;p &iacute;ch cho CLB. T&ocirc;i muốn l&agrave;m một gi&aacute;m đốc kỹ thuật v&agrave;o thời điểm n&agrave;o đ&oacute; trong tương lai. T&ocirc;i kh&ocirc;ng chắc l&agrave; ở Barca hay CLB n&agrave;o kh&aacute;c&quot;.</p>\r\n\r\n<p>H&egrave; n&agrave;y&nbsp;<a href=\"https://vnexpress.net/chu-de/lionel-messi-300\" rel=\"dofollow\">Messi&nbsp;</a>rời Barca sau 21 năm gắn b&oacute;, do đội b&oacute;ng khủng hoảng t&agrave;i ch&iacute;nh v&agrave; kh&ocirc;ng thể gia hạn hợp đồng. Tiền đạo 34 tuổi đ&atilde; chơi 778 trận cho đội một Barca, ghi 672 b&agrave;n v&agrave; kiến tạo 305 lần. Tại đ&acirc;y, anh cũng đ&atilde; gi&agrave;nh s&aacute;u Quả B&oacute;ng V&agrave;ng.</p>\r\n\r\n<p>Anh k&yacute; hợp đồng hai năm, k&egrave;m điều khoản gia hạn một năm, với PSG. &quot;Nếu được, t&ocirc;i muốn cống hiến cho Barca một lần nữa. T&ocirc;i c&oacute; thể l&agrave;m được bởi đ&oacute; l&agrave; CLB t&ocirc;i y&ecirc;u. T&ocirc;i cũng muốn l&agrave;m c&ocirc;ng việc đ&oacute; để tiếp tục học hỏi, trưởng th&agrave;nh v&agrave; tiếp tục l&agrave; một trong những người giỏi nhất&quot;, Messi n&oacute;i th&ecirc;m.</p>\r\n\r\n<p>Trước đ&acirc;y, tiền đạo 34 tuổi từng b&agrave;y tỏ nguyện vọng giải nghệ tại đội b&oacute;ng thời thơ ấu Newell&#39;s Old Boys (Argentina). Anh cũng b&aacute;c bỏ khả năng trở th&agrave;nh HLV sau khi treo gi&agrave;y, trong cuộc phỏng vấn với tờ&nbsp;<em>La Sexta</em>&nbsp;năm 2020. Messi n&oacute;i khi đ&oacute;: &quot;T&ocirc;i kh&ocirc;ng nghĩ m&igrave;nh th&iacute;ch hợp l&agrave;m HLV. C&oacute; lẽ sẽ tốt hơn nếu t&ocirc;i l&agrave;m gi&aacute;m đốc kỹ thuật để chi&ecirc;u mộ những cầu thủ m&agrave; bản th&acirc;n hoặc CLB muốn&quot;.</p>\r\n\r\n<p>Messi đ&atilde; chơi t&aacute;m trận cho PSG m&ugrave;a n&agrave;y, ghi ba b&agrave;n - đều tại Champions League. Anh chưa l&ecirc;n tiếng tại Ligue 1 d&ugrave; PSG đang đứng đầu, với 8 điểm nhiều hơn nh&igrave; bảng Nice. Cuối tuần trước, khi&nbsp;<a href=\"https://vnexpress.net/psg-thang-nguoc-sau-khi-messi-roi-san-4378992.html\" rel=\"dofollow\">PSG thắng Lille 2-1</a>, Messi đ&aacute; ch&iacute;nh nhưng bị thay ra v&agrave;o giờ giải lao.</p>\r\n', 14, 5, 2, 1, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(30, 'Djokovic: ‘Thua Mỹ Mở rộng giúp tôi mạnh hơn’', 'Djokovic:-‘Thua-My-Mo-Rong-Giup-Toi-Manh-Hon’', '<p>Tay vợt số một thế giới Novak Djokovic buồn v&igrave; kh&ocirc;ng đoạt cả bốn Grand Slam m&ugrave;a n&agrave;y, nhưng lạc quan về sự tiến bộ của bản th&acirc;n.</p>\r\n', 'tennis.jpg', '2021-11-01 01:57:45', 1, '<p>&quot;T&ocirc;i thất vọng v&igrave; thua chung kết Mỹ Mở rộng. Đ&oacute; c&oacute; thể l&agrave; thời khắc tệ nhất, nhưng cũng c&oacute; thể l&agrave; tốt nhất cho t&ocirc;i&quot;,&nbsp;<a href=\"https://vnexpress.net/chu-de/novak-djokovic-352\" rel=\"dofollow\">Djokovic&nbsp;</a>n&oacute;i trong họp b&aacute;o trước sự kiện Paris Masters tuần n&agrave;y. &quot;T&ocirc;i đ&atilde; thua trận đấu lớn nhưng nhận được những trải nghiệm chưa từng c&oacute; tại New York. Đ&oacute; l&agrave; nguồn năng lượng t&iacute;ch cực từ kh&aacute;n giả, một chiến thắng trong đời t&ocirc;i. T&ocirc;i sẽ mạnh mẽ hơn từ trải nghiệm đ&oacute;&quot;.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/th%E1%BB%83%20thao/tennis.jpg\" style=\"height:408px; width:680px\" /></p>\r\n\r\n<p>Djokovic g&aacute;c vợt 0-3 dưới tay&nbsp;<a href=\"https://vnexpress.net/chu-de/daniil-medvedev-198\" rel=\"dofollow\">Daniil Medvedev</a>&nbsp;ở chung kết Mỹ Mở rộng. Anh sau đ&oacute; nghỉ hai th&aacute;ng, trước khi trở lại ở Paris Masters &ndash; Masters 1000 cuối c&ugrave;ng trong năm khởi tranh h&ocirc;m nay. Djokovic giữ kỷ lục năm lần v&ocirc; địch giải v&agrave; đứng trước kỷ lục bảy lần kết th&uacute;c năm ở vị tr&iacute; số một ATP.</p>\r\n\r\n<p>&quot;T&ocirc;i đ&atilde; ở rất gần Calendar Slam v&agrave; điều đ&oacute; mang lại động lực lớn&quot;, Djokovic n&oacute;i th&ecirc;m. &quot;T&ocirc;i cũng cảm thấy khi&ecirc;m tốn hơn về sự nghiệp v&agrave; phong độ. T&ocirc;i c&oacute; thể cải thiện bản th&acirc;n khi quay trở lại s&acirc;n sau thời gian d&agrave;i nghỉ ngơi. T&ocirc;i hiểu những g&igrave; cần l&agrave;m để trở n&ecirc;n giỏi hơn&quot;.</p>\r\n\r\n<p>Nhờ hơn Medvedev gần 2.000 điểm, Djokovic nhiều khả năng giữ số một thế giới ngay sau Paris Masters. Trong trường hợp Medvedev kh&ocirc;ng v&agrave;o tứ kết, Djokovic sẽ ho&agrave;n th&agrave;nh mục ti&ecirc;u bất kể kết quả của bản th&acirc;n. Djokovic được miễn thi đấu v&ograve;ng một v&agrave; sẽ gặp Fabio Fognini hoặc Marton Fucsovics ở v&ograve;ng hai. Tay vợt Serbia c&ograve;n tham dự nội dung đ&ocirc;i, với đồng hương Filip Krajinovic.</p>\r\n\r\n<p>&quot;T&ocirc;i đang ở vị thế tốt để cạnh tranh c&ugrave;ng Daniil Medvedev&quot;, Djokovic n&oacute;i về cuộc đua gi&agrave;nh vị tr&iacute; số một ATP m&ugrave;a n&agrave;y. &quot;Giữ vị tr&iacute; số một tới cuối m&ugrave;a lu&ocirc;n l&agrave; mục ti&ecirc;u quan trọng của t&ocirc;i. T&ocirc;i c&ograve;n mục ti&ecirc;u kh&aacute;c v&agrave;o cuối năm l&agrave; Davis Cup. Việc thi đấu &iacute;t trong hai th&aacute;ng qua mang tới nhiều nguy cơ. T&ocirc;i cần trở lại từng bước, t&igrave;m thấy nhịp điệu v&agrave; phong độ tốt nhất&quot;.</p>\r\n', 15, 5, 0, 1, 1),
(31, 'Việt Nam có tránh được bi kịch của Thái Lan sau vòng loại World Cup?', 'Viet-Nam-Co-Tranh-Duoc-Bi-Kich-Cua-Thai-Lan-Sau-Vong-Loai-World-Cup?', '<p>D&ugrave; to&agrave;n thua sau bốn trận, những g&igrave; thầy tr&ograve; HLV Park Hang-seo thể hiện ở v&ograve;ng loại thứ ba World Cup 2022 vẫn đem lại nhiều điểm t&iacute;ch cực, thậm ch&iacute; tốt hơn so với Th&aacute;i Lan bốn năm trước.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:535.672px; top:115px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/16.png\" /></div>\r\n', 'the-thao.jpg', '2021-11-01 02:00:34', 1, '<p>Thủ m&ocirc;n Kawin Thamsatchanan lắc đầu ngao ng&aacute;n, c&ograve;n tiền đạo đội trưởng Teerasil Dangda thở d&agrave;i khi tiếng c&ograve;i m&atilde;n cuộc của trọng t&agrave;i vang l&ecirc;n tr&ecirc;n s&acirc;n Saitama tối 28/3/2017. Th&aacute;i Lan chịu thua chủ nh&agrave; Nhật Bản 0-4 ở v&ograve;ng loại thứ ba World Cup 2018 v&agrave; ch&iacute;nh thức hết cửa v&agrave;o v&ograve;ng chung kết. Ph&oacute;ng vi&ecirc;n Li&ecirc;n đo&agrave;n B&oacute;ng đ&aacute; ch&acirc;u &Aacute; AFC ngay lập tức k&eacute;o HLV Kiatisuk Senamuang v&agrave;o khu phỏng vấn. &Ocirc;ng gượng cười, rồi n&oacute;i: &quot;Nhật Bản chuyền v&agrave; s&uacute;t qu&aacute; tốt n&ecirc;n ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; g&igrave; ph&agrave;n n&agrave;n. Họ tận dụng cơ hội tốt hơn, c&ograve;n ch&uacute;ng t&ocirc;i nhận được b&agrave;i học đắt gi&aacute;. Một ng&agrave;y n&agrave;o đ&oacute; c&aacute;nh cửa đến World Cup sẽ mở ra với Th&aacute;i Lan&quot;.</p>\r\n\r\n<p>Tr&ograve;n một th&aacute;ng trước đ&oacute;, Li&ecirc;n đo&agrave;n B&oacute;ng đ&aacute; Th&aacute;i Lan (FAT) gia hạn với Kiatisuk th&ecirc;m một năm. Nhưng chỉ sau hai trận thua li&ecirc;n tiếp 0-3 trước Saudi Arabia v&agrave; 0-4 ở Nhật Bản, Th&aacute;i Lan lại rộ tin FAT sẽ sa thải Kiatisuk. Ba ng&agrave;y sau thảm bại ở Saitama, &ocirc;ng từ chức.</p>\r\n\r\n<p>&quot;FAT kh&ocirc;ng sa thải Kiatisuk, nhưng họ đ&atilde; sắp xếp một cuộc phỏng vấn g&acirc;y &aacute;p lực l&ecirc;n &ocirc;ng ấy&quot;, nh&agrave; b&aacute;o Kaweesak Pornwattanawate của tờ&nbsp;<em>Sanook</em>&nbsp;n&oacute;i với&nbsp;<em>VnExpress</em>. &quot;Cuối c&ugrave;ng ch&iacute;nh Kiatisuk tự th&ocirc;ng b&aacute;o sẽ từ chức&quot;.</p>\r\n\r\n<p>World Cup 2018 l&agrave; lần đầu ti&ecirc;n sau 16 năm Th&aacute;i Lan v&agrave;o tới v&ograve;ng loại cuối c&ugrave;ng. Tại v&ograve;ng loại cuối World Cup 2002, họ thua cả t&aacute;m trận. V&igrave; thế truyền th&ocirc;ng Th&aacute;i Lan chỉ kỳ vọng v&agrave;o một chiến thắng cho đội nh&agrave; trong 10 trận v&ograve;ng loại thứ ba World Cup 2018. Sau bảy trận, thầy tr&ograve; Kiatisuk chỉ kiếm được một điểm từ Australia, v&agrave; như thế chưa đủ để l&agrave;m FAT v&agrave; người h&acirc;m mộ h&agrave;i l&ograve;ng.</p>\r\n\r\n<p>M&agrave;n tr&igrave;nh diễn của Th&aacute;i Lan kh&ocirc;ng qu&aacute; tệ, khi số cơ hội họ tạo ra kh&ocirc;ng thua k&eacute;m Saudi Arabia hay Nhật Bản. Tr&ecirc;n s&acirc;n Saitama, Th&aacute;i Lan thậm ch&iacute; s&uacute;t nhiều hơn Nhật Bản, nhưng vấn đề nằm ở chất lượng trong từng pha dứt điểm như Kiatisuk đ&atilde; n&oacute;i. Đến đội trưởng Teerasil cũng đ&aacute; hỏng phạt đền. Chất lượng dứt điểm nằm ở tr&igrave;nh độ của cầu thủ tấn c&ocirc;ng, v&agrave; giữa Th&aacute;i Lan v&agrave; Nhật Bản c&oacute; một độ v&ecirc;nh lớn m&agrave; ri&ecirc;ng Kiatisuk kh&ocirc;ng thể cải thiện được.</p>\r\n\r\n<p>Kiatisuk rời Th&aacute;i Lan, bỏ lại đằng sau hai chức v&ocirc; địch AFF Cup li&ecirc;n tiếp năm 2014 v&agrave; 2016, một King&#39;s Cup 2016 v&agrave; HC v&agrave;ng SEA Games 2013. &quot;Kiatisuk phải từ chức l&agrave; b&igrave;nh thường, v&igrave; HLV sẽ phải ra đi khi kh&ocirc;ng đạt kết quả tốt&quot;, Kaweesak n&oacute;i th&ecirc;m.</p>\r\n\r\n<p><strong>Nhưng, người Th&aacute;i Lan kh&ocirc;ng ngờ rằng AFF Cup 2016 lại l&agrave; danh hiệu cuối c&ugrave;ng của đội tuyển họ cho đến l&uacute;c n&agrave;y</strong>. Họ bị loại ở b&aacute;n kết AFF Cup 2018 bởi luật b&agrave;n thắng s&acirc;n kh&aacute;ch trước Malaysia. Ở v&ograve;ng loại thứ hai World Cup 2022, họ thậm ch&iacute; chỉ đứng thứ tư sau UAE, Việt Nam v&agrave; Malaysia. FAT cũng ba lần thay HLV, từ Milovan Rajevac đến Akira Nishino v&agrave; giờ l&agrave; Mano Polking. Hợp đồng của Polking sẽ hết hạn sau AFF Cup cuối năm 2021, v&agrave; FAT sẽ t&igrave;m HLV mới nếu Th&aacute;i Lan kh&ocirc;ng v&ocirc; địch.</p>\r\n\r\n<p>Hiện tại, kh&ocirc;ng &iacute;t người h&acirc;m mộ Th&aacute;i Lan lại tiếc nuối v&igrave; đ&atilde; để mất Kiatisuk, v&agrave; họ muốn đưa &ocirc;ng trở lại. &quot;Nhiều người đang muốn Kiatisuk quay về dẫn dắt đội tuyển Th&aacute;i Lan&quot;, Kaweesak cho biết.</p>\r\n\r\n<p>HLV Park Hang-seo đ&atilde; đem về nhiều th&agrave;nh tựu cho đội tuyển Việt Nam, nhưng cũng như Kiatisuk, chưa đảm bảo &ocirc;ng sẽ tại vị nếu tiếp tục thua c&aacute;c trận c&ograve;n lại ở v&ograve;ng loại thứ ba World Cup 2022. &quot;Nếu Việt Nam thua cả 10 trận ở v&ograve;ng loại thứ ba, người h&acirc;m mộ c&oacute; quyền đặt c&acirc;u hỏi liệu VFF c&oacute; n&ecirc;n chấm dứt hợp đồng với HLV Park. Khả năng đ&oacute; lu&ocirc;n c&oacute; thể xảy ra&quot;, chuy&ecirc;n gia b&oacute;ng đ&aacute; ch&acirc;u &Aacute; Gabe Tan n&oacute;i với&nbsp;<em>VnExpress</em>.</p>\r\n\r\n<p>Cũng như Th&aacute;i Lan trước đ&acirc;y, Việt Nam đang l&agrave; đội duy nhất của Đ&ocirc;ng Nam &Aacute; c&ograve;n cơ hội dự World Cup 2022. Mục ti&ecirc;u của Việt Nam cũng l&agrave; kiếm &iacute;t nhất một chiến thắng, v&igrave; thế VFF nỗ lực để xin cơ chế tổ chức c&aacute;c trận đấu tr&ecirc;n s&acirc;n nh&agrave; Mỹ Đ&igrave;nh, thay v&igrave; đ&aacute; s&acirc;n trung lập như Trung Quốc hay Australia. Thầy tr&ograve; Park c&ograve;n bốn trận chưa đấu tr&ecirc;n s&acirc;n nh&agrave;, gặp Nhật Bản, Saudi Arabia, Trung Quốc v&agrave; Oman. Trong đ&oacute;, cơ hội thắng cho Việt Nam cao hơn trước Trung Quốc ng&agrave;y 1/2/2022 v&agrave; Oman ng&agrave;y 24/3/2022.</p>\r\n\r\n<p>Nhưng cũng như Th&aacute;i Lan, Việt Nam thua cả bốn trận đầu ti&ecirc;n ở v&ograve;ng loại thứ ba. Người h&acirc;m mộ bắt đầu lo ngại đội tuyển rơi v&agrave;o vết xe đổ của k&igrave;nh địch trong khu vực. V&agrave; một người h&ugrave;ng b&oacute;ng đ&aacute; Việt Nam như HLV Park cũng c&oacute; thể kh&ocirc;ng chịu nổi sức &eacute;p nếu đội tuyển kh&ocirc;ng ho&agrave;n th&agrave;nh mục ti&ecirc;u.</p>\r\n\r\n<p><strong>Với những g&igrave; Việt Nam đ&atilde; trải qua, c&oacute; thể thấy nhiều điểm t&iacute;ch cực, chứ kh&ocirc;ng chỉ l&agrave; mạch thua kỷ lục</strong>. &Iacute;t nhất l&agrave; khi so với Th&aacute;i Lan ở c&ugrave;ng thời điểm, c&aacute;c thống k&ecirc; ch&iacute;nh của Việt Nam đều tốt hơn.</p>\r\n\r\n<p>Việt Nam tạo ra nhiều cơ hội v&agrave; dứt điểm trong cấm địa nhiều hơn so với Th&aacute;i Lan c&ugrave;ng kỳ. Đ&acirc;y c&oacute; thể coi l&agrave; bất ngờ khi lối chơi của Park l&agrave; ph&ograve;ng ngự phản c&ocirc;ng, c&ograve;n Kiatisuk hướng tới kiểm so&aacute;t b&oacute;ng. Việt Nam cũng chắt chiu cơ hội tốt hơn với trung b&igrave;nh một b&agrave;n mỗi trận, trong khi Th&aacute;i Lan chỉ ghi được một b&agrave;n qua bốn trận.</p>\r\n\r\n<p>Trước c&aacute;c đội mạnh v&agrave; g&acirc;y &aacute;p lực tốt hơn, việc giữ b&oacute;ng nhiều c&ograve;n khiến Th&aacute;i Lan đối mặt nguy cơ mất b&oacute;ng v&agrave; bị phản c&ocirc;ng. Trung b&igrave;nh một trận, đối thủ của Th&aacute;i Lan dứt điểm tới 21 lần, trong đ&oacute; c&oacute; 15 pha thực hiện trong cấm địa. C&ograve;n đo&agrave;n qu&acirc;n của Park hứng &iacute;t c&uacute; s&uacute;t từ đối thủ hơn.</p>\r\n\r\n<p>Kể từ khi dẫn dắt Việt Nam bốn năm trước, HLV Park cải thiện r&otilde; rệt thể lực v&agrave; tinh thần chiến đấu đến ph&uacute;t cuối. Trong 10 trận v&ograve;ng loại thứ ba World Cup 2018, c&oacute; t&aacute;m trận Th&aacute;i Lan thủng lưới sau ph&uacute;t 80. C&ograve;n Việt Nam mới một trận thủng lưới trong thời gian n&agrave;y, trước Trung Quốc h&ocirc;m 7/10. Lối chơi ph&ograve;ng ngự với khối đội h&igrave;nh thấp gi&uacute;p Việt Nam giữ sức để v&ugrave;ng l&ecirc;n v&agrave;o thời điểm nhất định. &quot;T&ocirc;i nghĩ Việt Nam mạnh hơn Th&aacute;i Lan ở tinh thần chiến đấu&quot;, Kaweesak n&oacute;i.</p>\r\n\r\n<p>M&agrave;n tr&igrave;nh diễn của Việt Nam qua bốn trận đầu cũng để lại tiếc nuối. Quả phạt đền v&agrave; thẻ đỏ trong t&igrave;nh huống g&acirc;y tranh c&atilde;i của Đỗ Duy Mạnh khiến Việt Nam thua ngược Saudi Arabia. Trọng t&agrave;i cũng bỏ qua một quả phạt đền cho Việt Nam khi Hồng Duy s&uacute;t tr&uacute;ng tay cầu thủ Australia. Trước Trung Quốc, pha truy cản thiếu kinh nghiệm của h&agrave;ng thủ khiến Việt Nam nhận b&agrave;n thua ph&uacute;t cuối. Việt Nam kh&ocirc;ng c&ograve;n chơi tốt như thế trước Oman, nhưng c&aacute;c pha mắc lỗi của học tr&ograve; Park đều c&oacute; thể tr&aacute;nh được.</p>\r\n\r\n<p><strong>Việt Nam đ&atilde; thua bốn trận nhưng kh&ocirc;ng c&oacute; nghĩa đội tuyển thụt l&ugrave;i hay giậm ch&acirc;n tại chỗ</strong>. HLV Park nh&acirc;n cơ hội n&agrave;y để thử nghiệm v&agrave;i nh&acirc;n sự mới cho đội tuyển, bởi ở v&ograve;ng n&agrave;y kết quả kh&ocirc;ng qu&aacute; quan trọng. Những cuộc thử nghiệm kh&ocirc;ng ồ ạt, để tr&aacute;nh c&aacute;c trận thảm bại như Th&aacute;i Lan bốn năm trước.</p>\r\n\r\n<p>Ho&agrave;ng Đức giờ đ&acirc;y đ&atilde; th&agrave;nh trụ cột ở đội tuyển, thay vị tr&iacute; chấn thương của Đỗ H&ugrave;ng Dũng. Tầm ảnh hưởng của tiền vệ Viettel l&ecirc;n lối chơi đội tuyển ng&agrave;y c&agrave;ng lớn, cả trong ph&ograve;ng ngự lẫn tấn c&ocirc;ng. Trung vệ 21 tuổi Nguyễn Thanh B&igrave;nh cũng nhận được b&agrave;i học đắt gi&aacute; sau sai lầm trong trận thua Trung Quốc. Thanh B&igrave;nh chỉ mất một ng&agrave;y để qu&ecirc;n đi sai lầm v&agrave; hướng tới ph&iacute;a trước. Mới nhất, hậu vệ Hồ Tấn T&agrave;i được chọn đ&aacute; ch&iacute;nh thay v&igrave; Vũ Văn Thanh b&ecirc;n c&aacute;nh phải. Tấn T&agrave;i cũng lĩnh hội b&agrave;i học đ&aacute;ng nhớ khi vung tay trong cấm địa dẫn tới phạt đền. Đổi lại, anh tạo ra n&eacute;t mới trong kh&acirc;u tấn c&ocirc;ng ghi g&oacute;p c&ocirc;ng v&agrave;o hai b&agrave;n trong hai trận gần nhất. Người h&acirc;m mộ lu&ocirc;n k&ecirc;u gọi những nh&acirc;n tố mới cho đội tuyển, v&agrave; HLV Park đang cố gắng l&agrave;m điều đ&oacute;, trong khi vẫn theo đuổi mục ti&ecirc;u kiếm một trận thắng.</p>\r\n', 16, 5, 0, 1, 1),
(32, 'Vụ án cánh hoa hồng nổi tiếng nước Mỹ', 'Vu-An-Canh-Hoa-Hong-Noi-Tieng-Nuoc-My', '<p>Greg De Villers nằm chết tr&ecirc;n giường ngủ, ngực phủ đầy c&aacute;nh hồng nhung, b&ecirc;n cạnh l&agrave; khung ảnh cưới vỡ tan mặt k&iacute;nh, hiện trường y hệt bối cảnh kiệt t&aacute;c điện ảnh &ldquo;American Beauty&rdquo;.</p>\r\n', 'phap-luat-2.jpg', '2021-11-01 02:12:26', 1, '<p>21h22 &#39; tối 6/11/2000, trung t&acirc;m cứu trợ 911 ở bang California nhận được cuộc gọi b&aacute;o &aacute;n từ người phụ nữ 25 tuổi, Kristin Rossum khi ph&aacute;t hiện chồng m&igrave;nh vừa tự tử trong ph&ograve;ng ngủ của họ. Khi xe cứu thương v&agrave; cảnh s&aacute;t c&oacute; mặt tại hiện trường, anh Greg De Villers đ&atilde; chết, v&agrave;i ng&agrave;y trước sinh nhật tuổi 26.</p>\r\n\r\n<p>Điều khiến cảnh s&aacute;t ngỡ ng&agrave;ng kh&ocirc;ng phải l&agrave; nguy&ecirc;n nh&acirc;n Greg tự tử, m&agrave; l&agrave; hiện trường nơi anh qua đời. Tất cả chi tiết d&ugrave; l&agrave; nhỏ nhất ở hiện trường đều được d&agrave;n dựng rất c&ocirc;ng phu, m&ocirc; phỏng ho&agrave;n hảo khung cảnh một vụ &aacute;n trong một bộ phim rất nổi tiếng tại Mỹ l&uacute;c đ&oacute;,&nbsp;<a href=\"https://vnexpress.net/triet-ly-hoa-hong-va-tui-rac-qua-phim-ve-dep-my-2867292.html\" rel=\"dofollow\"><em>American Beauty.</em></a></p>\r\n\r\n<p>Greg nằm chết tr&ecirc;n s&agrave;n ph&ograve;ng ngủ với một tư thế ngay ngắn. Phủ k&iacute;n tr&ecirc;n ngực anh l&agrave; những c&aacute;nh hoa hồng đỏ thắm. B&ecirc;n cạnh khu&ocirc;n mặt v&ocirc; hồn của anh l&agrave; bức ảnh cưới của hai vợ chồng được chụp chưa đầy hai năm trước đ&oacute;. Thế nhưng, tấm k&iacute;nh của khung ảnh bức ảnh cưới đ&atilde; vỡ tan, vương v&atilde;i th&agrave;nh những mảnh thủy tinh khắp ph&ograve;ng.</p>\r\n\r\n<p>Gần thi thể, cảnh s&aacute;t ph&aacute;t hiện th&ecirc;m một bức thư t&igrave;nh nh&agrave;u n&aacute;t rơi xuống s&agrave;n. Vợ anh, Kristin, đ&atilde; nhận được n&oacute; từ người đ&agrave;n &ocirc;ng kh&aacute;c. Anh ta l&agrave; sếp của c&ocirc;, b&aacute;c sĩ người Australia bảnh bao m&agrave; c&ocirc; đang l&agrave;m việc c&ugrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh bức thư l&agrave; nhật k&yacute; của Kristin, trong đ&oacute; c&ocirc; t&acirc;m sự rằng c&ocirc; đang trải qua một mối quan hệ l&atilde;ng mạn nhưng đầy tội lỗi với người sếp đ&atilde; c&oacute; vợ. Ngo&agrave;i ra, c&ocirc; kh&ocirc;ng d&aacute;m n&oacute;i ri&ecirc;ng với ai rằng c&ocirc; lo sợ cuộc h&ocirc;n nh&acirc;n hiện tại của m&igrave;nh l&agrave; một sai lầm.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ dừng lại ở những manh mối tr&ecirc;n, cảnh s&aacute;t c&ograve;n t&igrave;m thấy một đoạn băng ghi h&igrave;nh gần đ&oacute; c&oacute; thể Greg đ&atilde; xem đoạn băng ấy trước khi kết liễu cuộc đời. Trong đoạn băng, những h&igrave;nh ảnh đ&aacute;m cưới của Kristin v&agrave; Greg v&agrave;o năm 1999 hiện l&ecirc;n tr&ecirc;n m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Sau khi xem x&eacute;t những vật chứng thu thập được tại hiện trường, cảnh s&aacute;t kh&ocirc;ng ph&aacute;t hiện th&ecirc;m được manh mối n&agrave;o chỉ điểm đ&acirc;y l&agrave; một vụ giết người. B&ecirc;n cạnh đ&oacute;, v&igrave; hiện trường c&oacute; vẻ kh&aacute; ngăn nắp v&agrave; kh&ocirc;ng c&oacute; hung kh&iacute;, c&aacute;c nh&agrave; điều tra cho rằng Greg đ&atilde; uống thuốc độc để tự tử. Tuy nhi&ecirc;n, hiện tại loại thuốc đ&oacute; l&agrave; g&igrave; họ vẫn chưa biết m&agrave; cần chờ th&ecirc;m kh&aacute;m nghiệm của ph&aacute;p y.</p>\r\n\r\n<p>Nh&igrave;n chung, bước đầu cảnh s&aacute;t tin rằng họ đ&atilde; t&igrave;m thấy l&yacute; do tại sao Greg tự tử. Kristin thuật lại, s&aacute;ng ấy, khi chuẩn bị đi l&agrave;m, c&ocirc; ph&aacute;t hiện ra chồng m&igrave;nh c&oacute; điều bất thường, Greg cứ nằm m&atilde;i tr&ecirc;n giường v&agrave; kh&ocirc;ng c&oacute; dấu hiệu sẽ thức dậy.</p>\r\n\r\n<p>Cảm thấy t&igrave;nh trạng sức khỏe của chồng c&oacute; vẻ kh&ocirc;ng ổn, Kristin đ&atilde; gọi điện xin ph&eacute;p cho cả hai người nghỉ l&agrave;m. Đến buổi trưa, sau khi ăn, Kristin kiểm tra sức khỏe của chồng v&agrave; anh n&oacute;i rằng m&igrave;nh vừa uống Oxycondone v&agrave; Clonazepam, một loại l&agrave; thuốc giảm đau v&agrave; một loại l&agrave; thuốc gi&atilde;n cơ.</p>\r\n\r\n<p>Theo lời khai của Kristin, trong suốt ng&agrave;y h&ocirc;m đ&oacute; mọi sinh hoạt của hai vợ chồng c&ocirc; vẫn diễn ra b&igrave;nh thường. Tuy nhi&ecirc;n, đến tối Greg c&oacute; vẻ hơi loạng choạng, nhưng c&ocirc; chỉ nghĩ đ&oacute; l&agrave; t&aacute;c dụng của thuốc cảm n&ecirc;n kh&ocirc;ng để &yacute;. M&atilde;i đến khi Kristin đi tắm v&agrave; bước ra khỏi bồn tắm, c&ocirc; mới ph&aacute;t hiện Greg đang nằm tr&ecirc;n giường v&agrave; kh&ocirc;ng c&ograve;n thở.</p>\r\n\r\n<p>Sau khi được tra hỏi về việc ngoại t&igrave;nh, Kristin th&uacute; nhận, gần đ&acirc;y vợ chồng c&ocirc; c&oacute; nhiều m&acirc;u thuẫn, bất đồng trong quan điểm sống. Ch&iacute;nh v&igrave; vậy, cuộc sống h&ocirc;n nh&acirc;n ngột ngạt n&agrave;y đ&atilde; khiến c&ocirc; nảy sinh t&igrave;nh cảm với sếp, c&ograve;n Greg th&igrave; mắc chứng bệnh trầm cảm.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, chuyện ngoại t&igrave;nh của c&ocirc; vẫn chưa bị ai ph&aacute;t gi&aacute;c. C&ocirc; lu&ocirc;n ch&ocirc;n giấu b&iacute; mật đ&oacute; trong l&ograve;ng bởi v&igrave; sếp c&ocirc; đ&atilde; c&oacute; vợ, c&ocirc; chỉ n&oacute;i với chồng l&agrave; c&ocirc; muốn ly h&ocirc;n. Kristin n&oacute;i rằng, những c&aacute;nh hoa hồng tại hiện trường l&agrave; từ b&oacute; hoa chồng mua tặng c&ocirc; v&agrave;o ng&agrave;y sinh nhật h&ocirc;m 25/10.</p>\r\n\r\n<p>Kết quả kh&aacute;m nghiệm tử thi cho thấy cơ thể Greg chứa một lượng lớn chất giảm đau Fentanyl, đủ để đẩy nạn nh&acirc;n v&agrave;o t&igrave;nh trạng h&ocirc;n m&ecirc; k&eacute;o d&agrave;i dẫn tới tử vong. Fentanyl c&ograve;n được biết như một loại ma t&uacute;y tổng hợp mạnh gấp 80 lần thuốc giảm đau.</p>\r\n\r\n<p>Một điều đặc biệt hơn nữa đ&acirc;y l&agrave; loại chất độc m&agrave; kh&ocirc;ng phải ai cũng c&oacute; thể mua được v&igrave; n&oacute; chủ yếu chỉ được sử dụng trong ph&ograve;ng th&iacute; nghiệm.</p>\r\n\r\n<p><strong>Khi t&igrave;m hiểu l&yacute; lịch của Kristin, họ đ&atilde; ph&aacute;t hiện ra những manh mối đ&aacute;ng ngờ.</strong>&nbsp;Hiện tại, Kristin đang l&agrave; một chuy&ecirc;n gia nghi&ecirc;n cứu về c&aacute;c loại thuốc độc tại văn ph&ograve;ng gi&aacute;m định Y khoa San Diego. Kristin trở th&agrave;nh nghi phạm ch&iacute;nh của vụ &aacute;n.</p>\r\n\r\n<p>Theo lời khai của gia đ&igrave;nh nh&agrave; Greg, trước khi trở th&agrave;nh chuy&ecirc;n gia nghi&ecirc;n cứu độc dược, Kristin đ&atilde; c&oacute; qu&aacute; khứ đ&aacute;ng qu&ecirc;n tại miền qu&ecirc; ngh&egrave;o Tijuana, Mexico.</p>\r\n\r\n<p>Khi c&ograve;n nhỏ, Kristin ngỗ nghịch lu&ocirc;n sống bằng men rượu v&agrave; ma t&uacute;y đ&aacute;. Trong một lần bỏ nh&agrave;, vượt bi&ecirc;n từ thị trấn Tijuana sang California mua ma t&uacute;y, Kristin đ&atilde; t&igrave;nh cờ gặp Greg.</p>\r\n\r\n<p>Tr&aacute;i ngược với ho&agrave;n cảnh của Kristin, Greg l&agrave; con trai của b&aacute;c sĩ phẫu thuật thẩm mỹ nổi tiếng c&oacute; văn ph&ograve;ng ở California v&agrave; Monaco. Anh đẹp trai, s&aacute;ng sủa, được v&iacute; như một &quot;thi&ecirc;n thần&quot; trong mắt nhiều c&ocirc; g&aacute;i. Thế nhưng, ngay từ lần đầu gặp nhau, Greg dường như đ&atilde; tr&uacute;ng tiếng s&eacute;t &aacute;i t&igrave;nh với Kristin. Thậm ch&iacute;, anh c&ograve;n nhiệt t&igrave;nh mời Kristin về sống chung với m&igrave;nh tại căn hộ c&ugrave;ng c&aacute;c anh trai.</p>\r\n\r\n<p>H&agrave;nh động tr&ecirc;n của Greg đ&atilde; khiến những người anh kh&ocirc;ng h&agrave;i l&ograve;ng, bởi v&igrave; trong mắt họ, Kristin l&agrave; một con nghiện ma t&uacute;y nguy hiểm. Mặc cho c&aacute;c anh lu&ocirc;n t&igrave;m c&aacute;ch khuy&ecirc;n ngăn chia tay, Greg vẫn cương quyết n&oacute;i rằng anh y&ecirc;u Kristin v&agrave; thề sẽ gi&uacute;p c&ocirc; x&oacute;a bỏ cơn nghiện của m&igrave;nh.</p>\r\n\r\n<p>Đến th&aacute;ng 5/1995, Greg đ&atilde; gi&uacute;p Kristin cai nghiện th&agrave;nh c&ocirc;ng, gi&uacute;p c&ocirc; học l&ecirc;n Đại học chuy&ecirc;n ng&agrave;nh H&oacute;a Sinh v&agrave; c&oacute; sự nghiệp bền vững.</p>\r\n\r\n<p>Hai ng&agrave;y sau vụ việc, một đồng nghiệp của Kristin gọi cho cảnh s&aacute;t v&agrave; th&ocirc;ng b&aacute;o sự nghi ngờ của anh ta.</p>\r\n\r\n<p>Anh khai từ ng&agrave;y Greg chết, Kristin ng&agrave;y c&agrave;ng ngoại t&igrave;nh t&aacute;o bạo hơn với sếp của họ, &ocirc;ng Michael Robertson. Thậm ch&iacute;, c&ocirc; c&ograve;n thuyết phục những đồng nghiệp đang kh&aacute;m nghiệp thi thể chồng c&ocirc; h&atilde;y kết luận rằng anh ấy chết v&igrave; uống thuốc cảm qu&aacute; liều.</p>\r\n\r\n<p>Lục so&aacute;t nơi l&agrave;m việc của Kristin tại văn ph&ograve;ng gi&aacute;m định Y khoa San Diego, cảnh s&aacute;t ph&aacute;t hiện một lượng lớn thuốc độc Fentanyl đ&atilde; biến mất. Tr&ugrave;ng hợp thay, đ&acirc;y cũng ch&iacute;nh l&agrave; loại ma t&uacute;y tổng hợp đ&atilde; giết chết Greg nhiều ng&agrave;y trước đ&oacute;.</p>\r\n\r\n<p>Đối diện với c&acirc;u hỏi ai l&agrave; người giữ ch&igrave;a kh&oacute;a tủ thuốc Fentanyl, th&igrave; người đang quản l&yacute; số thuốc ấy lại ch&iacute;nh l&agrave; người sếp m&agrave; Kristin đang ngoại t&igrave;nh. Tuy nhi&ecirc;n, &ocirc;ng ta n&oacute;i rằng m&igrave;nh kh&ocirc;ng biết loại thuốc độc ấy đ&atilde; biến mất khi n&agrave;o v&agrave; ri&ecirc;ng về phần Kristin, c&ocirc; n&oacute;i rằng m&igrave;nh đ&atilde; kh&ocirc;ng hề lấy n&oacute;.</p>\r\n\r\n<p>Kh&ocirc;ng dừng lại ở đ&oacute;, cảnh s&aacute;t chỉ ra một bằng chứng khiến Kristin phải n&iacute;n lặng. Đ&oacute; l&agrave; một tờ h&oacute;a đơn từ một cửa h&agrave;ng b&aacute;n hoa m&agrave; Kristin đ&atilde; mua v&agrave;o ng&agrave;y Greg chết. Do đ&oacute;, c&aacute;nh hoa hồng rải tr&ecirc;n ngực &aacute;o Greg tại hiện trường h&ocirc;m đ&oacute; l&agrave; những c&aacute;nh hoa được Kristin mua c&ugrave;ng ng&agrave;y, chứ kh&ocirc;ng phải từ b&oacute; b&ocirc;ng Greg tặng c&ocirc; ng&agrave;y sinh nhật như c&ocirc; đ&atilde; khai trước đ&oacute;.</p>\r\n\r\n<p>Ngo&agrave;i ra, gi&aacute;m định vi&ecirc;n khẳng định Greg đ&atilde; chết &iacute;t nhất một giờ trước khi cảnh s&aacute;t đến. B&agrave;ng quang của Greg c&oacute; một lượng nước tiểu đ&aacute;ng kể, n&ecirc;n nếu anh ta c&ograve;n tỉnh t&aacute;o chắc chắn sẽ cảm thấy rất kh&oacute; chịu. Theo t&iacute;nh to&aacute;n, Greg đ&atilde; bất tỉnh khoảng 6-12 giờ trước khi chết.</p>\r\n\r\n<p>Một người bạn của Greg khai với cảnh s&aacute;t, 4 ng&agrave;y trước khi chết, Greg đ&atilde; y&ecirc;u cầu Kristin nghỉ việc, nếu kh&ocirc;ng, anh đến vạch trần chuyện ngoại t&igrave;nh của c&ocirc; với sếp v&agrave; chuyện c&ocirc; đang nghiện ma tu&yacute;.</p>\r\n\r\n<p>C&ocirc;ng tố vi&ecirc;n c&aacute;o buộc Kristin đầu độc chồng bằng Fentanyl. M&atilde;i đến khi x&aacute;c nhận chồng thực sự đ&atilde; chết, c&ocirc; mới sắp xếp hiện trường giống với bộ phim nổi tiếng m&agrave; c&ocirc; h&acirc;m mộ</p>\r\n\r\n<p>Trong phi&ecirc;n t&ograve;a th&aacute;ng 10/2002, Kristin bị phạt t&ugrave; chung th&acirc;n kh&ocirc;ng &acirc;n x&aacute; v&igrave; tội&nbsp;<em>Giết người cấp độ một.</em></p>\r\n', 17, 6, 3, 1, 1),
(34, 'Tạo hiện trường vợ bị cưỡng bức để lừa cảnh sát', 'Tao-Hien-Truong-Vo-Bi-Cuong-Buc-De-Lua-Canh-Sat', '<p>V&agrave;i ng&agrave;y sau khi tố gi&aacute;c chồng s&aacute;t hại người vợ trước, thi thể Barbara Purcell được t&igrave;m thấy tại vị tr&iacute; giống y hệt.</p>\r\n', 'phap-luat-5.jpg', '2021-11-01 02:22:14', 1, '<p>Nh&agrave; vật l&yacute; trị liệu 42 tuổi, Barbara Purcell, sống một m&igrave;nh tại th&agrave;nh phố Rockford, bang Illinois. Tối 6/6/2001, Barbara c&oacute; ca l&agrave;m việc từ 7h đến 15h tại bệnh viện nhưng c&ocirc; kh&ocirc;ng xuất hiện.</p>\r\n\r\n<p>Nhận tin b&aacute;o của đồng nghiệp c&ocirc;, cảnh s&aacute;t tới nh&agrave; kiểm tra, họ ph&aacute;t hiện x&aacute;c l&otilde;a thể của c&ocirc; ở cầu thang. Cửa sau mở v&agrave; k&iacute;nh tr&ecirc;n cửa vỡ...</p>\r\n\r\n<p>Cảnh s&aacute;t nhận định đ&acirc;y l&agrave; &aacute;n mạng, chứ kh&ocirc;ng phải tai nạn. Dựa v&agrave;o độ cứng của thi thể v&agrave; sự xuất hiện của trứng ruồi, họ ước t&iacute;nh nạn nh&acirc;n tử vong từ đ&ecirc;m trước đ&oacute;, tức l&agrave; trước khi cảnh s&aacute;t ph&aacute;t hiện &iacute;t nhất 12 giờ.</p>\r\n\r\n<p>Nhẫn cưới tr&ecirc;n ng&oacute;n tay của Barbara biến mất, c&aacute;c đồ nữ trang v&agrave; bộ sưu tập tiền xu cổ cũng kh&ocirc;ng c&ograve;n. Đồ đạc trong c&aacute;c ph&ograve;ng bị lục lọi. Bạn b&egrave; n&oacute;i c&ocirc; lu&ocirc;n đeo nhẫn cưới d&ugrave; đang ly th&acirc;n.</p>\r\n\r\n<p>Ban đầu, cảnh s&aacute;t ph&aacute;n đo&aacute;n sự việc do một hoặc v&agrave;i t&ecirc;n trộm g&acirc;y ra. Nh&oacute;m điều tra ph&aacute;t hiện vết lốp xe hằn l&ecirc;n s&acirc;n v&agrave; b&atilde;i cỏ ph&iacute;a sau ng&ocirc;i nh&agrave;. Ch&oacute; nghiệp vụ c&ugrave;ng cảnh s&aacute;t t&igrave;m xung quanh song kh&ocirc;ng thấy manh mối n&agrave;o đ&aacute;ng ch&uacute; &yacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/mywebsite/upload/tintuc/images/images%203/barbara-purcell-jpeg-9253-1635495499.jpg\" style=\"height:427px; width:559px\" /></p>\r\n\r\n<p>Kết quả kh&aacute;m nghiệm tử thi Barbara cho thấy nạn nh&acirc;n mất mạng bởi 2-4 c&uacute; đ&aacute;nh mạnh v&agrave;o đầu bằng vật cứng. Một ng&oacute;n tay phải của c&ocirc; bầm dập, tụ m&aacute;u, v&agrave; một vết trầy da xuất hiện tr&ecirc;n b&agrave;n tay tr&aacute;i.</p>\r\n\r\n<p>Nh&oacute;m điều tra nhận định rất c&oacute; thể hung thủ bất ngờ tấn c&ocirc;ng Barbara từ ph&iacute;a sau. Theo bản năng, c&ocirc; đưa hai b&agrave;n tay &ocirc;m đầu khiến thương t&iacute;ch xuất hiện tr&ecirc;n hai b&agrave;n tay. Vị tr&iacute; hung thủ tấn c&ocirc;ng Barbara c&oacute; thể l&agrave; đầu cầu thang. Chuy&ecirc;n gia ph&aacute;p y kh&ocirc;ng t&igrave;m thấy dấu hiệu x&acirc;m hại t&igrave;nh dục.</p>\r\n\r\n<p>Vậy tại sao nạn nh&acirc;n kh&ocirc;ng mặc quần &aacute;o khi chết? Rất c&oacute; thể hung thủ lột trang phục của c&ocirc; để cảnh s&aacute;t tin rằng đ&oacute; l&agrave; vụ cưỡng bức, giết người diệt khẩu.</p>\r\n\r\n<p>Một điểm bất thường nữa khiến nh&oacute;m điều tra quan t&acirc;m l&agrave; k&iacute;nh sau vỡ nhưng lại rơi ra ph&iacute;a ngo&agrave;i, dấu hiệu cho thấy đập k&iacute;nh từ b&ecirc;n trong. Nếu hung thủ b&ecirc;n ngo&agrave;i ph&aacute; cửa v&agrave;o nh&agrave;, k&iacute;nh phải rơi v&agrave;o trong nh&agrave;.</p>\r\n\r\n<p>Cửa ra v&agrave;o v&agrave; cửa sổ trong nh&agrave; Barbara đều kh&oacute;a. Vậy l&agrave;m sao hung thủ c&oacute; thể v&agrave;o nh&agrave; của Barbara rồi đập k&iacute;nh ở cửa sau rồi bước ra ngo&agrave;i? Chắc chắn kẻ đ&oacute; c&oacute; ch&igrave;a kh&oacute;a nh&agrave;. Người duy nhất c&oacute; ch&igrave;a kh&oacute;a l&agrave; Willard Purcell, người chồng mới ly th&acirc;n 5 ng&agrave;y trước.</p>\r\n\r\n<p>Khoảng một tuần trước thảm kịch, Willard đ&atilde; tấn c&ocirc;ng Barbara bằng s&uacute;ng điện v&igrave; nghi c&ocirc; ngoại t&igrave;nh.</p>\r\n\r\n<p>Willard khai trong ng&agrave;y &aacute;n mạng xảy ra đi l&agrave;m ở th&agrave;nh phố Chicago v&agrave; ra khỏi c&ocirc;ng ty l&uacute;c 15 h. Sau đ&oacute;, anh ta tới nh&agrave; em trai v&agrave; ở đ&oacute; cả đ&ecirc;m. Th&agrave;nh phố Chicago c&aacute;ch nh&agrave; của Barbara khoảng 90 ph&uacute;t l&aacute;i xe.</p>\r\n\r\n<p>Tiếp tục điều tra những h&agrave;nh động trước đ&oacute; của nạn nh&acirc;n, cảnh s&aacute;t sửng sốt khi biết, chỉ v&agrave;i ng&agrave;y trước khi mất mạng, c&ocirc; đ&atilde; gọi điện tới cơ quan c&ocirc;ng tố v&agrave; b&aacute;o rằng rất c&oacute; thể 16 năm trước, Willard đ&atilde; giết người vợ đầu của anh ta l&agrave; Crystal Purcell.</p>\r\n\r\n<p>Trong vụ &aacute;n đ&oacute;, thi thể của Crystal nằm dưới ch&acirc;n cầu thang - t&igrave;nh huống giống hệt Barbara.</p>\r\n\r\n<p>Cảnh s&aacute;t khai quật mộ Crystal để kh&aacute;m nghiệm lại. D&ugrave; 16 năm tr&ocirc;i qua, chuy&ecirc;n gia ph&aacute;p y vẫn thấy một vết nứt c&oacute; chiều d&agrave;i tới 6 cm ở hộp sọ. Dấu hiệu cho thấy c&ocirc; đ&atilde; ng&atilde; từ đầu cầu thang xuống ph&iacute;a dưới. D&ugrave; vậy, nh&oacute;m điều tra kh&ocirc;ng thể kết luận Crystal v&ocirc; &yacute; ng&atilde; hay bị ai đ&oacute; đẩy xuống cầu thang.</p>\r\n\r\n<p>Cảnh s&aacute;t kiểm tra xe tải của Willard, đối chiếu lốp xe với những vết hằn tr&ecirc;n b&atilde;i cỏ v&agrave; s&acirc;n sau ng&ocirc;i nh&agrave; Barbara. Kết quả cho thấy k&iacute;ch thước lốp &ocirc;t&ocirc; khớp với những vết hằn. Tuy nhi&ecirc;n, họ kh&ocirc;ng thể kết luận chắc chắn v&igrave; c&aacute;c vết hằn tại hiện trường kh&aacute; mờ, khả năng nhầm lẫn c&oacute; thể xảy ra.</p>\r\n\r\n<p>Những vết trầy tr&ecirc;n mu b&agrave;n tay tr&aacute;i của nạn nh&acirc;n khiến nh&oacute;m điều tra ch&uacute; &yacute;, v&igrave; ch&uacute;ng giống những vết cắn. Nh&oacute;m điều tra y&ecirc;u cầu lấy mẫu h&agrave;m răng của Willard rồi đ&uacute;c khu&ocirc;n. Bằng c&aacute;ch nhờ nha sĩ giỏi đối chiếu với những vết cắn, họ x&aacute;c định 5 đặc điểm r&otilde; n&eacute;t v&agrave; chắc chắn để khẳng định h&agrave;m răng của Willard đ&atilde; tạo ra những vết thương tr&ecirc;n mu b&agrave;n tay tr&aacute;i của Barbara.</p>\r\n\r\n<p>Về chứng cứ ngoại phạm của Willard, chủ c&ocirc;ng ty n&oacute;i Willard rời trụ sở từ khoảng 11h trong ng&agrave;y &aacute;n mạng xảy ra. Camera gi&aacute;m s&aacute;t ghi nhận xe của Willard di chuyển gần nh&agrave; Barbara s&aacute;t thời điểm c&ocirc; tử vong. Dữ liệu của c&ocirc;ng ty viễn th&ocirc;ng cho thấy anh ta thực hiện cuộc gọi ở trong nh&agrave; Barbara trước khi c&ocirc; chết. Chủ một nh&agrave; h&agrave;ng gần hiện trường &aacute;n mạng x&aacute;c nhận Willard đ&oacute; v&agrave;o buổi chiều trước vụ &aacute;n.</p>\r\n\r\n<p>Cuối c&ugrave;ng, cảnh s&aacute;t kh&aacute;m x&eacute;t nơi ở của Willard v&agrave; ph&aacute;t hiện nhẫn cưới c&ugrave;ng nhiều đồ trang sức của Barbara trong một t&uacute;i. Với h&agrave;ng loạt chứng cứ, cảnh s&aacute;t bắt Willard.</p>\r\n\r\n<p><strong>Phi&ecirc;n xử Willard diễn ra v&agrave;o năm 2003 v&agrave; k&eacute;o d&agrave;i trong hai tuần.&nbsp;</strong>C&ocirc;ng tố vi&ecirc;n c&aacute;o buộc, h&ocirc;n nh&acirc;n giữa Willard v&agrave; Barbara đ&atilde; trở n&ecirc;n tồi tệ từ l&acirc;u v&agrave; họ kh&ocirc;ng ngủ chung suốt nhiều năm. Những người th&acirc;n của Barbara hiểu việc c&ocirc; ly h&ocirc;n chỉ c&ograve;n l&agrave; vấn đề thời gian nhưng Willard kh&ocirc;ng muốn, một phần v&igrave; kh&ocirc;ng muốn phải chia t&agrave;i sản. Trong một lần c&atilde;i v&atilde;, bị c&aacute;o d&ugrave;ng s&uacute;ng điện để tấn c&ocirc;ng Barbara khiến c&ocirc; b&aacute;o cảnh s&aacute;t. Bực tức việc n&agrave;y, Willard trả th&ugrave;.</p>\r\n\r\n<p>Ng&agrave;y 5/6/2001, Willard rời khỏi c&ocirc;ng ty ở Chicago l&uacute;c 11h rồi ph&oacute;ng về ph&iacute;a nh&agrave; Barbara. Anh ta gọi điện thoại để biết chắc c&ocirc; sẽ ở nh&agrave;, sau đ&oacute; ph&oacute;ng xe qua b&atilde;i cỏ ph&iacute;a sau, giấu v&agrave;o chỗ k&iacute;n. Willard bước tới cửa sau, d&ugrave;ng ch&igrave;a kh&oacute;a để mở cửa rồi r&igrave;nh sẵn ở cầu thang... Willard cởi quần, &aacute;o của vợ để đ&aacute;nh lạc hướng điều tra của cảnh s&aacute;t.</p>\r\n\r\n<p>Willard bị phạt t&ugrave; chung th&acirc;n về tội<em>&nbsp;Giết người cấp độ một</em>. Cơ quan c&ocirc;ng tố tin rằng bị c&aacute;o c&oacute; thể l&agrave; hung thủ giết người vợ đầu, nhưng kh&ocirc;ng c&oacute; bằng chứng đ&aacute;ng tin cậy để tiếp tục truy tố.</p>\r\n', 17, 6, 2, 1, 1),
(35, 'Án mạng bí ẩn thách thức cảnh sát suốt 30 năm', 'An-Mang-Bi-An-Thach-Thuc-Canh-Sat-Suot-30-Nam', '<p>Nữ doanh nh&acirc;n th&agrave;nh đạt Penny Bell vẫy tay ch&agrave;o tốp thợ đang sửa lại ng&ocirc;i biệt thự, l&ecirc;n chiếc Jaguar XJS đi gặp đối t&aacute;c. Chỉ 80 ph&uacute;t sau, x&aacute;c c&ocirc; được ph&aacute;t hiện với hơn 50 vết đ&acirc;m.</p>\r\n', 'phap-luat-6.jpg', '2021-11-01 02:23:51', 1, '<p>Penny, 43 tuổi, c&oacute; mọi thứ đ&aacute;ng để người đời ngưỡng mộ. C&ocirc; điều h&agrave;nh một c&ocirc;ng ty tuyển dụng th&agrave;nh c&ocirc;ng v&agrave; chồng c&ocirc;, Alistair, đang ph&aacute;t đạt với việc kinh doanh bất động sản. Họ sống trong ng&ocirc;i biệt thự lớn ở Denham, Buckinghamshire, với hai con.<br />\r\n<br />\r\n9h40&#39; ng&agrave;y 6/6/1991, Penny rời nh&agrave;, n&oacute;i với những người thợ đang sửa nh&agrave; cho m&igrave;nh rằng c&oacute; một cuộc hẹn l&agrave;m ăn trong 10 ph&uacute;t nữa.</p>\r\n\r\n<p>Chưa đầy 2 giờ sau, cảnh s&aacute;t nhận được cuộc gọi b&aacute;o &aacute;n tại b&atilde;i đậu xe của Trung t&acirc;m Giải tr&iacute; Gurnell, Greenford, London. Penny nằm nghi&ecirc;ng tr&ecirc;n tay l&aacute;i, bị đ&acirc;m 50 nh&aacute;t khi vẫn c&ograve;n trong xe.</p>\r\n\r\n<p>C&aacute;c nh&agrave; điều tra x&aacute;c định Penny đ&atilde; l&aacute;i xe đến b&atilde;i đậu của trung t&acirc;m giải tr&iacute; ở ngoại &ocirc; Greenford của T&acirc;y London v&agrave;o khoảng 10h30 s&aacute;ng. H&agrave;ng r&agrave;o c&acirc;y s&aacute;t mũi xe đ&atilde; che khuất tầm nh&igrave;n của người b&ecirc;n ngo&agrave;i v&agrave;o b&ecirc;n trong.</p>\r\n\r\n<p>Hai phụ nữ đến bơi ở trung t&acirc;m giải tr&iacute; cho biết đi qua thấy Penny đang ngồi thẳng tr&ecirc;n ghế l&aacute;i. Đ&egrave;n b&aacute;o nguy hiểm của xe bật s&aacute;ng v&agrave; cần gạt nước đang k&ecirc;u ồn &agrave;o. Họ cho rằng c&ocirc; đang ngủ. Tuy nhi&ecirc;n, khi rời trung t&acirc;m giải tr&iacute; v&agrave;o giữa trưa, họ nhận thấy c&oacute; m&aacute;u vương v&atilde;i, người phụ nữ b&ecirc;n trong đ&atilde; chết n&ecirc;n b&aacute;o cảnh s&aacute;t.</p>\r\n\r\n<p>Hai nh&acirc;n chứng nữa n&oacute;i thấy người giống Penny đang vật lộn với một người đ&agrave;n &ocirc;ng trong chiếc xe hơi từ từ chạy qua Greenfield. Người phụ nữ c&oacute; vẻ như đang n&oacute;i &quot;cứu t&ocirc;i&quot; với những t&agrave;i xế đi qua dường, nhưng kh&ocirc;ng được ai để &yacute;.</p>\r\n\r\n<p>Người đ&agrave;n &ocirc;ng trong xe được m&ocirc; tả khoảng 40 tuổi với m&aacute;i t&oacute;c sẫm m&agrave;u, cắt ngắn, vuốt ngược, c&oacute; thể để r&acirc;u v&agrave; đeo v&ograve;ng tay. Cảnh s&aacute;t đ&atilde; đưa ra m&ocirc; tả về người đ&agrave;n &ocirc;ng nhưng kh&ocirc;ng th&agrave;nh c&ocirc;ng trong việc x&aacute;c định danh t&iacute;nh.</p>\r\n\r\n<p>D&ugrave; vụ giết người xảy ra ban ng&agrave;y v&agrave; hung thủ sẽ b&ecirc; bết m&aacute;u song kh&ocirc;ng ai nh&igrave;n thấy bất cứ điều g&igrave; đ&aacute;ng ngờ trong b&atilde;i đậu xe s&aacute;ng h&ocirc;m đ&oacute; hoặc sau đ&oacute;. Hung kh&iacute;, được x&aacute;c định l&agrave; con dao lưỡi d&agrave;i 8-12 cm, cũng kh&ocirc;ng t&igrave;m thấy.</p>\r\n\r\n<p>Khi những người x&acirc;y dựng n&oacute;i với cảnh s&aacute;t về cuộc hẹn, chồng c&ocirc; n&oacute;i rằng kh&ocirc;ng c&oacute; cuộc hẹn n&agrave;o trong lịch l&agrave;m việc h&ocirc;m đ&oacute; của c&ocirc;. &quot;T&ocirc;i chỉ c&oacute; thể nghĩ rằng c&ocirc; ấy chắc đ&atilde; nhận được cuộc điện thoại hẹn gặp v&agrave;o ph&uacute;t cuối&quot;, anh n&oacute;i.</p>\r\n\r\n<p>Trung t&acirc;m giải tr&iacute; kh&ocirc;ng ở gần bất cứ lịch tr&igrave;nh di chuyển quen thuộc n&agrave;o của nạn nh&acirc;n, hơn nữa c&ocirc; kh&ocirc;ng bao giờ đến đ&acirc;y. Penny kh&ocirc;ng bị tấn c&ocirc;ng t&igrave;nh dục, chiếc t&uacute;i x&aacute;ch vẫn nguy&ecirc;n trong xe. Người m&agrave; Penny đ&atilde; sắp xếp để gặp v&agrave;o buổi s&aacute;ng h&ocirc;m đ&oacute;, nếu thực sự c&oacute;, chưa bao giờ được x&aacute;c định.</p>\r\n\r\n<p>Ngay từ đầu trong cuộc điều tra, cảnh s&aacute;t đ&atilde; nghi ngờ rằng người chồng c&oacute; thể l&agrave; thủ phạm. Trước khi kết h&ocirc;n, Alastair từng c&oacute; quan hệ đồng t&iacute;nh. Anh cũng sẽ l&agrave; người duy nhất thừa hưởng bất động sản v&agrave; khoản thanh to&aacute;n bảo hiểm nh&acirc;n thọ của Penny khi c&ocirc; chết. Song Alastair đang l&agrave;m việc ở Harrow, c&aacute;ch đ&oacute; hơn 20 km với sự chứng kiến của c&aacute;c đồng nghiệp v&agrave;o ng&agrave;y xảy ra &aacute;n mạng.</p>\r\n\r\n<p>Cũng c&oacute; một số suy đo&aacute;n rằng Penny ngoại t&igrave;nh, nhưng nếu vậy, sẽ kh&ocirc;ng c&oacute; l&yacute; do g&igrave; để c&ocirc; đề cập cuộc hẹn với nh&oacute;m thợ x&acirc;y.</p>\r\n\r\n<p>Một người đ&agrave;n &ocirc;ng t&ecirc;n John Richmond, từng l&agrave; h&agrave;ng x&oacute;m, đ&atilde; bị bắt sau khi dấu v&acirc;n tay của anh ta được t&igrave;m thấy tr&ecirc;n chiếc xe Jaguar của Penny. Nhưng sau đ&oacute;, anh ta được thả v&igrave; kh&ocirc;ng đủ bằng chứng buộc tội.</p>\r\n\r\n<p>Ba năm sau, th&aacute;ng 12/1994, Richmond n&oacute;i b&aacute;o ch&iacute; rằng c&oacute; quan hệ &aacute;i t&igrave;nh với nạn nh&acirc;n v&agrave; hẹn gặp v&agrave;o s&aacute;ng h&ocirc;m xảy ra &aacute;n mạng để n&oacute;i chuyện. Richmond tuy&ecirc;n bố biết danh t&iacute;nh s&aacute;t thủ, song sẽ chỉ h&eacute; răng tiết lộ nếu tờ b&aacute;o trả 80.000 bảng Anh. Tất nhi&ecirc;n, Richmond kh&ocirc;ng bao giờ nhận được số tiền đ&oacute;, v&agrave; cảnh s&aacute;t cũng kh&ocirc;ng thấy anh ta đủ tin tưởng để tiếp tục x&aacute;c minh c&acirc;u chuyện n&agrave;y.</p>\r\n\r\n<p>Richmond một lần nữa bị bắt v&agrave; thẩm vấn v&agrave;o năm 2005 nhưng vẫn kh&ocirc;ng đủ chứng cứ. Mặc d&ugrave; thẩm vấn 8.500 người v&agrave; nhận 2.500 bản khai, c&aacute;c th&aacute;m tử vẫn kh&ocirc;ng thể giải m&atilde; được vụ &aacute;n.</p>\r\n\r\n<p>Th&aacute;ng 6 vừa qua, 30 năm sau vụ &aacute;n, Lauren, con g&aacute;i của Penny Bell, k&ecirc;u gọi bất kỳ ai c&oacute; thể hỗ trợ giải quyết vụ &aacute;n để đưa ra bằng chứng dẫn đến việc x&aacute;c định được kẻ giết người. C&ocirc; đ&atilde; đề nghị phần thưởng 20.000 bảng Anh cho th&ocirc;ng tin tiềm năng.</p>\r\n\r\n<p>C&ocirc; đặt t&ecirc;n con g&aacute;i của m&igrave;nh l&agrave; Penny, theo t&ecirc;n người mẹ m&agrave; c&ocirc; đ&atilde; mất.</p>\r\n', 17, 6, 0, 1, 1),
(36, 'Xét xử nhóm người Trung Quốc dụ dỗ thiếu nữ đóng phim sex', 'Xet-Xu-Nhom-Nguoi-Trung-Quoc-Du-Do-Thieu-Nu-Dong-Phim-Sex', '<p>5 người Trung Quốc v&agrave; một c&ocirc; g&aacute;i Việt ra t&ograve;a với c&aacute;o buộc l&agrave;m phim sex, ph&aacute;t h&igrave;nh ảnh l&ecirc;n mạng x&atilde; hội để kiếm tiền.</p>\r\n', 'phap-luat-7.jpg', '2021-10-31 10:00:00', 1, '<p>Bản &aacute;n ra cuối th&aacute;ng 10 của TAND Đ&agrave; Nẵng tuy&ecirc;n 6 người phạm tội&nbsp;<em>Giao cấu hoặc thực hiện h&agrave;nh vi quan hệ t&igrave;nh dục kh&aacute;c với người từ 13 đến dưới 16 tuổ</em>i;&nbsp;<em>Sử dụng người dưới 16 tuổi v&agrave;o mục đ&iacute;ch khi&ecirc;u d&acirc;m,&nbsp;</em>gồm 5 bị c&aacute;o người Trung Quốc: Trương Huệ Mẫn (40 tuổi) &aacute;n 13 năm t&ugrave;; Lưu Tiểu Vệ (32 tuổi) 8 năm 6 th&aacute;ng; Tưởng Đăng Qu&acirc;n (32 tuổi) v&agrave; Đới Hồng Hi (24 tuổi) mỗi người 7 năm 6 th&aacute;ng. Cả 5 bị c&aacute;o mang quốc tịch Trung Quốc. Bị c&aacute;o thứ 6 l&agrave; Sầm Thị Sen (26 tuổi, qu&ecirc; Gia Lai) bị phạt 10 năm 6 th&aacute;ng t&ugrave;.</p>\r\n\r\n<p>Ở tội&nbsp;<em>D&acirc;m &ocirc; với người dưới 16 tuổi</em>, Phương Tuấn Kiệt (35 tuổi, quốc tịch Trung Quốc) bị tuy&ecirc;n phạt 2 năm 3 th&aacute;ng t&ugrave;.</p>\r\n\r\n<p>5 bị c&aacute;o người Trung Quốc bị buộc rời khỏi Việt Nam sau khi chấp h&agrave;nh xong &aacute;n t&ugrave;.</p>\r\n\r\n<p>Theo c&aacute;o trạng, Mẫn thu&ecirc; Sen l&agrave;m phi&ecirc;n dịch khi sang Việt Nam bằng visa du lịch v&agrave; trở th&agrave;nh t&igrave;nh nh&acirc;n. Mẫn đến Đ&agrave; Nẵng t&igrave;m địa điểm l&agrave;m &quot;phim sex&quot;. Sen tiếp tay bằng việc đứng ra thu&ecirc; nh&agrave; tr&ecirc;n đường L&ecirc; Minh Trung (quận Sơn Tr&agrave;) l&agrave;m &quot;trụ sở&quot;.</p>\r\n\r\n<p>Sen c&ograve;n đăng tin l&ecirc;n Facebook, tuyển c&aacute;c c&ocirc; g&aacute;i c&oacute; ngoại h&igrave;nh đẹp với lời hứa &quot;l&agrave;m việc nhẹ nh&agrave;ng với mức lương cao&quot;. Từ th&aacute;ng 2 đến th&aacute;ng 9/2019, Sen ứng vi&ecirc;n gửi trước ảnh để chấm điểm v&agrave; tuyển được 4 thiếu nữ, hẹn gặp mặt v&agrave; dụ dỗ &quot;đ&oacute;ng&quot; livestream khi&ecirc;u d&acirc;m.</p>\r\n\r\n<p>Để c&aacute;c c&ocirc; g&aacute;i kh&ocirc;ng e ngại, Sen n&oacute;i ứng dụng mạng x&atilde; hội chỉ c&oacute; người Trung Quốc xem. Ban đầu, c&aacute;c c&ocirc; diễn cảnh cởi đồ nhưng sau đ&oacute; l&agrave; &quot;đ&oacute;ng phim&quot; với Vệ v&agrave; Qu&acirc;n.</p>\r\n\r\n<p>Theo thỏa thuẫn, nh&oacute;m Mẫn trả th&ugrave; lao 700.000 đồng/6 giờ tho&aacute;t y; một triệu đồng/lượt giao cấu. Video được b&aacute;n cho c&aacute;c trang &quot;web đen&quot; ở nước ngo&agrave;i. Người d&ugrave;ng muốn xem phải đăng k&yacute; t&agrave;i khoản th&agrave;nh vi&ecirc;n v&agrave; trả tiền qua một t&agrave;i khoản mở tại ng&acirc;n h&agrave;ng ở Trung Quốc.</p>\r\n\r\n<p>C&aacute;c bị c&aacute;o c&ograve;n lập nhiều t&agrave;i khoản tr&ecirc;n mạng x&atilde; hội c&oacute; chức năng chia sẻ h&igrave;nh ảnh, clip, livestream v&agrave; người xem c&oacute; thể tặng qu&agrave; (quy đổi th&agrave;nh tiền) để t&aacute;n thưởng...</p>\r\n\r\n<p>Đầu th&aacute;ng 9/2019, Qu&acirc;n v&agrave; Vệ sắp hết hạn visa, Mẫn tuyển Hi v&agrave; Kiệt tiếp tục l&agrave;m &quot;diễn vi&ecirc;n&quot;. Tối 14/9/2019, c&ocirc;ng an triệt ph&aacute; đường d&acirc;y n&agrave;y.</p>\r\n\r\n<p>Theo cơ quan c&ocirc;ng an, Mẫn l&agrave; người cầm đầu đường d&acirc;y, ki&ecirc;m đạo diễn, quay phim; bốn người Trung Quốc c&ograve;n lại l&agrave; diễn vi&ecirc;n. Sen được cho l&agrave; &quot;T&uacute; B&agrave;&quot; đi tuyển c&aacute;c c&ocirc; g&aacute;i.</p>\r\n\r\n<p>Ngo&agrave;i &quot;phim trường&quot; ch&iacute;nh tr&ecirc;n đường L&ecirc; Minh Trung, Mẫn c&ograve;n y&ecirc;u cầu Sen t&igrave;m th&ecirc;m c&aacute;c căn nh&agrave; cho thu&ecirc; kh&aacute;c để thay đổi địa điểm. Mỗi nơi ở của cả nh&oacute;m được bảo vệ bằng nhiều lớp cửa đ&oacute;ng, bất kỳ c&ocirc; g&aacute;i n&agrave;o ra, v&agrave;o &quot;động sex&quot; đều phải qua Sen x&aacute;c nhận.</p>\r\n', 17, 6, 1, 1, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(37, 'Cái chết bí ẩn của nữ sinh trong đại học danh tiếng', 'Cai-Chet-Bi-An-Cua-Nu-Sinh-Trong-Dai-Hoc-Danh-Tieng', '<p>Nhiều sinh vi&ecirc;n đại học Pennsylvania dường như kh&ocirc;ng biết g&igrave; về &aacute;n mạng r&uacute;ng động nước Mỹ một thời, xảy ra ngay tại thư viện.</p>\r\n', 'phap-luat-8.jpg', '2021-11-01 02:28:33', 1, '<p>Betsy Aardsma, 22 tuổi, bang Michigan, mơ ước gia nhập Lực lượng t&igrave;nh nguyện g&igrave;n giữ h&ograve;a b&igrave;nh. V&igrave; t&igrave;nh y&ecirc;u với David Wright, Betsy đ&agrave;nh từ bỏ tham vọng v&agrave; kế hoạch của ri&ecirc;ng, theo bạn trai đến Pennsylvania.</p>\r\n\r\n<p>C&ocirc; ghi danh v&agrave;o chương tr&igrave;nh tiếng Anh sau đại học tại cơ sở ch&iacute;nh của Đại học Pennsylvania trong khi David đăng k&yacute; học y khoa ở Hershey. C&ocirc; thường bắt xe bu&yacute;t đến thăm anh v&agrave;o cuối tuần.</p>\r\n\r\n<p>Ng&agrave;y 28/11/1969, từ 16h45&#39; đến 16h55&#39;, Betsy đang ở trong thư viện, h&agrave;ng 51 của kho s&aacute;ch. Khoảng trống giữa c&aacute;c h&agrave;ng s&aacute;ch v&ocirc; c&ugrave;ng chật hẹp, kh&ocirc;ng c&oacute; đủ chỗ cho 2 người đi qua, trừ khi một người nghi&ecirc;ng m&igrave;nh. S&aacute;ch được xếp tạm cuối tường, tạo th&agrave;nh lối ra v&agrave;o duy nhất. Tức l&agrave; nếu ai đ&oacute; bị dồn v&agrave;o ch&acirc;n tường, họ kh&ocirc;ng c&oacute; đường tho&aacute;t.</p>\r\n\r\n<p>Một nh&acirc;n chứng ở h&agrave;ng ghế gần đ&oacute; n&oacute;i rằng nghe thấy một người đ&agrave;n &ocirc;ng v&agrave; một phụ nữ đang tr&ograve; chuyện. Ngay sau đ&oacute;, anh ta nghe thấy tiếng thở hổn hển v&agrave; s&aacute;ch rơi xuống đất.</p>\r\n\r\n<p>Một người đ&agrave;n &ocirc;ng bước ra khỏi thư viện, n&oacute;i với Mary Erdley, bạn của Betsy, khi ấy đang ngồi ở khu ngo&agrave;i: &quot;T&ocirc;i nghĩ bạn c&ocirc; cần gi&uacute;p đỡ&quot;. Erdley hớt hải chạy v&agrave;o trong, lục so&aacute;t thư viện v&agrave; t&igrave;m thấy Betsy nằm bất động ở h&agrave;ng gi&aacute; s&aacute;ch số 51.</p>\r\n\r\n<p>C&aacute;c sinh vi&ecirc;n tại thư viện ban đầu tin rằng Betsy ngất xỉu hoặc bị động kinh. Nhưng tại bệnh viện, b&aacute;c sĩ ph&aacute;t hiện c&ocirc; bị đ&acirc;m xuy&ecirc;n tim một lần bằng con dao kiểu chuy&ecirc;n d&ugrave;ng đi săn, lưỡi d&agrave;i khoảng 12 cm. M&aacute;u từ vết thương chảy tr&agrave;n v&agrave;o phổi. Chiếc v&aacute;y đỏ của c&ocirc; che đi một lượng m&aacute;u nhỏ rỉ ra.</p>\r\n\r\n<p>Khi Betsy bị đ&acirc;m, c&ocirc; ng&atilde; xuống s&agrave;n, k&eacute;o s&aacute;ch rơi xuống, chết sau khoảng năm ph&uacute;t. Vết thương cho thấy c&ocirc; bị tấn c&ocirc;ng từ ph&iacute;a sau.</p>\r\n\r\n<p><strong>Cảnh s&aacute;t bang Pennsylvania cử khoảng 35 nh&acirc;n sự để điều tra</strong>. H&agrave;ng trăm sinh vi&ecirc;n bị chất vấn, to&agrave;n bộ khu&ocirc;n vi&ecirc;n trường đ&atilde; được kh&aacute;m x&eacute;t nhưng kh&ocirc;ng thể t&igrave;m ra vũ kh&iacute;. Họ cũng treo thưởng 25.000 USD cho người cung cấp th&ocirc;ng tin c&oacute; gi&aacute; trị.</p>\r\n\r\n<p>400 người được coi l&agrave; thường xuy&ecirc;n đến thư viện v&agrave;o khung giờ n&agrave;y, nhưng kh&ocirc;ng ai trong số n&agrave;y bị coi l&agrave; nghi phạm.</p>\r\n\r\n<p>C&aacute;c điều tra vi&ecirc;n khi đến nơi đ&atilde; v&ocirc; c&ugrave;ng thất vọng khi hiện trường đ&atilde; được dọn sạch bởi thủ thư, do kh&ocirc;ng biết ngay rằng Betsy bị s&aacute;t hại. Nhiều người đ&atilde; ra v&agrave;o nơi n&agrave;y, chạm v&agrave;o s&aacute;ch g&acirc;y nhiễu bằng chứng.</p>\r\n\r\n<p>Người bị nghi vấn đầu ti&ecirc;n l&agrave; bạn trai nạn nh&acirc;n. Trong bất kỳ cuộc điều tra &aacute;n mạng, nghi ngờ đầu ti&ecirc;n đều rơi v&agrave;o những người th&acirc;n thiết nhất. Hung thủ cố &yacute; đ&acirc;m v&agrave;o tĩnh mạch chủ v&agrave; t&acirc;m thất phải, vị tr&iacute; trọng yếu, dễ d&agrave;ng g&acirc;y chết người n&ecirc;n cảnh s&aacute;t c&agrave;ng c&oacute; cơ sở nghi ngờ David. Anh ta lại l&agrave; sinh vi&ecirc;n y khoa.</p>\r\n\r\n<p>David khai đang học giải phẫu c&ugrave;ng bạn b&egrave; ở Hershey c&aacute;ch đ&oacute; 16 km v&agrave;o thời điểm xảy ra &aacute;n mạng. C&aacute;c bạn đều chứng thực bằng chứng ngoại phạm của anh ta.</p>\r\n\r\n<p>Betsy đến thăm bạn trai v&agrave;o Lễ Tạ ơn, một tuần trước khi vụ giết người xảy ra. David đ&atilde; đề nghị ở lại Hershey v&agrave;o cuối tuần nhưng c&ocirc; n&oacute;i c&ograve;n qu&aacute; nhiều b&agrave;i phải học. Họ chia tay ở bến xe bu&yacute;t v&agrave; đ&oacute; l&agrave; lần gặp cuối c&ugrave;ng.</p>\r\n\r\n<p>Hai cảnh s&aacute;t được cử về qu&ecirc; nạn nh&acirc;n để t&igrave;m ra liệu c&oacute; điều g&igrave; đ&oacute; đ&aacute;ng ch&uacute; &yacute; trong qu&aacute; khứ. Họ xem qua c&aacute;c l&aacute; thư của c&ocirc; v&agrave; phỏng vấn bạn b&egrave;, gia đ&igrave;nh nhưng kh&ocirc;ng mang lại kết quả.</p>\r\n\r\n<p>Trong những năm tiếp theo, nhiều giả thuyết được c&aacute;c nh&agrave; điều tra đưa ra.</p>\r\n\r\n<p>Giả thuyết đầu ti&ecirc;n li&ecirc;n quan người đ&agrave;n &ocirc;ng lạ mặt đ&atilde; b&aacute;o tin cho bạn c&ocirc; b&ecirc;n ngo&agrave;i sảnh thư viện. Cảnh s&aacute;t cho rằng Betsy bắt gặp một cuộc quan hệ đồng t&iacute;nh nam v&agrave; bị giết để bịt đầu mối. Tại hiện trường, b&aacute;c sĩ cũng ph&aacute;t hiện một vệt tinh dịch.</p>\r\n\r\n<p>Hơn nữa, 20-30 cuốn s&aacute;ch khi&ecirc;u d&acirc;m đ&atilde; được ph&aacute;t hiện nh&eacute;t giữa c&aacute;c kệ trong khu vực Betsy bị đ&acirc;m. Nhưng n&oacute; kh&ocirc;ng n&oacute;i l&ecirc;n điều g&igrave;. Chuyện nam sinh quan hệ trong thư viện kh&ocirc;ng xa lạ, vết tinh dịch c&oacute; thể đ&atilde; xuất hiện rất l&acirc;u trước vụ &aacute;n.</p>\r\n\r\n<p>Một trong những giả thuyết phổ biến nhất l&agrave; Betsy đ&atilde; bị giết bởi&nbsp;<a href=\"https://vnexpress.net/topic/ted-bundy-hung-than-cua-cac-thieu-nu-12052\" rel=\"dofollow\">Ted Bundy</a>, kẻ giết người h&agrave;ng loạt kh&eacute;t tiếng những năm 1970 với hơn 30 vụ bắt c&oacute;c, hiếp d&acirc;m v&agrave; s&aacute;t hại phụ nữ v&agrave; trẻ em g&aacute;i.</p>\r\n\r\n<p>Nhưng phương thức g&acirc;y &aacute;n của hung thủ trong vụ Betsy kh&ocirc;ng giống &quot;phong c&aacute;ch&quot; của Ted Bundy. Betsy cũng kh&ocirc;ng c&oacute; dấu vết bị hiếp d&acirc;m. Vụ &aacute;n tiếp tục bế tắc.</p>\r\n\r\n<p><strong>Sau n&agrave;y, nhiều th&aacute;m tử cho rằng kẻ t&igrave;nh nghi tiềm năng nhất đ&atilde; bị bỏ qua</strong>&nbsp;ngay từ khi bắt đầu cuộc điều tra: Richard Haefner, sinh vi&ecirc;n khoa địa chất 25 tuổi. Người Mỹ sau n&agrave;y sẽ nhớ đến &ocirc;ng nhiều hơn với cương vị Gi&aacute;m đốc Bảo t&agrave;ng Lịch sử Tự nhi&ecirc;n Los Angeles v&agrave; hơn hết l&agrave; vụ b&ecirc; bối ấu d&acirc;m khiến &ocirc;ng bị cấm giảng dạy đến cuối đời.</p>\r\n\r\n<p>Tại thời điểm vụ &aacute;n, Betsy c&oacute; hẹn h&ograve; v&agrave;i ng&agrave;y với Richard nhưng sau đ&oacute; nghĩ lại, đưa ra quyết định gắn b&oacute; l&acirc;u d&agrave;i với David. Bạn c&ugrave;ng ph&ograve;ng k&yacute; t&uacute;c x&aacute; của Betsy kể lại Richard li&ecirc;n tục đến thăm họ trước vụ &aacute;n song đều bị Betsy cự tuyệt v&agrave; đ&oacute;ng sầm cửa trước mặt.</p>\r\n\r\n<p>Sau h&ocirc;m vụ &aacute;n được loan b&aacute;o, Richard hốt hoảng vượt h&agrave;ng chục c&acirc;y số trong đ&ecirc;m để đến thổ lộ sự sợ h&atilde;i v&agrave; đau buồn với gi&aacute;o vi&ecirc;n của m&igrave;nh. Richard được cảnh s&aacute;t thẩm vấn khoảng một tuần sau, nhưng thẳng thắn v&agrave; cởi mở chia sẻ về mối t&igrave;nh bất th&agrave;nh với Betsy. Anh ta n&oacute;i thời điểm xảy ra vụ &aacute;n đang đi ăn tối trong to&agrave; nh&agrave; của hội sinh vi&ecirc;n.</p>\r\n\r\n<p>Kh&ocirc;ng đủ bằng chứng để buộc tội, cảnh s&aacute;t đ&atilde; thả anh ta đi m&agrave; kh&ocirc;ng tiến h&agrave;nh bất cứ cuộc gi&aacute;m s&aacute;t b&iacute; mật n&agrave;o sau đ&oacute;. Richard qua đời v&igrave; đau tim v&agrave;o năm 2002.</p>\r\n\r\n<p>Ng&agrave;y nay, cảnh s&aacute;t lập luận rằng vụ &aacute;n của Betsy chưa được giải quyết do những sai lầm trong việc bảo vệ hiện trường, kh&ocirc;ng giữ ch&acirc;n những kẻ t&igrave;nh nghi tiềm năng v&agrave; những phương ph&aacute;p điều tra tội phạm th&ocirc; sơ.</p>\r\n', 17, 6, 0, 1, 1),
(38, 'Vợ chồng Diệp Lâm Anh từ hạnh phúc tới tin đồn rạn nứt', 'Vo-Chong-Diep-Lam-Anh-Tu-Hanh-Phuc-Toi-Tin-Don-Ran-Nut', '<p>Diệp L&acirc;m Anh v&agrave; chồng - doanh nh&acirc;n Nghi&ecirc;m Đức - đối diện tin đồn ly h&ocirc;n sau ba năm gắn b&oacute;, nhưng c&ocirc; phủ nhận.</p>\r\n', 'giai-tri.jpg', '2021-11-01 23:47:47', 1, '<p><img alt=\"sinh nhật vào tối 31/10 của con trai và con gái Diệp Lâm Anh đã được chia sẻ. Đúng như cựu người mẫu thông báo từ trước, cô cùng gia đình đã chuẩn bị cho hai bé không gian mừng sinh nhật rất đẹp và ý nghĩa.  Đáng chú ý, buổi tiệc này là lần đầu tiên cả Diệp Lâm Anh và chồng Đức Phạm cùng xuất hiện sau khi rộ tin đồn trục trặc hôn nhân. Thậm chí trước buổi tiệc vài tiếng, Đức Phạm còn đăng đàn công khai đã ly thân và đang tiến hành ly hôn vợ.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/dla-3-jpeg-6197-1635759240.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=E_eEWQr2OhjNkdj_gwsLBg\" /></p>\r\n\r\n<p>Tối 31/10, vợ chồng Diệp L&acirc;m Anh tổ chức sinh nhật cho con trai v&agrave; con g&aacute;i. Lần đầu ti&ecirc;n xuất hiện sau tin đồn trục trặc h&ocirc;n nh&acirc;n, cả hai tr&aacute;nh chụp ảnh chung. Trước buổi tiệc v&agrave;i tiếng, Nghi&ecirc;m Đức th&ocirc;ng b&aacute;o tr&ecirc;n trang c&aacute; nh&acirc;n anh v&agrave; vợ đ&atilde; ly th&acirc;n từ th&aacute;ng 1, đang trong giai đoạn giải quyết thủ tục ly dị. Tuy nhi&ecirc;n, ph&iacute;a Diệp L&acirc;m Anh chiều 1/11 cho biết c&ocirc; vẫn sống ở nh&agrave; chồng, kh&ocirc;ng hiểu l&yacute; do b&agrave;i đăng của Nghi&ecirc;m Đức. Cả hai chỉ đang&nbsp;<a href=\"https://vnexpress.net/diep-lam-anh-toi-chua-ly-hon-4379772.html\" rel=\"dofollow\">c&atilde;i nhau</a>, kh&ocirc;ng n&oacute;i chuyện.<br />\r\nC&aacute;ch đ&acirc;y v&agrave;i ng&agrave;y, một số h&igrave;nh ảnh được cho l&agrave; chồng c&ocirc; đi c&ugrave;ng một nữ diễn vi&ecirc;n được đăng tải, dấy l&ecirc;n tin đồn anh c&oacute; người thứ ba. Diệp L&acirc;m Anh từ chối b&igrave;nh luận về th&ocirc;ng tin n&agrave;y. Ảnh:<em>&nbsp;Facebook Diệp L&acirc;m Anh</em></p>\r\n\r\n<p><img alt=\"Diệp Lâm Anh được khán giả biết đến sau khi trở thành quán quân chương trình Cuộc đua kỳ thú năm 2013. Vào tháng 9/2015, cô bất ngờ công khai mối quan hệ với Đức Phạm trong tiệc sinh nhật một người bạn. Gia đình Đức Phạm sở hữu nhiều cửa hàng kinh doanh xe máy lớn và bất động sản giá trị tại thành phố Hồ Chí Minh. Cả hai quen biết nhiều năm, nhưng chỉ mới bắt đầu tìm hiểu nhau trước đó không lâu. Diệp Lâm Anh từng chia sẻ trong một phỏng vấn với VnExpress năm 2016, cô không có suy nghĩ dựa dẫm vào chồng giàu có. Tôi nghĩ, đàn ông ai cũng thích che chở cho người phụ nữ của mình. Nhưng một cô gái độc lập về kinh tế, biết phấn đấu vẫn là người họ muốn gắn bó nhất, cô nói.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/batch-DLA-congkhaibantrai-FB-H-7367-6165-1635759240.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bH2OZg8g0N_wp6bIsR8Tww\" /></p>\r\n\r\n<p>Trước khi ồn &agrave;o xảy ra, cả hai lu&ocirc;n xuất hiện với h&igrave;nh ảnh hạnh ph&uacute;c. C&ocirc; v&agrave; doanh nh&acirc;n Nghi&ecirc;m Đức lần đầu c&ocirc;ng khai xuất hiện c&ugrave;ng nhau th&aacute;ng 9/2015, trong tiệc sinh nhật diễn vi&ecirc;n Hồng Loan (giữa). Họ kết bạn với nhau từ nhiều năm trước đ&oacute;. Ảnh:&nbsp;<em>Facebook Diệp L&acirc;m Anh</em></p>\r\n\r\n<p><img alt=\"Đến tháng 4/2018, Đức Phạm tổ chức buổi cầu hôn bất ngờ trong rạp chiếu phim cho Diệp Lâm Anh\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/dla-diep-lam-anh-jpeg-4030-1635759240.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3iqV0UMRYfoXYwjYpWeZcQ\" /></p>\r\n\r\n<p>Th&aacute;ng 4/2018, Nghi&ecirc;m Đức tổ chức buổi cầu h&ocirc;n trong rạp chiếu phim cho Diệp L&acirc;m Anh. Người đẹp rơi nước mắt v&igrave; x&uacute;c động v&agrave; bất ngờ. C&ocirc; từng nhận x&eacute;t chồng l&agrave; người đ&agrave;n &ocirc;ng chu đ&aacute;o, biết quan t&acirc;m. Doanh nh&acirc;n từng nhiều lần b&agrave;y tỏ t&igrave;nh cảm với c&ocirc; bằng những c&acirc;u n&oacute;i ngọt ng&agrave;o tr&ecirc;n mạng x&atilde; hội như: &quot;Em l&agrave; c&ocirc; g&aacute;i đặc biệt nhất trong đời anh. Em l&agrave; tất cả của anh&quot;, &quot;Thời gian b&ecirc;n nhau xảy ra thật nhiều chuyện, nhưng ch&uacute;ng ta đ&atilde; c&ugrave;ng nhau vượt qua. Anh tin kh&ocirc;ng g&igrave; c&oacute; thể l&agrave;m m&igrave;nh xa nhau nữa&quot;... Ảnh:&nbsp;<em>Facebook Diệp L&acirc;m Anh</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Hôn lễ của cả hai được tổ chức vào tháng 5 cùng năm với sự tham dự của nhiều bạn bè thân thiết như Hoa hậu Kỳ Duyên, rapper Tiến Đạt, vợ chồng Đàm Thu Trang - Cường Đô La... Đám cưới Diệp Lâm Anh còn thu hút nhiều sự chú ý vì dàn siêu xe rước dâu, loạt váy cưới trị giá hàng trăm triệu và chi phí hoa tươi lên đến 1,3 tỷ đồng.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/DLA-damcuoi2-Maison-de-Bil-jpe-2620-3179-1635759240.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GHyVH0RQ4WOigtrd8QUGsA\" /></p>\r\n\r\n<p>H&ocirc;n lễ của cả hai được tổ chức v&agrave;o th&aacute;ng 5 c&ugrave;ng năm với sự tham dự của nhiều bạn b&egrave; th&acirc;n thiết như Hoa hậu Kỳ Duy&ecirc;n, rapper Tiến Đạt, vợ chồng Đ&agrave;m Thu Trang - Cường Đ&ocirc; La...&nbsp;<a href=\"https://vnexpress.net/cuong-dola-dam-thu-trang-du-tiec-cuoi-diep-lam-anh-3745578.html\" rel=\"dofollow\" target=\"_blank\">Đ&aacute;m cướ</a>i c&ograve;n thu h&uacute;t sự ch&uacute; &yacute; với d&agrave;n si&ecirc;u xe rước d&acirc;u, loạt v&aacute;y cưới trị gi&aacute; h&agrave;ng trăm triệu đồng v&agrave; hoa tươi trang tr&iacute; l&ecirc;n đến 1,3 tỷ đồng. Ảnh:<em>Th&agrave;nh Nguyễn</em></p>\r\n\r\n<p><img alt=\"Đôi vợ chồng thường mặc đồ đồng điệu mỗi khi đi bên nhau. Gia đình chồng sở hữu nhiều cửa hàng kinh doanh xe máy và bất động sản giá trị tại TP HCM. Tuy nhiên, Diệp Lâm Anh cho biết cô không có suy nghĩ dựa dẫm, ỷ lại vào ai. Tôi nghĩ, đàn ông ai cũng thích che chở cho người phụ nữ của mình. Nhưng một cô gái độc lập về kinh tế, biết phấn đấu vẫn là người họ muốn gắn bó nhất, cô nói. Ảnh: Facebook Diệp Lâm Anh\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/DLA-su-kien-thang4-2019-tai-Da-1696-7886-1635759240.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zLb4lpko2UnLpUfG6CtQ1w\" /></p>\r\n\r\n<p>Đ&ocirc;i vợ chồng thường mặc đồ đồng điệu mỗi khi đi&nbsp;<a href=\"https://vnexpress.net/vo-chong-diep-lam-anh-tay-trong-tay-du-su-kien-3763876.html\" rel=\"dofollow\" target=\"_blank\">b&ecirc;n nhau</a>. Gia đ&igrave;nh chồng sở hữu nhiều cửa h&agrave;ng kinh doanh xe m&aacute;y v&agrave; bất động sản gi&aacute; trị tại TP HCM. Tuy nhi&ecirc;n, Diệp L&acirc;m Anh cho biết c&ocirc; kh&ocirc;ng c&oacute; suy nghĩ dựa dẫm, ỷ lại v&agrave;o ai. &quot;T&ocirc;i nghĩ, đ&agrave;n &ocirc;ng ai cũng th&iacute;ch che chở cho người phụ nữ của m&igrave;nh. Nhưng một c&ocirc; g&aacute;i độc lập về kinh tế, biết phấn đấu vẫn l&agrave; người họ muốn gắn b&oacute; nhất&quot;, c&ocirc; n&oacute;i.</p>\r\n', 11, 4, 4, 1, 1),
(39, 'Hoa hậu Đại dương Thu Thảo ly hôn', 'Hoa-Hau-Dai-Duong-Thu-Thao-Ly-Hon', '<p>Hoa hậu Đại dương 2014 Đặng Thu Thảo ly h&ocirc;n chồng - doanh nh&acirc;n Ph&uacute;c Th&agrave;nh - sau ba năm gắn b&oacute;.</p>\r\n', 'giai-tri-3.jpg', '2021-11-01 23:49:47', 1, '<p>Tối 1/11, anh Th&aacute;i B&igrave;nh - đại diện truyền th&ocirc;ng Đặng Thu Thảo - cho biết c&ocirc; ly h&ocirc;n từ th&aacute;ng 4 sau khi nộp đơn l&ecirc;n T&ograve;a &aacute;n Nh&acirc;n d&acirc;n tỉnh An Giang. Họ c&oacute; cặp song sinh hơn một tuổi, t&ecirc;n Đăng Minh v&agrave; Quốc Vượng, hiện được gia đ&igrave;nh ngoại chăm s&oacute;c. H&ocirc;m 29/10, c&ocirc; viết tr&ecirc;n trang c&aacute; nh&acirc;n: &quot;Ba mẹ con bắt đầu cuộc sống mới... Mọi thứ đều mới v&agrave; xuất ph&aacute;t từ con số &acirc;m, nhưng kh&ocirc;ng g&igrave; l&agrave; kh&ocirc;ng thể&quot;. C&ocirc; cảm ơn người th&acirc;n, bạn b&egrave; đ&atilde; s&aacute;t c&aacute;nh c&ugrave;ng c&ocirc; thời gian kh&oacute; khăn qua.</p>\r\n\r\n<p><img alt=\"Hoa hậu Đại dương 2014 Đặng Thu Thảo. Ảnh: Duy Quách\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/thu-thao-1-8480-1635781605.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LnoWqRgGN4L6eRrqnJWvfA\" /></p>\r\n\r\n<p>Hoa hậu Đại dương 2014 Đặng Thu Thảo. Ảnh:<em>&nbsp;Duy Qu&aacute;ch</em></p>\r\n\r\n<p>Nhiều đồng nghiệp như nghệ sĩ Trịnh Kim Chi, diễn vi&ecirc;n Kiều Trinh, H&ograve;a Hiệp... động vi&ecirc;n người đẹp. Đ&agrave;n chị Th&uacute;y Diễm nhắn nhủ: &quot;Ch&uacute;c em mạnh mẽ v&agrave; thật hạnh ph&uacute;c&quot;.</p>\r\n\r\n<p>Thu Thảo&nbsp;<a href=\"https://vnexpress.net/hoa-hau-dai-duong-dang-thu-thao-lien-tiep-hon-chong-o-tiec-cuoi-3844429.html\" rel=\"dofollow\" target=\"_blank\">kết h&ocirc;n</a>&nbsp;th&aacute;ng 11/2018. Chồng c&ocirc; - Ph&uacute;c Th&agrave;nh - từng đ&oacute;ng vai nhỏ trong một số phim truyền h&igrave;nh, sau đ&oacute; kinh doanh vật liệu x&acirc;y dựng. Cả hai quen nhau khi đ&oacute;ng phim&nbsp;<em>Hồn lụa&nbsp;</em>năm 2015. Sau bộ phim, họ nảy sinh t&igrave;nh cảm v&agrave; hẹn h&ograve;. Trong một lần phỏng vấn, c&ocirc; cho biết Ph&uacute;c Th&agrave;nh khiến c&ocirc; tự h&agrave;o v&igrave; ch&iacute;n chắn, đa t&agrave;i, l&agrave; chỗ dựa vững chắc. Sau khi lấy chồng, c&ocirc; hạn chế hoạt động showbiz, chủ yếu chăm s&oacute;c gia đ&igrave;nh, b&aacute;n h&agrave;ng online.</p>\r\n\r\n<p>Đặng Thu Thảo sinh năm 1995, qu&ecirc; Cần Thơ. Sau khi tốt nghiệp phổ th&ocirc;ng, c&ocirc; l&agrave;m quản l&yacute; nh&acirc;n sự cho một chi nh&aacute;nh nhỏ của một c&ocirc;ng ty chuy&ecirc;n b&aacute;n c&agrave; ph&ecirc;. Năm 2014, c&ocirc;&nbsp;<a href=\"https://vnexpress.net/nguoi-dep-can-tho-doat-vuong-mien-hoa-hau-dai-duong-2995456.html#ctr=related_news_click\" rel=\"dofollow\" target=\"_blank\">đăng quang</a>&nbsp;Hoa hậu Đại Dương với chiều cao 1,71 m, nặng 57 kg, số đo h&igrave;nh thể l&agrave; 91-63-90 cm. C&ocirc; cũng tham gia diễn xuất trong c&aacute;c bộ phim:&nbsp;<em>Mỹ nam kế, Hợp đồng bắt ma</em>,&nbsp;<em><a href=\"https://vnexpress.net/ve-si-tieu-thu-va-thang-kho-ton-vinh-ve-dep-vinh-ha-long-3507457.html\" rel=\"dofollow\" target=\"_blank\">Vệ sĩ - tiểu thư v&agrave; thằng khờ</a>, Ho&agrave;ng tử anh ở đ&acirc;u, Nh&agrave; &ocirc;ng Ho&agrave;ng c&oacute; ma...</em></p>\r\n', 11, 4, 3, 1, 1),
(40, 'Mỹ nhân TVB đeo vàng đầy tay ngày cưới', 'My-Nhan-Tvb-Deo-Vang-Day-Tay-Ngay-Cuoi', '<p>Trần Thi H&acirc;n - diễn vi&ecirc;n TVB - đeo nhiều v&ograve;ng v&agrave;ng do gia đ&igrave;nh chồng tặng trong ng&agrave;y th&agrave;nh h&ocirc;n.</p>\r\n', 'giai-tri-4.jpg', '2021-11-01 23:52:07', 1, '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ngày 31/10, Thi Hân đăng trên trang cá nhân ảnh ngày\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/sao3-1975-1635731998.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fqahTsBY6lPMC8SDVddCPw\" /></p>\r\n\r\n<p>Ng&agrave;y 31/10, Thi H&acirc;n đăng tr&ecirc;n trang c&aacute; nh&acirc;n loạt ảnh ở lễ cưới ch&uacute; rể Trần Nhĩ Ch&iacute;nh, l&agrave;m ngo&agrave;i ng&agrave;nh giải tr&iacute;. C&ocirc; d&acirc;u đeo v&ograve;ng v&agrave;ng khắc h&igrave;nh long phụng, mang &yacute; nghĩa may mắn, tốt l&agrave;nh. V&ograve;ng được cha mẹ ch&uacute; rể tặng Thi H&acirc;n hồi th&aacute;ng 9.</p>\r\n\r\n<p><img alt=\"Đôi uyên ương ra mắt họ hàng ở lễ rước dâu theo phong tục truyền thống của người Hoa.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/sao2-7181-1635731998.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=CgZqNyWrQws8RLapzMWdjg\" /></p>\r\n\r\n<p>Đ&ocirc;i uy&ecirc;n ương ra mắt họ h&agrave;ng ở lễ rước d&acirc;u theo phong tục truyền thống của người Hoa.</p>\r\n\r\n<p><img alt=\"Trần Thi Hân bên những người bạn thân.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/sao4-7572-1635731998.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=QQw3hlNc7wESH1Yxen-5WQ\" /></p>\r\n\r\n<p>Trần Thi H&acirc;n b&ecirc;n những người bạn th&acirc;n.</p>\r\n\r\n<p><img alt=\"Thi Hân, Nhĩ Chính cử hành nghi thức thành hôn ở nhà thờ. Trên trang cá nhân, diễn viên viết cô quyết định cưới Trần Nhĩ Chính vì một câu nói của anh: Chúng ta là một đội. Bấy giờ cô cô đơn và thiếu niềm tin, câu nói của Trần Nhĩ Chính làm Thi Hân thấy có thêm sức mạnh.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/dam-cuoi-2-3794-1635731998.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jD7YGif7gcce0x5XKf8yZQ\" /></p>\r\n\r\n<p>Thi H&acirc;n, Nhĩ Ch&iacute;nh cử h&agrave;nh nghi thức th&agrave;nh h&ocirc;n ở nh&agrave; thờ. Tr&ecirc;n trang c&aacute; nh&acirc;n, diễn vi&ecirc;n viết c&ocirc; quyết định cưới Trần Nhĩ Ch&iacute;nh v&igrave; một c&acirc;u n&oacute;i của anh: &quot;Ch&uacute;ng ta l&agrave; một đội&quot;. Bấy giờ c&ocirc; c&ocirc; đơn v&agrave; thiếu niềm tin, c&acirc;u n&oacute;i của Trần Nhĩ Ch&iacute;nh l&agrave;m Thi H&acirc;n thấy c&oacute; th&ecirc;m sức mạnh, kh&ocirc;ng lẻ loi, đơn độc.</p>\r\n\r\n<p><img alt=\"Thi Hân diện đầm trễ vai, đeo trang sức ngọc trai khi làm lễ ở nhà thờ.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/dam-cuoi-4697-1635731998.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pk3ueaw43nBzP8v17Iqghw\" /></p>\r\n\r\n<p>Thi H&acirc;n diện đầm trễ vai, đeo trang sức ngọc trai khi l&agrave;m lễ ở nh&agrave; thờ.</p>\r\n\r\n<p><img alt=\"Thi Hân 29 tuổi, là ca sĩ, diễn viên kiêm MC của đài TVB. Cô từng đóng các phim Mái ấm gia đình 4, Hình trinh nhật ký, Bĩ tử điện hạ.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/01/han2-jpeg-1320-1635731998.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FuT-0dg6CPC75FurA0GhAQ\" /></p>\r\n\r\n<p>C&ocirc; mặc đầm xẻ ngực c&ugrave;ng chồng đ&oacute;n kh&aacute;ch dự tiệc cưới ở kh&aacute;ch sạn.<br />\r\nThi H&acirc;n 29 tuổi, l&agrave; ca sĩ, diễn vi&ecirc;n ki&ecirc;m MC của đ&agrave;i TVB. C&ocirc; từng đ&oacute;ng c&aacute;c phim &quot;M&aacute;i ấm gia đ&igrave;nh 4&quot;, &quot;H&igrave;nh trinh nhật k&yacute;&quot;, &quot;Bĩ tử điện hạ&quot;.</p>\r\n', 11, 4, 1, 1, 1),
(41, 'Gu thời trang của tỷ phú Hàn Quốc', 'Gu-Thoi-Trang-Cua-Ty-Phu-Han-Quoc', '<p>Lee Boo-jin - con g&aacute;i cố chủ tịch Samsung - y&ecirc;u th&iacute;ch gam đen trắng, chuộng trang sức hoa tai.</p>\r\n', 'giai-tri-5.jpg', '2021-11-01 23:53:46', 1, '<p>Lee Boo-jin thường xuất hiện với trang phục thanh lịch, nh&atilde; nhặn. B&agrave; nhiều lần phối &aacute;o trắng với ch&acirc;n v&aacute;y d&agrave;i ngang hoặc qu&aacute; đầu gối. Doanh nh&acirc;n sinh năm 1970, l&agrave; con thứ hai của cố chủ tịch tập đo&agrave;n Samsung Lee Kun-hee (1942-2020). Hiện b&agrave; l&agrave; chủ tịch ki&ecirc;m CEO<em>&nbsp;</em>của Hotel Shilla, một trong những kh&aacute;ch sạn ki&ecirc;m trung t&acirc;m hội nghị nổi bật nhất ở Seoul. Theo&nbsp;<em>Forbes</em>, b&agrave; sở hữu t&agrave;i sản 3,8 tỷ USD, xếp thứ 90 danh s&aacute;ch phụ nữ quyền lực nhất thế giới năm 2020.</p>\r\n\r\n<p><img alt=\"Ảnh: ajunews\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/han1-1495-1635839114.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_vA65eJCVI8n3vWE-6OoRQ\" /></p>\r\n\r\n<p>Trang phục của Lee Boo-jin thường theo xu hướng tối giản song được biến tấu ở một số chi tiết, tạo sự mềm mại. Bộ đầm đơn sắc của Tom Ford nhấn nh&aacute; ở phần cổ tay, tăng n&eacute;t nữ t&iacute;nh. Ảnh:&nbsp;<em>Ajunews</em></p>\r\n\r\n<p><img alt=\"Lee Boo-jin trong thiết kế của Alexander McQueen. Ảnh: Yonhapnews\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/sao5-7517-1635844901.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ReD2ngqBKUMnlpBtgOAW_Q\" /></p>\r\n\r\n<p>Lee Boo-jin trong thiết kế của Alexander McQueen. Ảnh:&nbsp;<em>Yonhapnews/Alexander McQueen</em></p>\r\n\r\n<p><img alt=\"Ảnh: MT\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/lee3-3450-1635844901.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AaOLCS8vzIxdWmEyMxLgdQ\" /></p>\r\n\r\n<p>Ảnh:&nbsp;<em>MT</em></p>\r\n\r\n<p><img alt=\"Trang Hankyung nhận xét tóc gợn sóng để xõa tự nhiên, giày cao dưới 5 cm và màu đen trắng là phong cách đại diện của doanh nhân quyền lực. Ảnh: MT\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/ty-phu2-1608-1635839114.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=i-ig8PCesJVoCW_HOu3qNw\" /></p>\r\n\r\n<p>Theo&nbsp;<em>Hankyung,&nbsp;</em>t&oacute;c gợn s&oacute;ng để x&otilde;a tự nhi&ecirc;n, gi&agrave;y cao dưới 5 cm v&agrave; m&agrave;u đen trắng l&agrave; phong c&aacute;ch đại diện của doanh nh&acirc;n quyền lực. B&agrave; kết h&ocirc;n với nh&acirc;n vi&ecirc;n c&ocirc;ng ty bảo vệ thuộc tập đo&agrave;n năm 1999, ly th&acirc;n từ năm 2012 v&agrave; ho&agrave;n tất ly dị năm 2017. Theo Chosun, Lee Boo-jin trả cho chồng cũ 7,6 triệu USD v&agrave; được quyền nu&ocirc;i con trai. Ảnh:&nbsp;<em>MT</em></p>\r\n\r\n<p><img alt=\"Trang Styles123 nhận xét bà thêm phần thanh lịch, sang trọng khi kết hợp áo khoác với đầm tay ngắn.\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/anh14-2675-1635839114.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fD7JbMMNLerscIOgzUtg5A\" /></p>\r\n\r\n<p>Trang&nbsp;<em>Styles123</em>&nbsp;nhận x&eacute;t b&agrave; th&ecirc;m phần thanh lịch, sang trọng khi kết hợp &aacute;o kho&aacute;c với đầm tay ngắn. Ảnh:&nbsp;<em>MK</em></p>\r\n\r\n<p><img alt=\"Ảnh: Chosun\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/lee2-8682-1635844901.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6k9Bs-Tz62BjFIRJmoxfbA\" /></p>\r\n\r\n<p>Lee Boo Jin chuộng c&aacute;c mẫu &aacute;o dạ d&aacute;ng v&aacute;y cho m&ugrave;a thu đ&ocirc;ng. Ảnh:&nbsp;<em>Chosun</em></p>\r\n\r\n<p><img alt=\"Doanh nhân trong thiết kế thuộc bộ sưu tập năm 2020 của Burberry. Ảnh: Dcinside/Vogue\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/ao4-8722-1635844901.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=O2wUe78fb8mFxEWYFlu1NA\" /></p>\r\n\r\n<p>Doanh nh&acirc;n trong thiết kế thuộc bộ sưu tập năm 2020 của Burberry. Ảnh:&nbsp;<em>Dcinside/Vogue</em></p>\r\n\r\n<p><img alt=\"Ảnh: newspim\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/anh4-8739-1635839114.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YEjFiUHTMnFKoVskt_MPhA\" /></p>\r\n\r\n<p>B&agrave; chuộng đeo hoa tai v&agrave; thường xuất hiện với c&aacute;c mẫu trang sức đa dạng kiểu d&aacute;ng. Ảnh:&nbsp;<em>Newspim</em></p>\r\n\r\n<p>Lee Boo-jin tại sự kiện năm 2016. Video:<em>&nbsp;Focusnews</em></p>\r\n\r\n<p><img alt=\"Ảnh: Sisaweek\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/sam-sung-9049-1635844901.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NCWJUjHRMRiwvztN5OZ9Ew\" /></p>\r\n\r\n<p>Ảnh:<em>&nbsp;Sisaweek</em></p>\r\n\r\n<p><img alt=\"Ảnh: Chosun\" src=\"https://i1-giaitri.vnecdn.net/2021/11/02/ty-phu-jpeg-9551-1635839114.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-tCW0Ew9Lq-tAa6265E8IQ\" /></p>\r\n\r\n<p>Lee Boo-jin thường để hai m&agrave;u t&oacute;c đen hoặc n&acirc;u, trang điểm trong suốt, hiếm khi sử dụng m&agrave;u phấn, son đậm. Ảnh:&nbsp;<em>Chosun</em></p>\r\n', 12, 4, 0, 1, 1),
(42, 'Năm kiểu áo len dễ phối đồ ngày lạnh', 'Nam-Kieu-Ao-Len-De-Phoi-Do-Ngay-Lanh', '<p>Ph&aacute;i nữ c&oacute; thể kết hợp &aacute;o len cổ tim, l&ocirc;ng cừu hay họa tiết với mọi trang phục, vừa giữ ấm ng&agrave;y lạnh, vừa khoe vẻ s&agrave;nh điệu.</p>\r\n', 'giai-tri-6.png', '2021-11-01 23:55:12', 1, '<p>&Aacute;o len l&agrave; m&oacute;n đồ cơ bản, quen thuộc v&agrave; quan trọng của ph&aacute;i nữ lẫn nam giới trong những ng&agrave;y lạnh. Trang phục mang t&iacute;nh ứng dụng cao, kh&ocirc;ng chỉ giữ ấm m&agrave; c&ograve;n gi&uacute;p người mặc khoe trọn phong c&aacute;ch. Đại diện thương hiệu thời trang Canifa cho biết &aacute;o len c&oacute; nhiều kiểu d&aacute;ng, kh&ocirc;ng k&eacute;n người mặc v&agrave; dễ phối với quần, v&aacute;y, legging c&ugrave;ng loạt phụ kiện bắt mắt.<br />\r\nCanifa gợi &yacute; năm kiểu &aacute;o len dễ phối đồ ng&agrave;y lạnh:</p>\r\n\r\n<p><strong>&Aacute;o len cổ tim</strong></p>\r\n\r\n<p><img alt=\"Áo len cổ tim được nhiều quý cô ưa chuộng vì giữ ấm cơ thể, sành điệu và có phần dễ chịu khi bỏ ngỏ phần cổ. Có thể kết hợp trang phục với chân váy cách điệu, quần jeans... Khi trời se lạnh, nàng có thể quàng thêm một chiếc khăn mỏng. Thiết kế cổ tim phù hợp với các cô gái muốn khoe sợi dây chuyền mảnh, vòng cổ choker, cổ áo sơ mi hay đơn giản là phần xương quai xanh.\" src=\"https://i1-giaitri.vnecdn.net/2021/10/30/PR-LEN-03-1510-1635607445.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=h3B6AzehqnvmxNhCD2WaXg\" /></p>\r\n\r\n<p>&Aacute;o len cổ tim được nhiều qu&yacute; c&ocirc; ưa chuộng v&igrave; giữ ấm cơ thể, s&agrave;nh điệu v&agrave; c&oacute; phần dễ chịu khi bỏ ngỏ phần cổ. C&oacute; thể kết hợp trang phục với ch&acirc;n v&aacute;y c&aacute;ch điệu, quần jeans... Khi trời se lạnh, n&agrave;ng c&oacute; thể qu&agrave;ng th&ecirc;m một chiếc khăn mỏng.<br />\r\nThiết kế cổ tim ph&ugrave; hợp với c&aacute;c c&ocirc; g&aacute;i muốn khoe sợi d&acirc;y chuyền mảnh, v&ograve;ng cổ choker, cổ &aacute;o sơ mi hay đơn giản l&agrave; phần xương quai xanh.</p>\r\n\r\n<p><strong>&Aacute;o len vặn thừng</strong></p>\r\n\r\n<p><img alt=\"Những chiếc áo len vặn thừng luôn phảng phất nét cổ điển của quý cô Pháp, nhẹ nhàng, nhưng vẫn cuốn hút. Không cần mix-match cầu kỳ, chúng có thể diện ăn ý với nhiều phong cách: từ vintage, cổ điển đến trẻ trung, thời thượng, cá tính...Mùa đông năm nay, những màu sắc trang nhã như nâu, kem, xám... được nhiều người ưu ái khi chọn áo len vặn thừng. Món đồ này được ví là vạn năng vì dễ dàng kết hợp với jeans ống rộng, chân váy midi, chân váy ngắn hay áo sơ mi.\" src=\"https://i1-giaitri.vnecdn.net/2021/10/30/PR-LEN-05-7718-1635607445.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tY-432MKMDdKf8Z-QgLqYA\" /></p>\r\n\r\n<p>Những chiếc &aacute;o len vặn thừng lu&ocirc;n phảng phất n&eacute;t cổ điển của qu&yacute; c&ocirc; Ph&aacute;p, nhẹ nh&agrave;ng, nhưng vẫn cuốn h&uacute;t. Kh&ocirc;ng cần mix-match cầu kỳ, ch&uacute;ng c&oacute; thể diện ăn &yacute; với nhiều phong c&aacute;ch: từ vintage, cổ điển đến trẻ trung, thời thượng, c&aacute; t&iacute;nh...<br />\r\nM&ugrave;a đ&ocirc;ng năm nay, những m&agrave;u sắc trang nh&atilde; như n&acirc;u, kem, x&aacute;m... được nhiều người ưu &aacute;i khi chọn &aacute;o len vặn thừng. M&oacute;n đồ n&agrave;y được v&iacute; l&agrave; &quot;vạn năng&quot; v&igrave; dễ d&agrave;ng kết hợp với jeans ống rộng, ch&acirc;n v&aacute;y midi, ch&acirc;n v&aacute;y ngắn hay &aacute;o sơ mi.</p>\r\n\r\n<p><strong>&Aacute;o len l&ocirc;ng cừu</strong></p>\r\n\r\n<p><img alt=\"Lông cừu là chất liệu không lỗi mốt trong thời trang, được ưa chuộng nhờ khả năng giữ nhiệt tốt, ấm vượt trội hơn các loại len khác. Sợi len mềm mịn, độ đàn hồi cao nên thân thiện với làn da, vừa tôn dáng.Có thể phối áo len lông cừu với mọi kiểu chân váy, quần hay legging.\" src=\"https://i1-giaitri.vnecdn.net/2021/10/30/PR-LEN-04-9648-1635607446.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4BiovM-ibx-hz-3V2_Wq_g\" /></p>\r\n\r\n<p>L&ocirc;ng cừu l&agrave; chất liệu kh&ocirc;ng lỗi mốt trong thời trang, được ưa chuộng nhờ khả năng giữ nhiệt tốt, ấm vượt trội hơn c&aacute;c loại len kh&aacute;c. Sợi len mềm mịn, độ đ&agrave;n hồi cao n&ecirc;n th&acirc;n thiện với l&agrave;n da, vừa t&ocirc;n d&aacute;ng.<br />\r\nC&oacute; thể phối &aacute;o len l&ocirc;ng cừu với mọi kiểu ch&acirc;n v&aacute;y, quần hay legging.</p>\r\n\r\n<p><strong>&Aacute;o len cổ tr&ograve;n</strong></p>\r\n\r\n<p><img alt=\"Áo len cổ tròn kiểu dáng Hàn Quốc lên ngôi vào mỗi dịp thu đông. Kiểu áo này thông dụng nhất nhưng mỗi mùa lại có cách mix-match khác nhau. Đại diện Canifa cho hay: Kiểu áo len cổ tròn năng động có thể phối với hầu hết trang phục như quần jeans, chân váy dài, ngắn.\" src=\"https://i1-giaitri.vnecdn.net/2021/10/30/PR-LEN-01-9952-1635607446.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=HHFnYXbYR_G1J7glncNT3A\" /></p>\r\n\r\n<p>&Aacute;o len cổ tr&ograve;n kiểu d&aacute;ng H&agrave;n Quốc &quot;l&ecirc;n ng&ocirc;i&quot; v&agrave;o mỗi dịp thu đ&ocirc;ng. Kiểu &aacute;o n&agrave;y th&ocirc;ng dụng nhất nhưng mỗi m&ugrave;a lại c&oacute; c&aacute;ch mix-match kh&aacute;c nhau.<br />\r\nĐại diện Canifa cho hay: &quot;Kiểu &aacute;o len cổ tr&ograve;n năng động c&oacute; thể phối với hầu hết trang phục như quần jeans, ch&acirc;n v&aacute;y d&agrave;i, ngắn&quot;.</p>\r\n\r\n<p><strong>&Aacute;o len họa tiết</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Áo len họa tiết là món đồ không thể thiếu trong tủ đồ thu đông của các cô nàng cá tính. Hình khối, động vật cách điệu cùng màu sắc bắt mắt tạo điểm nhấn cho chiếc áo vốn giản đơn. Tham khảo loạt áo len Thu Đông 2021 đa mẫu mã, kiểu dáng, màu sắc tại Canifa.\" src=\"https://i1-giaitri.vnecdn.net/2021/10/30/PR-LEN-06-5655-1635607446.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=UnRpeBVn-zYD5_vWY1UimQ\" /></p>\r\n\r\n<p>&Aacute;o len họa tiết l&agrave; m&oacute;n đồ kh&ocirc;ng thể thiếu trong tủ đồ thu đ&ocirc;ng của c&aacute;c c&ocirc; n&agrave;ng c&aacute; t&iacute;nh. H&igrave;nh khối, động vật c&aacute;ch điệu c&ugrave;ng m&agrave;u sắc bắt mắt tạo điểm nhấn cho chiếc &aacute;o vốn giản đơn.<br />\r\nTham khảo loạt &aacute;o len Thu Đ&ocirc;ng 2021 đa mẫu m&atilde;, kiểu d&aacute;ng, m&agrave;u sắc tại&nbsp;<a href=\"https://canifa.com/nu/ao-len-nu.html?utm_source=bao&amp;utm_medium=vnexpress\" rel=\"nofollow\">Canifa</a>.</p>\r\n', 12, 4, 0, 1, 1),
(43, 'Hoa hậu Kỳ Duyên kiểm soát lượng calo khi ăn', 'Hoa-Hau-Ky-Duyen-Kiem-Soat-Luong-Calo-Khi-An', '<p>Hoa hậu Kỳ Duy&ecirc;n ăn thịt trắng, rau củ luộc v&agrave; c&aacute;c loại hạt kết hợp tập luyện, giảm 5 kg trong thời gian gi&atilde;n c&aacute;ch x&atilde; hội.</p>\r\n', 'giai-tri-9.jpg', '2021-11-02 00:02:08', 1, '<p><a href=\"https://vnexpress.net/hoa-hau-ky-duyen-tu-lam-my-pham-4348176.html\" rel=\"dofollow\" target=\"_blank\">Kỳ Duy&ecirc;n</a>&nbsp;cho biết tuy y&ecirc;u th&iacute;ch h&igrave;nh ảnh đầy đặn, gi&agrave;u sức sống, c&ocirc; vẫn muốn lấy lại v&oacute;c d&aacute;ng mảnh mai như trước. Sau khoảng một th&aacute;ng, c&ocirc; đ&atilde; giảm 5 kg, c&ograve;n 55 kg, l&agrave;n da cũng c&oacute; những cải thiện r&otilde; rệt. Hoa hậu kh&ocirc;ng d&ugrave;ng phương ph&aacute;p detox hay thuốc giảm c&acirc;n, chỉ điều chỉnh chế độ ăn uống c&ugrave;ng luyện tập.</p>\r\n\r\n<p>C&ocirc; kh&ocirc;ng ăn uống qu&aacute; ki&ecirc;ng khem m&agrave; ch&uacute; trọng c&acirc;n bằng dinh dưỡng. Nhờ ở nh&agrave; d&agrave;i ng&agrave;y v&agrave; tự nấu ăn, c&ocirc; kiểm so&aacute;t được h&agrave;m lượng calo v&agrave; chất b&eacute;o nạp v&agrave;o cơ thể. Trong hai tuần đầu ti&ecirc;n, c&ocirc; chỉ d&ugrave;ng hai nguồn đạm l&agrave; c&aacute; v&agrave; ức g&agrave;, sau đ&oacute; mới đổi sang c&aacute;c loại thịt trắng, hải sản kh&aacute;c như mực, t&ocirc;m, cua... Thỉnh thoảng, Kỳ Duy&ecirc;n d&ugrave;ng đậu hũ trắng v&agrave; trứng (hai quả một ng&agrave;y) để đổi khẩu vị. Người đẹp hạn chế d&ugrave;ng thịt đỏ (heo, b&ograve;) v&igrave; n&oacute; cung cấp lượng lớn calo, ảnh hưởng đến qu&aacute; tr&igrave;nh giảm c&acirc;n.</p>\r\n\r\n<p>Khi nấu, Kỳ Duy&ecirc;n ưu ti&ecirc;n c&aacute;c phương ph&aacute;p luộc, nướng giấy bạc hoặc d&ugrave;ng nồi chi&ecirc;n kh&ocirc;ng dầu để giảm tối đa chất b&eacute;o. C&aacute;c m&oacute;n y&ecirc;u th&iacute;ch của c&ocirc; l&agrave; c&aacute; saba nướng, c&aacute; hồi, c&aacute; ngừ chi&ecirc;n bằng nồi kh&ocirc;ng dầu hoặc &aacute;p chảo, chả c&aacute; chi&ecirc;n, bầu luộc... Những m&oacute;n n&agrave;y được chế biến tối giản, &iacute;t gia vị n&ecirc;n c&ocirc; thường d&ugrave;ng k&egrave;m với một &iacute;t nước chấm để ngon miệng hơn.</p>\r\n\r\n<p>Chất xơ, vitamin được c&ocirc; bổ sung qua c&aacute;c loại rau củ v&agrave; tr&aacute;i c&acirc;y. C&ocirc; chọn những loại tr&aacute;i c&acirc;y gi&agrave;u dinh dưỡng v&agrave; &iacute;t ngọt như t&aacute;o, kiwi, thanh long, t&aacute;o xanh... Ngo&agrave;i ra, trong nh&agrave; Kỳ Duy&ecirc;n lu&ocirc;n chuẩn bị sẵn c&aacute;c loại hạt, tr&aacute;i c&acirc;y kh&ocirc; để d&agrave;nh khi muốn ăn vặt. Theo chuy&ecirc;n trang sức khỏe&nbsp;<em>Healthline</em>, c&aacute;c loại hạt chứa h&agrave;m lượng cao vitamin, chất b&eacute;o c&oacute; lợi v&agrave; c&oacute; rất &iacute;t tinh bột. Ăn hạt thường xuy&ecirc;n kh&ocirc;ng chỉ hỗ trợ giảm c&acirc;n m&agrave; c&ograve;n gi&uacute;p cải thiện c&aacute;c vấn đề về tim mạch, đường huyết, đồng thời l&agrave;m chậm qu&aacute; tr&igrave;nh oxy h&oacute;a của tế b&agrave;o. Một số loại hạt kh&ocirc; phổ biến, c&oacute; thể dễ d&agrave;ng t&igrave;m mua l&agrave; hạnh nh&acirc;n, hạt dẻ, đậu phộng...</p>\r\n\r\n<p>B&ecirc;n cạnh ăn uống, Kỳ Duy&ecirc;n tập cardio bằng m&aacute;y chạy trong 45 ph&uacute;t để ti&ecirc;u hao mỡ thừa mỗi ng&agrave;y. C&ocirc; bắt đầu b&agrave;i tập bằng c&aacute;ch đi bộ ở mức 4.2 trong 3-5 ph&uacute;t để l&agrave;m n&oacute;ng người, sau đ&oacute; chạy từ 10-12 ph&uacute;t với tốc độ 8.0, nghỉ đi bộ 3-5 ph&uacute;t rồi tiếp tục chạy. C&aacute;ch l&agrave;m n&agrave;y gi&uacute;p duy tr&igrave; sức bền v&agrave; cơ thể ra mồ h&ocirc;i nhiều nhất c&oacute; thể. Theo c&ocirc;, tập chạy cardio nhanh v&agrave; đ&uacute;ng c&aacute;ch c&oacute; thể l&agrave;m ti&ecirc;u hao mỡ to&agrave;n cơ thể, gi&uacute;p thu gọn eo v&agrave; tay, ch&acirc;n. Kỳ Duy&ecirc;n cũng bổ sung c&aacute;c b&agrave;i tập cơ cho v&ugrave;ng eo, m&ocirc;ng, tập k&eacute;o tạ để duy tr&igrave; tỷ lệ cơ thể c&acirc;n đối.</p>\r\n\r\n<p>Người đẹp cho biết yếu tố h&agrave;ng đầu gi&uacute;p giảm c&acirc;n th&agrave;nh c&ocirc;ng l&agrave; sự ki&ecirc;n tr&igrave;. &quot;Trước đ&acirc;y, t&ocirc;i thường ăn vặt v&agrave; c&aacute;c m&oacute;n đậm vị n&ecirc;n dễ tăng c&acirc;n. Thay đổi hai th&oacute;i quen n&agrave;y kh&ocirc;ng dễ, nhưng t&ocirc;i phải quyết t&acirc;m để c&oacute; h&igrave;nh thể như mong muốn. Giảm c&acirc;n l&agrave; qu&aacute; tr&igrave;nh d&agrave;i, chỉ cần bu&ocirc;ng lỏng một ch&uacute;t cũng c&oacute; thể l&agrave;m uổng ph&iacute; những c&ocirc;ng sức ban đầu&quot;, c&ocirc; n&oacute;i.</p>\r\n', 13, 4, 0, 1, 1),
(44, 'CĐV đập phòng VAR vì thua trận', 'Cdv-Dap-Phong-Var-Vi-Thua-Tran', '<p>CĐV Gremio tr&agrave;n xuống s&acirc;n đập ph&aacute; ph&ograve;ng của tổ trọng t&agrave;i VAR, sau khi chứng kiến b&agrave;n thắng của đội nh&agrave; bị từ chối v&igrave; lỗi việt vị.</p>\r\n', 'bong-da-2.jpg', '2021-11-02 00:04:00', 1, '<p>Sự cố xảy ra sau trận Gremio gặp Palmeiras ở giải VĐQG Brazil h&ocirc;m 1/11. Khi đội chủ nh&agrave; đang bị dẫn 1-2 th&igrave; Elias s&uacute;t tung lưới Palmeiras. H&agrave;ng chục ngh&igrave;n CĐV Gremio h&ograve; reo ăn mừng. Mỗi điểm số với Gremio l&uacute;c n&agrave;y đều quan trọng v&igrave; họ đang ở nh&oacute;m đua trụ hạng. Tuy nhi&ecirc;n, sau v&agrave;i ph&uacute;t kiểm tra VAR, trọng t&agrave;i kh&ocirc;ng c&ocirc;ng nhận b&agrave;n gỡ của Elias. Gremio sau đ&oacute; thua th&ecirc;m một b&agrave;n v&agrave; rời s&acirc;n với thất bại 1-3.</p>\r\n\r\n<p>Tức giận với quyết định của VAR, h&agrave;ng trăm CĐV Gremio tr&agrave;n xuống s&acirc;n sau m&atilde;n cuộc. Họ băng qua r&agrave;o an ninh, lao v&agrave;o ph&ograve;ng VAR v&agrave; đập ph&aacute; c&aacute;c thiết bị. M&aacute;y quay v&agrave; m&aacute;y ảnh của ph&oacute;ng vi&ecirc;n cũng bị ph&aacute; hủy. Nhiều cầu thủ Gremio sợ h&atilde;i trốn v&agrave;o đường hầm v&agrave; ph&ograve;ng thay đồ.</p>\r\n\r\n<p>Vụ bạo loạn chỉ dừng lại khi cảnh s&aacute;t d&ugrave;ng hơi cay v&agrave; d&ugrave;i cui trấn &aacute;p những kẻ qu&aacute; kh&iacute;ch. Tổng kiểm s&aacute;t vi&ecirc;n của T&ograve;a &aacute;n C&ocirc;ng l&yacute; Thể thao Tối cao Brazil cho biết Gremio c&oacute; thể bị phạt 24.000 USD v&agrave; cấm kh&aacute;n giả tới s&acirc;n trong phần c&ograve;n lại của giải VĐQG. Gremio th&ocirc;ng b&aacute;o phản đối bạo lực v&agrave; cam kết hợp t&aacute;c với cảnh s&aacute;t trong vụ điều tra.</p>\r\n\r\n<p>Gremio đang sở hữu hai cựu ng&ocirc;i sao Bayern l&agrave; Douglas Costa v&agrave; Rafinha. Họ đứng &aacute;p ch&oacute;t bảng điểm sau 27 trận, k&eacute;m nh&oacute;m an to&agrave;n bảy điểm. Gremio c&ograve;n 10 trận, gồm bốn trận s&acirc;n nh&agrave;, để thực hiện mục ti&ecirc;u trụ hạng.</p>\r\n', 14, 5, 0, 1, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(45, 'Ba lần chiến thắng tử thần của cô bé mê diễn xuất', 'Ba-Lan-Chien-Thang-Tu-Than-Cua-Co-Be-Me-Dien-Xuat', '<p>&#39;Mẹ ơi, con kh&oacute; thở qu&aacute;&#39;, b&eacute; An th&igrave; th&agrave;o rồi ngất v&agrave;o một s&aacute;ng m&ugrave;a h&egrave; 2018.</p>\r\n', 'doi-song.jpg', '2021-11-02 00:05:40', 1, '<p>H&ocirc;m đ&oacute; em c&oacute; một lịch quay quảng c&aacute;o cho h&atilde;ng b&aacute;nh p&iacute;a.</p>\r\n\r\n<p>Một ng&agrave;y trước buổi s&aacute;ng định mệnh, mẹ vừa cho An đi kh&aacute;m h&ocirc; hấp v&agrave; được kết luận tương tự lần kh&aacute;m v&agrave;i ng&agrave;y trước. &quot;Đến đ&ecirc;m con thở c&oacute; tiếng r&iacute;t như t&agrave;u chạy, t&ocirc;i chủ quan nghĩ mới kh&aacute;m, mới thay đơn thuốc n&ecirc;n cố đợi trời s&aacute;ng xem sao&quot;, chị Cao Th&uacute;y Hạnh, ở quận G&ograve; Vấp, mẹ b&eacute; kể.</p>\r\n\r\n<p>An vốn chậm tăng c&acirc;n, thi thoảng chảy m&aacute;u cam. Chị Hạnh b&aacute;n qu&aacute;n ăn tại nh&agrave; n&ecirc;n c&oacute; thời gian chăm s&oacute;c con. Đ&atilde; v&agrave;i lần đi kh&aacute;m, b&eacute; lu&ocirc;n được chẩn đo&aacute;n bị ni&ecirc;m mạc mũi mỏng, kh&ocirc;ng c&oacute; g&igrave; nguy hiểm cho tới khi Bệnh viện Nhi đồng 2 ph&aacute;t hiện An bị tr&agrave;n dịch m&agrave;ng phổi, c&oacute; một u Lympho ch&egrave;n &eacute;p tim g&acirc;y kh&oacute; thở, ti&ecirc;n lượng xấu.</p>\r\n\r\n<p>Chị Hạnh kh&ocirc;ng c&ograve;n b&igrave;nh tĩnh nữa. Trong chị l&agrave; nỗi &acirc;n hận v&agrave; day dứt &quot;gi&aacute; như đưa con đi viện sớm, gi&aacute; như t&igrave;m đ&uacute;ng nơi chẩn bệnh cho con&quot;.</p>\r\n\r\n<p><img alt=\"Trước khi phát hiện bệnh, bé An là một diễn viên nhí từ năm 3 tuổi và đã đóng hàng chục quảng cáo. Trong hình là An ở..., lúc. ... tuổi. Ảnh: Gia đình cung cấp\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/a3-3530-1635464228.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0_tr0WlhChqck5fFf0qOZw\" /></p>\r\n\r\n<p>Trước khi ph&aacute;t hiện bệnh, b&eacute; An l&agrave; một diễn vi&ecirc;n nh&iacute; từ năm 3 tuổi v&agrave; đ&atilde; đ&oacute;ng h&agrave;ng chục quảng c&aacute;o. Trong h&igrave;nh l&agrave; An l&uacute;c 7 tuổi. Ảnh:&nbsp;<em>Gia đ&igrave;nh cung cấp</em></p>\r\n\r\n<p>Hay tin dữ, bố b&eacute;, anh Nguyễn Văn Tuấn v&agrave; con trai đang ở Hải Ph&ograve;ng đặt v&eacute; về TP HCM rồi chạy thẳng v&agrave;o viện. Bốn đ&ecirc;m, cha mẹ ngồi chờ ngo&agrave;i ph&ograve;ng cấp cứu. Đến ng&agrave;y thứ năm, b&eacute; An được chuyển ra hồi sức. L&uacute;c n&agrave;y, người l&agrave;m cha mẹ mới cảm thấy &quot;thở được&quot;.</p>\r\n\r\n<p>Một tuần sau sức khỏe ổn hơn, c&ocirc; b&eacute; 6 tuổi ch&iacute;nh thức bước v&agrave;o ph&aacute;c đồ điều trị ung thư m&aacute;u. Anh Tuấn, chị Hạnh cắm chốt trong bệnh viện. Một th&aacute;ng đầu An v&agrave;o h&oacute;a chất, t&acirc;m trạng cả nh&agrave; căng như d&acirc;y d&agrave;n theo những cơn sốt cao, đau đầu, n&ocirc;n mửa, ti&ecirc;u chảy, tụt hồng cầu v&agrave; phải truyền m&aacute;u li&ecirc;n tục của con.</p>\r\n\r\n<p>Mờ s&aacute;ng một ng&agrave;y cuối th&aacute;ng 7, chị Hạnh vừa thiếp đi sau ba đ&ecirc;m canh con sốt, bỗng cho&agrave;ng tỉnh v&igrave; An giật đ&ugrave;ng đ&ugrave;ng. Người mẹ hoảng hốt bế con lao đi t&igrave;m b&aacute;c sĩ, một người nh&agrave; bệnh nh&acirc;n giường b&ecirc;n cạnh cầm c&acirc;y chuyền chạy theo, chẳng ai d&eacute;p guốc g&igrave;.</p>\r\n\r\n<p>B&aacute;c sĩ một mặt động vi&ecirc;n gia đ&igrave;nh &quot;kh&ocirc;ng sao đ&acirc;u&quot;, nhưng quay đi, dồn dập chỉ đạo m&aacute;y m&oacute;c v&agrave; thuốc. Chị Hạnh đứng nh&igrave;n bất lực, rơi v&agrave;o nỗi bất an tột c&ugrave;ng.</p>\r\n\r\n<p>Một lần nữa An được cấp cứu kịp thời. Nh&igrave;n tấm phim kết quả chụp CT n&atilde;o, b&aacute;c sĩ mỉm cười n&oacute;i may mắn trong đầu b&eacute; kh&ocirc;ng c&oacute; m&aacute;u tụ. Gi&acirc;y ph&uacute;t ấy, chị Hạnh d&ugrave; kh&ocirc;ng hiểu r&otilde; được t&igrave;nh thế ng&agrave;n c&acirc;n treo sợi t&oacute;c, cũng cảm thấy &quot;tr&uacute;t được cả tấn đ&aacute; khỏi người&quot;.</p>\r\n\r\n<p>Lần th&oacute;t tim n&agrave;y chưa qua th&igrave; những đau đớn kh&aacute;c ập đến. Chưa đầy một th&aacute;ng sau, c&ocirc; b&eacute; bị phản ứng phụ với một loại thuốc g&acirc;y ra vi&ecirc;m tụy cấp. Em được chỉ định truyền dịch, nhịn ăn, nhịn uống 20 ng&agrave;y để tụy ho&agrave;n to&agrave;n nghỉ ngơi.</p>\r\n\r\n<p>Điều n&agrave;y qu&aacute; sức chịu đựng của một đứa trẻ. Được v&agrave;i ng&agrave;y, An kh&aacute;t nước kh&ocirc;ng chịu nổi, n&agrave;i nỉ ba mẹ &quot;cho con một giọt nước th&ocirc;i&quot;. Bố v&agrave; mẹ chỉ biết ngoảnh mặt quay đi, mắt đỏ hoe v&igrave; kh&ocirc;ng thể đ&aacute;p ứng con. Họ d&ugrave;ng khăn ướt chấm l&ecirc;n đ&ocirc;i m&ocirc;i kh&ocirc; nứt nẻ.</p>\r\n\r\n<p>Những ng&agrave;y đau đớn cực điểm, b&aacute;c sĩ buộc phải cho c&ocirc; b&eacute; d&ugrave;ng morphine. Thuốc v&agrave;o người kh&aacute;c c&oacute; thể giảm đau được 4-6 tiếng, nhưng v&agrave;o An chỉ được 2 tiếng. Nhiều l&uacute;c con đau qu&aacute;, bố b&ecirc;n tr&aacute;i, mẹ b&ecirc;n phải, cầm tay con tỉ t&ecirc; đủ thứ chuyện để qu&ecirc;n đi thực tại. &quot;Con đau đến mức chỉ c&oacute; tinh thần mới chịu đựng được. Nhưng trong t&igrave;nh huống ấy, ch&iacute;nh con động vi&ecirc;n lại ch&uacute;ng t&ocirc;i&quot;, chị Hạnh n&oacute;i.</p>\r\n\r\n<p><img alt=\"Bé An gửi thư động viên y bác sĩ tại khoa Ung thư Nhi, Bệnh viện Nhi đồng 2 kiên cường và bình an trong dịch bệnh, thông qua cuộc thi Vì một Việt Nam tất thắng. Con học được con chữ, viết được câu là nhờ 3 năm qua mẹ dạy. Ảnh: Vì một Việt Nam tất thắng\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/anh4-2168-1635464228.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=gWTFOGHEdnunYivh6qDpbA\" /></p>\r\n\r\n<p>Th&ocirc;ng qua cuộc thi V&igrave; một Việt Nam tất thắng, b&eacute; An gửi thư động vi&ecirc;n y b&aacute;c sĩ tại khoa Ung bướu - Huyết học, Bệnh viện Nhi đồng 2 ki&ecirc;n cường v&agrave; b&igrave;nh an trong dịch bệnh hồi th&aacute;ng 8/2021. Ảnh:&nbsp;<em>V&igrave; một Việt Nam tất thắng</em></p>\r\n\r\n<p>Đến ng&agrave;y thứ 16, c&ocirc; b&eacute; được tập h&uacute;p nước đường, dần sau đ&oacute; l&agrave; h&uacute;p ch&aacute;o lo&atilde;ng, ch&aacute;o đặc hơn. Cuối c&ugrave;ng em bước qua được cơn nguy kịch.</p>\r\n\r\n<p>&quot;Sau đ&oacute; vẫn c&ograve;n v&agrave;i lần con bị phản ứng một một loại thuốc hạ sốt, uống v&agrave;o l&agrave; người run bần bật như cỗ m&aacute;y, m&ocirc;i, mắt, miệng t&iacute;m bầm&quot;, chị Hạnh chia sẻ.</p>\r\n\r\n<p>Con g&aacute;i b&eacute; nhỏ bị bệnh hiểm ngh&egrave;o l&agrave; c&uacute; sốc qu&aacute; lớn với gia đ&igrave;nh. Anh Tuấn nghỉ lu&ocirc;n c&ocirc;ng việc tại một c&ocirc;ng ty xăng dầu của nước ngo&agrave;i để ở viện với con. Em trai chị Hạnh v&agrave; mẹ từ Nghệ An v&agrave;o S&agrave;i G&ograve;n, ng&agrave;y ng&agrave;y lo cơm nước, tiếp tế v&ograve;ng ngo&agrave;i. Cả gia đ&igrave;nh dồn to&agrave;n lực chăm s&oacute;c An. Đến cuối năm 2018, c&ocirc; b&eacute; vượt qua được lộ tr&igrave;nh điều trị đầu ti&ecirc;n.</p>\r\n\r\n<p>Sang năm tiếp theo, mỗi th&aacute;ng một lần An v&agrave;o viện truyền h&oacute;a chất v&agrave; mang thuốc về uống. Trong tiến tr&igrave;nh, đ&ocirc;i l&uacute;c con vẫn dị ứng, sốt cao, tụt hồng cầu... Hiện, con đ&atilde; điều trị xong ph&aacute;c đồ, cứ 3 th&aacute;ng một lần đến viện theo d&otilde;i.</p>\r\n\r\n<p>B&aacute;c sĩ Nguyễn Đ&igrave;nh Văn, trưởng khoa Ung bướu - Huyết học, Bệnh viện Nhi đồng 2, vẫn ấn tượng với h&agrave;nh tr&igrave;nh chữa bệnh gian nan m&agrave; ki&ecirc;n cường của c&ocirc; b&eacute;. &quot;Con được đưa v&agrave;o với ch&uacute;ng t&ocirc;i trong t&igrave;nh trạng đang đặt ống để thở. Trải qua rất nhiều trục trặc tr&ecirc;n lộ tr&igrave;nh, con đ&atilde; vượt qua v&agrave; về cơ bản đ&aacute;p ứng thuốc tốt v&agrave; nhanh hơn một số bạn&quot;, b&aacute;c sĩ chia sẻ.</p>\r\n\r\n<p><img alt=\"Bé An, 9 tuổi đang học online lớp Một. Vì phải chữa bệnh, em học kém các bạn mất 2 năm. Ảnh: Gia đình cung cấp\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/a1-1366-1635464229.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-ELK3u8nGJowmifgxBru_A\" /></p>\r\n\r\n<p>B&eacute; An, 9 tuổi đang học online lớp Một. V&igrave; phải chữa bệnh, em học chậm 2 năm so với c&aacute;c bạn c&ugrave;ng tuổi. Ảnh:&nbsp;<em>Gia đ&igrave;nh cung cấp</em></p>\r\n\r\n<p>Ph&aacute;t hiện ung thư ngay trước khi v&agrave;o lớp 1, b&eacute; An mất 3 năm để chữa trị. Đầu thu năm nay, gia đ&igrave;nh quyết định đăng k&yacute; cho con v&agrave;o học tiểu học. Hiện tại An học online n&ecirc;n tham gia lớp học đầy đủ. Nếu sắp tới phải đi học ở trường, chưa chắc b&eacute; c&oacute; thể đi được. B&aacute;c sĩ Văn cũng khuyến c&aacute;o n&ecirc;n cho con ti&ecirc;m đầy đủ vaccine, trước khi đi học.</p>\r\n\r\n<p>Qu&aacute; tr&igrave;nh chữa trị cũng ngăn cản con đường nghệ thuật của An. Năm 3 tuổi, c&ocirc; b&eacute; đ&atilde; được ph&aacute;t hiện c&oacute; năng khiếu diễn xuất. Chị Hạnh tạo cơ hội cho con ph&aacute;t triển bằng c&aacute;ch t&igrave;m c&aacute;c c&ocirc;ng ty quảng c&aacute;o, người mẫu nh&iacute; v&agrave; đăng k&yacute; cho b&eacute; thử vai. Từ 4 tuổi, con được gọi đi quay li&ecirc;n tục. V&agrave;i ng&agrave;y trước l&uacute;c ph&aacute;t bệnh, em k&yacute; được hợp đồng với một k&ecirc;nh YouTube đồ chơi tại Mỹ.</p>\r\n\r\n<p>Sau n&agrave;y trong qu&aacute; tr&igrave;nh điều trị c&oacute; rất nhiều lời mời đi diễn nhưng đều phải từ chối. M&atilde;i sang năm điều trị thứ hai, đ&ocirc;i khi tranh thủ sức khỏe cho ph&eacute;p, mẹ mới cho An đi quay cho con đỡ nhớ nghề. Trước khi Covid-19 b&ugrave;ng ph&aacute;t, An cũng quay v&agrave;i video li&ecirc;n quan đến ph&ograve;ng chống Covid-19.</p>\r\n\r\n<p>C&ocirc; b&eacute; 9 tuổi kể, c&oacute; những lần phải quay sớm phải dậy từ 5h s&aacute;ng nhưng con rất h&agrave;o hứng. &quot;Một lần đi chụp cho h&atilde;ng thuốc giảm đau, con được gặp lại 3 người bạn từng l&agrave;m việc với nhau trước đ&oacute; n&ecirc;n rất vui. V&igrave; con tập trung nghe b&aacute;c đạo diễn hướng dẫn n&ecirc;n quay shot h&igrave;nh n&agrave;o l&agrave; được lu&ocirc;n. Mọi người khen, con vui lắm&quot;, em n&oacute;i.</p>\r\n\r\n<p>An đam m&ecirc; diễn tới nỗi v&igrave; dịch bệnh kh&ocirc;ng đi quay được, em vẫn tự l&agrave;m video đăng l&ecirc;n TikTok v&agrave; Instagram. Mỗi lần d&ugrave; c&oacute; ai hỏi đến ước mơ, c&ocirc; b&eacute; xinh xắn lu&ocirc;n c&oacute; một c&acirc;u trả lời: Con muốn l&agrave;m một diễn vi&ecirc;n nổi tiếng.</p>\r\n', 18, 7, 4, 1, 1),
(46, 'Cho người già chơi trò con nít để bớt cô đơn', 'Cho-Nguoi-Gia-Choi-Tro-Con-Nit-De-Bot-Co-Don', '<p>Trong trung t&acirc;m chăm s&oacute;c người cao tuổi, t&aacute;m m&aacute;i đầu bạc chụm quanh một chiếc b&agrave;n, mỗi người &ocirc;m chặt một đoạn ống nhựa.</p>\r\n', 'doi-song-1.jpg', '2021-11-02 00:06:35', 1, '<p>&quot;C&aacute;c b&aacute;c chuyền b&oacute;ng từ ống nhựa của m&igrave;nh sang người b&ecirc;n cạnh n&agrave;o. Ai kh&ocirc;ng bắt v&agrave; kh&ocirc;ng chuyền b&oacute;ng sẽ bị phạt đấy ạ&quot;, nữ nh&acirc;n vi&ecirc;n hướng dẫn nh&oacute;m, cười n&oacute;i.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; một trong nhiều hoạt động c&aacute;c nh&acirc;n vi&ecirc;n trung t&acirc;m chăm s&oacute;c người cao tuổi t&iacute;ch hợp Huayang (ở Thượng Hải) tổ chức cho&nbsp;<a href=\"https://vnexpress.net/nguoi-gia-nhat-ban-co-tinh-vao-tu-vi-co-don-va-ngheo-doi-3879228.html\" rel=\"dofollow\" target=\"_blank\">người cao tuổi</a>&nbsp;trong khu phố. Căn ph&ograve;ng c&oacute; vẻ giống đang tổ chức tiệc cho một đứa trẻ: c&oacute; dụng cụ vẽ tranh c&aacute;t, bộ tr&ograve; chơi n&eacute;m v&ograve;ng, chồng thẻ luyện tr&iacute; nhớ. Nhưng c&aacute;c tr&ograve; vui n&agrave;y đều c&oacute; mục ti&ecirc;u nghi&ecirc;m t&uacute;c l&agrave; tăng cường sức khỏe tinh thần v&agrave; thể chất cho hội vi&ecirc;n cao tuổi.</p>\r\n\r\n<p><img alt=\"Các cụ già chơi trò chuyền bóng tại trung tâm Huayang. Ảnh: Sixthtone\" src=\"https://i1-giadinh.vnecdn.net/2021/11/01/1-9977-1635752789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bSxHAfiVQICsDil8cW41Jg\" /></p>\r\n\r\n<p>C&aacute;c cụ gi&agrave; chơi tr&ograve; chuyền b&oacute;ng qua ống nhựa tại trung t&acirc;m Huayang. Ảnh:&nbsp;<em>Sixthtone</em></p>\r\n\r\n<p>Shi Junjie, gi&aacute;m đốc cơ sở của Huayang ở quận Trường Ninh, Thượng Hải cho biết: &quot;Người cao tuổi c&ocirc; đơn khi phải ở nh&agrave; một m&igrave;nh. Tương t&aacute;c với nh&acirc;n vi&ecirc;n v&agrave; những người cao tuổi kh&aacute;c khiến họ rất vui&quot;. Huayang l&agrave; một trong nhiều tổ chức của Trung Quốc đang g&oacute;p phần giải quyết cuộc khủng hoảng c&ocirc; đơn, tạo niềm vui cho người gi&agrave;.</p>\r\n\r\n<p><a href=\"https://vnexpress.net/nguoi-gia-co-don-3798019.html\" rel=\"dofollow\" target=\"_blank\">C&ocirc; đơn</a>&nbsp;l&agrave; vấn đề lớn của người gi&agrave; tr&ecirc;n khắp thế giới v&agrave; đặc biệt nghi&ecirc;m trọng ở Trung Quốc. Nhiều thập kỷ di cư trong nước với quy m&ocirc; lớn (di chuyển khắp nơi để chăm s&oacute;c con ch&aacute;u) khiến h&agrave;ng triệu người về hưu trở th&agrave;nh &quot;người gi&agrave; tr&ocirc;i dạt&quot;. Nhiều người phải vật lộn để th&iacute;ch nghi với cuộc sống mới ở th&agrave;nh phố, nơi họ thường kh&ocirc;ng quen biết ai ngo&agrave;i con.</p>\r\n\r\n<p>Theo c&aacute;c nghi&ecirc;n cứu, nỗi c&ocirc; đơn ảnh hưởng đến sức khỏe tinh thần, l&agrave; t&aacute;c nh&acirc;n của lo lắng v&agrave; trầm cảm, g&oacute;p phần suy giảm nhận thức v&agrave; g&acirc;y tử vong sớm. Nhận thức được mối nguy, ng&agrave;nh c&ocirc;ng nghiệp chăm s&oacute;c Trung Quốc đẩy mạnh hơn cung cấp đồ chơi v&agrave; tr&ograve; chơi cho người gi&agrave;.</p>\r\n\r\n<p>Họ kỳ vọng chơi nhiều gi&uacute;p người về hưu vui vẻ v&agrave; dễ kết bạn hơn. Con c&aacute;i cũng bắt đầu &yacute; thức được điều n&agrave;y, n&ecirc;n mua đồ chơi cho cha mẹ. Tuy nhi&ecirc;n, điều người cao tuổi cần kh&ocirc;ng phải l&agrave; &quot;n&eacute;m cho họ t&uacute;i đồ chơi&quot;, m&agrave; cần sự đồng h&agrave;nh.</p>\r\n\r\n<p>Wang Fuqing, một th&agrave;nh vi&ecirc;n hội L&atilde;o khoa Trung Quốc, cho biết tổ chức n&agrave;y đang khuyến kh&iacute;ch c&aacute;c cơ sở chăm s&oacute;c người cao tuổi tr&ecirc;n to&agrave;n quốc thử nghiệm buổi chơi đ&ugrave;a. &Ocirc;ng ủng hộ mạnh mẽ &yacute; tưởng n&agrave;y sau cuộc thử nghiệm tại viện dưỡng l&atilde;o ở Bắc Kinh.</p>\r\n\r\n<p>Nơi đ&acirc;y tổ chức hai buổi vui chơi cho người gi&agrave; mỗi tuần, mỗi buổi khoảng hai giờ. Tỷ lệ người tham dự l&agrave; 98%. Sau ba th&aacute;ng tham gia, nh&acirc;n vi&ecirc;n viện dưỡng l&atilde;o thấy c&aacute;c th&agrave;nh vi&ecirc;n cao tuổi cải thiện r&otilde; rệt khả năng nhận thức v&agrave; cảm x&uacute;c. &quot;Một số người n&oacute;i v&igrave; được chơi tr&ograve; chơi m&agrave; họ sẵn l&ograve;ng ra hỏi ph&ograve;ng v&agrave; l&agrave;m quen với người kh&aacute;c. Tr&iacute; nhớ của họ cũng được cải thiện đ&aacute;ng kể&quot;, Wang Fuqing n&oacute;i.</p>\r\n\r\n<p>Tại Huayang, hoạt động y&ecirc;u th&iacute;ch của c&aacute;c cao ni&ecirc;n l&agrave; vẽ tranh c&aacute;t v&agrave; t&ocirc; m&agrave;u. Ngo&agrave;i ra, c&aacute;c nh&acirc;n vi&ecirc;n b&agrave;y th&ecirc;m hoạt động thể chất kh&aacute;c như tung v&ograve;ng để c&aacute;c cụ tập luyện. &quot;Người lớn tuổi kh&ocirc;ng thể dục sẽ nhanh gi&agrave;. Đồ chơi v&agrave; c&aacute;c tr&ograve; chơi c&oacute; thể tr&igrave; ho&atilde;n qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a&quot;, Shi Junjie n&oacute;i.</p>\r\n\r\n<p><img alt=\"Zhong Zhixiang (giữa) chơi trò ném vòng tại Trung tâm chăm sóc người cao tuổi tích hợp Huayang, Thượng Hải, ngày 20 tháng 8 năm 2021. Ảnh: Sixthtone\" src=\"https://i1-giadinh.vnecdn.net/2021/11/01/1-9874-1635752789.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Vvxsw8gX8qa8gwrH8EKHQw\" /></p>\r\n\r\n<p>Zhong Zhixiang (giữa) chơi tr&ograve; n&eacute;m v&ograve;ng tại Trung t&acirc;m chăm s&oacute;c người cao tuổi t&iacute;ch hợp Huayang, Thượng Hải, ng&agrave;y 20 th&aacute;ng 8 năm 2021. Ảnh:<em>&nbsp;Sixthtone</em></p>\r\n\r\n<p>Trong tr&ograve; chơi bắt b&oacute;ng gi&uacute;p kết bạn, &ocirc;ng Zhong Zhixiang, 98 tuổi, l&agrave;m rơi quả b&oacute;ng khỏi ống nhựa n&ecirc;n bị phạt h&aacute;t một b&agrave;i. &Ocirc;ng đứng khỏi ghế, h&aacute;t một giai điệu bằng tiếng Anh.</p>\r\n\r\n<p>&Ocirc;ng cụ l&agrave; c&ocirc;ng nh&acirc;n về hưu cho biết, rất th&iacute;ch vui chơi ở Huayang v&agrave; m&ecirc; nhất tr&ograve; tung v&ograve;ng. Tiếc nuối của &ocirc;ng l&agrave; trung t&acirc;m kh&ocirc;ng c&oacute; tr&ograve; b&uacute;ng bi, đ&oacute; l&agrave; tr&ograve; &ocirc;ng cụ y&ecirc;u th&iacute;ch ng&agrave;y b&eacute;.</p>\r\n\r\n<p>B&ecirc;n cạnh những người mở l&ograve;ng với c&aacute;c &quot;tr&ograve; trẻ con&quot; d&agrave;nh cho người gi&agrave;, nhiều người kh&aacute;c cho rằng người cao tuổi chỉ n&ecirc;n gắn b&oacute; với hoạt động nghi&ecirc;m t&uacute;c như nấu ăn, chăm trẻ v&agrave; viết thư ph&aacute;p.</p>\r\n\r\n<p>Những định kiến n&agrave;y khiến thị trường đồ chơi cho người gi&agrave; d&ugrave; c&oacute; tiềm năng lớn vẫn chỉ ở giai đoạn sơ khai. Hiện tại, thị trường nội địa chưa c&oacute; c&ocirc;ng ty hay tổ chức n&agrave;o của Trung Quốc chuy&ecirc;n sản xuất đồ chơi cho người cao tuổi. Trong khi hiện tại, đ&acirc;y l&agrave; quốc gia c&oacute; hơn 260 triệu người từ 60 tuổi trở l&ecirc;n, dự kiến năm 2040 sẽ vượt 400 triệu.</p>\r\n\r\n<p>V&igrave; hiếm đồ chơi chuy&ecirc;n dụng cho người gi&agrave;, một số cơ sở đ&agrave;nh phải tự sản xuất thủ c&ocirc;ng.</p>\r\n', 18, 7, 0, 1, 1),
(47, 'Bí quyết cường thịnh của gia tộc giàu nhất thế giới', 'Bi-Quyet-Cuong-Thinh-Cua-Gia-Toc-Giau-Nhat-The-Gioi', '<p>Từ đời &ocirc;ng, cha đến đời ch&aacute;u, gia đ&igrave;nh Walson, sở hữu đế chế b&aacute;n lẻ Walmart, lu&ocirc;n nằm trong danh s&aacute;ch những gia tộc gi&agrave;u nhất thế giới.</p>\r\n', 'doi-song-2.jpg', '2021-11-02 00:07:33', 1, '<p>Doanh nghiệp gia đ&igrave;nh c&oacute; thể tồn tại bao thế hệ? Sở Thuế vụ Mỹ đ&atilde; r&uacute;t ra quy luật &quot;kh&ocirc;ng gi&agrave;u qu&aacute; ba đời&quot; qua theo d&otilde;i v&agrave; nghi&ecirc;n cứu 400 người nộp thuế cao nhất từ năm 1992 đến 2000, chỉ 13% c&oacute; mặt trong danh s&aacute;ch n&agrave;y nhiều hơn 2 năm.</p>\r\n\r\n<p>Nh&agrave; kinh tế học người Mỹ Gregory Clark, Đại học California, tin rằng một nửa số con c&aacute;i của c&aacute;c gia đ&igrave;nh thuộc tầng lớp tr&ecirc;n, cuối c&ugrave;ng sẽ bước xuống tầng lớp x&atilde; hội thấp hơn.</p>\r\n\r\n<p>Nhưng c&oacute; vẻ&nbsp;<a href=\"https://vnexpress.net/tag/gia-toc-99477\" rel=\"dofollow\">gia tộc&nbsp;</a>Walton, những người đứng sau chuỗi si&ecirc;u thị b&aacute;n lẻ Walmart đ&atilde; &quot;tr&aacute;nh được lời nguyền&quot;. Từ thế hệ Sam Walton vay bố vợ 20.000 USD để mở cửa h&agrave;ng tạp h&oacute;a đầu ti&ecirc;n v&agrave;o năm 1951, đến khi trở th&agrave;nh gia tộc gi&agrave;u nhất thế giới năm 2019, gia đ&igrave;nh Walton đ&atilde; trải qua ba thế hệ li&ecirc;n tục ph&aacute;t triển.</p>\r\n\r\n<p><img alt=\"Vợ chồng Sam, Helen và 4 con. Ảnh: Bloomberg\" src=\"https://i1-giadinh.vnecdn.net/2021/11/01/1200x-1-7583-1635783158.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VG9B4cQmBqcRdejd4c4z7A\" /></p>\r\n\r\n<p>Vợ chồng Sam, Helen v&agrave; 4 con Rob, John, Jim, Alice. Ảnh:&nbsp;<em>Bloomberg</em></p>\r\n\r\n<p>Trong danh s&aacute;ch 25 gia tộc gi&agrave;u nhất thế giới năm 2021 m&agrave;&nbsp;<em>Bloomberg&nbsp;</em>vừa c&ocirc;ng bố, Walton vẫn giữ vị tr&iacute; đầu bảng. Trong 12 th&aacute;ng qua, họ kiếm được 23 tỷ USD, n&acirc;ng tổng t&agrave;i sản l&ecirc;n 238 tỷ USD, nhiều hơn gần 100 tỷ USD so với gia tộc gi&agrave;u thứ hai thế giới l&agrave; Mars (141,9 tỷ USD).</p>\r\n\r\n<p>C&acirc;u chuyện về Walmart bắt nguồn từ người s&aacute;ng lập Sam Walton. &Ocirc;ng sinh ra tại một thị trấn nhỏ ở bang Oklahoma v&agrave;o năm 1918. &Ocirc;ng Sam đ&atilde; l&agrave;m nhiều c&ocirc;ng việc từ khi c&ograve;n l&agrave; một đứa trẻ như nu&ocirc;i thỏ, vắt sữa b&ograve;, giao b&aacute;o v&agrave; bồi b&agrave;n.</p>\r\n\r\n<p>Trước khi kết h&ocirc;n với b&agrave; Helen Robson v&agrave;o năm 1942, &ocirc;ng Sam từng l&agrave; sinh vi&ecirc;n xuất sắc ng&agrave;nh kinh tế tại Đại học Missouri. Những năm 1950, &ocirc;ng để &yacute; hai tập đo&agrave;n b&aacute;n lẻ khổng lồ Kmart v&agrave; Sears sở hữu hầu hết hệ thống cửa h&agrave;ng b&aacute;n lẻ tại Arkansas v&agrave; c&aacute;c bang l&acirc;n cận nhưng kh&ocirc;ng hề xuất hiện ở c&aacute;c thị trấn nhỏ b&eacute; v&agrave; hẻo l&aacute;nh như qu&ecirc; m&igrave;nh. &Ocirc;ng đ&atilde; vay tiền bố vợ, c&ugrave;ng khoản tiết kiệm của m&igrave;nh, thu&ecirc; 8 c&ocirc;ng nh&acirc;n v&agrave; th&agrave;nh lập cửa h&agrave;ng b&aacute;n lẻ đầu ti&ecirc;n lấy t&ecirc;n l&agrave; Walmart, ngay tại thị trấn Bentonville qu&ecirc; hương v&agrave;o năm 1962.</p>\r\n\r\n<p>Theo&nbsp;<em>New York Times</em>, đến năm 1991, chuỗi si&ecirc;u thị của &ocirc;ng đ&atilde; vượt qua Sears, Roebuck &amp; Company để trở th&agrave;nh nh&agrave; b&aacute;n lẻ lớn nhất quốc gia. V&agrave;o ng&agrave;y 1/4/1991, Walmart c&oacute; 1.735 cửa h&agrave;ng ở 42 tiểu bang, cũng như 2 cửa h&agrave;ng ở Mexico.</p>\r\n\r\n<p>Th&agrave;nh c&ocirc;ng của Walmart c&oacute; thể đ&uacute;c kết ở c&aacute;c ti&ecirc;u ch&iacute;: hạ gi&aacute; b&aacute;n bằng c&aacute;ch mua số lượng lớn; kh&aacute;ch h&agrave;ng l&agrave; thượng đế; mở ở c&aacute;c thị trấn nhỏ; kịp thời ủy quyền cho c&aacute;c nh&agrave; quản l&yacute; doanh nghiệp...</p>\r\n\r\n<p>Bản th&acirc;n Sam l&agrave; một người tham c&ocirc;ng tiếc việc. &Ocirc;ng dậy từ 4h30 s&aacute;ng mỗi ng&agrave;y để l&agrave;m việc. Ngay cả khi gia đ&igrave;nh đi nghỉ, &ocirc;ng cũng tranh thủ &quot;vi h&agrave;nh&quot; c&aacute;c cửa h&agrave;ng địa phương của m&igrave;nh.</p>\r\n\r\n<p><img alt=\"Ba người con Jim, Alice và Rob Walton của gia tộc Walton (từ trái qua). Ảnh: Walton Family Foundation\" src=\"https://i1-giadinh.vnecdn.net/2021/11/01/5c0852054598b50e7917be05-jpeg-6629-1635783158.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-cPCbnxkGfghoYhvqnbbFg\" /></p>\r\n\r\n<p>Ba người con Jim, Alice v&agrave; Rob Walton của gia tộc Walton (từ tr&aacute;i qua). Ảnh:&nbsp;<em>Walton Family Foundation</em></p>\r\n\r\n<p>Sự cường thịnh của gia tộc Walton li&ecirc;n quan rất nhiều đến gi&aacute; trị của vợ chồng Sam v&agrave; ảnh hưởng của họ đối với c&aacute;c thế hệ tương lai.</p>\r\n\r\n<p>&Ocirc;ng Sam v&agrave; vợ Helen đều l&agrave; những người Mỹ điển h&igrave;nh, sinh ra v&agrave; lớn l&ecirc;n ở c&aacute;c thị trấn nhỏ, với c&aacute;c gi&aacute; trị: coi trọng mối quan hệ gia đ&igrave;nh; rất s&ugrave;ng đạo Thi&ecirc;n Ch&uacute;a; phản đối chủ nghĩa duy vật; coi trọng sự phấn đấu c&aacute; nh&acirc;n; l&agrave;m từ thiện... Mối quan hệ giữa Sam v&agrave; vợ lu&ocirc;n h&ograve;a thuận, họ c&ugrave;ng trưởng th&agrave;nh, c&ugrave;ng gi&agrave; đi, sống đạm bạc. Ngay cả sau khi trở th&agrave;nh người gi&agrave;u nhất nước Mỹ năm 1985, &ocirc;ng vẫn quen mặc quần &aacute;o b&igrave;nh d&acirc;n v&agrave; l&aacute;i một chiếc xe b&aacute;n tải cũ.</p>\r\n\r\n<p>B&agrave; Helen sinh ra trong một gia đ&igrave;nh c&oacute; bố l&agrave;m ng&acirc;n h&agrave;ng, vốn xinh đẹp v&agrave; chỉ số th&ocirc;ng minh cao. Trong những ng&agrave;y đầu khởi nghiệp, Helen nhất quyết kh&ocirc;ng tham gia v&agrave;o điều h&agrave;nh m&agrave; đứng sau hỗ trợ chồng.</p>\r\n\r\n<p>Cả hai vợ chồng<a href=\"https://vnexpress.net/chu-de/nuoi-day-con-1147\" rel=\"dofollow\">&nbsp;nu&ocirc;i dạy con</a>&nbsp;nghi&ecirc;m khắc. &quot;T&ocirc;i kh&ocirc;ng c&oacute; điểm kiểm tra dưới A khi c&ograve;n đi học. C&aacute;c con cũng phải c&oacute; điểm kiểm tra A&quot;, Helen n&oacute;i. C&ograve;n &ocirc;ng Sam th&igrave; n&oacute;i: &quot;C&aacute;c con t&ocirc;i c&oacute; thể cảm thấy bị đối xử như n&ocirc; lệ khi c&ograve;n nhỏ. Tại sao kh&ocirc;ng để ch&uacute;ng l&agrave;m việc nh&agrave; v&agrave; hiểu được gi&aacute; trị của sức lao động&quot;.</p>\r\n\r\n<p>Robertson, con trai cả của Sam nhớ lại: &quot;Hồi đ&oacute; ch&uacute;ng t&ocirc;i lu&ocirc;n l&agrave;m việc ở cửa h&agrave;ng, lau s&agrave;n, dọn nh&agrave; cửa. Cha mẹ cho t&ocirc;i &iacute;t tiền ti&ecirc;u vặt hơn nhiều bạn b&egrave;, đ&ocirc;i khi khiến ch&uacute;ng t&ocirc;i c&ograve;n tưởng nh&agrave; m&igrave;nh ngh&egrave;o. Cha t&ocirc;i cũng y&ecirc;u cầu t&ocirc;i đầu tư tiền t&uacute;i v&agrave;o cửa h&agrave;ng. Tất nhi&ecirc;n, khoản đầu tư n&agrave;y sau đ&oacute; đ&atilde; gi&uacute;p t&ocirc;i trả tiền mua nh&agrave;&quot;.</p>\r\n\r\n<p>Trước khi qua đời, Sam đ&atilde; viết cuốn hồi k&yacute; với chỉ với mục đ&iacute;ch l&agrave; &quot;lưu giữ cho c&aacute;c ch&aacute;u v&agrave; chắt của m&igrave;nh, để ch&uacute;ng kh&ocirc;ng c&oacute; th&oacute;i quen ng&ocirc;ng cuồng&quot;.</p>\r\n\r\n<p>Năm 1992, Sam Walson qua đời. Hai c&acirc;u hỏi đặt ra trước mắt gia đ&igrave;nh Walton, một l&agrave; l&agrave;m thế n&agrave;o để giải quyết số vốn sở hữu, hai l&agrave; ai sẽ tiếp quản. Nhiều nh&agrave; quan s&aacute;t ph&acirc;n t&iacute;ch rằng gia đ&igrave;nh Walton c&oacute; thể chọn c&aacute;ch b&aacute;n 50% cổ phiếu Walmart của họ để lấy tiền mặt.</p>\r\n\r\n<p>Khi c&ograve;n sống, Sam từng b&agrave;y tỏ trăn trở khi thế hệ thứ hai gi&agrave;u c&oacute; của một số doanh nghiệp gia đ&igrave;nh duy tr&igrave; cuộc sống xa hoa bằng c&aacute;ch b&aacute;n cổ phần. &Ocirc;ng hy vọng c&aacute;c con m&igrave;nh ki&ecirc;n quyết nắm giữ t&agrave;i sản. &quot;Ch&uacute;ng ta kh&ocirc;ng cần qu&aacute; nhiều tiền, kh&ocirc;ng cần mua du thuyền hay đảo ri&ecirc;ng&quot;, &ocirc;ng dặn d&ograve;.</p>\r\n\r\n<p>V&igrave; vậy, hầu như tất cả t&agrave;i sản của gia đ&igrave;nh Walton đ&atilde; được duy tr&igrave; trong cổ phiếu của Walmart. Mười năm sau khi &ocirc;ng Sam qua đời, gi&aacute; trị thị trường của Walmart đ&atilde; tăng gần t&aacute;m lần. C&oacute; thể khẳng định nếu b&aacute;n cổ phần lấy tiền mặt, họ đ&atilde; kh&ocirc;ng thể đứng đầu trong danh s&aacute;ch gia tộc gi&agrave;u nhất thế giới l&acirc;u như thế.</p>\r\n\r\n<p>David Glass, cựu Gi&aacute;m đốc điều h&agrave;nh của Walmart, từng nhận x&eacute;t: &quot;Đ&acirc;y kh&ocirc;ng phải l&agrave; kh&aacute;i niệm m&agrave; mọi doanh nghiệp gia đ&igrave;nh đều c&oacute; thể c&oacute;. Nh&agrave; Walton c&oacute; tầm nh&igrave;n rất xa&quot;.</p>\r\n\r\n<p>Bốn người con của &ocirc;ng Sam, ngoại trừ con cả, ba người kh&aacute;c kh&ocirc;ng quan t&acirc;m đến ng&agrave;nh b&aacute;n lẻ. Trước khi qua đời, &ocirc;ng đ&atilde; thiết lập ch&iacute;nh s&aacute;ch đồng quản l&yacute; doanh nghiệp. Walmart kh&ocirc;ng c&oacute; nh&agrave; l&atilde;nh đạo tuyệt đối, m&agrave; chỉ c&oacute; l&atilde;nh đạo tập thể.</p>\r\n\r\n<p>Sự chuyển giao quyền lực su&ocirc;n sẻ của đế chế b&aacute;n lẻ một phần l&agrave; do gia đ&igrave;nh Walton lu&ocirc;n c&oacute; mối quan hệ h&ograve;a thuận. Mặt kh&aacute;c, cũng l&agrave; do &ocirc;ng Sam rất c&oacute; uy t&iacute;n trong c&ocirc;ng ty v&agrave; gia đ&igrave;nh. Nhiều năm sau c&aacute;i chết của &ocirc;ng, những &quot;c&acirc;y đại thụ&quot; của doanh nghiệp vẫn n&oacute;i về &quot;tư tưởng của Sam&quot; v&agrave; &quot;đường lối của Sam&quot; mỗi khi thảo luận những vấn đề lớn. Walmart đ&atilde; ph&aacute;t triển nhanh ch&oacute;ng trong gần 30 năm theo m&ocirc; h&igrave;nh l&atilde;nh đạo tập thể n&agrave;y.</p>\r\n\r\n<p><img alt=\"Ảnh: Walton Family Foundation\" src=\"https://i1-giadinh.vnecdn.net/2021/11/01/steuart-walton-4897-1635783159.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1QQRQisvIShVFQ3JJnc8HA\" /></p>\r\n\r\n<p>Steuart Walton, một luật sư v&agrave; doanh nh&acirc;n, con trai của Jim Walton - chủ ng&acirc;n h&agrave;ng, trở th&agrave;nh người thừa kế của Walmart hiện nay. Ảnh:&nbsp;<em>Walton Family Foundation</em></p>\r\n\r\n<p>Năm 2016, Steuart Walton (con trai Jim Walton) được chọn l&agrave; người tiếp theo ch&egrave;o l&aacute;i Walmart. Với tầm hiểu biết chiến lược trong lĩnh vực kinh doanh điện tử, Steuart nhanh ch&oacute;ng ph&aacute;t triển Walmart l&ecirc;n hơn 11.000 cửa h&agrave;ng tại khoảng 30 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ tr&ecirc;n thế giới.</p>\r\n\r\n<p>Steuart Walton cũng nối tiếp truyền thống của gia tộc với cuộc sống giản dị, trẻ trung. Luật sư v&agrave; doanh nh&acirc;n n&agrave;y c&oacute; niềm đam m&ecirc; s&acirc;u sắc với bộ m&ocirc;n leo n&uacute;i, thể thao mạo hiểm v&agrave; từ thiện.</p>\r\n\r\n<p>Ngoại trừ Steuart Walton, c&aacute;c th&agrave;nh vi&ecirc;n trẻ kh&aacute;c của gia tộc n&agrave;y kh&ocirc;ng mấy khi xuất hiện tr&ecirc;n mặt b&aacute;o. Họ chọn một cuộc sống k&iacute;n đ&aacute;o, b&igrave;nh dị ở thị trấn nhỏ giống như truyền thống gia đ&igrave;nh.</p>\r\n', 19, 7, 0, 1, 1),
(48, '6 điều nên làm khi gặp cướp', '6-Dieu-Nen-Lam-Khi-Gap-Cuop', '<p>Khi đối mặt với một vụ cướp, thay v&igrave; nổi kh&ugrave;ng l&ecirc;n hay &quot;ăn thua đủ&quot; với ch&uacute;ng, đ&acirc;y l&agrave; 6 điều bạn cần thực hiện nghi&ecirc;m t&uacute;c.</p>\r\n', 'doi-song-3.jpg', '2021-11-02 00:08:47', 1, '<p><strong>Đừng đ&oacute;ng vai anh h&ugrave;ng.</strong></p>\r\n\r\n<p>Tốt nhất bạn n&ecirc;n gạt bỏ &yacute; định l&agrave;m anh h&ugrave;ng sang một b&ecirc;n v&agrave; tr&aacute;nh to&agrave;n bộ những cuộc đối đầu. Những người cố gắng t&oacute;m gọn một t&ecirc;n cướp chỉ l&agrave;m t&igrave;nh h&igrave;nh căng thẳng v&agrave; khiến ch&uacute;ng trở n&ecirc;n bạo lực hơn. Bạn kh&ocirc;ng thể biết ch&uacute;ng giữ vũ kh&iacute; trong người hay kh&ocirc;ng, v&agrave; h&agrave;nh động chống lại của bạn v&ocirc; t&iacute;nh khiến họ nổi đi&ecirc;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/a2-1635498837.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=HuF_Zfbq2ijzb0OBGFmSIA\" /></p>\r\n\r\n<p><strong>Kh&ocirc;ng n&ecirc;n tạo ra c&aacute;c chuyển động đột ngột.</strong></p>\r\n\r\n<p>Mọi chuyển động bất ngờ đều c&oacute; thể bị nghi l&agrave; h&agrave;nh động chống đối. Th&ocirc;ng thường bọn cướp đ&atilde; rất nhạy cảm khi l&agrave;m điều xấu, việc di chuyển bất ngờ khiến ch&uacute;ng cảnh gi&aacute;c. Nếu muốn thay đổi vị tr&iacute; hoặc c&oacute; y&ecirc;u cầu g&igrave; h&atilde;y n&oacute;i trước để ch&uacute;ng biết trước.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/a3-1635498838.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=rj2TD27aZVLfhrxSbyi3qQ\" /></p>\r\n\r\n<p><strong>Ch&uacute; &yacute; quan s&aacute;t từng tiểu tiết.</strong></p>\r\n\r\n<p>H&atilde;y ch&uacute; &yacute; đến từng chi tiết nhỏ của c&aacute;c t&ecirc;n cướp như chiều cao, đặc điểm cơ thể, tư thế đứng, m&ugrave;i hương, gi&agrave;y, quần &aacute;o. Nhưng h&agrave;nh động n&agrave;y cần l&agrave;m cẩn trọng, tr&aacute;nh bị ph&aacute;t hiện. Th&ocirc;ng tin n&agrave;y c&oacute; thể gi&uacute;p cảnh s&aacute;t t&igrave;m ra tội phạm một c&aacute;ch nhanh nhất.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; h&atilde;y ghi nhớ những nơi ch&uacute;ng đ&atilde; chạm tay để hy vọng c&oacute; thể lấy được dấu v&acirc;n tay.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/a4-1635498839.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Q5CjAKM7g9I9JJBsDpJzUQ\" /></p>\r\n\r\n<p><strong>Th&ocirc;ng b&aacute;o trước về những điều sắp xảy ra.</strong></p>\r\n\r\n<p>Nếu c&oacute; cuộc hẹn trước với ai đ&oacute; bạn cần th&ocirc;ng b&aacute;o để t&ecirc;n cướp biết, tr&aacute;nh những trường hợp bạo lực c&oacute; thể xảy ra khi c&oacute; người xuất hiện đột ngột. Che giấu th&ocirc;ng tin c&oacute; thể khiến người đến &quot;sai nơi, sai thời điểm&quot; gặp nguy hiểm.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/a5-1635498839.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aPmq_ST4eazzlLLOiAFxzA\" /></p>\r\n\r\n<p><strong>Tr&aacute;nh tiếp x&uacute;c l&acirc;u bằng mắt.</strong></p>\r\n\r\n<p>Rất hữu &iacute;ch khi bạn quan s&aacute;t tỉ mỉ những chi tiết của t&ecirc;n cướp. Việc nh&igrave;n chằm chằm đầy lộ liễu sẽ khiến ch&uacute;ng nhận ra mục đ&iacute;ch của bạn.</p>\r\n\r\n<p>Một l&yacute; do kh&aacute;c để kh&ocirc;ng giao tiếp bằng mắt qu&aacute; l&acirc;u l&agrave; những t&ecirc;n cướp c&oacute; thể nghĩ bạn đang cố gắng nhận dạng ch&uacute;ng v&agrave; tr&igrave;nh b&aacute;o khi c&oacute; cơ hội.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/29/a6-1635498840.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9vqPGU9Cf9cdnwjF9ZkCXA\" /></p>\r\n\r\n<p><strong>Cung cấp mọi thứ được y&ecirc;u cầu.</strong></p>\r\n\r\n<p>Đa phần thứ bọn cướp mong muốn chỉ l&agrave; tiền. Sở cảnh s&aacute;t New Westminster, Mỹ khuy&ecirc;n c&aacute;c con tin n&ecirc;n giữ b&igrave;nh tĩnh v&agrave; tr&aacute;nh xảy ra xung đột với cướp. Bạn kh&ocirc;ng n&ecirc;n mạo hiểm cả mạng sống chỉ để giữ tiền, nhẫn hay kim cương. Tiền mất c&oacute; thể kiếm lại, nhưng kh&ocirc;ng ai mua lại được một cuộc sống mới hay cơ thể mới.</p>\r\n\r\n<p>Nếu kh&ocirc;ng đ&agrave;nh l&ograve;ng để mất tiền, h&atilde;y mang &iacute;t hơn hoặc chia nhỏ c&aacute;c t&agrave;i sản c&oacute; gi&aacute; trị ra nhiều t&uacute;i.</p>\r\n', 19, 7, 1, 1, 1),
(49, 'Màu sơn được dự đoán lên ngôi năm 2022', 'Mau-Son-Duoc-Du-Doan-Len-Ngoi-Nam-2022', '<p>Sherwin-Williams, c&ocirc;ng ty sản xuất sơn hơn 100 năm tuổi ở Mỹ, chọn m&agrave;u Evergreen Fog (xanh sương m&ugrave;) l&agrave;m &quot;M&agrave;u của năm 2022&quot;.</p>\r\n', 'doi-song-4.jpg', '2021-11-02 00:09:39', 1, '<p>Evergreen Fog được Sherwin-Williams m&ocirc; tả l&agrave; m&agrave;u xanh l&aacute; pha x&aacute;m, lấy cảm hứng từ thi&ecirc;n nhi&ecirc;n. Để chọn ra m&agrave;u n&agrave;y, đội ngũ chuy&ecirc;n gia m&agrave;u sắc v&agrave; thiết kế của c&ocirc;ng ty đ&atilde; ph&acirc;n t&iacute;ch một loại m&agrave;u sắc xu hướng.</p>\r\n\r\n<p><img alt=\"Màu Evergreen Fog (trên tường) được Sherwin-Williams chọn làm Màu của năm 2022. Ảnh: Sherwin-Williams\" src=\"https://i1-giadinh.vnecdn.net/2021/10/25/FAATeO5UcAMGv87-jpeg-2409-1635152611.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hOGnZQffFfm_d02YzEhC2A\" /></p>\r\n\r\n<p>M&agrave;u Evergreen Fog (tr&ecirc;n tường) được Sherwin-Williams chọn l&agrave;m &quot;M&agrave;u của năm&quot; 2022. Ảnh:&nbsp;<em>Sherwin-Williams</em></p>\r\n\r\n<p>&quot;Evergreen Fog l&agrave; m&agrave;u sắc gi&uacute;p kh&ocirc;ng gian tinh tế m&agrave; vẫn g&acirc;y ấn tượng&quot;, Sue Wadden, gi&aacute;m đốc tiếp thị m&agrave;u sắc của Sherwin-Williams n&oacute;i. &quot;N&oacute; gi&uacute;p ch&uacute;ng ta cảm thấy tươi mới hơn v&agrave; l&agrave; lựa chọn tuyệt vời cho cả nội thất lẫn ngoại thất hiện đại&quot;.</p>\r\n\r\n<p>Theo Wadden, Evergreen Fog ph&ugrave; hợp với những kh&ocirc;ng gian cần &quot;cảm gi&aacute;c t&aacute;i tạo&quot; như nh&agrave; ở, ph&ograve;ng kh&aacute;ch sạn, nh&agrave; h&agrave;ng v&agrave; văn ph&ograve;ng. Ri&ecirc;ng với nh&agrave; ở, gia chủ c&oacute; thể d&ugrave;ng m&agrave;u sắc n&agrave;y cho mọi khu vực, từ ph&ograve;ng kh&aacute;ch đến ph&ograve;ng tắm. Evergreen Fog dễ d&agrave;ng kết hợp với chất liệu tự nhi&ecirc;n v&agrave; c&aacute;c m&agrave;u sắc t&ocirc;ng đen hoặc t&ocirc;ng kim loại như m&agrave;u champagne, v&agrave;ng đồng.</p>\r\n\r\n<p><img alt=\"Sắc xanh xám của Evergreen Fog phù hợp với nhiều không gian nhà ở, từ phòng đến nhà tắm. Ảnh: Sherwin-Williams\" src=\"https://i1-giadinh.vnecdn.net/2021/10/25/Evergreen-Fog-SW-9130-living-r-6025-9732-1635152611.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YLvUeewsW-9TMwD41Xvk7Q\" /></p>\r\n\r\n<p>Sắc xanh x&aacute;m của Evergreen Fog ph&ugrave; hợp với nhiều kh&ocirc;ng gian nh&agrave; ở, từ ph&ograve;ng đến nh&agrave; tắm. Ảnh:&nbsp;<em>Sherwin-Williams</em></p>\r\n\r\n<p>Trước đ&acirc;y, c&aacute;c m&agrave;u mang sắc xanh l&aacute; v&agrave; x&aacute;m từng nhiều lần được Sherwin-Williams chọn l&agrave;m &quot;M&agrave;u của năm&quot;. V&iacute; dụ, năm 2012, c&ocirc;ng ty n&agrave;y đưa ra m&agrave;u xanh ngọc Argyle. Năm 2013, tới lượt Aloe, m&agrave;u bạc h&agrave; nhạt. Năm ngo&aacute;i, m&agrave;u x&aacute;m đậm Urbane Bronze l&ecirc;n ng&ocirc;i.</p>\r\n', 20, 7, 33, 1, 1),
(50, 'Mẫu bàn trà giúp phòng khách rộng hơn', 'Mau-Ban-Tra-Giup-Phong-Khach-Rong-Hon', '<p>Nếu diện t&iacute;ch ph&ograve;ng kh&aacute;ch kh&ocirc;ng lớn, gia chủ h&atilde;y lựa chọn b&agrave;n tr&agrave; bằng k&iacute;nh hoặc nhựa trong.</p>\r\n', 'doi-song-5.jpg', '2021-11-02 00:10:28', 1, '<p>Đ&acirc;y l&agrave; mẹo thường được c&aacute;c chuy&ecirc;n gia nội thất Mỹ sử dụng.</p>\r\n\r\n<p>&quot;Hầu hết b&agrave;n tr&agrave; ch&uacute;ng t&ocirc;i d&ugrave;ng khi trang tr&iacute; nh&agrave; cửa c&oacute; mặt k&iacute;nh v&agrave; ngăn đựng b&ecirc;n dưới&quot;, Peter Riolo, nh&agrave; m&ocirc;i giới bất động sản ở New York tiết lộ. Theo &ocirc;ng, b&agrave;n tr&agrave; loại n&agrave;y trong đem tới cảm gi&aacute;c nhẹ nh&agrave;ng đồng thời phản chiếu &aacute;nh s&aacute;ng n&ecirc;n khiến kh&ocirc;ng gian tr&ocirc;ng rộng r&atilde;i hơn.</p>\r\n\r\n<p><img alt=\"Bàn trà có mặt trong suốt sẽ giúp không gian trông rộng hơn. Ảnh: Visual Hunt\" src=\"https://i1-giadinh.vnecdn.net/2021/11/01/convenience-concepts-134036-ro-7026-7213-1635757302.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7uytuTBzL3ahWoQVoKyIqg\" /></p>\r\n\r\n<p>B&agrave;n tr&agrave; c&oacute; mặt trong suốt sẽ gi&uacute;p kh&ocirc;ng gian tr&ocirc;ng rộng hơn. Ảnh:&nbsp;<em>Visual Hunt</em></p>\r\n\r\n<p>Amy Monroe v&agrave; Katie Knitter, hai nh&agrave; thiết kế nội thất ki&ecirc;m điều h&agrave;nh dịch vụ trang tr&iacute; nh&agrave; cửa Becoming Home đồng t&igrave;nh với quan điểm tr&ecirc;n. &quot;B&agrave;n tr&agrave; mặt k&iacute;nh hoặc mặt nhựa c&oacute; khả năng mở rộng kh&ocirc;ng gian v&igrave; cho ph&eacute;p bạn nh&igrave;n thấy mặt s&agrave;n&quot;, họ n&oacute;i.</p>\r\n\r\n<p>Để tr&aacute;nh sự đơn điệu v&agrave; trống rỗng, gia chủ n&ecirc;n điểm xuyết một ch&uacute;t m&agrave;u sắc bằng c&aacute;ch b&agrave;y lọ hoa hoặc s&aacute;ch l&ecirc;n tr&ecirc;n. Lưu &yacute;, bạn kh&ocirc;ng n&ecirc;n d&ugrave;ng khăn trải b&agrave;n.</p>\r\n\r\n<p><img alt=\"Gia chủ có thể lựa chọn bàn trà tròn hoặc chữ nhật tùy vào căn phòng. Ảnh: hearstapps\" src=\"https://i1-giadinh.vnecdn.net/2021/11/01/6fd2dd1a569082ccd08167079f3b77-5145-3946-1635757302.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=moCX0U1a3C5CJYSO7zPc6Q\" /></p>\r\n\r\n<p>Gia chủ c&oacute; thể lựa chọn b&agrave;n tr&agrave; tr&ograve;n hoặc chữ nhật t&ugrave;y v&agrave;o căn ph&ograve;ng. Ảnh:&nbsp;<em>hearstapps</em></p>\r\n\r\n<p>Kiểu d&aacute;ng b&agrave;n tr&agrave; phụ thuộc v&agrave;o căn ph&ograve;ng. Một lựa chọn bạn c&oacute; thể c&acirc;n nhắc l&agrave; b&agrave;n tr&agrave; d&aacute;ng tr&ograve;n với đường k&iacute;nh 90 cm.</p>\r\n', 20, 7, 15, 1, 1),
(51, 'Việt Nam là một trong 16 quốc gia đa dạng sinh học cao nhất thế giới', 'Viet-Nam-La-Mot-Trong-16-Quoc-Gia-Da-Dang-Sinh-Hoc-Cao-Nhat-The-Gioi', '<p>&quot;Đ&aacute;nh gi&aacute; Đa dạng Sinh học tại Việt Nam&quot; c&ocirc;ng bố h&ocirc;m nay (1/11) cho thấy Việt Nam c&oacute; đa dạng sinh học cao nhưng đang bị suy tho&aacute;i do hoạt động khai th&aacute;c qu&aacute; mức.</p>\r\n', 'khoa-hoc-1.jpg', '2021-11-02 00:13:20', 1, '<p>B&aacute;o c&aacute;o được Tổ chức Quốc tế về Bảo tồn Thi&ecirc;n nhi&ecirc;n tại Việt Nam (WWF-Việt Nam) v&agrave; Cục Bảo tồn Thi&ecirc;n nhi&ecirc;n v&agrave; Đa dạng Sinh học (BCA) c&ocirc;ng bố.</p>\r\n\r\n<p>Theo đ&oacute; Việt Nam l&agrave; một trong 16 quốc gia sở hữu sự đa dạng sinh học cao nhất thế giới. Hơn 50.000 lo&agrave;i đ&atilde; được x&aacute;c định trong đ&oacute; c&oacute; 20.000 lo&agrave;i thực vật tr&ecirc;n cạn v&agrave; dưới nước, 10.500 động vật tr&ecirc;n cạn, 2.000 lo&agrave;i động vật kh&ocirc;ng xương sống v&agrave; c&aacute; nước ngọt, c&ugrave;ng hơn 11.000 lo&agrave;i sinh vật biển.</p>\r\n\r\n<p>B&aacute;o c&aacute;o n&ecirc;u, cũng như nhiều quốc gia kh&aacute;c, Việt Nam đang phải đối mặt với t&igrave;nh trạng suy tho&aacute;i đa dạng sinh học ng&agrave;y c&agrave;ng lớn. Trong đ&oacute; hoạt động sản xuất của con người (sản xuất n&ocirc;ng nghiệp, l&acirc;m nghiệp v&agrave; nu&ocirc;i trồng thuỷ sản...) ảnh hưởng rất lớn đối với suy giảm đa dạng sinh học tại Việt Nam.</p>\r\n\r\n<p>Từ c&aacute;c dữ liệu ph&acirc;n t&iacute;ch, b&aacute;o c&aacute;o cho thấy 21% c&aacute;c lo&agrave;i th&uacute;, 6,5% c&aacute;c lo&agrave;i chim, 19% c&aacute;c lo&agrave;i b&ograve; s&aacute;t, 24% c&aacute;c lo&agrave;i lưỡng cư, 38% c&aacute;c lo&agrave;i c&aacute;... đ&atilde; bị đe doạ.</p>\r\n\r\n<p><img alt=\"Cầy hương bị mắc bẫy dây Khu bảo tồn Sao la tỉnh Thừa Thiên-Huế. Ảnh: WWF\" src=\"https://i1-vnexpress.vnecdn.net/2021/11/01/bay-thu-7272-1635762523.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fmWCAZmjpu33Cvl9uT6MmA\" /></p>\r\n\r\n<p>Cầy hương bị mắc bẫy d&acirc;y Khu bảo tồn Sao la tỉnh Thừa Thi&ecirc;n-Huế. Ảnh:&nbsp;<em>WWF</em></p>\r\n\r\n<p>Trong gần 20 năm trở lại đ&acirc;y, c&aacute;c khu vực c&oacute; rừng l&agrave; sinh cảnh bị ảnh hưởng nhiều nhất với hơn 10.544 km2 diện t&iacute;ch đất rừng đ&atilde; bị mất, chủ yếu do chuyển đổi th&agrave;nh đất rừng trồng v&agrave; đất trồng c&acirc;y ăn quả. Khoảng 2,8 triệu ha rừng tự nhi&ecirc;n đ&atilde; bị mất do chuyển đổi sang ph&aacute;t triển c&aacute;c lo&agrave;i c&acirc;y trồng thương mại kh&aacute;c.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; b&aacute;o c&aacute;o đầu ti&ecirc;n trong chuỗi c&aacute;c hoạt động đ&aacute;nh gi&aacute; khoa học chỉ ra hiện trạng tổng quan về đa dạng sinh học tại Việt Nam, ph&acirc;n t&iacute;ch một số những nguy&ecirc;n nh&acirc;n trực tiếp v&agrave; gi&aacute;n tiếp dẫn tới sự suy giảm đa dạng sinh học. Từ đ&oacute;, khuyến nghị c&aacute;c m&ocirc; h&igrave;nh chuyển đổi c&oacute; t&iacute;nh t&iacute;ch cực cho thi&ecirc;n nhi&ecirc;n v&agrave; con người trong tương lai.</p>\r\n\r\n<p>&Ocirc;ng Vương Quốc Chiến, WWF-Việt Nam cho rằng, giải quyết c&aacute;c nguy&ecirc;n nh&acirc;n cốt l&otilde;i bằng c&aacute;ch lồng gh&eacute;p đa dạng sinh học trong tất cả c&aacute;c lĩnh vực sản xuất kinh tế đang được xem l&agrave; một trong những xu hướng tất yếu. Trong đ&oacute;, c&aacute;c doanh nghiệp sản xuất được xem l&agrave; những nh&acirc;n tố quan trọng đ&oacute;ng g&oacute;p để tạo ra sự thay đổi n&agrave;y. &quot;Năng lực, nhận thức v&agrave; sự chủ động của c&aacute;c doanh nghiệp khi tham gia c&aacute;c m&ocirc; h&igrave;nh cam kết tự nguyện hướng đến sản xuất bền vững sẽ l&agrave; yếu tố then chốt để g&oacute;p phần phục hồi đa dạng sinh học của Việt Nam&quot;, &ocirc;ng Chiến n&oacute;i.</p>\r\n', 21, 8, 0, 1, 1),
(52, 'Trí tuệ nhân tạo phủ sóng nhiều lĩnh vực, ngành nghề', 'Tri-Tue-Nhan-Tao-Phu-Song-Nhieu-Linh-Vuc,-Nganh-Nghe', '<p>Theo c&aacute;c chuyện gia, tr&iacute; tuệ nh&acirc;n tạo (AI) đang c&oacute; tốc độ ph&aacute;t triển nhanh, nếu biết khai th&aacute;c kết nối mở sẽ gi&uacute;p Việt Nam l&agrave;m chủ c&ocirc;ng nghệ.</p>\r\n', 'khoa-hoc-2.jpg', '2021-11-02 00:14:22', 1, '<p>Th&ocirc;ng tin được c&aacute;c chuy&ecirc;n gia chia sẻ tại ng&agrave;y hội Tr&iacute; tuệ nh&acirc;n tạo AI NOW: Academic &amp; Career do Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng v&agrave; tập đo&agrave;n Naver tổ chức s&aacute;ng 30/10.</p>\r\n\r\n<p>&Ocirc;ng Trần Trung Hiếu, Founder &amp; CEO TopCV, cho biết AI đang c&oacute; tốc độ ph&aacute;t triển nhanh ch&oacute;ng, được ứng dụng trong nhiều lĩnh vực ng&agrave;nh nghề. Tương lai ng&agrave;nh c&ocirc;ng nghiệp &aacute;p dụng AI ng&agrave;y c&agrave;ng rộng mở, trong đ&oacute; nổi trội về ứng dụng li&ecirc;n quan đến tự động h&oacute;a, c&aacute;c sản phẩm như xe tự l&aacute;i, nh&agrave; th&ocirc;ng minh dần trở n&ecirc;n ho&agrave;n thiện hơn dưới sự hỗ trợ của AI. &quot;Chỉ khoảng 3 năm nữa, tự động h&oacute;a sẽ trở n&ecirc;n phổ biến, hỗ trợ cuộc sống con người dễ chịu hơn&quot;, &ocirc;ng Hiếu n&oacute;i.</p>\r\n\r\n<p><img alt=\"Các diễn giả chia sẻ tại chương trình Ngày hội Trí tuệ nhân tạo AI NOW.\" src=\"https://i1-vnexpress.vnecdn.net/2021/10/30/untitled-1319-1635569361-16355-7110-6876-1635578517.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=guVSt5gNH3egTsv58trEPg\" /></p>\r\n\r\n<p>C&aacute;c diễn giả chia sẻ tại chương tr&igrave;nh (từ tr&aacute;i sang phải), &ocirc;ng Phạm Kim Cương, CEO Co-host AI; CEO TopCV &ocirc;ng Trần Trung Hiếu v&agrave; &ocirc;ng Nguyễn Hải Nam, Gi&aacute;m đốc FUNiX xSeries.</p>\r\n\r\n<p>CEO Co-host AI &ocirc;ng Phạm Kim Cương cho biết c&ocirc;ng nghệ AI gi&uacute;p ng&agrave;nh dịch vụ, kh&aacute;ch sạn hoạt động tối ưu hơn. &Ocirc;ng ph&acirc;n t&iacute;ch, ng&agrave;nh dịch vụ c&oacute; rất nhiều y&ecirc;u cầu, nh&acirc;n lực cần phải đ&agrave;o tạo qua nhiều bước, nhưng mọi thứ trở n&ecirc;n dễ d&agrave;ng hơn với AI. Th&ocirc;ng qua việc thu thập v&agrave; ph&acirc;n t&iacute;ch dữ liệu, c&ocirc;ng nghệ AI c&oacute; thể nắm bắt th&ocirc;ng tin sử dụng dịch vụ của kh&aacute;ch h&agrave;ng, từ đ&oacute; mang lại những giải ph&aacute;p ph&ugrave; hợp với nhu cầu.</p>\r\n\r\n<p>N&oacute;i về tiềm năng, &ocirc;ng Nguyễn Hải Nam, Gi&aacute;m đốc FUNiX xSeries, nguy&ecirc;n trưởng nh&oacute;m R&amp;D c&ocirc;ng ty Asilla Japan, nhận định AI sẽ tạo ra những thay đổi lớn trong lĩnh vực gi&aacute;o dục. &Ocirc;ng cho biết, AI sẽ l&agrave; nh&acirc;n tố ch&iacute;nh gi&uacute;p nền gi&aacute;o dục c&aacute; nh&acirc;n h&oacute;a. &quot;Mỗi người c&oacute; quỹ thời gian kh&aacute;c nhau, c&aacute; nh&acirc;n h&oacute;a với trợ l&yacute; ảo sẽ gi&uacute;p t&igrave;m được lộ tr&igrave;nh ph&ugrave; hợp với năng lực&quot;, &ocirc;ng n&oacute;i. AI cũng sẽ t&aacute;c động về mặt trải nghiệm gi&aacute;o dục v&agrave; t&igrave;m ra nội dung học tập ph&ugrave; hợp với bản th&acirc;n, tiếp cận tốt mục ti&ecirc;u.</p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia cho biết AI đang t&aacute;c động nhiều kh&iacute;a cạnh trong cuộc sống, trong đ&oacute; g&oacute;p phần gi&uacute;p kiểm so&aacute;t đại dịch Covid-19, như ứng dụng th&uacute;c đẩy điều chế v&agrave; thử nghiệm vaccine. &Ocirc;ng Trần Trung Hiếu đơn cử về việc AI ph&acirc;n t&iacute;ch dựa tr&ecirc;n dữ liệu tiếng ho ph&aacute;t hiện Covid-19, từ đ&oacute; gi&uacute;p truy vết v&agrave; dự đo&aacute;n đỉnh dịch, khoanh v&ugrave;ng dịch. &quot;Với những t&igrave;nh huống bất ngờ như Covid-19, sự hỗ trợ AI v&ocirc; c&ugrave;ng quan trọng, gi&uacute;p can thiệp, xử l&yacute; v&agrave; hỗ trợ con người trong đời sống&quot;, &ocirc;ng Hiếu n&oacute;i.</p>\r\n\r\n<p>Theo Diễn đ&agrave;n kinh tế thế giới, đến năm 2025 lực lượng lao động sẽ ph&acirc;n chia tỷ lệ con người (chiếm 48%) c&ograve;n m&aacute;y m&oacute;c v&agrave; thuật to&aacute;n (chiếm 52%). Tuy nhi&ecirc;n, c&aacute;c diễn giả cho rằng nguồn nh&acirc;n lực ng&agrave;nh AI, nhất l&agrave; con người, rất quan trọng trong thời kỳ c&aacute;ch mạng c&ocirc;ng nghiệp 4.0 hiện nay. &quot;Người ta cho rằng ứng dụng AI c&oacute; thể lấy đi c&ocirc;ng việc của con người, nhưng điều n&agrave;y c&oacute; thể mất tới 50 năm để thay thế. Tr&ecirc;n thực tế, con người sử dụng AI như c&ocirc;ng cụ hỗ trợ gi&uacute;p c&ocirc;ng việc dễ d&agrave;ng hơn, thời gian lao động &iacute;t hơn nhưng hiệu quả cao&quot;, &ocirc;ng n&oacute;i.</p>\r\n\r\n<p>&Ocirc;ng Cương cho biết th&ecirc;m, ngo&agrave;i chuy&ecirc;n gia ng&agrave;nh AI, những người sử dụng v&agrave; ứng dụng th&agrave;nh thạo tr&iacute; tuệ nh&acirc;n tạo cũng l&agrave; nguồn nh&acirc;n lực tiềm năng v&agrave; rất cần thiết ở ng&agrave;nh n&agrave;y. &quot;Với ng&agrave;nh kh&aacute;ch sạn dịch vụ, rất cần những người xử l&yacute; v&agrave; l&agrave;m sạch dữ liệu để sử dụng trong c&aacute;c doanh nghiệp lớn&quot;, &ocirc;ng n&oacute;i.</p>\r\n\r\n<p>Đồng quan điểm, &ocirc;ng Hải Nam cho biết c&aacute;c doanh nghiệp chuy&ecirc;n s&acirc;u về c&ocirc;ng nghệ, startup cũng c&oacute; nhu cầu lớn về nh&acirc;n lực về AI, trong đ&oacute; c&oacute; nh&oacute;m c&ocirc;ng việc li&ecirc;n quan đến t&agrave;i nguy&ecirc;n AI như kĩ sư dữ liệu...</p>\r\n', 21, 8, 438, 1, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(53, 'Gạch lưu trữ nhiệt năng', 'Gach-Luu-Tru-Nhiet-Nang', '<p>Gạch MGA được l&agrave;m từ nh&ocirc;m v&agrave; than ch&igrave;, c&oacute; độ bền ước t&iacute;nh 30 năm v&agrave; chứa nhiệt năng khoảng 1 kWh mỗi vi&ecirc;n.</p>\r\n', 'khoa-hoc-6.jpg', '2021-11-02 00:20:30', 1, '<p>Nh&oacute;m kỹ sư tại Đại học Newcastle, Australia, được cấp bằng s&aacute;ng chế cho loại vật liệu mới d&ugrave;ng để đ&oacute;ng th&agrave;nh gạch v&agrave; lưu trữ nhiệt năng,&nbsp;<em>Reuters</em>&nbsp;h&ocirc;m 27/10 đưa tin. Họ hy vọng sản phẩm n&agrave;y sẽ gi&uacute;p chuyển đổi hoạt động của c&aacute;c nh&agrave; m&aacute;y nhiệt điện than.</p>\r\n\r\n<p>Loại gạch mới mang t&ecirc;n Miscibility Gaps Alloy (MGA), được chế tạo từ nh&ocirc;m, than ch&igrave; v&agrave; c&oacute; thể lưu trữ năng lượng sản xuất từ c&aacute;c nguồn t&aacute;i tạo. Độ bền ước t&iacute;nh của ch&uacute;ng l&agrave; 30 năm. Mỗi vi&ecirc;n gạch nặng khoảng 6 kg v&agrave; chứa nhiệt năng khoảng 1 kWh. Kisi từ chối đưa ra mức gi&aacute; dự đo&aacute;n cho mỗi vi&ecirc;n.</p>\r\n\r\n<p>Theo Erich Kisi, nh&agrave; đồng s&aacute;ng chế gạch nhiệt năng, nh&oacute;m nghi&ecirc;n cứu nảy ra &yacute; tưởng về việc lưu trữ năng lượng khi đang nghi&ecirc;n cứu c&aacute;c bộ chuyển đổi ph&aacute;t xạ nhiệt - thiết bị tạo ra điện từ sức n&oacute;ng.</p>\r\n\r\n<p>&quot;Nguy&ecirc;n liệu quan trọng nhất của vi&ecirc;n gạch l&agrave; c&aacute;c hạt nh&ocirc;m gi&uacute;p cung cấp nhiệt ẩn (sức n&oacute;ng được hấp thụ hoặc giải ph&oacute;ng bởi một chất khi thay đổi trạng th&aacute;i, v&iacute; dụ từ lỏng sang kh&iacute;, trong khi nhiệt độ của chất đ&oacute; kh&ocirc;ng đổi). Ch&uacute;ng sẽ chảy ra rồi cứng lại h&agrave;ng ngh&igrave;n lần trong suốt v&ograve;ng đời của vi&ecirc;n gạch nhưng vẫn giữ nguy&ecirc;n vị tr&iacute;. Ch&uacute;ng giữ được vị tr&iacute; nhờ than ch&igrave;. Ch&uacute;ng t&ocirc;i c&oacute; c&aacute;c cơ chế kh&aacute;c nhưng than ch&igrave; l&agrave; th&agrave;nh phần ch&iacute;nh&quot;, Kisi n&oacute;i.</p>\r\n\r\n<p>Kisi l&agrave; CEO của MGA Thermal, c&ocirc;ng ty sản xuất gạch MGA. MGA Thermal đang hợp t&aacute;c với c&ocirc;ng ty E2S Power AG của Thụy Sĩ để sử dụng loại gạch mới trong việc cải tiến v&agrave; chuyển đổi mục đ&iacute;ch sử dụng c&aacute;c nh&agrave; m&aacute;y nhiệt điện than ở ch&acirc;u &Acirc;u. MGA c&oacute; thể nhận năng lượng từ c&aacute;c nguồn t&aacute;i tạo (như điện gi&oacute; v&agrave; điện mặt trời), lưu trữ năng lượng dưới dạng nhiệt năng một c&aacute;ch rẻ v&agrave; an to&agrave;n, sau đ&oacute; sử dụng năng lượng n&agrave;y để chạy c&aacute;c turbine hơi nước trong nh&agrave; m&aacute;y điện thay v&igrave; đốt than đ&aacute;.</p>\r\n', 23, 8, 234, 1, 1),
(54, 'Thiết bị lọc không khí bằng rêu', 'Thiet-Bi-Loc-Khong-Khi-Bang-Reu', '<p>Bộ lọc ngo&agrave;i trời CityTree kh&ocirc;ng chỉ khắc phục t&igrave;nh trạng &ocirc; nhiễm ở khu vực xung quanh m&agrave; c&ograve;n gi&uacute;p giảm nhiệt độ kh&ocirc;ng kh&iacute; xuống 2,5 độ C.</p>\r\n', 'khoa-hoc-7.jpg', '2021-11-02 00:21:44', 1, '<p>Nhằm giải quyết vấn đề &ocirc; nhiễm kh&ocirc;ng kh&iacute;, c&ocirc;ng ty Đức Green City Solutions ph&aacute;t triển bộ lọc ngo&agrave;i trời tận dụng khả năng của một trong những sinh vật lọc kh&iacute; sinh s&ocirc;i mạnh nhất của tự nhi&ecirc;n - r&ecirc;u,&nbsp;<em>Cnet&nbsp;</em>h&ocirc;m 23/10 đưa tin. R&ecirc;u chuyển đổi chất &ocirc; nhiễm th&agrave;nh sinh khối, ch&uacute;ng ăn chất &ocirc; nhiễm theo đ&uacute;ng nghĩa đen, theo Liang Wu, đồng s&aacute;ng lập c&ocirc;ng ty.</p>\r\n\r\n<p>Để thực hiện giải ph&aacute;p n&agrave;y, Green City Solutions thiết kế một m&ocirc;i trường ph&ugrave; hợp để r&ecirc;u ph&aacute;t triển trong th&agrave;nh phố. Thiết bị mới mang t&ecirc;n CityTree, được giới thiệu l&agrave; bộ lọc bụi mịn c&ocirc;ng nghệ sinh học đầu ti&ecirc;n tr&ecirc;n thế giới d&agrave;nh cho kh&ocirc;ng gian đ&ocirc; thị.</p>\r\n\r\n<p>CityTree c&oacute; thể giảm t&igrave;nh trạng &ocirc; nhiễm bụi mịn trong khu vực xung quanh tới 53%. Thiết bị n&agrave;y lọc 3.500 m3 kh&ocirc;ng kh&iacute; mỗi giờ, tương ứng với lượng kh&ocirc;ng kh&iacute; h&iacute;t thở mỗi giờ của khoảng 7.000 người. Ngo&agrave;i lọc bụi mịn, bộ lọc c&ograve;n gi&uacute;p giảm nhiệt độ kh&ocirc;ng kh&iacute; tới 2,5 độ C th&ocirc;ng qua sự bay hơi từ bề mặt r&ecirc;u. Để duy tr&igrave; khả năng lọc v&agrave; l&agrave;m m&aacute;t, thiết bị cần khoảng 4 l&iacute;t nước mỗi giờ v&agrave; 120 watt điện.</p>\r\n\r\n<p>CityTree cũng trang bị c&aacute;c cảm biến c&oacute; thể ph&aacute;t hiện loại &ocirc; nhiễm v&agrave; mức độ &ocirc; nhiễm của kh&ocirc;ng gian xung quanh. Dữ liệu n&agrave;y sẽ hữu &iacute;ch cho việc x&aacute;c định những nguồn g&acirc;y &ocirc; nhiễm ch&iacute;nh, gi&uacute;p sửa đổi ch&iacute;nh s&aacute;ch v&agrave; tổ chức c&aacute;c chiến dịch cộng đồng.</p>\r\n\r\n<p>Green City Solutions cũng cung cấp sản phẩm t&iacute;ch hợp m&agrave;n h&igrave;nh LCD mang t&ecirc;n CityBreeze để hiển thị quảng c&aacute;o hoặc c&aacute;c th&ocirc;ng b&aacute;o cho c&ocirc;ng ch&uacute;ng. H&atilde;ng n&agrave;y hy vọng trong tương lai, số tiền thu được từ quảng c&aacute;o c&oacute; thể b&ugrave; cho tiền thu&ecirc; thiết bị, gi&uacute;p những người sống ở khu vực xung quanh tận hưởng kh&ocirc;ng kh&iacute; sạch m&agrave; kh&ocirc;ng phải tự trả tiền. Một sản phẩm kh&aacute;c mang t&ecirc;n WallBreeze, bức tường r&ecirc;u dạng module, cũng đang được ph&aacute;t triển v&agrave; dự kiến ho&agrave;n thiện đầu năm sau.</p>\r\n\r\n<p>CityTree cũng c&oacute; một số hạn chế. Ở những nơi &ocirc; nhiễm trầm trọng, chất &ocirc; nhiễm c&oacute; thể ph&aacute; hủy r&ecirc;u. Liang cho biết, c&ocirc;ng ty chưa lắp đặt bộ lọc ở những nơi như vậy, nhưng &ocirc;ng tin rằng cuối c&ugrave;ng điều n&agrave;y c&oacute; thể hiện thực h&oacute;a nhờ tiến bộ c&ocirc;ng nghệ. Hiện tại, CityTree đ&atilde; xuất hiện ở một số th&agrave;nh phố ch&acirc;u &Acirc;u như Berlin, Oslo, London, Paris v&agrave; Cork.</p>\r\n', 23, 8, 192, 1, 1),
(55, 'Lần đầu tiên ghi hình gấu Bắc Cực truy sát tuần lộc', 'Lan-Dau-Tien-Ghi-Hinh-Gau-Bac-Cuc-Truy-Sat-Tuan-Loc', '<p>Gấu Bắc Cực nguy cấp đang chuyển sang săn tuần lộc tr&ecirc;n cạn thay v&igrave; hải cẩu do sự sụt giảm băng tr&ecirc;n biển.</p>\r\n', 'khoa-hoc-8.jpg', '2021-11-02 00:23:51', 1, '<p>C&aacute;c nh&agrave; sinh vật học ở Trạm địa cực Ba Lan tr&ecirc;n quần đảo Svalbard ở Na Uy chia sẻ video đầu ti&ecirc;n ghi lại qu&aacute; tr&igrave;nh gấu Bắc Cực săn v&agrave; ăn thịt tuần lộc, ghi h&igrave;nh v&agrave;o th&aacute;ng 8/2020. Khi đ&oacute; con gấu Bắc Cực c&aacute;i trưởng th&agrave;nh đi theo sau tuần lộc. Tuần lộc cố gắng trốn tho&aacute;t kẻ săn mồi bằng c&aacute;ch bơi tr&ecirc;n vịnh Isbj&oslash;rnhamna. Tuy nhi&ecirc;n, gấu Bắc Cực chỉ mất hơn một ph&uacute;t để đuổi kịp con mồi, hạ gục n&oacute; v&agrave; k&eacute;o v&agrave;o bờ.</p>\r\n\r\n<p>Nh&oacute;m nghi&ecirc;n cứu cho rằng h&agrave;nh vi n&agrave;y kh&ocirc;ng chỉ xuất ph&aacute;t từ sự suy giảm băng tr&ecirc;n biển m&agrave; cả do số lượng tuần lộc ở Bắc Cực tăng mạnh trong 3 thập kỷ qua v&agrave; dễ tiếp cận hơn đối với gấu Bắc Cực nguy cấp. Quần thể tuần lộc tăng l&ecirc;n từ năm 1973 khi ch&iacute;nh phủ li&ecirc;n bang đưa ch&uacute;ng v&agrave;o danh mục bảo vệ. Theo c&aacute;c nh&agrave; nghi&ecirc;n cứu, trước năm 2000, gấu Bắc Cực kh&ocirc;ng tấn c&ocirc;ng tuần lộc Svalbard. B&agrave;i b&aacute;o c&ocirc;ng bố h&ocirc;m 12/10 tr&ecirc;n tạp ch&iacute; Polar Biology l&agrave; t&agrave;i liệu ho&agrave;n chỉnh đầu ti&ecirc;n m&ocirc; tả to&agrave;n bộ h&agrave;nh vi săn tuần lộc trưởng th&agrave;nh của một con gấu Bắc Cực ở Hornsund, ph&iacute;a t&acirc;y nam Spitsbergen.</p>\r\n\r\n<p>B&agrave;i b&aacute;o cũng bao gồm &iacute;t nhất 12 b&aacute;o c&aacute;o từ những người qua đường từng chứng kiến gấu Bắc Cực săn hoặc ăn thịt tuần lộc. Nh&oacute;m nghi&ecirc;n cứu cho biết c&oacute; bằng chứng cho thấy c&aacute;c vụ tấn c&ocirc;ng ng&agrave;y c&agrave;ng phổ biến. Họ ghi h&igrave;nh cuộc tấn c&ocirc;ng h&ocirc;m 21/8/2000 nhưng giờ đ&acirc;y mới chia sẻ video. Con gấu Bắc Cực xuất hiện c&aacute;ch Trạm địa cực Ba Lan khoảng 100 m. Một số nh&agrave; khoa học đi ra ngo&agrave;i để quan s&aacute;t con vật hoang d&atilde; r&otilde; hơn. Họ nhận thấy n&oacute; tiến về ph&iacute;a bờ biển vịnh Isbj&oslash;rnhamna, nơi một nh&oacute;m tuần lộc đang gặm cỏ.</p>\r\n\r\n<p>Phần lớn tuần lộc ph&acirc;n t&aacute;n rải r&aacute;c, nhưng một con trong số đ&oacute; thu h&uacute;t sự ch&uacute; &yacute; của con gấu. Tuần lộc mạo hiểm bơi ra vịnh. Gấu Bắc Cực tăng tốc đuổi theo v&agrave; nhảy xuống l&agrave;n nước lạnh theo con mồi tiềm năng. Cuối c&ugrave;ng, n&oacute; bắt kịp tuần lộc khi ở c&aacute;ch bờ khoảng 25 m. N&oacute; d&ugrave;ng m&oacute;ng vuốt b&aacute;m v&agrave;o h&ocirc;ng tuần lộc, buộc con mồi dừng bơi v&agrave; leo l&ecirc;n lưng để d&igrave;m chết n&oacute;.</p>\r\n\r\n<p>&quot;Con mồi v&agrave; kẻ tấn c&ocirc;ng vật lộn một l&uacute;c. Mỗi con vật đều lu&acirc;n phi&ecirc;n ngoi l&ecirc;n mặt nước. Sau đ&oacute;, gấu Bắc Cực t&oacute;m được cổ tuần lộc từ b&ecirc;n dưới v&agrave; giết chết n&oacute; trong v&ograve;ng một ph&uacute;t. N&oacute; lật ngược cơ thể tuần lộc, d&igrave;m con mồi dưới nước nhiều lần trong khoảng 15 ph&uacute;t mặc d&ugrave; tuần lộc đ&atilde; chết&quot;, nh&oacute;m nghi&ecirc;n cứu m&ocirc; tả.</p>\r\n\r\n<p>Sau cuộc chiến sinh tử, gấu Bắc Cực k&eacute;o cơ thể bất động của tuần lộc v&agrave;o bờ v&agrave; bắt đầu x&eacute; x&aacute;c con mồi. Tiếp đ&oacute;, n&oacute; d&ugrave;ng những vi&ecirc;n đ&aacute; che phủ phần x&aacute;c c&ograve;n lại để ngăn những kẻ săn mồi kh&aacute;c tiếp cận, lựa chọn địa điểm gần đ&oacute; để ngủ trong suốt 12 giờ v&agrave; quay trở lại với bữa ăn. Tuy nhi&ecirc;n, c&aacute;o v&agrave; chim ch&oacute;c vẫn tranh thủ rỉa phần x&aacute;c c&ograve;n lại trong l&uacute;c con gấu ngủ. Đến trưa ng&agrave;y h&ocirc;m sau, con gấu lu&acirc;n phi&ecirc;n nằm gần c&aacute;i x&aacute;c hoặc ăn phần thịt c&ograve;n lại. N&oacute; đ&atilde; ăn hết 80% thịt tuần lộc.</p>\r\n', 22, 8, 231, 1, 1),
(56, 'Thần ưng California lần đầu sinh sản không cần con đực', 'Than-Ung-California-Lan-Dau-Sinh-San-Khong-Can-Con-Duc', '<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu v&ocirc; c&ugrave;ng bất ngờ khi ph&aacute;t hiện hiện tượng trinh sản ở thần ưng California cực kỳ nguy cấp.</p>\r\n', 'khoa-hoc-9.jpg', '2021-11-02 00:24:50', 1, '<p>Trong nhiều thập kỷ, c&aacute;c nh&agrave; khoa học t&igrave;m c&aacute;ch cứu thần ưng California khỏi bờ vực tuyệt chủng. To&agrave;n bộ quần thể lo&agrave;i chim n&agrave;y giảm xuống 22 con năm 1982. Năm 2019, nỗ lực nh&acirc;n giống nu&ocirc;i nhốt v&agrave; thả về tự nhi&ecirc;n gi&uacute;p số lượng thần ưng California chậm r&atilde;i tăng l&ecirc;n hơn 500 con. Qu&aacute; tr&igrave;nh tr&ecirc;n đ&ograve;i hỏi quản l&yacute; cẩn thận chim nu&ocirc;i nhốt, lựa chọn kỹ c&agrave;ng con đực v&agrave; con c&aacute;i giao phối để sinh ra chim non khỏe mạnh.</p>\r\n\r\n<p>Khi xem x&eacute;t kỹ hơn dữ liệu di truyền, nh&agrave; nghi&ecirc;n cứu Oliver Ryder v&agrave; cộng sự Leona Chemnick ph&aacute;t hiện hai con chim đực c&oacute; số hiệu SB260 v&agrave; SB517 kh&ocirc;ng c&oacute; yếu tố di truyền n&agrave;o giống những con chim được cho l&agrave; cha của ch&uacute;ng. N&oacute;i c&aacute;ch kh&aacute;c, ch&uacute;ng ra đời th&ocirc;ng qua &quot;trinh sản&quot;, theo nghi&ecirc;n cứu c&ocirc;ng bố tr&ecirc;n tạp ch&iacute; Heredity h&ocirc;m 28/10. Hiện tượng sinh sản v&ocirc; t&iacute;nh như vậy thường xảy ra ở lo&agrave;i sinh sản hữu t&iacute;nh khi một số tế b&agrave;o hoạt động giống tinh tr&ugrave;ng v&agrave; hợp nhất với trứng của c&aacute; thể c&aacute;i.</p>\r\n\r\n<p>D&ugrave; hiếm gặp ở động vật c&oacute; xương sống, trinh sản từng được ghi nhận ở c&aacute; mập, c&aacute; đuối v&agrave; thằn lằn. C&aacute;c nh&agrave; khoa học cũng quan s&aacute;t hiện tượng tự thụ tinh ở v&agrave;i lo&agrave;i chim nu&ocirc;i nhốt như g&agrave; t&acirc;y, g&agrave; nh&agrave; v&agrave; chim c&uacute;t ngực lam, khi con c&aacute;i được nu&ocirc;i trong m&ocirc;i trường kh&ocirc;ng c&oacute; con đực. Nhưng đ&acirc;y l&agrave; lần đầu ti&ecirc;n giới nghi&ecirc;n cứu bắt gặp trinh sản ở thần ưng California. Theo Ryder, điều đặc biệt kỳ lạ l&agrave; SB260 v&agrave; SB517 kh&aacute;c mẹ. Mẹ của ch&uacute;ng được nu&ocirc;i c&ugrave;ng với con đực. Cả hai con chim mẹ đều sinh sản th&agrave;nh c&ocirc;ng c&ugrave;ng con đực trước v&agrave; sau đ&oacute;.</p>\r\n\r\n<p>Chỉ c&oacute; khoảng 300 con thần ưng cực kỳ nguy cấp sinh sống ở California, Arizona, v&agrave; Utah. Với số lượng thấp như vậy, c&oacute; thể thần ưng sử dụng trinh sản như một c&ocirc;ng cụ sinh tồn, theo Reshma Ramachandran, nh&agrave; sinh l&yacute; học sinh sản v&agrave; vi sinh vật học ở Đại học Mississippi. C&oacute; bằng chứng ở c&aacute;c lo&agrave;i kh&aacute;c cho thấy trinh sản l&agrave; &quot;phao cứu mạng&quot; của động vật sắp tuyệt chủng. V&iacute; dụ, c&aacute; kiếm răng nhỏ cực kỳ nguy cấp chuyển sang trinh sản do ng&agrave;y c&agrave;ng kh&oacute; t&igrave;m được bạn t&igrave;nh trong tự nhi&ecirc;n.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, giả thuyết tr&ecirc;n c&oacute; thể kh&ocirc;ng đ&uacute;ng với thần ưng California. Đầu ti&ecirc;n, chim c&aacute;i nu&ocirc;i nhốt c&oacute; thể gặp gỡ bạn t&igrave;nh. Thứ hai, kh&ocirc;ng con non n&agrave;o ra đời qua trinh sản sống s&oacute;t đến tuổi thuần thục. SB260 chết sau chưa đầy hai năm trong khi SB517 chết trước khi 8 tuổi. Một số con thần ưng California c&oacute; thể sống tới 60 tuổi. Do c&aacute;c nh&agrave; khoa học kiểm tra nguy cơ rối loạn di truyền rất cẩn thận khi nh&acirc;n giống thần ưng nu&ocirc;i nhốt, c&oacute; thể chim non mang đột biến gene khiến ch&uacute;ng chết sớm.</p>\r\n\r\n<p>Đầu năm nay, Robinson, Ryder, v&agrave; cộng sự c&ocirc;ng bố nghi&ecirc;n cứu n&ecirc;u chi tiết to&agrave;n bộ hệ gene của thần ưng California. Trong tương lai, dữ liệu di truyền qu&yacute; gi&aacute; đ&oacute; c&oacute; thể gi&uacute;p ch&uacute;ng ta hiểu r&otilde; hơn trinh sản diễn ra như thế n&agrave;o ở lo&agrave;i vật n&agrave;y. Theo Ramachandran, d&ugrave; trinh sản được ghi nhận chủ yếu ở động vật nu&ocirc;i nhốt, kh&ocirc;ng c&oacute; l&yacute; do n&agrave;o để kết luận hiện tượng kh&ocirc;ng xảy ra trong tự nhi&ecirc;n.</p>\r\n', 22, 8, 343, 1, 1),
(57, 'Song Hye Kyo được mẹ khuyên chăm da từ sớm', 'Song-Hye-Kyo-Duoc-Me-Khuyen-Cham-Da-Tu-Som', '<p>Mẹ Song Hye Kyo nhắc con g&aacute;i cần chống l&atilde;o h&oacute;a từ khi c&ograve;n trẻ, bởi nghề diễn dễ l&agrave;m tổn hại l&agrave;n da.</p>\r\n', 'giai-tri-10.jpg', '2021-11-02 00:28:15', 1, '<p>Ở tuổi 40, Song Hye Kyo nhận nhiều lời khen về nhan sắc, được mời l&agrave;m gương mặt đại diện cho c&aacute;c thương hiệu mỹ phẩm h&agrave;ng đầu H&agrave;n Quốc. Tr&ecirc;n&nbsp;<em>Instagram&nbsp;</em>c&aacute; nh&acirc;n, kh&aacute;n giả&nbsp;<a href=\"https://vnexpress.net/fan-khen-song-hye-kyo-tre-nhu-doi-muoi-4272205.html\" rel=\"dofollow\" target=\"_blank\">b&igrave;nh luận</a>&nbsp;dưới những bức ảnh của c&ocirc;: &quot;Như thiếu nữ đ&ocirc;i mươi. Kh&ocirc;ng tin nổi c&ocirc; ấy đ&atilde; bước v&agrave;o tuổi 40&quot;, &quot;Hye Kyo ng&agrave;y c&agrave;ng trẻ đẹp&quot;, &quot;L&agrave;n da căng mịn, trắng s&aacute;ng&quot;...</p>\r\n\r\n<p><img alt=\"Nhan sắc Song Hye Kyo ở tuổi 40.\" src=\"https://i1-giaitri.vnecdn.net/2021/10/12/SHK-2-Insta-SongHyeKyo-6091-1634050587.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=w9WwM-5KotYt5J4h_Yf1Dg\" /></p>\r\n\r\n<p>Nhan sắc Song Hye Kyo ở tuổi 40.</p>\r\n\r\n<p>Theo trang&nbsp;<em>Today&nbsp;</em>(Singapore), nữ diễn vi&ecirc;n tập th&oacute;i quen chăm s&oacute;c da tỉ mỉ từ mẹ. &quot;Mẹ t&ocirc;i lu&ocirc;n n&oacute;i phụ nữ cần chống l&atilde;o h&oacute;a c&agrave;ng sớm c&agrave;ng tốt, nhất l&agrave; khi c&ocirc;ng việc của t&ocirc;i thường xuy&ecirc;n phải phơi nắng hoặc tiếp x&uacute;c dưới &aacute;nh s&aacute;ng mạnh của đ&egrave;n phim trường, studio&quot;, c&ocirc; n&oacute;i. Song Hye Kyo cho biết như bao phụ nữ kh&aacute;c, da dẻ c&ocirc; cũng xuống cấp từng ch&uacute;t theo năm th&aacute;ng. Nhưng c&ocirc; hạn chế những thay đổi n&agrave;y ở mức tối thiểu bằng c&aacute;ch dưỡng từ trong ra ngo&agrave;i.</p>\r\n\r\n<p>Song Hye Kyo cũng cho rằng c&ocirc;ng việc ảnh hưởng nhiều đến nhan sắc của c&ocirc;. Kh&ocirc;ng chỉ phải trang điểm d&agrave;y, c&ocirc; c&ograve;n di chuyển li&ecirc;n tục bằng m&aacute;y bay khiến da dễ bị kh&ocirc;, thiếu sức sống. Để giải quyết vấn đề, c&ocirc; bổ sung ẩm bằng c&aacute;c loại mỹ phẩm c&oacute; khả năng thẩm thấu nhanh như essence khi bay. C&ocirc; ưu ti&ecirc;n để mặt mộc bất cứ l&uacute;c n&agrave;o c&oacute; thể. Nếu đi chơi với bạn b&egrave; hoặc dạo phố, c&ocirc; cũng chọn lớp nền mỏng nhẹ nhất.</p>\r\n\r\n<p><img alt=\"Diễn viên cho biết làn da cô đã có những dấu hiện lão hóa, nhưng cô cải thiện bằng cách chăm sóc kỹ hàng ngày.\" src=\"https://i1-giaitri.vnecdn.net/2021/10/12/Screen-Shot-2021-10-12-at-9-38-4810-9044-1634050587.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=R6RRlxBRt5I-m_qFJrsxsw\" /></p>\r\n\r\n<p>Diễn vi&ecirc;n cho biết l&agrave;n da c&ocirc; đ&atilde; c&oacute; những dấu hiện l&atilde;o h&oacute;a, nhưng c&ocirc; cải thiện bằng c&aacute;ch chăm s&oacute;c kỹ h&agrave;ng ng&agrave;y.<br />\r\nSong Hye Kyo sinh năm 1981 tại Daegu, H&agrave;n Quốc. Sau chiến thắng trong cuộc thi người mẫu tuổi teen năm 1995, c&ocirc; gia nhập l&agrave;ng giải tr&iacute; với vai tr&ograve; mẫu ảnh. Người đẹp ghi dấu với<em>Tr&aacute;i tim m&ugrave;a thu, Một cho tất cả, Ng&ocirc;i nh&agrave; hạnh ph&uacute;c, Gi&oacute; m&ugrave;a đ&ocirc;ng năm ấy, Hậu duệ mặt trời</em>... C&ocirc; sở hữu nhiều bất động sản gi&aacute; trị tại H&agrave;n v&agrave; New York (Mỹ). Trước Song Joong Ki, c&ocirc; từng hẹn h&ograve; Lee Byung Hun, Hyun Bin.</p>\r\n\r\n<p>Theo&nbsp;<em>South China Morning Post</em>, kể cả khi lịch l&agrave;m việc d&agrave;y đặc, c&ocirc; vẫn cố gắng dưỡng da b&ugrave; v&agrave;o ng&agrave;y nghỉ. Sau bước tẩy trang, rửa mặt sạch, người đẹp d&ugrave;ng &quot;nước thần&quot; c&oacute; th&agrave;nh phần nh&acirc;n s&acirc;m để da hấp thu tốt nhất. Tiếp theo, c&ocirc; lần lượt b&ocirc;i serum, kem dưỡng v&agrave; kết th&uacute;c quy tr&igrave;nh bằng một lớp mặt nạ giấy. Thay v&igrave; d&ugrave;ng nhiều loại, ng&ocirc;i sao&nbsp;<em>Hậu duệ mặt trời&nbsp;</em>đầu tư cho c&aacute;c sản phẩm cao cấp. &quot;Nước thần&quot; c&ocirc; d&ugrave;ng c&oacute; gi&aacute; khoảng s&aacute;u triệu đồng, mặt nạ giấy khoảng 600 ngh&igrave;n đồng.</p>\r\n\r\n<p>Hye Kyo lu&ocirc;n ch&uacute; trọng c&aacute;ch trang điểm để tr&ocirc;ng tươi tắn, tự nhi&ecirc;n. C&ocirc; giữ d&aacute;ng ch&acirc;n m&agrave;y vốn c&oacute;, tối giản kẻ mắt, mascara v&agrave; chọn c&aacute;c m&agrave;u son t&ocirc;ng hồng nhạt hoặc hồng tươi. Thỉnh thoảng, người đẹp cũng thử lối trang điểm đậm như đ&aacute;nh mắt m&egrave;o, mắt kh&oacute;i... Tuy nhi&ecirc;n, những trải nghiệm n&agrave;y thường tạo ra nhiều&nbsp;<a href=\"https://vnexpress.net/song-hye-kyo-trang-diem-dam-4090686.html\" rel=\"dofollow\" target=\"_blank\">tranh c&atilde;i</a>&nbsp;tr&ecirc;n c&aacute;c diễn đ&agrave;n bởi kh&aacute;n giả vốn quen thuộc với h&igrave;nh ảnh trong trẻo của c&ocirc;.</p>\r\n\r\n<p><strong>B&ecirc;n cạnh mỹ phẩm, nữ diễn vi&ecirc;n cũng theo đuổi lối sống l&agrave;nh mạnh để duy tr&igrave; sự trẻ trung.&nbsp;</strong>Tr&ecirc;n trang&nbsp;<em>Allkpop,&nbsp;</em>c&ocirc;<em>&nbsp;</em>cho biết tập thể thao mỗi ng&agrave;y. Trước đ&acirc;y, c&ocirc; rất lười vận động, chỉ khi bước qua tuổi 30 mới nhận thấy m&igrave;nh cần tập luyện nhiều hơn. Người đẹp ăn &iacute;t muối, tập aerobic v&agrave; yoga. L&uacute;c rảnh, c&ocirc; c&ograve;n ra s&ocirc;ng H&agrave;n chạy bộ. &quot;L&agrave;n da thực sự được cải thiện khi ch&uacute;ng ta tập luyện v&agrave; đổ mồ h&ocirc;i&quot;, c&ocirc; n&oacute;i.</p>\r\n\r\n<p>Tuy ch&uacute; trọng vẻ ngo&agrave;i, Song Hye Kyo vẫn cho rằng sức sống thực sự n&ecirc;n đến từ t&acirc;m hồn. Trả lời phỏng vấn của tạp ch&iacute;&nbsp;<em>Harper&#39;s Bazaar Taiwan&nbsp;</em>v&agrave;o th&aacute;ng 4, c&ocirc; n&oacute;i cảm thấy kh&ocirc;ng qu&aacute; kh&aacute;c so với những năm mới v&agrave;o nghề: &quot;Trước khi biến th&agrave;nh b&agrave; l&atilde;o, t&ocirc;i muốn được sống với t&acirc;m hồn tự do. Dẫu ngoại h&igrave;nh c&oacute; thay đổi hay kh&ocirc;ng, t&ocirc;i vẫn l&agrave; t&ocirc;i như ng&agrave;y 20 tuổi&quot;.</p>\r\n', 13, 4, 168, 1, 1),
(59, 'Căn hộ phong cách nhà nông trại phương Tây', 'Can-Ho-Phong-Cach-Nha-Nong-Trai-Phuong-Tay', '<p>Phong c&aacute;ch farmhouse kết hợp với sự điều chỉnh về bố cục gi&uacute;p căn hộ cũ kỹ &quot;lột x&aacute;c&quot; th&agrave;nh kh&ocirc;ng gian sống tươi s&aacute;ng cho gia đ&igrave;nh trẻ.</p>\r\n', 'doi-song-10.png', '2021-11-01 10:00:00', 1, '<p>Căn hộ rộng hơn 170 m2 ở quận Thanh Xu&acirc;n được cải tạo cho một gia đ&igrave;nh trẻ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/247254181-2290359637778145-6351953339793145495-n-jpeg.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3aGR304Q9lT2cfLhRleTGw\" /></p>\r\n\r\n<p>Trước đ&oacute;, căn hộ đ&atilde; cũ kỹ v&agrave; kh&ocirc;ng c&ograve;n hợp l&yacute; với nhu cầu người sử dụng. Diện t&iacute;ch d&ugrave; rộng nhưng c&aacute;c kh&ocirc;ng gian bố tr&iacute; kh&ocirc;ng tương xứng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB04997_1635323694.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=xZ6-1CxTEzK0AFLZ6NkSyQ\" /></p>\r\n\r\n<p>Sau ba th&aacute;ng thi c&ocirc;ng, căn hộ như &quot;lột x&aacute;c&quot; ho&agrave;n to&agrave;n. Thiết kế mới theo phong c&aacute;ch farmhouse (nh&agrave; n&ocirc;ng trại) bởi gia chủ muốn tổ ấm ph&aacute; c&aacute;ch m&agrave; kh&ocirc;ng cầu kỳ, ph&ocirc; trương.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB04928_1635323761.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_C2SJ92DvzTj8zILRXUzWw\" /></p>\r\n\r\n<p>Phong c&aacute;ch farmhouse hướng đến sự ấm c&uacute;ng, giản dị, th&acirc;n thiện, thường sử dụng nội thất gỗ kiểu cổ điển phương T&acirc;y.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB04934.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zBngWkUSB11zkQbThhpRFw\" /></p>\r\n\r\n<p>Yếu tố &aacute;nh s&aacute;ng v&agrave; kh&ocirc;ng gian sinh hoạt chung đặc biệt được ch&uacute; trọng. Ph&ograve;ng kh&aacute;ch li&ecirc;n th&ocirc;ng với ph&ograve;ng ăn l&agrave; chỗ gia đ&igrave;nh qu&acirc;y quần xem phim, d&ugrave;ng bữa hoặc đ&oacute;n những sự kiện đặc biệt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB04961-1635321168.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9vWEvJ_5BTeu8GjNKb3_EA\" /></p>\r\n\r\n<p>Bếp chuyển từ dạng chữ L sang dạng chữ U, tăng diện t&iacute;ch lưu trữ, gi&uacute;p kh&ocirc;ng gian ngăn nắp. Kiểu bếp chữ U cũng cho ph&eacute;p nhiều người thao t&aacute;c c&ugrave;ng l&uacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB05099-1635318706.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KPAs1a1QWhGTVYa8Dcykdg\" /></p>\r\n\r\n<p>Phong c&aacute;ch farmhome thường lấy m&agrave;u trắng l&agrave;m m&agrave;u chủ đạo. Tuy nhi&ecirc;n, gia chủ muốn căn hộ th&ecirc;m tươi mới n&ecirc;n nh&oacute;m thiết kế đ&atilde; th&ecirc;m v&agrave;o những m&agrave;u sắc như blue green (xanh dương pha xanh l&aacute;) ở tủ bếp v&agrave; tủ rượu.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB05024.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TUYtnFoyth2rbmyeTOxNYw\" /></p>\r\n\r\n<p>Đồ nội thất c&ograve;n nhấn nh&aacute; th&ecirc;m chất Anh với phần ghế b&agrave;nh d&ugrave;ng vải họa tiết hoa văn nổi thay v&igrave; để m&agrave;u trơn như phong c&aacute;ch farmhouse th&ocirc;ng thường.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB05433-1635318784.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=D03Jgc_qezghw-hhKmH95w\" /></p>\r\n\r\n<p>So với thiết kế cũ, ph&ograve;ng ngủ master cũng được thay đổi.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB05448-1635322108.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dIOXN0HfH3jFHlP6khuU4Q\" /></p>\r\n\r\n<p>Ph&ograve;ng tắm lớn trước kia được thu nhỏ, nhường chỗ cho kh&ocirc;ng gian thay đồ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB05418-1635322132.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Jly8_60ETcp_X7JrHUreew\" /></p>\r\n\r\n<p>Hệ tủ trong ph&ograve;ng tắm tạo sự đồng bồ với kh&ocirc;ng gian b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-giadinh.vnecdn.net/2021/10/27/ABB05123_1635325367.png?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bNcYvlXXouaPdUmMhL1DEQ\" /></p>\r\n\r\n<p>To&agrave;n bộ nội thất được thiết kế v&agrave; gia c&ocirc;ng ri&ecirc;ng. Chi ph&iacute; ho&agrave;n thiện căn hộ khoảng 900 triệu đồng.</p>\r\n', 20, 7, 284, 1, 1);

